        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPPAR5--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGG3,ISUBRO,INAME
   52   FORMAT('IBUGG3,ISUBRO = ',A4,2X,A4,2X,A40)
