          XL=(Y(I) - KSLOC)/KSSCAL
          CALL BU9PDF(DBLE(XL),DBLE(SHAPE1),DBLE(SHAPE2),DXOUT)
          Y2(I)=REAL(DXOUT)
          Y2(I)=Y2(I)/KSSCAL
 2520   CONTINUE
C
      ELSEIF(ICASPL.EQ.'BU10')THEN
        DO2530I=1,N
          XL=(Y(I) - KSLOC)/KSSCAL
          IF(XL.GT.0.0)THEN
