              CALL LEXPDF(DBLE(XL),DBLE(SHAPE1),DXOUT)
              Y2(I)=REAL(DXOUT)
              Y2(I)=Y2(I)/KSSCAL
            ENDIF
          ELSE
            IF(XL.GE.0.0)THEN
              CALL LEXPDF(DBLE(XL),DBLE(SHAPE1),DXOUT)
              Y2(I)=REAL(DXOUT)
              Y2(I)=Y2(I)/KSSCAL
            ENDIF
          ENDIF
 2610   CONTINUE
C
      ELSEIF(ICASPL.EQ.'TPAR')THEN
        DO2620I=1,N
          XL=(Y(I) - KSLOC)/KSSCAL
          CALL TNPPDF(DBLE(XL),DBLE(SHAPE1),DBLE(SHAPE2),
     1                DBLE(SHAPE3),DXOUT)
          Y2(I)=REAL(DXOUT)
          Y2(I)=Y2(I)/KSSCAL
 2620   CONTINUE
C
      ELSEIF(ICASPL.EQ.'BFRA')THEN
        DO2630I=1,N
          XL=(Y(I) - KSLOC)/KSSCAL
          IF(XL.GT.0.0)THEN
            CALL BFRPDF(DBLE(XL),DBLE(SHAPE1),DBLE(SHAPE2),
     1                DBLE(SHAPE3),DXOUT)
            Y2(I)=REAL(DXOUT)
            Y2(I)=Y2(I)/KSSCAL
          ENDIF
 2630   CONTINUE
C
      ELSEIF(ICASPL.EQ.'L3EX')THEN
        DO2640I=1,N
          XL=(Y(I) - KSLOC)/KSSCAL
          IF(SHAPE1.LT.1.0)THEN
            IF(XL.GT.0.0)THEN
              CALL LE3PDF(DBLE(XL),DBLE(SHAPE1),DBLE(SHAPE2),
     1                    DBLE(SHAPE3),DXOUT)
              Y2(I)=REAL(DXOUT)
              Y2(I)=Y2(I)/KSSCAL
            ENDIF
          ELSE
            IF(XL.GE.0.0)THEN
              CALL LE3PDF(DBLE(XL),DBLE(SHAPE1),DBLE(SHAPE2),
     1                    DBLE(SHAPE3),DXOUT)
              Y2(I)=REAL(DXOUT)
