     1                DXOUT)
          Y2(I)=REAL(DXOUT)
          Y2(I)=Y2(I)/KSSCAL
 2660   CONTINUE
C
      ELSEIF(ICASPL.EQ.'EEWE')THEN
        DO2670I=1,N
          XL=Y(I)
          IF(XL.GT.0.0)THEN
            CALL EEWPDF(DBLE(XL),DBLE(SHAPE1),DBLE(SHAPE2),DBLE(SHAPE3),
     1                  DBLE(SHAPE4),DBLE(SHAPE5),DXOUT)
            Y2(I)=REAL(DXOUT)
          ENDIF
 2670   CONTINUE
