CCCCC IF(IHOLD1.EQ.'AUTO')IHOLD2=IDEFSD
CCCCC IF(IHOLD1.EQ.'DEFA')IHOLD2=IDEFSD
      DO1315I=1,NUMSPI
      ISPIDI(I)=IHOLD2
 1315 CONTINUE
      GOTO1370
C
 1370 CONTINUE
      IF(IFEEDB.EQ.'OFF')GOTO1319
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
