      ELSEIF(NUMARG.GE.3.AND.
     1      (ICOM.EQ.'10TH' .OR. ICOM.EQ.'TENT' .OR. ICOM.EQ.'10') .AND.
     1       IHARG(1).EQ.'DEGR'.AND.IHARG(2).EQ.'SPLI'.AND.
