              CALL SHIFTL(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1                    IBUGG2,IERROR)
            ENDIF
            ICOM='PLOT'
            ICOM2='    '
            IHARG(1)=IVARN1(IRES)
            IHARG2(1)=IVARN2(IRES)
            IHARG(2)=IVARN1(IFACT)
            IHARG2(2)=IVARN2(IFACT)
          ENDIF
          CALL MAINGR(ANOPL1,ANOPL2,NPLOTV,NPLOTP,NS,ICASPL,
     1                MAXNPP,ISEED,IBOOSS,
     1                IX1TSV,IX2TSV,IY1TSV,IY2TSV,
     1                IX1ZSV,IX2ZSV,IY1ZSV,IY2ZSV,
     1                BARHEF,BARWEF,
     1                IRHSTG,IHSTCW,IHSTEB,IHSTOU,IASHWT,
     1                IHSTMC,IHSTOP,
     1                ICAPSW,IFORSW,
     1                IGUIFL,IERRFA,
     1                IAND1,IAND2,ICONT,NUMHPP,NUMVPP,
CCCCC1                TEMP,TEMP2,TEMP3,XTEMP1,XTEMP2,MAXNXT,
     1                MAXNXT,
     1                ISUBRO,IFOUND,IERROR)
          IF(IEMPTY.EQ.'NO')THEN
            CALL DPSPM3(ICASPL,IOUNI5,
     1                  IROW,ICOL,
     1                  PX2LD2,NPLOTP,
     1                  IFORSW,
     1                  ISPX2L,ISPX2P,ISPX2S,
     1                  IHRIGH,IHRIG2,IHWUSE,
     1                  ISUBN1,ISUBN2,MESSAG,
     1                  IBUGG2,ISUBRO,IERROR)
          ENDIF
C
          ISHIFT=NARGT-NUMARG
          IF(ISHIFT.GT.0)THEN
            CALL SHIFTR(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1                IBUGG2,IERROR)
          ELSEIF(ISHIFT.LT.0)THEN
            ISHIFT=-ISHIFT
            CALL SHIFTL(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1                IBUGG2,IERROR)
          ENDIF
          IF(IERROR.EQ.'YES')GOTO6699
          ICOM='PLOT'
          ICOM2='    '
          IF(IRESP.EQ.0)THEN
            IHARG(1)=IVARN1(ICOL)
            IHARG2(1)=IVARN2(ICOL)
            IHARG(2)=IVARN1(ICOL)
            IHARG2(2)=IVARN2(ICOL)
          ELSE
            IHARG(1)=IVARN1(1)
            IHARG2(1)=IVARN2(1)
            IHARG(2)=IVARN1(ICOL)
            IHARG2(2)=IVARN2(ICOL)
            IHARG(3)=IVARN1(ICOL)
            IHARG2(3)=IVARN2(ICOL)
          ENDIF
          IF(ISPMPT.EQ.'DEXC')THEN
            IHARG(4)=IVARN1(NUMVAR+1)
            IHARG2(4)=IVARN2(NUMVAR+1)
          ENDIF
          GOTO6690
C
 6690     CONTINUE
          ICONT=IDCONT(1)
          IPOWE=IDPOWE(1)
          NUMHPP=IDNHPP(1)
          IMPARG=2
          CALL DPGRAP(Y,X,X3D,D,N,NPLOTP,ICASPL,ICONT,IPOWE,NUMHPP,
     1                XMATN,YMATN,XMITN,YMITN,
     1                ISQUAR,
     1                IVGMSW,IHGMSW,
     1                IHNAME,IHNAM2,IUSE,IN,IVALUE,VALUE,NUMNAM,
     1                IVSTAR,IVSTOP,IFUNC,NUMCHF,IREPCH,
     1                YPLOT,XPLOT,X2PLOT,TAGPLO,
     1                IMPSW,IMPNR,IMPNC,IMPCO,IMPCO9,
     1                IMPARG,
     1                PMXMIN,PMXMAX,PMYMIN,PMYMAX,
     1                MAXCOL,
     1                DSIZE,DSYMB,DCOLOR,DFILL,
     1                ICAPSW,
     1                IBUGUG,IBUGU2,IBUGU3,IBUGU4,ISUBRO,
     1                IERROR)
C
          ICNTPL=ICNTPL+1
          IF(N.GT.0)THEN
            DO6671II=1,N
              WRITE(IOUNI5,3118)ICNTPL,Y(II),X(II),D(II)
 6671       CONTINUE
          ENDIF
C
 6699     CONTINUE
          IF(IERROR.EQ.'NO')IAND1=IAND2
          IF(IEMPTY.EQ.'YES')THEN
            DO6207I=1,MAXSUB
              ISUBSW(I)=ISU2SW(I)
 6207       CONTINUE
          ENDIF
          PX1LDS=PX1LD2
          GX1MIN=GX1MNS
          GX1MAX=GX1MXS
          GX2MIN=GX2MNS
          GX2MAX=GX2MXS
          GY1MIN=GY1MNS
          GY1MAX=GY1MXS
          GY2MIN=GY2MNS
          GY2MAX=GY2MXS
          IX1MIN=IX1MNS
          IX1MAX=IX1MXS
          IX2MIN=IX2MNS
          IX2MAX=IX2MXS
          IY1MIN=IY1MNS
          IY1MAX=IY1MXS
          IY2MIN=IY2MNS
          IY2MAX=IY2MXS
          PX1ZDS=PX1ZD2
          PX2ZDS=PX2ZD2
          PY1ZDS=PY1ZD2
          PY2ZDS=PY2ZD2
C
          ISUBSW(1)=ISUBZZ
          IREFSW(1)=IREFZZ
C
 6700   CONTINUE
 6600 CONTINUE
      GOTO8000
C
C               **************************************************
C               **   STEP 25--                                  **
C               **   PLOT THE CURRENT PLOT                      **
C               **************************************************
          ICONT=IDCONT(1)
          IPOWE=IDPOWE(1)
          NUMHPP=IDNHPP(1)
          IF(IBUGG3.EQ.'ON'.OR.ISUBRO.EQ.'SPMA')THEN
            WRITE(ICOUT,7907)IMANUF,NUMDEV,IDMANU(1)
 7907       FORMAT('IMANUF,NUMDEV,IDMANU(1) = ',A4,I8,2X,A4)
            CALL DPWRST('XXX','BUG ')
          ENDIF
          IMPARG=2
          CALL DPGRAP(Y,X,X3D,D,N,NPLOTP,ICASPL,ICONT,IPOWE,NUMHPP,
     1                XMATN,YMATN,XMITN,YMITN,
     1                ISQUAR,
     1                IVGMSW,IHGMSW,
     1                IHNAME,IHNAM2,IUSE,IN,IVALUE,VALUE,NUMNAM,
     1                IVSTAR,IVSTOP,IFUNC,NUMCHF,IREPCH,
     1                YPLOT,XPLOT,X2PLOT,TAGPLO,
     1                IMPSW,IMPNR,IMPNC,IMPCO,IMPCO9,
     1                IMPARG,
     1                PMXMIN,PMXMAX,PMYMIN,PMYMAX,
     1                MAXCOL,
     1                DSIZE,DSYMB,DCOLOR,DFILL,
     1                ICAPSW,
     1                IBUGUG,IBUGU2,IBUGU3,IBUGU4,ISUBRO,
     1                IERROR)
C
          ICNTPL=ICNTPL+1
          IF(N.GT.0)THEN
            DO7917II=1,N
              WRITE(IOUNI5,3118)ICNTPL,Y(II),X(II),D(II)
 7917       CONTINUE
          ENDIF
C
          IF(IERROR.EQ.'NO')IAND1=IAND2
          IF(IERROR.EQ.'YES')GOTO7900
          PX1LDS=PX1LD2
          GX1MIN=GX1MNS
          GX1MAX=GX1MXS
          GX2MIN=GX2MNS
          GX2MAX=GX2MXS
          GY1MIN=GY1MNS
          GY1MAX=GY1MXS
          GY2MIN=GY2MNS
          GY2MAX=GY2MXS
          IX1MIN=IX1MNS
          IX1MAX=IX1MXS
          IX2MIN=IX2MNS
          IX2MAX=IX2MXS
          IY1MIN=IY1MNS
          IY1MAX=IY1MXS
          IY2MIN=IY2MNS
          IY2MAX=IY2MXS
          PX1ZDS=PX1ZD2
          PX2ZDS=PX2ZD2
          PY1ZDS=PY1ZD2
          PY2ZDS=PY2ZD2
C
 7900   CONTINUE
        ISPMFR=ISPMFZ
C
C               **************************************************
C               **   STEP 28--                                  **
C               **   REINSTATE INITIAL SETTINGS                 **
C               **************************************************
C
 8000 CONTINUE
C
      IF(IBUGG3.EQ.'ON'.OR.ISUBRO.EQ.'4PLO')THEN
        ISTEPN='28'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,8807)IMANUF,NUMDEV,IDMANU(1)
 8807   FORMAT('IMANUF,NUMDEV,IDMANU(1) = ',A4,I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IFLAG=2
      CALL DPSPM5(IFLAG,IMPSW,IMPCO,IMPNR,IMPNC,
     1            IBUGG2,ISUBRO,IFOUND,IERROR)
      ISPMTA=ISPMTZ
      ISPMFR=ISPMFZ
      ISPMPT=ISPMPZ
      ISPMLD=ISPMLZ
      ISPMLA=ISPML2
      ISPMXA=ISPMXZ
      ISPMYA=ISPMYZ
      ISPMDI=ISPMDZ
      ISPMST=ISPMZT
      ISPMS2=ISPMZ2
      ISPMS3=ISPMZ3
      ISPMS4=ISPMZ4
C
      IOPTN=2
      IDX=0
      IDY=0
      CALL DPSPM4(ICASPL,IOPTN,IDX,IDY,
     1            ISUBNU,
     1            ISUBSW,
     1            ASUBXL,ASUBXU,ASUBYL,ASUBYU,
     1            ISUBN9,
     1            ISUBSZ,
     1            ASBXL2,ASBXU2,ASBYL2,ASBYU2,
     1            PSPLSL,PSPLSU,PSPLSL,PSPLSU,
     1            IBUGG2,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      IFEEDB=IFEED9
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGG2,ISUBRO,IERROR)
      IFITAU=IFITA2
C
      IF(IBUGG2.EQ.'ON' .OR. ISUBRO.EQ.'SPMA')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSPMA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IFOUND,IERROR
 9012   FORMAT('IFOUND,IERROR = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NPLOTV,NPLOTP,NS,ICASPL,IAND1,IAND2
 9013   FORMAT('NPLOTV,NPLOTP,NS,ICASPL,IAND1,IAND2 = ',3I8,3(2X,A4))
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSPM1(ICASPL,IVARN1,IVARN2,ICOLL,
     1                  IMPNR,IMPNC,IROW,ICOL,IRES,
     1                  IFACT,IPLOT,NPLOTS,NUMVAR,
     1                  ICHAP2,ILINP2,
     1                  GY1MNS,GY1MXS,GY2MNS,GY2MXS,
     1                  GX1MNS,GX1MXS,GX2MNS,GX2MXS,
     1                  IY1MNS,IY1MXS,IY2MNS,IY2MXS,
     1                  IX1MNS,IX1MXS,IX2MNS,IX2MXS,
     1                  IX1TSV,IX2TSV,IY1TSV,IY2TSV,
     1                  IX1ZSV,IX2ZSV,IY1ZSV,IY2ZSV,
     1                  PX1LD2,PX2LD2,
     1                  IY1LJ2,IY1LD2,PY1LD2,PY1LA2,
     1                  IX1LT2,IX2LT2,IY1LT2,IY2LT2,
     1                  NCX1L2,NCX2L2,NCY1L2,NCY2L2,
     1                  PSPXLL,PSPXUL,PSPYLL,PSPYUL,IXLIST,
     1                  ISPMLA,ISPMLD,ISPMPT,ISPMFR,ISPMXA,
     1                  ISPMYA,ISPMDI,ISPX1L,
     1                  ISPMXT,ISPMXL,ISPMYT,ISPMYL,
     1                  ISPMTD,PSPMTD,IVNMEX,
     1                  IBUGG2,ISUBRO)
C
C     PURPOSE--UTILTY ROUTINE FOR SCATTER PLOT MATRIX.  GENERATE
C              TIC MARKS, TIC MARK LABELS, AXIS LABELS.  ALSO
C              USED BY FACTOR AND CONDITIONING PLOTS.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--99/11
C     ORIGINAL VERSION (AS A SEPARATE SUBROUTINE)--NOVEMBER 1999.
C     UPDATED       --JUNE       2002.  UPDATES FOR PARTIAL REGRESSION
C     UPDATED       --JUNE       2002.  UPDATES FOR PARTIAL RESIDUAL
C     UPDATED       --JUNE       2002.  UPDATES FOR PARTIAL LEVERAGE
C     UPDATED       --MAY        2007.  UPDATES FOR BINARY TABULATION
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES---------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOPC.INC'
      INCLUDE 'DPCOHK.INC'
C
      CHARACTER*4 ICASPL
      CHARACTER*4 IBUGG2
C
      CHARACTER*4 ISUBRO
C
      CHARACTER*4 ISPMLA
      CHARACTER*4 ISPMLD
      CHARACTER*4 ISPMPT
      CHARACTER*4 ISPMFR
      CHARACTER*4 ISPMXA
      CHARACTER*4 ISPMYA
      CHARACTER*4 ISPMDI
      CHARACTER*4 ISPMTD
      CHARACTER*4 IVNMEX
      CHARACTER*4 ISPX1L
      CHARACTER*4 ISPMXT
      CHARACTER*4 ISPMXL
      CHARACTER*4 ISPMYT
      CHARACTER*4 ISPMYL
C
      CHARACTER*105 IXT
      CHARACTER*52 IX2T
      CHARACTER*52 IY1T
      CHARACTER*4 IXLABT(52)
      CHARACTER*4 IXLAB2(52)
      CHARACTER*4 IYLABT(52)
C
      CHARACTER*24 ICHAP2(100)
      CHARACTER*4 ILINP2(100)
      CHARACTER*4 IY1MNS
      CHARACTER*4 IY1MXS
      CHARACTER*4 IY2MNS
      CHARACTER*4 IY2MXS
      CHARACTER*4 IY1LJ2
      CHARACTER*4 IY1LD2
      CHARACTER*4 IX1MNS
      CHARACTER*4 IX1MXS
      CHARACTER*4 IX2MNS
      CHARACTER*4 IX2MXS
      CHARACTER*4 IX1TSV
      CHARACTER*4 IX2TSV
      CHARACTER*4 IY1TSV
      CHARACTER*4 IY2TSV
      CHARACTER*4 IX1ZSV
      CHARACTER*4 IX2ZSV
      CHARACTER*4 IY1ZSV
      CHARACTER*4 IY2ZSV
      CHARACTER*4 IX1LT2(*)
      CHARACTER*4 IX2LT2(*)
      CHARACTER*4 IY1LT2(*)
      CHARACTER*4 IY2LT2(*)
C
      CHARACTER*4 IVARN1
      CHARACTER*4 IVARN2
C
      DIMENSION ICOLL(*)
      DIMENSION IVARN1(*)
      DIMENSION IVARN2(*)
      DIMENSION PSPXLL(*)
      DIMENSION PSPXUL(*)
      DIMENSION PSPYLL(*)
      DIMENSION PSPYUL(*)
C
C-----COMMON------------------------------------------------------
C
C-----COMMON VARIABLES (GENERAL)----------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-------------------------------------------------
C
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'SPM1')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,1011)
 1011   FORMAT('***** AT THE BEGINNING OF DPSPM1--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,1013)IX1MNS,IX1MXS,IX2MNS,IX2MXS
 1013   FORMAT('IX1MNS,IX1MXS,IX2MNS,IX2MXS = ',3(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,1015)IY1MNS,IY1MXS,IY2MNS,IY2MXS
 1015   FORMAT('IY1MNS,IY1MXS,IY2MNS,IY2MXS = ',3(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      ITEMP=0
C
C               ***************************************
C               **  STEP 1--                         **
C               **  TURN EVERYTHING OFF IF DATAPLOT  **
C               **  DETERMINES AXIS APPEARANCE AND   **
C               **  RESET DEFAULTS WHERE APPROPRIATE **
C               ***************************************
C
C
      DO10I=1,52
        IXLABT(I)=' '
        IXLAB2(I)=' '
        IYLABT(I)=' '
   10 CONTINUE
      DO15I=1,MAXCH
        IX1LTE(I)=IX1LT2(I)
        IX2LTE(I)=IX2LT2(I)
        IY1LTE(I)=IY1LT2(I)
        IY2LTE(I)=IY2LT2(I)
   15 CONTINUE
      IXT=' '
      IX2T=' '
      IY1T=' '
      NCX1LA=NCX1L2
      NCX2LA=NCX2L2
      NCY1LA=NCY1L2
      NCY2LA=NCY2L2
C
      IF(ISPMFR.EQ.'DEFA')THEN
        IX1TSW='OFF'
        IX1ZSW='OFF'
        IX2TSW='OFF'
        IX2ZSW='OFF'
        IY1TSW='OFF'
        IY1ZSW='OFF'
        IY2TSW='OFF'
        IY2ZSW='OFF'
        DO105I=1,MAXCH
          IX1LTE(I)='    '
          IX2LTE(I)='    '
          IY1LTE(I)='    '
          IY2LTE(I)='    '
  105   CONTINUE
        NCX1LA=0
        NCY1LA=0
        NCY2LA=0
        PX1LDS=PX1LD2
        PY1LDS=PY1LD2
        PY1LAN=PY1LA2
        IY1LJU=IY1LJ2
        IY1LDI=IY1LD2
      ELSE
        IX1TSW=IX1TSV
        IX1ZSW=IX1ZSV
        IX2TSW=IX2TSV
        IX2ZSW=IX2ZSV
        IY1TSW=IY1TSV
        IY1ZSW=IY1ZSV
        IY2TSW=IY2TSV
        IY2ZSW=IY2ZSV
      ENDIF
C
      DO110I=1,100
        ICHAPA(I)=ICHAP2(I)
        ILINPA(I)=ILINP2(I)
  110 CONTINUE
C
C               ***************************************
C               **  STEP 2--                         **
C               **  DETERMINE Y AXIS LIMITS (I.E.,   **
C               **  DEFAULT OR USER SPECIFIED)       **
C               ***************************************
C
      IF(IRES.GT.0)THEN
        YLOWL=PSPYLL(IRES)
        YUPPL=PSPYUL(IRES)
      ELSE
        YLOWL=CPUMIN
        YUPPL=CPUMIN
      ENDIF
      IF(IXLIST.GT.0)THEN
        XLOWL=PSPXLL(IXLIST)
        XUPPL=PSPXUL(IXLIST)
      ELSE
        XLOWL=CPUMIN
        XUPPL=CPUMIN
      ENDIF
      IF(YLOWL.NE.CPUMIN.AND.YUPPL.NE.CPUMIN)THEN
        GY1MIN=YLOWL
        GY1MAX=YUPPL
        GY2MIN=YLOWL
        GY2MAX=YUPPL
        IY1MIN='FIXE'
        IY1MAX='FIXE'
        IY2MIN='FIXE'
        IY2MAX='FIXE'
      ELSE
        IF(IY1MIN.NE.'FIXE')GY1MIN=GY1MNS
        IF(IY2MIN.NE.'FIXE')GY2MIN=GY2MNS
        IF(IY1MAX.NE.'FIXE')GY1MAX=GY1MXS
        IF(IY2MAX.NE.'FIXE')GY2MAX=GY2MXS
      ENDIF
C
C               ***************************************
C               **  STEP 3--                         **
C               **  DETERMINE X AXIS LIMITS (I.E.,   **
C               **  DEFAULT OR USER SPECIFIED)       **
C               ***************************************
C
      IF(XLOWL.NE.CPUMIN.AND.XUPPL.NE.CPUMIN)THEN
        GX1MIN=XLOWL
        GX1MAX=XUPPL
        GX2MIN=XLOWL
        GX2MAX=XUPPL
        IX1MIN='FIXE'
        IX1MAX='FIXE'
        IX2MIN='FIXE'
        IX2MAX='FIXE'
      ELSE
        IF(IX1MIN.NE.'FIXE')GX1MIN=GX1MNS
        IF(IX2MIN.NE.'FIXE')GX2MIN=GX2MNS
        IF(IX1MAX.NE.'FIXE')GX1MAX=GX1MXS
        IF(IX2MAX.NE.'FIXE')GX2MAX=GX2MXS
      ENDIF
C
C               ***************************************
C               **  STEP 4--                         **
C               **  DETERMINE TEXT FOR X AND Y AXIS  **
C               **  LABELS.  DO ONCE HERE TO SIMPLIFY**
C               **  CODE BELOW.                      **
C               ***************************************
C
      IF(ISPMLA.EQ.'OFF')GOTO9000
CCCCC IF(ISPMLA.EQ.'ROSE')GOTO9000
CCCCC IF(ISPMLA.EQ.'ROS2')GOTO9000
C
      IF(ISPMPT.EQ.'PLOT'.OR.ISPMPT.EQ.'QQSP'.OR.ISPMPT.EQ.'CROS')THEN
        IXT=' '
        NX1=-1
        IX1DS=1
        IX2T=' '
        NX2=0
        IX2DS=0
        IY1T=' '
        NY1=-1
      ELSEIF(ISPMPT.EQ.'BITA' .OR. ISPMPT.EQ.'BIPL')THEN
        IXT=' '
        NX1=0
        IX1DS=0
        IX2T=' '
        NX2=0
        IX2DS=0
        IY1T=' '
        NY1=0
      ELSEIF(ISPMPT.EQ.'PREG')THEN
        IXT=' '
        IXT='Res: '
        NX1=5
        NX1=NX1+1
        IXT(NX1:NX1+3)=IVARN1(IPLOT+1)(1:4)
        IXT(NX1+4:NX1+7)=IVARN2(IPLOT+1)(1:4)
        DO140I=NX1+7,NX1,-1
          NXTEMP=I
          IF(IXT(I:I).NE.'    ')GOTO145
  140   CONTINUE
  145   CONTINUE
        NX1=NXTEMP
        NX1=NX1+1
        NXTEMP=NX1+14
        IXT(NX1:NXTEMP)=' versus other X'
        NX1=NXTEMP
C
        IX1DS=1
        IX2T=' '
        NX2=0
        IX2DS=0
C
        NY1=5
        IY1T(1:NY1)='Res: '
        NY1=NY1+1
        IY1T(NY1:NY1+3)=IVARN1(IPLOT+1)(1:4)
        IY1T(NY1+4:NY1+7)=IVARN2(IPLOT+1)(1:4)
        DO130I=NY1+7,NY1,-1
          NYTEMP=I
          IF(IY1T(I:I).NE.'    ')GOTO135
  130   CONTINUE
  135   CONTINUE
        NY1=NYTEMP
        NY1=NY1+1
        NYTEMP=NY1+7
        IY1T(NY1:NYTEMP)=' Removed'
        NY1=NYTEMP
      ELSEIF(ISPMPT.EQ.'PLEV')THEN
        IXT=' '
        NX1=5
        IXT(1:NX1)='Index'
C
        IX1DS=1
        IX2T=' '
        NX2=0
        IX2DS=0
C
        IY1T=' '
        IY1T='Partial Leverage: '
        NY1=18
        NY1=NY1+1
        IY1T(NY1:NY1+3)=IVARN1(IPLOT+1)(1:4)
        IY1T(NY1+4:NY1+7)=IVARN2(IPLOT+1)(1:4)
        DO170I=NY1+7,NY1,-1
          NYTEMP=I
          IF(IY1T(I:I).NE.'    ')GOTO175
  170   CONTINUE
  175   CONTINUE
        NY1=NYTEMP
      ELSEIF(ISPMPT.EQ.'PRES'.OR.ISPMPT.EQ.'CCPR')THEN
        NX1=1
