C
C  X1LABEL
C
C  1) IF NX1 > 0, USE USER SPECIFIED LABEL IF ALREADY DEFINED,
C     OTHERWISE, DEFAULT TO A DATAPLOT SPECIFIED LABEL.
C  2) IF NX1 = 0, USE USER SPECIFIED LABEL IF ALREADY DEFINED,
C     BUT NO DEFAULT PROVIDED.
C  3) IF NX1 = -1, USE VARIABLE NAME (AND SUBSTITUTE VARIABLE
C     LABEL IF PROVIDED).
C  4) IF NX1 = -2, USE: VARIABLE1 * VARIABLE2 (AND SUBSTITUTE
C     VARIABLE LABEL IF PROVIDED).
C  5) IF NX1 = -3, USE: 1*2 (AND SUBSTITUTE
C     VARIABLE LABEL IF PROVIDED).
C
      IF(ICOL.EQ.0)THEN
        NCXLA=0
        GOTO299
      ENDIF
C
      IF(NX1.GE.0)THEN
        IF(NCX1L2.GT.0)THEN
          DO210I=1,NCX1L2
            IXLABT(I)=IX1LT2(I)
  210     CONTINUE
          NCXLA=NCX1L2
        ELSE
          NCXLA=0
          IF(NX1.GT.0)THEN
            DO220I=1,NX1
              IXLABT(I)(1:1)=IXT(I:I)
  220       CONTINUE
            NCXLA=NX1
          ENDIF
        ENDIF
      ELSEIF(NX1.LT.0)THEN
        ITEMP=IFACT
        IF(NX1.EQ.-2 .OR. NX2.EQ.-2)ITEMP=IRES
        IF(ISPMPT.EQ.'BIHI')ITEMP=IRES
        ICOLID=ICOLL(ITEMP)
        IF(IVARLB(ICOLID).EQ.' '.OR.IVNMEX.EQ.'OFF')THEN
          DO230I=1,4
            IXLABT(I)=IVARN1(ITEMP)(I:I)
            IXLABT(I+4)=IVARN2(ITEMP)(I:I)
  230     CONTINUE
          NCXLA=8
          DO240I=8,1,-1
            NCXLA=I
            IF(IXLABT(I).NE.'    ')GOTO245
  240     CONTINUE
  245     CONTINUE
        ELSE
          ILAST=40
          DO250I=40,1,-1
            IF(IVARLB(ICOLID)(I:I).NE.' ')THEN
              ILAST=I
              GOTO259
            ENDIF
  250     CONTINUE
  259     CONTINUE
          DO270I=1,ILAST
            IXLABT(I)(1:1)=IVARLB(ICOLID)(I:I)
  270     CONTINUE
          NCXLA=ILAST
        ENDIF
        IF(NX1.EQ.-1 .OR. IRES.EQ.IFACT)GOTO299
        NCXLA=NCXLA+1
        IXLABT(NCXLA)='*'
        ITEMP=IFACT
        ICOLID=ICOLL(ITEMP)
        IF(IVARLB(ICOLID).EQ.' ')THEN
          DO280I=1,4
            IXLABT(NCXLA+I)=IVARN1(ITEMP)(I:I)
            IXLABT(NCXLA+I+4)=IVARN2(ITEMP)(I:I)
  280     CONTINUE
          ILAST=NCXLA+8
          DO285I=ILAST,1,-1
            NCXLA=I
            IF(IXLABT(I).NE.'    ')GOTO288
  285     CONTINUE
  288     CONTINUE
        ELSE
          ILAST=40
          DO290I=40,1,-1
            IF(IVARLB(ICOLID)(I:I).NE.' ')THEN
              ILAST=I
              GOTO293
            ENDIF
  290     CONTINUE
  293     CONTINUE
          DO295I=1,ILAST
            NCXLA=NCXLA+1
            IXLABT(NCXLA)(1:1)=IVARLB(ICOLID)(I:I)
  295     CONTINUE
        ENDIF
      ENDIF
  299 CONTINUE
C
C  X2LABEL
C
C  1) IF NX2 > 0, USE USER SPECIFIED LABEL IF ALREADY DEFINED,
C     OTHERWISE, DEFAULT TO A DATAPLOT SPECIFIED LABEL.
C  2) IF NX2 = 0, USE USER SPECIFIED LABEL IF ALREADY DEFINED,
C     BUT NO DEFAULT PROVIDED.
C  3) IF NX2 = -1, USE VARIABLE NAME (AND SUBSTITUTE VARIABLE
C     LABEL IF PROVIDED).
C  4) IF NX2 = -2, USE: VARIABLE1 * VARIABLE2 (AND SUBSTITUTE
C     VARIABLE LABEL IF PROVIDED).
C
      IF(ICOL.EQ.0)THEN
        NCXLA2=0
        GOTO399
      ENDIF
C
      IF((NX1.EQ.-1.AND.NX2.EQ.-1).AND.IRES.EQ.IFACT)THEN
         NCXLA2=0
         GOTO399
      ENDIF
      IF(NX2.GE.0)THEN
        IF(NCX2L2.GT.0)THEN
          DO310I=1,NCX2L2
            IXLAB2(I)=IX2LT2(I)
  310     CONTINUE
          NCXLA2=NCX2L2
        ELSE
          NCXLA2=0
          IF(NX2.GT.0)THEN
            DO320I=1,NX2
              IXLAB2(I)(1:1)=IX2T(I:I)
  320       CONTINUE
            NCXLA2=NX2
          ENDIF
        ENDIF
      ELSEIF(NX2.LT.0)THEN
        ITEMP=IFACT
        IF(NX2.EQ.-2)ITEMP=IRES
        IF(ISPMPT.EQ.'BIHI')ITEMP=IFACT
        ICOLID=ICOLL(ITEMP)
        IF(IVARLB(ICOLID).EQ.' '.OR.IVNMEX.EQ.'OFF')THEN
          DO330I=1,4
            IXLAB2(I)=IVARN1(ITEMP)(I:I)
            IXLAB2(I+4)=IVARN2(ITEMP)(I:I)
  330     CONTINUE
          NCXLA2=8
          DO340I=8,1,-1
            NCXLA2=I
            IF(IXLAB2(I).NE.'    ')GOTO345
  340     CONTINUE
  345     CONTINUE
        ELSE
          ILAST=40
          DO350I=40,1,-1
            IF(IVARLB(ICOLID)(I:I).NE.' ')THEN
              ILAST=I
              GOTO359
            ENDIF
  350     CONTINUE
  359     CONTINUE
          DO370I=1,ILAST
            IXLAB2(I)(1:1)=IVARLB(ICOLID)(I:I)
  370     CONTINUE
          NCXLA2=ILAST
        ENDIF
        IF(NX2.EQ.-1.OR.IRES.EQ.IFACT)GOTO399
        NCXLA2=NCXLA2+1
        IXLAB2(NCXLA2)='*'
        ITEMP=IFACT
        ICOLID=ICOLL(ITEMP)
        IF(IVARLB(ICOLID).EQ.' ')THEN
          DO380I=1,4
            IXLAB2(NCXLA2+I)=IVARN1(ITEMP)(I:I)
            IXLAB2(NCXLA2+I+4)=IVARN2(ITEMP)(I:I)
  380     CONTINUE
          ILAST=NCXLA2+8
          DO385I=ILAST,1,-1
            NCXLA2=I
            IF(IXLAB2(I).NE.'    ')GOTO388
  385     CONTINUE
  388     CONTINUE
        ELSE
          ILAST=40
          DO390I=40,1,-1
            IF(IVARLB(ICOLID)(I:I).NE.' ')THEN
              ILAST=I
              GOTO393
            ENDIF
  390     CONTINUE
  393     CONTINUE
          DO395I=1,ILAST
            NCXLA2=NCXLA2+1
            IXLAB2(NCXLA2)(1:1)=IVARLB(ICOLID)(I:I)
  395     CONTINUE
        ENDIF
      ENDIF
  399 CONTINUE
C
C  Y1LABEL
C
C  1) IF NY1 > 0, USE USER SPECIFIED LABEL IF ALREADY DEFINED,
C     OTHERWISE, DEFAULT TO A DATAPLOT SPECIFIED LABEL.
C  2) IF NY1 = 0, USE USER SPECIFIED LABEL IF ALREADY DEFINED,
C     BUT NO DEFAULT PROVIDED.
C  3) IF NY1 = -1, USE VARIABLE NAME (AND SUBSTITUTE VARIABLE
C     LABEL IF PROVIDED).
C
      IF(ISPMLA.EQ.'BOX'.AND.IROW.EQ.IMPNR)THEN
        NCYLA=0
        GOTO599
      ENDIF
C
      IF(NY1.GE.0)THEN
        IF(NCY1L2.GT.0)THEN
          DO510I=1,NCY1L2
            IYLABT(I)=IY1LT2(I)
  510     CONTINUE
          NCYLA=NCY1L2
        ELSE
          NCYLA=0
          IF(NY1.GT.0)THEN
            DO520I=1,NY1
              IYLABT(I)(1:1)=IY1T(I:I)
  520       CONTINUE
            NCYLA=NY1
          ENDIF
        ENDIF
      ELSEIF(NY1.LT.0)THEN
        ITEMP=IRES
        IF(ISPMPT.EQ.'DEXI'.OR.ISPMPT.EQ.'DEXS')ITEMP=1
        ICOLID=ICOLL(ITEMP)
        IF(IVARLB(ICOLID).EQ.' '.OR.IVNMEX.EQ.'OFF')THEN
          DO530I=1,4
            IYLABT(I)=IVARN1(ITEMP)(I:I)
            IYLABT(I+4)=IVARN2(ITEMP)(I:I)
  530     CONTINUE
          NCYLA=8
          DO540I=8,1,-1
            NCYLA=I
            IF(IYLABT(I).NE.'    ')GOTO545
  540     CONTINUE
  545     CONTINUE
        ELSE
          ILAST=40
          DO560I=40,1,-1
            IF(IVARLB(ICOLID)(I:I).NE.' ')THEN
              ILAST=I
              GOTO569
            ENDIF
  560     CONTINUE
  569     CONTINUE
          DO570I=1,ILAST
            IYLABT(I)(1:1)=IVARLB(ICOLID)(I:I)
  570     CONTINUE
          NCYLA=ILAST
        ENDIF
      ENDIF
  599 CONTINUE
C
C               ***************************************
C               **  STEP 5--                         **
C               **  USER SPECIFIES AXIS ATTRIBUTES,  **
C               **  BUT DATAPLOT MAY SUBSTITUTE      **
C               **  VARIABLE NAME (OR LABEL) FOR     **
C               **  X1LABEL AND Y1LABEL              **
C               ***************************************
C
      IF(ISPMFR.EQ.'USER')THEN
        NCX1LA=NCXLA
        IF(NCX1LA.LE.0)GOTO1119
        DO1110I=1,NCX1LA
          IX1LTE(I)=IXLABT(I)
 1110   CONTINUE
 1119   CONTINUE
        IF(IX1DS.LT.0)PX1LDS=-((PYMAX-PYMIN)-PX1LD2)
        NCY1LA=NCYLA
        IF(NCY1LA.LE.0)GOTO1129
        DO1120I=1,NCY1LA
          IY1LTE(I)=IYLABT(I)
 1120   CONTINUE
 1129   CONTINUE
        NCX2LA=NCXLA2
        IF(NCX2LA.LE.0)GOTO1139
        DO1130I=1,NCX2LA
          IX2LTE(I)=IXLAB2(I)
 1130   CONTINUE
        PX2LDS=-((PYMAX-PYMIN)-PX2LD2)
        IF(IX2DS.EQ.-2)PX2LDS=-PX2LD2
 1139   CONTINUE
C
C               *******************************************
C               **  STEP 6--                             **
C               **  DATAPLOT SPECIFIES AXIS ATTRIBUTES   **
C               *******************************************
C
      ELSEIF(ISPMFR.EQ.'DEFA')THEN
C
        ITEMP1=MOD(ICOL,2)
        ITEMP2=MOD(IROW,2)
        IROWL=IMPNR
        IF(ISPMLA.EQ.'BOX'.AND.ICASPL.EQ.'SPMA')IROWL=NUMVAR+1
        ICOLF=1
        ICOLLA=IMPNC
        IF(ISPMLA.EQ.'BOX')ICOLF=0
C
C  IX1DS < 0 OR IX2DS < 0 SPECIFIES THAT THIS LABEL IS DRAWN
C  ON ALL PLOTS (AND DISPLACEMENT IS DISTANCE FROM TOP FRAME)
C
        IF(IX1DS.LT.0.AND.ISPMLA.NE.'BOX')THEN
          NCX1LA=NCXLA
          IF(NCX1LA.GT.0)THEN
            DO1505I=1,NCX1LA
              IX1LTE(I)=IXLABT(I)
 1505       CONTINUE
          ENDIF
          PX1LDS=-((PYMAX-PYMIN)-PX1LD2)
        ENDIF
        IF(IX2DS.LT.0.AND.ISPMLA.NE.'BOX')THEN
          NCX2LA=NCXLA2
          IF(NCX2LA.GT.0)THEN
            DO1508I=1,NCX2LA
              IX2LTE(I)=IXLAB2(I)
 1508       CONTINUE
          ENDIF
          PX2LDS=-((PYMAX-PYMIN)-PX2LD2)
          IF(IX2DS.EQ.-2)PX2LDS=-PX1LD2
        ENDIF
C
        IF(ISPMXA.EQ.'YON')GOTO1499
        IF(ISPMXA.EQ.'ALTE')THEN
          IF((IROW.EQ.IROWL.AND.ITEMP1.EQ.1).OR.
     1       (ISPMLD.EQ.'OFF'.AND.ITEMP1.EQ.1.AND.IROW.EQ.ICOL).OR.
     1       (IROW.EQ.IMPNR-1.AND.ICOL.EQ.IMPNC.AND.ITEMP.EQ.1.AND.
     1       NPLOTS.LT.IMPNR*IMPNC).OR.
     1       (ISPMLA.EQ.'BOX'.AND.IROW.EQ.IROWL)
     1       )THEN
            IF(ISPMLA.EQ.'BOX'.AND.ICOL.EQ.0)GOTO1519
            IF(ISPMLA.EQ.'BOX'.AND.ITEMP1.EQ.0)GOTO1512
            IF(ISPMLA.EQ.'YON')THEN
              IX1TSW='OFF'
              IX1ZSW='OFF'
            ELSE
              IX1TSW='ON'
              IX1ZSW='ON'
            ENDIF
            IX2TSW='OFF'
            IX2ZSW='OFF'
 1512       CONTINUE
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(IX1DS.LT.0.AND.ISPMLA.NE.'BOX')IFLAG=1
            IFLAG2=0
            IF(IX2DS.LT.0.AND.ISPMLA.NE.'BOX')IFLAG2=1
            IF(IFLAG.EQ.0)THEN
              NCX1LA=NCXLA
              IF(NCX1LA.GT.0)THEN
                DO1510I=1,NCX1LA
                IX1LTE(I)=IXLABT(I)
 1510           CONTINUE
              ENDIF
            ENDIF
            IF(IFLAG2.EQ.0)THEN
              NCX2LA=NCXLA2
              IF(NCX2LA.GT.0)THEN
                DO1516I=1,NCX2LA
                IX2LTE(I)=IXLAB2(I)
 1516           CONTINUE
              ENDIF
            ENDIF
 1519       CONTINUE
            IF(ISPMLA.EQ.'BOX')PX1LDS=-((PYMAX-PYMIN)/2.0)
          ENDIF
C
          IF(IROW.EQ.1.AND.ITEMP1.EQ.0)THEN
            IF(ISPMLA.EQ.'BOX'.AND.ICOL.EQ.0)GOTO1529
            IX1TSW='OFF'
            IX1ZSW='OFF'
            IF(ISPMLA.EQ.'YON')THEN
              IX2TSW='OFF'
              IX2ZSW='OFF'
            ELSE
              IX2TSW='ON'
              IX2ZSW='ON'
            ENDIF
            IFLAG=0
            IF(ISPMLA.EQ.'BOX')IFLAG=1
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(IX1DS.LT.0)IFLAG=1
            IFLAG2=0
            IF(IX2DS.LT.0)IFLAG2=1
            IF(IFLAG.EQ.0)THEN
              NCX1LA=NCXLA
              IF(NCX1LA.GT.0)THEN
                DO1520I=1,NCX1LA
                IX1LTE(I)=IXLABT(I)
 1520           CONTINUE
              ENDIF
            ENDIF
            IF(IFLAG2.EQ.0)THEN
              NCX2LA=NCXLA2
              IF(NCX2LA.GT.0)THEN
                DO1526I=1,NCX2LA
                IX2LTE(I)=IXLAB2(I)
 1526           CONTINUE
              ENDIF
            ENDIF
 1529       CONTINUE
            IF(IX1DS.GT.0)PX1LDS=-((PYMAX-PYMIN)+PX1LD2)
            IF(ISPMLA.EQ.'BOX')PX1LDS=-((PYMAX-PYMIN)/2.0)
          ENDIF
        ELSEIF(ISPMXA.EQ.'BOTT' .OR. ISPMXA.EQ.'BOLE' .OR.
     1         ISPMXA.EQ.'BORI')THEN
C
          IF(ISPMXA.EQ.'BOLE')THEN
            IF(ISPMLD.EQ.'ON')THEN
              IF(ICOL.NE.1 .OR. IROW.NE.IROWL)THEN
                NCXLA=0
                NCX1LA=NCXLA
                GOTO1618
              ENDIF
            ELSE
              IF(ICOL.NE.1 .OR. IROW.NE.1)THEN
                NCXLA=0
                NCX1LA=NCXLA
                GOTO1618
              ENDIF
            ENDIF
          ELSEIF(ISPMXA.EQ.'BORI')THEN
            IF(ICOL.NE.ICOLLA .OR. IROW.NE.IROWL)THEN
              NCXLA=0
              NCX1LA=NCXLA
              IF(ISPMXT.EQ.'OFF')IX1TSW='OFF'
              IF(ISPMXL.EQ.'OFF')IX1ZSW='OFF'
              GOTO1618
            ENDIF
          ELSE
            IF(ISPMLD.EQ.'ON')THEN
              IF(IROW.NE.IROWL)THEN
                NCXLA=0
                NCX1LA=NCXLA
                GOTO1618
              ENDIF
            ELSE
              IF(IROW.NE.ICOL)THEN
                NCXLA=0
                NCX1LA=NCXLA
                GOTO1618
              ENDIF
            ENDIF
          ENDIF
C
          IF((ISPMLD.EQ.'ON'.AND.IROW.EQ.IROWL) .OR.
     1       (ISPMLD.EQ.'OFF'.AND.IROW.EQ.ICOL))THEN
            IF(ISPMLA.EQ.'BOX'.AND.ICOL.EQ.0)GOTO1619
            IF(ISPMLA.EQ.'YON')THEN
              IX1TSW='OFF'
              IX1ZSW='OFF'
            ELSE
              IX1TSW='ON'
              IX1ZSW='ON'
              IF(ISPMXT.EQ.'OFF')IX1TSW='OFF'
              IF(ISPMXL.EQ.'OFF')IX1ZSW='OFF'
            ENDIF
            IF(ISPMTD.EQ.'STAG'.AND.ITEMP1.EQ.0)PX1ZDS=PSPMTD
            IX2TSW='OFF'
            IX2ZSW='OFF'
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(IX1DS.LT.0.AND.ISPMLA.NE.'BOX')IFLAG=1
            IFLAG2=0
            IF(IX2DS.LT.0.AND.ISPMLA.NE.'BOX')IFLAG2=1
            IF(IFLAG.EQ.0)THEN
              NCX1LA=NCXLA
              IF(NCX1LA.GT.0)THEN
                DO1610I=1,NCX1LA
                IX1LTE(I)=IXLABT(I)
 1610           CONTINUE
              ENDIF
            ENDIF
            IF(IFLAG2.EQ.0)THEN
              NCX2LA=NCXLA2
              IF(NCX2LA.GT.0)THEN
                DO1616I=1,NCX2LA
                IX2LTE(I)=IXLAB2(I)
 1616           CONTINUE
              ENDIF
            ENDIF
 1619       CONTINUE
            IF(ISPMLA.EQ.'BOX')PX1LDS=-((PYMAX-PYMIN)/2.0)
          ENDIF
 1618     CONTINUE
        ELSEIF(ISPMXA.EQ.'TOP' .OR. ISPMXA.EQ.'TOLE' .OR.
     1         ISPMXA.EQ.'TORI')THEN
C
          IF(ISPMXA.EQ.'TOLE')THEN
            IF(IROW.NE.1 .OR. ICOL.NE.1)THEN
                NCXLA=0
                NCX1LA=NCXLA
                GOTO1728
            ENDIF
          ELSEIF(ISPMXA.EQ.'TORI')THEN
            IF(IROW.NE.1 .OR. ICOL.NE.ICOLLA)THEN
              NCXLA=0
              NCX1LA=NCXLA
              GOTO1728
            ENDIF
          ELSE
            IF(IROW.NE.1)THEN
                NCXLA=0
                NCX1LA=NCXLA
                GOTO1728
            ENDIF
          ENDIF
C
          IF(IROW.EQ.1)THEN
            IF(ISPMLA.EQ.'BOX'.AND.ICOL.EQ.0)GOTO1719
            IX1TSW='OFF'
            IX1ZSW='OFF'
            IF(ISPMLA.EQ.'YON')THEN
              IX2TSW='OFF'
              IX2ZSW='OFF'
            ELSE
              IX2TSW='ON'
              IX2ZSW='ON'
              IF(ISPMXT.EQ.'OFF')IX2TSW='OFF'
              IF(ISPMXL.EQ.'OFF')IX2ZSW='OFF'
            ENDIF
            IF(ISPMTD.EQ.'STAG'.AND.ITEMP1.EQ.0)PX2ZDS=PSPMTD
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(IX1DS.LT.0)IFLAG=1
            IF(ISPMLA.EQ.'BOX')GOTO1719
            IFLAG2=0
            IF(IX2DS.LT.0)IFLAG2=1
            IF(IFLAG.EQ.0)THEN
              NCX1LA=NCXLA
              IF(NCX1LA.GT.0)THEN
                DO1710I=1,NCX1LA
                IX1LTE(I)=IXLABT(I)
 1710           CONTINUE
              ENDIF
            ENDIF
            IF(IFLAG2.EQ.0)THEN
              NCX2LA=NCXLA2
              IF(NCX2LA.GT.0)THEN
                DO1716I=1,NCX2LA
                IX2LTE(I)=IXLAB2(I)
 1716           CONTINUE
              ENDIF
            ENDIF
 1719       CONTINUE
            PX1LDS=-(100.0+PX1LD2)
          ENDIF
C
          IF(ISPMLA.EQ.'BOX'.AND.IROW.EQ.IROWL.AND.ICOL.GT.0)THEN
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(IX1DS.LT.0.AND.ISPMLA.NE.'BOX')IFLAG=1
            IFLAG2=0
            IF(IX2DS.LT.0.AND.ISPMLA.NE.'BOX')IFLAG2=1
            IF(IFLAG.EQ.0)THEN
              NCX1LA=NCXLA
              IF(NCX1LA.GT.0)THEN
                DO1720I=1,NCX1LA
                IX1LTE(I)=IXLABT(I)
 1720           CONTINUE
              ENDIF
            ENDIF
            PX1LDS=-(100.0+PX1LD2)
          ENDIF
 1728     CONTINUE
        ENDIF
C
 1499   CONTINUE
        IF(ISPMYA.EQ.'OFF')GOTO1699
        IF(ISPMYA.EQ.'ALTE')THEN
          IF((ICOL.EQ.IMPNC.AND.ITEMP2.EQ.0).OR.
     1       (ISPMLA.EQ.'BOX'.AND.ICOL.EQ.IMPNC-1.AND.ITEMP2.EQ.0).OR.
     1       (IPLOT.EQ.NPLOTS.AND.ITEMP2.EQ.0))THEN
            IY1TSW='OFF'
            IY1ZSW='OFF'
            IF(ISPMLA.EQ.'XON')THEN
              IY2TSW='OFF'
              IY2ZSW='OFF'
            ELSE
              IY2TSW='ON'
              IY2ZSW='ON'
            ENDIF
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(ISPMLA.EQ.'BOX')IFLAG=1
            IF(IFLAG.EQ.0)THEN
              NCY2LA=NCYLA
              IF(NCY2LA.LE.0)GOTO1539
              DO1530I=1,NCY2LA
                IY2LTE(I)=IYLABT(I)
 1530         CONTINUE
 1539         CONTINUE
            ENDIF
          ENDIF
C
          IF(ICOL.EQ.ICOLF.AND.ITEMP2.EQ.1.OR.
     1      (ISPMLA.EQ.'BOX'.AND.ICOL.EQ.ICOLF).OR.
     1      (ISPMLD.EQ.'OFF'.AND.ITEMP2.EQ.1.AND.IROW.EQ.ICOL))THEN
            IF(ISPMLA.EQ.'BOX'.AND.IROW.EQ.IROWL)GOTO1549
            IF(ISPMLA.EQ.'BOX'.AND.ITEMP2.EQ.0)GOTO1542
            IF(ISPMLA.EQ.'XON')THEN
              IY1TSW='OFF'
              IY1ZSW='OFF'
            ELSE
              IY1TSW='ON'
              IY1ZSW='ON'
            ENDIF
            IY2TSW='OFF'
            IY2ZSW='OFF'
 1542       CONTINUE
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(IFLAG.EQ.0)THEN
              NCY1LA=NCYLA
              IF(NCY1LA.LE.0)GOTO1549
              DO1540I=1,NCY1LA
                IY1LTE(I)=IYLABT(I)
 1540         CONTINUE
            ENDIF
            IF(ISPMLA.EQ.'BOX')THEN
              IY1LJU='CENT'
              PY1LDS=-((PXMAX-PXMIN)/2.0)
              PY1LAN=0.0
              IY1LDI='HORI'
            ENDIF
 1549       CONTINUE
          ENDIF
        ELSEIF(ISPMYA.EQ.'LEFT' .OR. ISPMYA.EQ.'LETO' .OR.
     1         ISPMYA.EQ.'LEBO')THEN
          IF(ISPMYA.EQ.'LETO' .AND. IROW.NE.1)GOTO1648
          IF(ISPMYA.EQ.'LEBO' .AND. IROW.NE.IROWL)GOTO1648
          IF(ICOL.EQ.ICOLF.OR.(ISPMLD.EQ.'OFF'.AND.IROW.EQ.ICOL))THEN
            IF(ISPMLA.EQ.'BOX'.AND.IROW.EQ.IROWL)GOTO1649
            IF(ISPMLA.EQ.'XON')THEN
              IY1TSW='OFF'
              IY1ZSW='OFF'
            ELSE
              IY1TSW='ON'
              IY1ZSW='ON'
              IF(ISPMYT.EQ.'OFF')IY1TSW='OFF'
              IF(ISPMYL.EQ.'OFF')IY1ZSW='OFF'
            ENDIF
            IF(ISPMTD.EQ.'STAG'.AND.ITEMP2.EQ.0)PY1ZDS=PSPMTD
            IY2TSW='OFF'
            IY2ZSW='OFF'
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(IFLAG.EQ.0)THEN
              NCY1LA=NCYLA
              IF(NCY1LA.LE.0)GOTO1649
              DO1640I=1,NCY1LA
                IY1LTE(I)=IYLABT(I)
 1640         CONTINUE
            ENDIF
 1649       CONTINUE
            IF(ISPMLA.EQ.'BOX')THEN
              IY1LJU='CENT'
              PY1LDS=-((PXMAX-PXMIN)/2.0)
              PY1LAN=0.0
              IY1LDI='HORI'
            ENDIF
          ENDIF
 1648     CONTINUE
        ELSEIF(ISPMYA.EQ.'RIGH' .OR. ISPMYA.EQ.'RITO' .OR.
     1         ISPMYA.EQ.'RIBO')THEN
          IF(ISPMYA.EQ.'RITO' .AND. IROW.NE.1)GOTO1848
          IF(ISPMYA.EQ.'RIBO' .AND. IROW.NE.IROWL)GOTO1848
          IF(ICOL.EQ.IMPNC)THEN
            IF(ISPMLA.EQ.'BOX'.AND.IROW.EQ.IROWL)GOTO1839
            IY1TSW='OFF'
            IY1ZSW='OFF'
            IF(ISPMLA.EQ.'XON')THEN
              IY2TSW='OFF'
              IY2ZSW='OFF'
            ELSE
              IY2TSW='ON'
              IY2ZSW='ON'
              IF(ISPMYT.EQ.'OFF')IY2TSW='OFF'
              IF(ISPMYL.EQ.'OFF')IY2ZSW='OFF'
            ENDIF
            IF(ISPMTD.EQ.'STAG'.AND.ITEMP2.EQ.0)PY2ZDS=PSPMTD
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(ISPMLA.EQ.'BOX')IFLAG=1
            IF(IFLAG.EQ.0)THEN
              NCY2LA=NCYLA
              IF(NCY2LA.LE.0)GOTO1839
              DO1830I=1,NCY2LA
                IY2LTE(I)=IYLABT(I)
 1830         CONTINUE
            ENDIF
 1839       CONTINUE
          ENDIF
C
          IF(ISPMLA.EQ.'BOX'.AND.ICOL.EQ.ICOLF.AND.IROW.LT.IROWL)THEN
            IFLAG=0
            IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1        ISPMDI.EQ.'BLAN')IFLAG=1
            IF(IFLAG.EQ.0)THEN
              NCY1LA=NCYLA
              IF(NCY1LA.LE.0)GOTO1849
              DO1840I=1,NCY1LA
                IY1LTE(I)=IYLABT(I)
 1840         CONTINUE
            ENDIF
 1849       CONTINUE
          ENDIF
 1848     CONTINUE
        ENDIF
C
        IF(ICASPL.EQ.'SPMA'.AND.ISPMPT.EQ.'PLOT'.AND.
     1    ISPMDI.EQ.'BLAN'.AND.IROW.EQ.ICOL)THEN
          NCX1LA=NCXLA
          NCY1LA=0
          NCY2LA=0
          IF(NCX1LA.LE.0)GOTO1919
          DO1910I=1,NCX1LA
            IX1LTE(I)=IXLABT(I)
 1910     CONTINUE
 1919     CONTINUE
          PX1LDS=-((PYMAX-PYMIN)/2.0)
        ENDIF
 1699   CONTINUE
C
      ENDIF
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'SPM1')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSPM1--')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSPM2(ICASPL,IVARN1,IVARN2,ICOLL,NUMVAR,NPLOTP,
     1                  IROW,ICOL,
     1                  TEMP,TEMP2,TEMP3,XTEMP1,XTEMP2,MAXNXT,
     1                  ALOWFR,ALOWDG,
     1                  IANGLU,MAXNPP,IAND1,IAND2,
     1                  ISPMFI,ISPMTA,
     1                  XMATN,YMATN,XMITN,YMITN,
     1                  ISQUAR,
     1                  IVGMSW,IHGMSW,
     1                  IMPSW,IMPNR,IMPNC,IMPCO,IMPCO9,
     1                  IREPCH,
     1                  PMXMIN,PMXMAX,PMYMIN,PMYMAX,
     1                  ICNTPL,IOUNI5,
     1                  IBUGG2,IBUGG3,IBUGCO,IBUGEV,IBUGQ,
     1                  IBUGUG,IBUGU2,IBUGU3,IBUGU4,
     1                  ISUBRO,IFOUND,IERROR)
C
C     PURPOSE--UTILTY ROUTINE FOR SCATTER PLOT MATRIX.  GENERATE
C              OVERLAID SMOOTH OR FITTED CURVE ON PLOT.  ALSO
C              USED BY FACTOR AND CONDITIONING PLOTS.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--99/11
C     ORIGINAL VERSION (AS A SEPARATE SUBROUTINE)--NOVEMBERR 1999.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES---------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOPC.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOSP.INC'
C
      CHARACTER*4 ICASPL
      CHARACTER*4 ICASAN
      CHARACTER*4 IANGLU
      CHARACTER*4 IMPSW
      CHARACTER*4 IREPCH
      CHARACTER*4 ISQUAR
      CHARACTER*4 IVGMSW
      CHARACTER*4 IHGMSW
      CHARACTER*4 IAND1
      CHARACTER*4 IAND2
      CHARACTER*4 IFTEXP
      CHARACTER*4 IFTORD
      CHARACTER*4 IOPTME
      CHARACTER*4 IOPTHE
C
      CHARACTER*4 IBUGG2
      CHARACTER*4 IBUGG3
      CHARACTER*4 IBUGUG
      CHARACTER*4 IBUGU2
      CHARACTER*4 IBUGU3
      CHARACTER*4 IBUGU4
      CHARACTER*4 IBUGCO
      CHARACTER*4 IBUGEV
      CHARACTER*4 IBUGQ
C
      CHARACTER*4 ICONT
      CHARACTER*4 IPOWE
      CHARACTER*4 IWRITE
      CHARACTER*4 IFOUND
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*4 ISPMFI
      CHARACTER*4 ISPMTA
C
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICASP2
      CHARACTER*4 IFORSW
      CHARACTER*4 ICOMT
      CHARACTER*4 ICOM2T
C
      CHARACTER*4 IVARN1
      CHARACTER*4 IVARN2
C
      DIMENSION ICOLL(*)
      DIMENSION IVARN1(*)
      DIMENSION IVARN2(*)
C
      DIMENSION TEMP(*)
      DIMENSION TEMP2(*)
      DIMENSION TEMP3(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
C
      PARAMETER (MAXART=20)
      CHARACTER*4 ITHARG
      CHARACTER*4 ITHAR2
      CHARACTER*4 ITARGT
      CHARACTER*4 IANST
      DIMENSION ITHARG(MAXART)
      DIMENSION ITHAR2(MAXART)
      DIMENSION ITARG(MAXART)
      DIMENSION TARG(MAXART)
      DIMENSION ITARGT(MAXART)
      DIMENSION IANST(MAXSTR)
C
C-----COMMON VARIABLES (GENERAL)----------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-------------------------------------------------
C
      DO11I=1,MAXNXT
        TEMP(I)=0.0
        TEMP2(I)=0.0
        TEMP3(I)=0.0
        XTEMP1(I)=0.0
        XTEMP2(I)=0.0
   11 CONTINUE
C
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'SPM2')THEN
        WRITE(ICOUT,12)ICASPL,IVARN1(1),IVARN2(1)
   12   FORMAT('ICASPL,IVARN1(1),IVARN2(1) = ',A4,2X,2A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,14)ICOLL(1),NUMVAR,NPLOTP,IROW,ICOL
   14   FORMAT('ICOLL(1),NUMVAR,NPLOTP,IROW,ICOL = ',5I6)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
CCCCC NOTE: CURRENTLY, LOWESS (AND OTHER) TYPE FITS ONLY
CCCCC       USED FOR "PLOT Y X" TYPE COMMANDS.  NOTE THAT
CCCCC       SOME OF THE LOGIC OF THIS ROUTINE WILL NEED TO
CCCCC       BE UPDATED IF THIS CAPABILITY IS EXTENDED TO
CCCCC       ADDITIONAL PLOT TYPES (I.E., SOME CARE NEEDS TO BE 
CCCCC       TAKEN TO ENSURE THAT SUBSET CLAUSES ARE CARRIED 
CCCCC       ALONG PROPERLY).
C
C               ***************************************
C               **  STEP 1--                         **
C               ***************************************
C
      ICAPSW='OFF'
      IFORSW='-7'
      IERROR='NO'
C
C     PARTIAL REGRESSION, PARTIAL RESIDUAL, AND PARTIAL
C     LEVERAGE PLOT ALLOW FITTED CURVE TO OVERLAID.
C
      IF(ICASPL.EQ.'PREG' .OR. ICASPL.EQ.'PRES' .OR.
     1   ICASPL.EQ.'PLEV')THEN
        ICASP2='PLOT'
      ELSE
        IF(ICOM.NE.'PLOT')GOTO9000
      ENDIF
C
      ICOMT=ICOM
      ICOM2T=ICOM2
      ICASP2=ICASPL
      NUMART=NUMARG
      DO100I=1,NUMARG
        ITHARG(I)=IHARG(I)
        ITHAR2(I)=IHARG2(I)
        ITARG(I)=IARG(I)
        TARG(I)=ARG(I)
        ITARGT(I)=IARGT(I)
  100 CONTINUE
      DO102I=1,MAXSTR
        IANST(I)=IANS(I)
  102 CONTINUE
      IWIDT=IWIDTH
C
      IFLAG=3
      CALL DPSPM5(IFLAG,IMPSW,IMPCO,IMPNR,IMPNC,
     1            IBUGG2,ISUBRO,IFOUND,IERROR)
C
      IF(ISPMTA.EQ.'ON')THEN
        ISHIFT=1
        CALL SHIFTL(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1              IBUGG2,IERROR)
        ITAGCO=3
        DO119I=1,NUMARG
          IF(I.EQ.ITAGCO)GOTO119
          IHARG(I)=ITHARG(I)
          IHARG2(I)=ITHAR2(I)
          IARG(I)=ITARG(I)
          ARG(I)=TARG(I)
          IARGT(I)=ITARGT(I)
  119   CONTINUE
      ENDIF
C
      IF(ICASPL.EQ.'PREG' .OR. ICASPL.EQ.'PRES' .OR.
     1   ICASPL.EQ.'PLEV')THEN
         NUMARG=2
         ICOM='PLOT'
         ICOM2='    '
         IHARG(1)='YPLO'
         IHARG2(1)='T   '
         IHARG(2)='XPLO'
         IHARG2(2)='T   '
      ENDIF
C
      IF(ISPMFI.EQ.'LOES')THEN
        ICOM='LOWE'
        ICOM2='SS  '
        CALL DPLOW(ALOWFR,ALOWDG,ICAPSW,IFORSW,
     1             TEMP,TEMP2,XTEMP1,XTEMP2,MAXNXT,
     1             IBUGG2,IBUGG3,IBUGQ,ISUBRO,IFOUND,IERROR)
      ELSEIF(ISPMFI.EQ.'LINE')THEN
        ICOM='FIT '
        ICOM2='    '
        ICASAN='FIT'
        CALL DPFIT(ICAPSW,IFORSW,
     1             IBUGG2,IBUGG3,IBUGCO,IBUGEV,IBUGQ,ISUBRO,
     1             IFOUND,IERROR)
      ELSEIF(ISPMFI.EQ.'QUAD')THEN
        ISHIFT=1
        CALL SHIFTR(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1              IBUGG2,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
        ICOM='QUAD'
        ICOM2='RATI'
        IHARG(1)='FIT '
        IHARG2(1)='    '
        ICASAN='FIT'
        CALL DPFIT(ICAPSW,IFORSW,
     1             IBUGG2,IBUGG3,IBUGCO,IBUGEV,IBUGQ,ISUBRO,
     1             IFOUND,IERROR)
      ELSEIF(ISPMFI.EQ.'SMOO')THEN
        ICOM='SMOO'
        ICOM2='TH  '
        ICASAN='SMOO'
        CALL DPSMOO(IBUGG2,IBUGG3,IBUGQ,IFOUND,IERROR)
      ENDIF
C
      ICOM='PLOT'
      ICOM2='    '
      ISHIFT=1
      IF(ISHIFT.GT.0)THEN
        CALL SHIFTR(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1            IBUGG2,IERROR)
      ELSEIF(ISHIFT.LT.0)THEN
        CALL SHIFTL(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1            IBUGG2,IERROR)
      ENDIF
      IF(ISPMTA.EQ.'OFF')THEN
        ITEMP=2
      ELSE
        IWRITE='OFF'
        CALL MAXIM(TAGPLO,NPLOTP,IWRITE,XMAX,IBUGG3,IERROR)
        ITEMP=1+INT(XMAX)
        IF(ITEMP.LT.1.OR.ITEMP.GT.100)ITEMP=2
      ENDIF
C
      IF(ICASPL.EQ.'PREG' .OR. ICASPL.EQ.'PRES' .OR.
     1   ICASPL.EQ.'PLEV')THEN
        NUMARG=3
        ICOM='LET '
        ICOM2='    '
        IHARG(1)='XTEM'
        IHARG2(1)='P   '
        IHARG(2)='=   '
        IHARG2(2)='    '
        IHARG(3)='XPLO'
        IHARG2(3)='T   '
        IANS(1)='L   '
        IANS(2)='E   '
        IANS(3)='T   '
        IANS(4)='    '
        IANS(5)='X   '
        IANS(6)='T   '
        IANS(7)='E   '
        IANS(8)='M   '
        IANS(9)='P   '
        IANS(10)='    '
        IANS(11)='=   '
        IANS(12)='    '
        IANS(13)='X   '
        IANS(14)='P   '
        IANS(15)='L   '
        IANS(16)='O   '
        IANS(17)='T   '
        IWIDTH=17
        CALL DPLET(IANGLU,ISEED,IBUGG2,IBUGG3,IBUGCO,IBUGEV,IBUGQ,
     1             TEMP,TEMP2,XTEMP1,XTEMP2,MAXNXT,
     1             IFTEXP,IFTORD,IFORSW,
     1             ROOTAC,OPTACC,IOPTME,IOPTHE,
     1             ISUBRO,IFOUND,IERROR)
C
        IF(IERROR.EQ.'YES')GOTO9000
        ICOM='PLOT'
        ICOM2='    '
        IHARG(1)='PRED'
        IHARG2(1)='    '
        IHARG(2)='VS  '
        IHARG2(2)='    '
        IHARG(3)='XTEM'
        IHARG2(3)='P   '
      ELSE
        IHARG(1)='PRED'
        IHARG2(1)='    '
        IHARG(2)='VS  '
        IHARG2(2)='    '
        IHARG(3)=IVARN1(ICOL)
        IHARG2(3)=IVARN2(ICOL)
      ENDIF
      CALL DPPLOT(NPLOTV,NPLOTP,NS,ICASPL,IAND1,IAND2,
     1            IANGLU,MAXNPP,
