     1            IVSTAR,IVSTOP,IFUNC,NUMCHF,IREPCH,
     1            YPLOT,XPLOT,X2PLOT,TAGPLO,
     1            IMPSW,IMPNR,IMPNC,IMPCO,IMPCO9,
     1            IMPARG,
