      IF(N.GT.0 .AND. IOUNI5.GT.0)THEN
        DO3115II=1,N
          WRITE(IOUNI5,3118)ICNTPL,Y(II),X(II),D(II)
 3115   CONTINUE
 3118   FORMAT(I12,3E15.7)
      ENDIF
