          IF(ISPX2L.EQ.'PCOR')THEN
            NCX2LA=NCX2LA+1
            IX2LTE(NCX2LA)='%'
          ENDIF
        ELSEIF(ISPX2S.NE.' ')THEN
          DO210I=16,1,-1
            IF(ISPX2S(I:I).NE.' ')THEN
              NTEMP=I
              DO220J=1,NTEMP
