  310     CONTINUE
  329     CONTINUE
        ENDIF
        CONST=0.5
        AV=REAL(NACC)/REAL(NACC+NREJ)
        AV=100.0*AV
        IF(IOUNI5.GT.0)WRITE(IOUNI5,'(G15.7)')AV
        IF(AV.LT.0.0)CONST=-0.5
        IF(NUMDIG.GE.0)THEN
