        CONST=0.5
        PX2LDS=-((PYMAX-PYMIN)-PX2LD2)
C
      ELSEIF(ISPX2L.EQ.'NACC')THEN
        IF(ISPX2P.EQ.'DEFAULT')THEN
          NCX2LA=0
        ELSEIF(ISPX2P.EQ.' ')THEN
          NCX2LA=0
        ELSE
          DO410I=16,1,-1
            IF(ISPX2P(I:I).NE.' ')THEN
              NCX2LA=I
              DO420J=1,NCX2LA
                IX2LTE(J)(1:1)=ISPX2P(J:J)
  420         CONTINUE
              GOTO429
            ENDIF
  410     CONTINUE
  429     CONTINUE
        ENDIF
        CONST=0.5
        AV=REAL(NACC)
        IF(IOUNI5.GT.0)WRITE(IOUNI5,'(G15.7)')AV
