  470         CONTINUE
              GOTO479
            ENDIF
  460     CONTINUE
  479     CONTINUE
        ENDIF
        CONST=0.5
        PX2LDS=-((PYMAX-PYMIN)-PX2LD2)
C
      ELSEIF(ISPX2L.EQ.'AT  ')THEN
        IF(ISPX2P.EQ.'DEFAULT')THEN
          NCX2LA=0
        ELSEIF(ISPX2P.EQ.' ')THEN
          NCX2LA=0
        ELSE
          DO810I=16,1,-1
            IF(ISPX2P(I:I).NE.' ')THEN
              NCX2LA=I
              DO820J=1,NCX2LA
                IX2LTE(J)(1:1)=ISPX2P(J:J)
  820         CONTINUE
              GOTO829
            ENDIF
  810     CONTINUE
  829     CONTINUE
        ENDIF
        CONST=0.5
        AV=REAL(NACC)
        AV1=AV
        IF(AV.LT.0.0)CONST=-0.5
        IF(NUMDIG.GE.0)THEN
          IVAL=INT(AV*10**NUMDIG + CONST)
          AV=REAL(IVAL)/(10**NUMDIG)
        ENDIF
        NCX2LA=NCX2LA+1
        CALL DPCONH(IVAL,AV,IX2LTE(NCX2LA),NH,IBUGG2,IERROR)
        NCX2LA=NCX2LA+NH
        NCX2LA=NCX2LA+1
        IX2LTE(NCX2LA)(1:1)='/'
        AV=REAL(NACC+NREJ)
        IF(IOUNI5.GT.0)WRITE(IOUNI5,'(2G15.7)')AV1,AV
        IF(AV.LT.0.0)CONST=-0.5
        IF(NUMDIG.GE.0)THEN
          IVAL=INT(AV*10**NUMDIG + CONST)
          AV=REAL(IVAL)/(10**NUMDIG)
        ENDIF
        NCX2LA=NCX2LA+1
        CALL DPCONH(IVAL,AV,IX2LTE(NCX2LA),NH,IBUGG2,IERROR)
        NCX2LA=NCX2LA+NH
        IF(ISPX2S.EQ.'DEFAULT')THEN
          CONTINUE
        ELSEIF(ISPX2P.NE.' ')THEN
          DO860I=16,1,-1
            IF(ISPX2S(I:I).NE.' ')THEN
              NTEMP=I
              DO870J=1,NTEMP
                NCX2LA=NCX2LA+1
                IX2LTE(NCX2LA)(1:1)=ISPX2P(J:J)
  870         CONTINUE
              GOTO879
            ENDIF
  860     CONTINUE
  879     CONTINUE
