        ELSE
          DO610I=16,1,-1
            IF(ISPX2P(I:I).NE.' ')THEN
              NCX2LA=I
              DO620J=1,NCX2LA
                IX2LTE(J)(1:1)=ISPX2P(J:J)
  620         CONTINUE
              GOTO629
            ENDIF
  610     CONTINUE
  629     CONTINUE
        ENDIF
        CONST=0.5
C
C       2018/04: SIGN IS WRONG DIRECTION
C
CCCCC   AVAL=ALOWH
        AVAL=-ALOWH
        IF(IOUNI5.GT.0)WRITE(IOUNI5,'(G15.7)')AVAL
        IF(AVAL.LT.0.0)CONST=-0.5
