  670         CONTINUE
              GOTO679
            ENDIF
  660     CONTINUE
  679     CONTINUE
        ENDIF
        CONST=0.5
        PX2LDS=-((PYMAX-PYMIN)-PX2LD2)
C
C       NOW ADD RELATIVE EFFECTS IF REQUESTED
C
        AVAL=GRAND
        IF(ISPX2L.EQ.'REFF' .AND. AVAL.NE.CPUMIN)THEN
