          ELSEIF(ICOL.LE.99)THEN
            WRITE(IXVAL(1:2),'(I2)')ICOL
            NCX2LA=NCX2LA+1
            IX2LTE(NCX2LA)(1:1)=IXVAL(1:1)
            NCX2LA=NCX2LA+1
            IX2LTE(NCX2LA)(1:1)=IXVAL(2:2)
          ENDIF
        ENDIF
        NCX2LA=NCX2LA+1
        IX2LTE(NCX2LA)(1:1)=':'
        NCX2LA=NCX2LA+1
        NCX2LA=NCX2LA+1
        IX2LTE(NCX2LA)(1:1)=' '
        CONST=0.5
CCCCC   AVAL=ALOWH
        AVAL=-ALOWH
        IF(IOUNI5.GT.0)WRITE(IOUNI5,'(G15.7)')AVAL
        IF(AVAL.LT.0.0)CONST=-0.5
        IF(NUMDIG.GE.0)THEN
          IVAL=INT(AVAL*10**NUMDIG + CONST)
          AVAL=REAL(IVAL)/(10**NUMDIG)
        ENDIF
        NCX2LA=NCX2LA+1
        CALL DPCONH(IVAL,AVAL,IX2LTE(NCX2LA),NH,IBUGG2,IERROR)
        NCX2LA=NCX2LA+NH
        CONST=0.5
        PX2LDS=-((PYMAX-PYMIN)-PX2LD2)
C
C       NOW ADD RELATIVE EFFECTS IF REQUESTED
C
        AVAL2=GRAND
        IF(AVAL2.NE.0.0)THEN
          AVAL=100.0*(AVAL/AVAL2)
        ELSE
          AVAL=CPUMIN
        ENDIF
        IF(ISPX2L.EQ.'FREF' .AND. AVAL.NE.CPUMIN)THEN
          CONST=0.5
          IF(AVAL.LT.0.0)CONST=-0.5
          IVAL=INT(100.0*AVAL + CONST)
          AVAL=REAL(IVAL)/100.0
          NCX2LA=NCX2LA+1
          IX2LTE(NCX2LA)(1:1)=' '
          NCX2LA=NCX2LA+1
          IX2LTE(NCX2LA)(1:1)=' '
          NCX2LA=NCX2LA+1
          IX2LTE(NCX2LA)(1:1)='('
          NCX2LA=NCX2LA+1
          CALL DPCONH(IVAL,AVAL,IX2LTE(NCX2LA),NH,IBUGG2,IERROR)
          NCX2LA=NCX2LA+NH
          NCX2LA=NCX2LA+1
          IX2LTE(NCX2LA)(1:1)='%'
          NCX2LA=NCX2LA+1
          IX2LTE(NCX2LA)(1:1)=')'
        ENDIF
      ENDIF
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'SPM3')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSPM3--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)J,NCX2LA,PX2LDS
 9013   FORMAT('J,NCX2LA,PX2LDS = ',2I8,G15.7)
        CALL DPWRST('XXX','BUG ')
        IF(NCX2LA.GT.0)THEN
          DO9014I=1,NCX2LA
            WRITE(ICOUT,9015)I,IX2LTE(I)(1:1)
 9015       FORMAT('I,IX2LTE(J)(1:1) = ',I6,A1)
            CALL DPWRST('XXX','BUG ')
 9014     CONTINUE
        ENDIF
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSPM4(ICASPL,IOPTN,IDX,IDY,
     1                  ISUBNU,
     1                  ISUBSW,
     1                  ASUBXL,ASUBXU,ASUBYL,ASUBYU,
     1                  ISUBN9,
     1                  ISUBSZ,
     1                  ASBXL2,ASBXU2,ASBYL2,ASBYU2,
     1                  PSPXSL,PSPXSU,PSPYSL,PSPYSU,
     1                  IBUGG2,ISUBRO,IERROR)
C
C     PURPOSE--UTILTY ROUTINE FOR SCATTER PLOT MATRIX.  SET SUBREGION
C              LIMITS (IF SPECIFIED BY USER).
C              IOPTN = 1  - SAVE CURENT SETTINGS
C              IOPTN = 2  - RESTORE CURENT SETTINGS
C              IOPTN = 3  - SET SUBREGION LIMITS FOR GIVEN Y, X
C                           PAIR OF VARIABLES.  NOTE IF LIMIT SET TO
C                           CPUMIN OR CPUMAX, THEN NOTHING SET.
C                           ALSO, MATRIX PLOTS ONLY RESET FIRST
C                           SUBREGION (OTHERS ARE LEFT AS IS)
C              IDX   = SETTING OF PSPXSL, PSPXSU TO USE
C              IDY   = SETTING OF PSPYSL, PSPYSU TO USE
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--99/12
C     ORIGINAL VERSION (AS A SEPARATE SUBROUTINE)--DECEMBER  1999.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES---------------
C
      CHARACTER*4 IBUGG2
      CHARACTER*4 ICASPL
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*4 ISUBSW
      CHARACTER*4 ISUBSZ
C
      DIMENSION ISUBSW(*)
      DIMENSION ASUBXL(*)
      DIMENSION ASUBXU(*)
      DIMENSION ASUBYL(*)
      DIMENSION ASUBYU(*)
      DIMENSION PSPXSL(*)
      DIMENSION PSPXSU(*)
      DIMENSION PSPYSL(*)
      DIMENSION PSPYSU(*)
C
C-----COMMON VARIABLES (GENERAL)----------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-------------------------------------------------
C
      IF(ISUBRO.EQ.'SPM4')THEN
        WRITE(ICOUT,12)ICASPL,IFOUND
   12   FORMAT('ICASPL,IFOUND = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IERROR='NO'
C
C               ***************************************
C               **  STEP 1--SAVE INITIAL SETTINGS    **
C               ***************************************
C
      IF(IOPTN.EQ.1)THEN
        ISUBSZ=ISUBSW(1)
        ASBXL2=ASUBXL(1)
        ASBXU2=ASUBXU(1)
        ASBYL2=ASUBYL(1)
        ASBYU2=ASUBYU(1)
        ISUBN9=ISUBNU
      ELSEIF(IOPTN.EQ.2)THEN
        ISUBSW(1)=ISUBSZ
        ASUBXL(1)=ASBXL2
        ASUBXU(1)=ASBXU2
        ASUBYL(1)=ASBYL2
        ASUBYU(1)=ASBYU2
        ISUBNU=ISUBN9
      ELSEIF(IOPTN.EQ.3)THEN
        IF(PSPXSL(IDX).NE.CPUMIN .AND. PSPXSU(IDX).NE.CPUMIN)THEN
          ISUBSW(1)='ON'
          IF(ISUBNU.EQ.0)ISUBNU=1
          ASUBXL(1)=PSPXSL(IDX)
          ASUBXU(1)=PSPXSU(IDX)
        ENDIF
        IF(PSPYSL(IDY).NE.CPUMIN .AND. PSPYSU(IDY).NE.CPUMIN)THEN
          ISUBSW(1)='ON'
          IF(ISUBNU.EQ.0)ISUBNU=1
          ASUBYL(1)=PSPYSL(IDY)
          ASUBYU(1)=PSPYSU(IDY)
        ENDIF
      ENDIF
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'SPM4')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSPM4--')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSPM5(IFLAG,IMPSW,IMPCO,IMPNR,IMPNC,
     1                  IBUGG2,ISUBRO,IFOUND,IERROR)
C
C     PURPOSE--UTILTY ROUTINE FOR SCATTER PLOT MATRIX.  DO ONE OF THE
C              FOLLOWING:
C                 1) SAVE CURRENT PLOT CONTROL SETTINGS FOR PLOT
C                 2) RESTORE CURRENT PLOT CONTROL SETTINGS FOR PLOT
C                 3) SAVE CURRENT PLOT CONTROL SETTINGS FOR OVERLAID FIT
C                 4) RESTORE CURRENT PLOT CONTROL SETTINGS FOR OVERLAID FIT
C               ALSO USED BY CONDITION PLOT AND FACTOR PLOT
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2011/7
C     ORIGINAL VERSION--JULY 2011.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES---------------
C
      CHARACTER*4 IMPSW
      CHARACTER*4 IBUGG2
      CHARACTER*4 ISUBRO
      CHARACTER*4 IFOUND
      CHARACTER*4 IERROR
C
C-----COMMON VARIABLES (GENERAL)----------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOPC.INC'
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOSP.INC'
      INCLUDE 'DPCOF2.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-------------------------------------------------
C
      IFOUND='YES'
      IERROR='NO'
C
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'SPM5')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSPM5--')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************************************
C               **  STEP 0--SAVE/RESTORE PLOT CONTROL SETTINGS THAT ARE **
C               **          COMMON TO BOTH CASES.                       **
C               **********************************************************
C
      IF(IFLAG.EQ.1 .OR. IFLAG.EQ.3)THEN
C
        GY1MNS=GY1MIN
        GY1MXS=GY1MAX
        GY2MNS=GY2MIN
        GY2MXS=GY2MAX
        GX1MNS=GX1MIN
        GX1MXS=GX1MAX
        GX2MNS=GX2MIN
        GX2MXS=GX2MAX
C
        IY1MNS=IY1MIN
        IY1MXS=IY1MAX
        IY2MNS=IY2MIN
        IY2MXS=IY2MAX
        IX1MNS=IX1MIN
        IX1MXS=IX1MAX
        IX2MNS=IX2MIN
        IX2MXS=IX2MAX
C
C           **********************************************************
C           **  STEP 1--SAVE PLOT CONTROL SETTINGS FOR INITIAL PLOT **
C           **********************************************************
C
        IF(IFLAG.EQ.1)THEN
C
          PXMN2=PXMIN
          PXMX2=PXMAX
          PYMN2=PYMIN
          PYMX2=PYMAX
          PWXMN2=PWXMIN
          PWXMX2=PWXMAX
          PWYMN2=PWYMIN
          PWYMX2=PWYMAX
          IF(ISPMFR.EQ.'DEFA')THEN
            PXMIN=0.0
            PXMAX=100.0
            PYMIN=0.0
            PYMAX=100.0
          ENDIF
C
          IFENC2=IFENSW
          IERAS2=IERASW
          IPPTB2=IPPTBI
          ISORS2=ISORSW
C
          IX1TSV=IX1TSW
          IX2TSV=IX2TSW
          IY1TSV=IY1TSW
          IY2TSV=IY2TSW
          IX1ZSV=IX1ZSW
          IX2ZSV=IX2ZSW
          IY1ZSV=IY1ZSW
          IY2ZSV=IY2ZSW
          PX1LD2=PX1LDS
          PX2LD2=PX2LDS
          PY1LD2=PY1LDS
          PY1LA2=PY1LAN
          IY1LJ2=IY1LJU
          IY1LD2=IY1LDI
          IX1FSV=IX1FSW
          IX2FSV=IX2FSW
          IY1FSV=IY1FSW
          IY2FSV=IY2FSW
          PX1ZD2=PX1ZDS
          PX2ZD2=PX2ZDS
          PY1ZD2=PY1ZDS
          PY2ZD2=PY2ZDS
          DO1010I=1,100
            ICHAP2(I)=ICHAPA(I)
            ILINP2(I)=ILINPA(I)
            IBARS2(I)=IBARSW(I)
            ISPIS2(I)=ISPISW(I)
 1010     CONTINUE
C
          DO1020I=1,MAXCH
            IX1LT2(I)=IX1LTE(I)
            IX2LT2(I)=IX2LTE(I)
            IY1LT2(I)=IY1LTE(I)
            IY2LT2(I)=IY2LTE(I)
 1020     CONTINUE
          NCX1L2=NCX1LA
          NCX2L2=NCX2LA
          NCY1L2=NCY1LA
          NCY2L2=NCY2LA
C
          DO1030I=1,MAXCH
            ITITSV(I)=ITITTE(I)
 1030     CONTINUE
          NCTITS=NCTITL
          PTITDZ=PTITDS
C
C           **********************************************************
C           **  STEP 3--SAVE PLOT CONTROL SETTINGS FOR OVERLAID PLOT *
C           **********************************************************
C
        ELSEIF(IFLAG.EQ.3)THEN
C
          DO3010I=1,MAXSUB
            ISU2SW(I)=ISUBSW(I)
            ISUBSW(I)='OFF'
 3010     CONTINUE
C
          DO3020I=1,100
            ILI2CO(I)=ILINCO(I)
            PLI2TH(I)=PLINTH(I)
            ICH2PO(I)=ICHAPO(I)
            ICH2FO(I)=ICHAFO(I)
            ICH2CA(I)=ICHACA(I)
            ICH2JU(I)=ICHAJU(I)
            ICH2DI(I)=ICHADI(I)
            ICH2FI(I)=ICHAFI(I)
            ICH2CO(I)=ICHACO(I)
            PCH2HE(I)=PCHAHE(I)
            PCH2WI(I)=PCHAWI(I)
            PCH2VG(I)=PCHAVG(I)
            PCH2HG(I)=PCHAHG(I)
            PCH2HO(I)=PCHAHO(I)
            PCH2VO(I)=PCHAVO(I)
            ACH2AN(I)=ACHAAN(I)
 3020     CONTINUE
C
          GY1MIN=FY1MNZ
          GY1MAX=FY1MXZ
          GY2MIN=GY1MIN
          GY2MAX=GY1MAX
          GX1MIN=FX1MNZ
          GX1MAX=FX1MXZ
          GX2MIN=GX1MIN
          GX2MAX=GX1MAX
          IY1MIN='FIXE'
          IY1MAX='FIXE'
          IY2MIN='FIXE'
          IY2MAX='FIXE'
          IX1MIN='FIXE'
          IX1MAX='FIXE'
          IX2MIN='FIXE'
          IX2MAX='FIXE'
          IX1TSW='OFF'
          IX1ZSW='OFF'
          IX2TSW='OFF'
          IX2ZSW='OFF'
          IY1TSW='OFF'
          IY1ZSW='OFF'
          IY2TSW='OFF'
          IY2ZSW='OFF'
C
CCCCC     NOTE: SHOULD ITEMP BE PASSED AS ARGUMENT?  NEED TO
CCCCC           INVESTIGATE THIS FURTHER.
C
          ITEMP=1
          ICHAPA(1)=ICHAP2(ITEMP)
          ILINPA(1)=ILINP2(ITEMP)
          ILINCO(1)=ILI2CO(ITEMP)
          PLINTH(1)=PLI2TH(ITEMP)
          ICHAPO(1)=ICH2PO(ITEMP)
          ICHAFO(1)=ICH2FO(ITEMP)
          ICHACA(1)=ICH2CA(ITEMP)
          ICHAJU(1)=ICH2JU(ITEMP)
          ICHADI(1)=ICH2DI(ITEMP)
          ICHAFI(1)=ICH2FI(ITEMP)
          ICHACO(1)=ICH2CO(ITEMP)
          PCHAHE(1)=PCH2HE(ITEMP)
          PCHAWI(1)=PCH2WI(ITEMP)
          PCHAVG(1)=PCH2VG(ITEMP)
          PCHAHG(1)=PCH2HG(ITEMP)
          PCHAHO(1)=PCH2HO(ITEMP)
          PCHAVO(1)=PCH2VO(ITEMP)
          ACHAAN(1)=ACH2AN(ITEMP)
        ENDIF
      ELSEIF(IFLAG.EQ.2 .OR. IFLAG.EQ.4)THEN
C
        GX1MIN=GX1MNS
        GX1MAX=GX1MXS
        GX2MIN=GX2MNS
        GX2MAX=GX2MXS
        GY1MIN=GY1MNS
        GY1MAX=GY1MXS
        GY2MIN=GY2MNS
        GY2MAX=GY2MXS
C
        IX1MIN=IX1MNS
        IX1MAX=IX1MXS
        IX2MIN=IX2MNS
        IX2MAX=IX2MXS
        IY1MIN=IY1MNS
        IY1MAX=IY1MXS
        IY2MIN=IY2MNS
        IY2MAX=IY2MXS
C
C         *********************************************************
C         **  STEP 2--RESTORE PLOT CONTROL SETTINGS FOR INITIAL  **
C         **          PLOT                                       **
C         *********************************************************
C
        IF(IFLAG.EQ.2)THEN
C
          PWXMIN=PWXMN2
          PWXMAX=PWXMX2
          PWYMIN=PWYMN2
          PWYMAX=PWYMX2
          PXMIN=PXMN2
          PXMAX=PXMX2
          PYMIN=PYMN2
          PYMAX=PYMX2
C
          IERASW=IERAS2
          IFENSW=IFENC2
          ISORSW=ISORS2
          IPPTBI=IPPTB2
C
          IX1TSW=IX1TSV
          IX2TSW=IX2TSV
          IY1TSW=IY1TSV
          IY2TSW=IY2TSV
          IX1ZSW=IX1ZSV
          IX2ZSW=IX2ZSV
          IY1ZSW=IY1ZSV
          IY2ZSW=IY2ZSV
          PX1LDS=PX1LD2
          PX2LDS=PX2LD2
          PY1LDS=PY1LD2
          PY1LAN=PY1LA2
          IY1LJU=IY1LJ2
          IY1LDI=IY1LD2
          PX1ZDS=PX1ZD2
          PX2ZDS=PX2ZD2
          PY1ZDS=PY1ZD2
          PY2ZDS=PY2ZD2
C
          DO2010I=1,100
            ICHAPA(I)=ICHAP2(I)
            ILINPA(I)=ILINP2(I)
            IBARSW(I)=IBARS2(I)
            ISPISW(I)=ISPIS2(I)
 2010   CONTINUE
C
          IMPSW='OFF'
          IMPCO=1
          IMPNR=IMPNR2
          IMPNC=IMPNC2
C
          DO2020I=1,MAXCH
            IX1LTE(I)=IX1LT2(I)
            IX2LTE(I)=IX2LT2(I)
            IY1LTE(I)=IY1LT2(I)
            IY2LTE(I)=IY2LT2(I)
 2020   CONTINUE
          NCX1LA=NCX1L2
          NCX2LA=NCX2L2
          NCY1LA=NCY1L2
          NCY2LA=NCY2L2
          DO2030I=1,MAXCH
            ITITTE(I)=ITITSV(I)
 2030     CONTINUE
          NCTITL=NCTITS
          PTITDS=PTITDZ
C
C           **********************************************************
C           **  STEP 4--RESTORE PLOT CONTROL SETTINGS FOR OVERLAID  **
C           **          PLOT                                        **
C           **********************************************************
C
        ELSEIF(IFLAG.EQ.4)THEN
C
          DO4010I=1,100
            ILINCO(I)=ILI2CO(I)
            PLINTH(I)=PLI2TH(I)
            ICHAPO(I)=ICH2PO(I)
            ICHAFO(I)=ICH2FO(I)
            ICHACA(I)=ICH2CA(I)
            ICHAJU(I)=ICH2JU(I)
            ICHADI(I)=ICH2DI(I)
            ICHAFI(I)=ICH2FI(I)
            ICHACO(I)=ICH2CO(I)
            PCHAHE(I)=PCH2HE(I)
            PCHAWI(I)=PCH2WI(I)
            PCHAVG(I)=PCH2VG(I)
            PCHAHG(I)=PCH2HG(I)
            PCHAHO(I)=PCH2HO(I)
            PCHAVO(I)=PCH2VO(I)
            ACHAAN(I)=ACH2AN(I)
 4010     CONTINUE
        ENDIF
C
      ENDIF
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'SPM2')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSPM5--')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
