C               **  (CORRELATION AND RANK CORRELATION).   **
C               ********************************************
C
      ISTEPN='4'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STAC')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
