          NUMVIN=1
        ELSEIF(IH.EQ.'FOR '.AND.IH2.EQ.'    ')THEN
          NUMVIN=1
        ELSE
          NUMVIN=2
        ENDIF
      ELSEIF(ISTANR.EQ.2)THEN
        NUMVIN=2
      ELSEIF(ISTANR.EQ.3)THEN
        NUMVIN=3
      ELSE
        NUMVIN=1
