        ELSEIF(IH.EQ.'SUBS'.AND.IH2.EQ.'ET  ')THEN
          NUMVIN=2
        ELSEIF(IH.EQ.'EXCE'.AND.IH2.EQ.'PT  ')THEN
          NUMVIN=2
        ELSEIF(IH.EQ.'FOR '.AND.IH2.EQ.'    ')THEN
          NUMVIN=2
        ELSE
          NUMVIN=3
        ENDIF
      ENDIF
