            IF(ICOLT.EQ.MAXCP2)TEMP(NS2)=RES(I)
            IF(ICOLT.EQ.MAXCP3)TEMP(NS2)=YPLOT(I)
            IF(ICOLT.EQ.MAXCP4)TEMP(NS2)=XPLOT(I)
            IF(ICOLT.EQ.MAXCP5)TEMP(NS2)=X2PLOT(I)
            IF(ICOLT.EQ.MAXCP6)TEMP(NS2)=TAGPLO(I)
11103     CONTINUE
11105     CONTINUE
11101   CONTINUE
      ENDIF
