            IF(ICOL2.EQ.MAXCP1)TEMP2(NS3)=PRED(I)
            IF(ICOL2.EQ.MAXCP2)TEMP2(NS3)=RES(I)
            IF(ICOL2.EQ.MAXCP3)TEMP2(NS3)=YPLOT(I)
            IF(ICOL2.EQ.MAXCP4)TEMP2(NS3)=XPLOT(I)
            IF(ICOL2.EQ.MAXCP5)TEMP2(NS3)=X2PLOT(I)
            IF(ICOL2.EQ.MAXCP6)TEMP2(NS3)=TAGPLO(I)
C
11200     CONTINUE
11209     CONTINUE
        ELSE
          NLOOP=NCOL2
          IF(NLOOP.LT.1)NLOOP=1
          NS3=0
          DO11201JLOOP=1,NLOOP
            DO11203I=1,NINEW
              IF(ISUB(I).EQ.0)GOTO11203
              IF(I.GT.NIRIG2)GOTO11205
              NS3=NS3+1
              IF(NS3.GT.MAXOBV)THEN
                WRITE(ICOUT,999)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,221)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,11106)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,11107)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,11108)MAXOBV
                CALL DPWRST('XXX','BUG ')
                IERROR='YES'
                GOTO9000
              ENDIF
              ICOLT=ICOL2+JLOOP-1
              IJ=MAXN*(ICOLT-1)+I
              IF(ICOLT.LE.MAXCOL)TEMP2(NS3)=V(IJ)
              IF(ICOLT.EQ.MAXCP1)TEMP2(NS3)=PRED(I)
              IF(ICOLT.EQ.MAXCP2)TEMP2(NS3)=RES(I)
              IF(ICOLT.EQ.MAXCP3)TEMP2(NS3)=YPLOT(I)
              IF(ICOLT.EQ.MAXCP4)TEMP2(NS3)=XPLOT(I)
              IF(ICOLT.EQ.MAXCP5)TEMP2(NS3)=X2PLOT(I)
              IF(ICOLT.EQ.MAXCP6)TEMP2(NS3)=TAGPLO(I)
11203       CONTINUE
11205       CONTINUE
11201     CONTINUE
        ENDIF
      ENDIF
C
      IF(NUMVAR.GE.3)THEN
        IF(IFLGP3.EQ.1)THEN
          NS4=1
          TEMP3(NS4)=AVAL3
        ELSEIF(ICOL32.LE.0)THEN
          DO11300I=1,NINEW
            IF(ISUB(I).EQ.0)GOTO11300
            IF(I.GT.NIRIG3)GOTO11309
            NS4=NS4+1
C
            IJ=MAXN*(ICOL3-1)+I
            IF(ICOL3.LE.MAXCOL)TEMP3(NS4)=V(IJ)
            IF(ICOL3.EQ.MAXCP1)TEMP3(NS4)=PRED(I)
            IF(ICOL3.EQ.MAXCP2)TEMP3(NS4)=RES(I)
            IF(ICOL3.EQ.MAXCP3)TEMP3(NS4)=YPLOT(I)
            IF(ICOL3.EQ.MAXCP4)TEMP3(NS4)=XPLOT(I)
            IF(ICOL3.EQ.MAXCP5)TEMP3(NS4)=X2PLOT(I)
            IF(ICOL3.EQ.MAXCP6)TEMP3(NS4)=TAGPLO(I)
C
11300     CONTINUE
11309     CONTINUE
        ELSE
          NLOOP=NCOL3
          IF(NLOOP.LT.1)NLOOP=1
          NS4=0
          DO11301JLOOP=1,NLOOP
            DO11303I=1,NINEW
              IF(ISUB(I).EQ.0)GOTO11303
              IF(I.GT.NIRIG3)GOTO11305
              NS4=NS4+1
              IF(NS4.GT.MAXOBV)THEN
                WRITE(ICOUT,999)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,221)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,11106)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,11107)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,11108)MAXOBV
                CALL DPWRST('XXX','BUG ')
                IERROR='YES'
                GOTO9000
              ENDIF
              ICOLT=ICOL3+JLOOP-1
              IJ=MAXN*(ICOLT-1)+I
              IF(ICOLT.LE.MAXCOL)TEMP3(NS4)=V(IJ)
              IF(ICOLT.EQ.MAXCP1)TEMP3(NS4)=PRED(I)
              IF(ICOLT.EQ.MAXCP2)TEMP3(NS4)=RES(I)
              IF(ICOLT.EQ.MAXCP3)TEMP3(NS4)=YPLOT(I)
              IF(ICOLT.EQ.MAXCP4)TEMP3(NS4)=XPLOT(I)
              IF(ICOLT.EQ.MAXCP5)TEMP3(NS4)=X2PLOT(I)
              IF(ICOLT.EQ.MAXCP6)TEMP3(NS4)=TAGPLO(I)
11303       CONTINUE
11305       CONTINUE
11301     CONTINUE
        ENDIF
      ENDIF
C
      IF(NS2.LE.0)THEN
        IF(ICASL8.EQ.'NUMB')THEN
          RIGHT=0
          IFOUND='YES'
          IERROR='NO'
          IF(ICASEL.EQ.'PARA')GOTO15000
          IF(ICASEL.EQ.'ELEM')GOTO16000
        ELSE
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,12111)ICASL8
12111     FORMAT('****** ERROR--AFTER SUBSET/FOR/EXCEPT CLAUSE ',
     1           'APPLIED FOR STATISTIC ',A4,',')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,12113)
12113     FORMAT('       THE RESPONSE VARIABLE IS EMPTY.  THE ',
     1           'STATISTIC WAS NOT COMPUTED.')
          CALL DPWRST('XXX','BUG ')
          IFOUND='YES'
          IERROR='YES'
          GOTO19000
        ENDIF
      ENDIF
C
      IWRITE='ON'
      IF(IPRINT.EQ.'OFF')IWRITE='OFF'
      IF(IFEEDB.EQ.'OFF')IWRITE='OFF'
C
      ISTEPN='12'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STC2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CALL CMPST2(TEMP,TEMP2,TEMP3,XTEMP1,XTEMP2,XTEMP3,
     1            MAXNXT,NS2,NS3,NS4,NUMVAR,ICASL8,
     1            ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1            DTEMP1,DTEMP2,DTEMP3,
     1            RIGHT,
     1            ISUBRO,IBUGA3,IERROR)
C
      GOTO11900
C
11900 CONTINUE
      ISTEPN='13'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STC2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IFOUND='YES'
      IF(IERROR.EQ.'YES')GOTO19000
      IF(ICASEL.EQ.'PARA')GOTO15000
      IF(ICASEL.EQ.'ELEM')GOTO16000
C
C               *****************************************************
C               **  STEP 15--                                      **
C               **  TREAT THE PARAMETER CASE.                      **
C               **  EXAMPLE--LET A = MEAN X                        **
C               **           WHERE A WAS PREVIOUSLY UNDEFINED      **
C               **           OR WHERE A WAS PREVIOUSLY A PARAMETER.**
C               **  CARRY OUT THE LIST UPDATING  AND               **
C               **  GENERATE THE INFORMATIVE PRINTING.             **
C               **  THEN EXIT.                                     **
C               *****************************************************
C
15000 CONTINUE
      ISTEPN='15'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STC2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHNAME(ILISTL)=ILEFT
      IHNAM2(ILISTL)=ILEFT2
      IUSE(ILISTL)='P'
      VALUE(ILISTL)=RIGHT
      IF(VALUE(ILISTL).GE.0.0)THEN
        IVALUE(ILISTL)=INT(VALUE(ILISTL)+0.5)
      ELSE
        IVALUE(ILISTL)=INT(VALUE(ILISTL)-0.5)
      ENDIF
      IN(ILISTL)=1
      IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
C
      IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,15111)ILEFT,ILEFT2,RIGHT
15111   FORMAT('THE COMPUTED VALUE OF THE CONSTANT ',
     1         A4,A4,'      = ',G15.7)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
      GOTO19000
C
C               *********************************************
C               **  STEP 16--                              **
C               **  TREAT THE ELEMENT SPECIFICATION CASE.  **
C               **  EXAMPLE--LET Y(4)=MEAN X               **
C               **  ALSO, CARRY OUT THE LIST UPDATING AND  **
C               **  GENERATE THE INFORMATIVE PRINTING.     **
C               *********************************************
C
16000 CONTINUE
      ISTEPN='16'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STC2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IARGL=INT1(4)
      IF(1.LE.IARGL.AND.IARGL.LE.MAXN)GOTO16100
      WRITE(ICOUT,221)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,16002)IARGL,ILEFT
16002 FORMAT('      THE SPECIFIED ROW (',I8,') OF VARIABLE ',A4,A4)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,16003)RIGHT
16003 FORMAT('      (THAT WAS TO BE SET = ',G15.7,')')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,16004)
16004 FORMAT('      WAS LESS THAN 1 OR')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,16005)MAXN
16005 FORMAT('      GREATER THAN THE MAX ALLOWABLE ',I8)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO19000
C
16100 CONTINUE
      IF(NEWNAM.EQ.'NO')NIOLD=IN(ILISTL)
      IF(NEWNAM.EQ.'YES')NIOLD=1
      NINEW=NIOLD
      IF(IARGL.GT.NINEW)NINEW=IARGL
      NS2=1
C
      IJ=MAXN*(ICOLL-1)+IARGL
      IF(ICOLL.LE.MAXCOL)V(IJ)=RIGHT
      IF(ICOLL.EQ.MAXCP1)PRED(IARGL)=RIGHT
      IF(ICOLL.EQ.MAXCP2)RES(IARGL)=RIGHT
      IF(ICOLL.EQ.MAXCP3)YPLOT(IARGL)=RIGHT
      IF(ICOLL.EQ.MAXCP4)XPLOT(IARGL)=RIGHT
      IF(ICOLL.EQ.MAXCP5)X2PLOT(IARGL)=RIGHT
      IF(ICOLL.EQ.MAXCP6)TAGPLO(IARGL)=RIGHT
C
      IHNAME(ILISTL)=ILEFT
      IHNAM2(ILISTL)=ILEFT2
      IUSE(ILISTL)='V'
      IVALUE(ILISTL)=ICOLL
      VALUE(ILISTL)=ICOLL
      IN(ILISTL)=NINEW
C
      IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
      IF(NEWNAM.EQ.'YES')NUMCOL=NUMCOL+1
C
      DO16200J4=1,NUMNAM
      IF(IUSE(J4).EQ.'V'.AND.IVALUE(J4).EQ.ICOLL)GOTO16205
      GOTO16200
16205 CONTINUE
      IUSE(J4)='V'
      IVALUE(J4)=ICOLL
      VALUE(J4)=ICOLL
      IN(J4)=NINEW
16200 CONTINUE
C
      IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,16111)ILEFT,ILEFT2,IARGL,RIGHT
16111   FORMAT('THE COMPUTED VALUE OF ',
     1         A4,A4,'(',I6,')      = ',G15.7)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
      GOTO19000
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
19000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STC2')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,19011)
19011   FORMAT('***** AT THE END       OF DPSTC2--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,19012)IFOUND,IERROR
19012   FORMAT('IFOUND,IERROR = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9014)ICASL8,ICASEL,IWRITE,IFLAGD,ILOCV
 9014   FORMAT('ICASL8,ICASEL,IWRITE,IFLAGD,ILOCV = ',4(A4,2X),I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,19015)NS2,NS3,NS4,RIGHT
19015   FORMAT('NS2,NS3,NS4,RIGHT = ',3I8,G15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTCH(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--CONVERT AN ARRAY OF NUMBERS TO A STRING THAT
C              CONTAINS THE ASCII CHARACTERS CORRESPONDING TO
C              THE NUMBERS.
C     EXAMPLE--LET SOUT = CHARACTER IVAL
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/11
C     ORIGINAL VERSION--NOVEMBER  2008.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 ICASER
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
C
      CHARACTER*1 IC
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*4 ILAB(10)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='CH  '
      IERROR='NO'
C
      ILOC3=0
      ICOLR=0
      AVAL=0.0
C
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCH')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTCH--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCH')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCH')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN CHARACTER--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE FIRST NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCH')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHRIGH=IHARG(4)
      IHRIG2=IHARG2(4)
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).EQ.'P')THEN
            ICASER='PARA'
            ILISTR=I4
            IVAL=IVALUE(ILISTR)
            NIOLD=1
            GOTO3099
          ELSEIF(IUSE(I4).EQ.'V')THEN
            ICASER='VARI'
            ILISTR=I4
            ICOLR=IVALUE(ILISTR)
            NIOLD=IN(ILISTR)
            GOTO3099
          ELSE
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER ',
     1             'OR A VARIABLE.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 3000 CONTINUE
C
      IF(NUMARG.GE.4)THEN
        IF(IARGT(4).EQ.'NUMB')THEN
          IVAL=IARG(4)
          ICASER='PARA'
          GOTO3099
        ENDIF
      ENDIF
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  CREATE THE STRING                              **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCH')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ILISTR,NIOLD
 4011   FORMAT('ILISTR,NIOLD = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL,ICASER
 4013   FORMAT('ICASEL,ICASER = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(ICASER.EQ.'PARA')THEN
C
        IF(IVAL.LT.0 .OR. IVAL.GT.255)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4021)
 4021     FORMAT('      THE PARAMETER ON THE RIGHT HAND SIDE IS')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4023)
 4023     FORMAT('      OUTSIDE THE (0,255) INTERVAL.')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4025)IVAL
 4025     FORMAT('      THE VALUE OF THE PARAMETER IS ',I8)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
C
        ICNT=1
        IFUNC2(ICNT)=' '
        CALL DPCONA(IVAL,IC)
        IFUNC2(ICNT)(1:1)=IC
      ELSEIF(ICASER.EQ.'VARI')THEN
        ICNT=0
        DO4110I=1,NIOLD
          IJ=MAXN*(ICOLR-1)+I
          IF(ICOLR.LE.MAXCOL)AVAL=V(IJ)
          IF(ICOLR.EQ.MAXCP1)AVAL=PRED(I)
          IF(ICOLR.EQ.MAXCP2)AVAL=RES(I)
          IF(ICOLR.EQ.MAXCP3)AVAL=YPLOT(I)
          IF(ICOLR.EQ.MAXCP4)AVAL=XPLOT(I)
          IF(ICOLR.EQ.MAXCP5)AVAL=X2PLOT(I)
          IF(ICOLR.EQ.MAXCP6)AVAL=TAGPLO(I)
          IVAL=INT(AVAL+0.5)
C
          IF(IVAL.LT.0 .OR. IVAL.GT.255)THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,4121)I
 4121       FORMAT('      ROW ',I8,' OF THE VARIABLE ON THE RIGHT ',
     1             'HAND SIDE IS')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,4123)
 4123       FORMAT('      OUTSIDE THE (0,255) INTERVAL.')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,4125)IVAL
 4125       FORMAT('      THE VALUE OF THE ROW ELEMENT IS ',I8)
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
C
          ICNT=ICNT+1
          IFUNC2(ICNT)=' '
          CALL DPCONA(IVAL,IC)
          IFUNC2(ICNT)(1:1)=IC
C
 4110   CONTINUE
      ELSE
        IERROR='YES'
        GOTO9000
      ENDIF
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      IF(ICASEL.EQ.'STRI')THEN
C
        ISTEPN='5'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCH')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        CALL DPINFU(IFUNC2,ICNT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1              NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
CCCCC1              NEWNAM,MAXN3,
     1              NEWNAM,MAXNAM,
     1              IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,6606)IHLEFT,IHLEF2
 6606     FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
          CALL DPWRST('XXX','BUG ')
          ILAB(1)='TO T'
          ILAB(2)='HE F'
          ILAB(3)='UNCT'
          ILAB(4)='ION '
          ILAB(5)='    '
          ILAB(6)=' -- '
          NUMWDL=6
          CALL DPPRIF(ILAB,NUMWDL,IFUNC2,ICNT,IBUGA3)
C
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
C
        ENDIF
C
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCH')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTCH--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTCK(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--CHECK IF THE ARGUMENTS SPECIFIED ON THE COMMAND ARE
C              CURRENTLY DEFINED NAMES
C     EXAMPLE--LET IFLAG = CHECK NAMES S1 S2 S3
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2015/03
C     ORIGINAL VERSION--MARCH     2015.
C     UPDATED         --JUNE      2016. CHECK NAME OUTPUT
C                                             <FILLIBEN/DEFAULT>
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
C
      CHARACTER*4 IHWUSE
      CHARACTER*4 MESSAG
      CHARACTER*4 IHP
      CHARACTER*4 IHP2
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IFOUND
C
      CHARACTER*8 ISTR
      CHARACTER*8 IQUOTE
C
      PARAMETER (MAXIND=100)
      CHARACTER*4 IVARN1(MAXIND)
      CHARACTER*4 IVARN2(MAXIND)
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOST.INC'
C
      CHARACTER (LEN=MAXFNC) :: IMANAM(10)
      COMMON/IMAC/IMACNU,IMALEV,IMANAM
C
C-----COMMON VARIABLES (GENERAL)--------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='CK  '
      IERROR='NO'
      IFOUND='NO'
      IQUOTE="'"
C
      ILOC3=0
      ICOLL=0
      ILISTL=0
      NIOLD=0
C
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCK')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTCK--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM,NUMCHF,MAXCHF
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM,NUMCHF,MAXCHF = ',2(A4,2X),3I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCK')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE LEFT-HAND SIDE--                     *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A PARAMETER (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCK')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO1910I=1,4
        IF(IHLEFT(I:I).EQ.'(')THEN
          IHLEFT(I:4)=' '
          IHLEF2=' '
          ICASEL='ELEM'
          GOTO1999
        ENDIF
 1910 CONTINUE
      DO1920I=1,4
        IF(IHLEF2(I:I).EQ.'(')THEN
          IHLEF2(I:4)=' '
          ICASEL='ELEM'
          GOTO1999
        ENDIF
 1920 CONTINUE
 1999 CONTINUE
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'P')THEN
            ICASEL='PARA'
            ILISTL=I2
            GOTO2900
          ELSEIF(IUSE(I2).EQ.'V')THEN
            ICASEL='ELEM'
            ILISTL=I2
            ICOLL=IVALUE(ILISTL)
            NIOLD=IN(ILISTL)
            GOTO2900
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN CHECK NAME--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='PARA'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2900 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE NAMES ON THE RIGHT HAND SIDE       **
C               *****************************************************
C
      ISTEPN='3'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCK')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IFRST=5
      IF(NUMARG.LT.IFRST)THEN
        IFLAG=-1
        GOTO3900
      ENDIF
      IFLAG=1
C
      JMIN=IFRST
      JMAX=NUMARG
      CALL EXTVA3(IHARG,IHARG2,NUMARG,JMIN,JMAX,MAXIND,
     1            IHNAME,IHNAM2,NUMNAM,
     1            IVARN1,IVARN2,NUMIND,
     1            IBUGA3,ISUBRO,IERROR)
      IERROR='NO'
C
      DO3010II=1,NUMIND
        IHRIGH=IVARN1(II)
        IHRIG2=IVARN2(II)
        DO3020I=1,NUMNAM
          I4=I
          IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
            GOTO3029
          ENDIF
 3020   CONTINUE
C
        IFLAG=0
C
C       2016/06: OPTION TO PRINT ERROR MESSAGE AS IN JIM FILLIBEN'S
C                CHECKINPUT.DP MACRO.
C
        IF(ICHKNA.EQ.'DEFA')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,3023)IHRIGH,IHRIG2
 3023     FORMAT('      NAME ',2A4,' WAS NOT FOUND IN THE CURRENT ',
     1           'NAME LIST.')
          CALL DPWRST('XXX','BUG ')
        ELSE
C
C         CHECK FOR "HTMLSW" PARAMETER
C         CHECK FOR "IBATCH" PARAMETER
C
C         IF THIS IS SET TO 1, JIM HAS SOME SPECIAL CODE FOR PYTHON
C         BASED INTERFACE.  SO HERE, JUST SET IFLAG TO -1.
C
          IHP='HTML'
          IHP2='SW  '
          IHWUSE='P'
          MESSAG='NO'
          CALL CHECKN(IHP,IHP2,IHWUSE,
     1                IHNAME,IHNAM2,IUSE,IN,IVALUE,VALUE,NUMNAM,MAXNAM,
     1                ISUBN1,ISUBN2,MESSAG,IANS,IWIDTH,ILOCP,IERROR)
          IHTML=0
          IF(IERROR.EQ.'NO')IHTML=INT(VALUE(ILOCP)+0.5)
          IF(IHTML.NE.1)IHTML=0
          IERROR='NO'
C
          IHP='IBAT'
          IHP2='CH  '
          IHWUSE='P'
          MESSAG='NO'
          CALL CHECKN(IHP,IHP2,IHWUSE,
     1                IHNAME,IHNAM2,IUSE,IN,IVALUE,VALUE,NUMNAM,MAXNAM,
     1                ISUBN1,ISUBN2,MESSAG,IANS,IWIDTH,ILOCP,IERROR)
          IBATCH=0
          IF(IERROR.EQ.'NO')IBATCH=INT(VALUE(ILOCP)+0.5)
          IF(IBATCH.NE.1)IBATCH=0
          IERROR='NO'
C
          IF(IHTML.EQ.1)THEN
            IFLAG=-1
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3026)
            CALL DPWRST('XXX','BUG ')
C
            IF(IMACNU.NE.5)THEN
              WRITE(ICOUT,3031)IMANAM(IMALEV)
              CALL DPWRST('XXX','BUG ')
            ELSE
              WRITE(ICOUT,3032)
              CALL DPWRST('XXX','BUG ')
            ENDIF
            IF(IHRIGH.EQ.'K   ')THEN
              WRITE(ICOUT,3041)
              CALL DPWRST('XXX','BUG ')
            ELSE
              WRITE(ICOUT,3051)IHRIGH,IHRIG2
              CALL DPWRST('XXX','BUG ')
            ENDIF
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3120)
 3120       FORMAT('       1. Close this error-message window ',
     1             '(via X-out)')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3122)
 3122       FORMAT('       2. Terminate the Dataplot run ',
     1             '(via Ctrl-C)')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3124)IQUOTE
 3124       FORMAT('       3. Edit Dataplot',A1,'s main routine to ',
     1             'define k')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3126)
 3126       FORMAT('               Example: let k = 3')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3128)
 3128       FORMAT('       4. Rerun Dataplot')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3026)
            CALL DPWRST('XXX','BUG ')
            CALL DPPAUS(IBUGA3,IFOUND,IERROR)
C
          ELSE
C
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3026)
 3026       FORMAT('========================================')
            CALL DPWRST('XXX','BUG ')
C
            IF(IMACNU.NE.5)THEN
              WRITE(ICOUT,3031)IMANAM(IMALEV)
 3031         FORMAT('Error--Macro ',A80)
              CALL DPWRST('XXX','BUG ')
            ELSE
              WRITE(ICOUT,3032)
 3032         FORMAT('Error--From terminal')
              CALL DPWRST('XXX','BUG ')
            ENDIF
            IF(IHRIGH.EQ.'K   ')THEN
              WRITE(ICOUT,3041)
 3041         FORMAT('        is missing required input:  k (= ',
     1               'number of factors')
              CALL DPWRST('XXX','BUG ')
            ELSE
              WRITE(ICOUT,3051)IHRIGH,IHRIG2
 3051         FORMAT('        is missing required input: ',2A4)
              CALL DPWRST('XXX','BUG ')
            ENDIF
            IF(IBATCH.EQ.0)THEN
              WRITE(ICOUT,3058)
 3058         FORMAT('        Click    Enter   to continue ...')
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3026)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,999)
              CALL DPWRST('XXX','BUG ')
              CALL DPPAUS(IBUGA3,IFOUND,IERROR)
            ELSE
              WRITE(ICOUT,3026)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,999)
              CALL DPWRST('XXX','BUG ')
            ENDIF
          ENDIF
        ENDIF
C
 3029   CONTINUE
 3010 CONTINUE
C
 3900 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  SAVE PARAMETER                                 **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCK')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ISTART,ISTOP,IVAL
 4011   FORMAT('ISTART,ISTOP,IVAL = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL
 4013   FORMAT('ICASEL = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(ICASEL.EQ.'PARA')THEN
C
        ISTEPN='4A'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCK')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='P'
        VALUE(ILISTL)=REAL(IFLAG)
        IVALUE(ILISTL)=IFLAG
        IN(ILISTL)=1
        IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
      ELSEIF(ICASEL.EQ.'ELEM')THEN
C
C       SEARCH IANS STRING FOR "(xx) =".  IF NO PARENTHESIS
C       FOUND BEFORE "=", THEN DO NOT KNOW WHAT ROW OF THE
C       VARIABLE TO SAVE.  TREAT THIS AS AN ERROR.
C
        NLEFT=-1
        NRIGHT=-1
        NEQUAL=-1
        DO4201I=1,IWIDTH
          IF(IANS(I)(1:1).EQ.'(' .AND. NLEFT.LT.0)THEN
            NLEFT=I
          ELSEIF(IANS(I)(1:1).EQ.')' .AND. NRIGHT.LT.0)THEN
            NRIGHT=I
          ELSEIF(IANS(I)(1:1).EQ.'=' .AND. NEQUAL.LT.0)THEN
            NEQUAL=I
          ENDIF
 4201   CONTINUE
C
C       NEED  NLEFT < NRIGHT < NEQUAL
C
        NSTRT=NLEFT+1
        NSTOP=NRIGHT-1
        NLEN=NSTOP-NSTRT+1
        IF(NLEFT.GT.NRIGHT .OR. NRIGHT.GT.NEQUAL .OR.
     1     NSTRT.GT.NSTOP .OR. NLEN.GT.8) THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4211)
 4211     FORMAT('      UNRECOGNIZED SYNTAX FOR VARIABLE ELEMENT ON ',
     1           'LEFT HAND SIDE EQUAL SIGN.')
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ELSE
          ISTR=' '
          DO4216I=1,NLEN
            ISTR(I:I)=IANS(NSTRT+I-1)(1:1)
 4216     CONTINUE
          READ(ISTR,'(I8)',ERR=4218)IARGL
          GOTO4219
C
 4218     CONTINUE
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4211)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
C
 4219     CONTINUE
        ENDIF
C
        IF(IARGL.LT.1 .OR. IARGL.GT.MAXN)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4231)IARGL,ILEFT
 4231     FORMAT('      THE SPECIFIED ROW (',I8,') OF VARIABLE ',A4,A4)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4233)
 4233     FORMAT('      WAS LESS THAN 1 OR GREATER THAN THE')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4235)MAXN
 4235     FORMAT('      MAXIMUM ALLOWABLE ',I8)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
C
        IF(NEWNAM.EQ.'YES')THEN
          NIOLD=1
        ENDIF
        NINEW=NIOLD
        IF(IARGL.GT.NINEW)NINEW=IARGL
        NS2=1
C
        RIGHT=REAL(IFLAG)
        IJ=MAXN*(ICOLL-1)+IARGL
        IF(ICOLL.LE.MAXCOL)V(IJ)=RIGHT
        IF(ICOLL.EQ.MAXCP1)PRED(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP2)RES(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP3)YPLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP4)XPLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP5)X2PLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP6)TAGPLO(IARGL)=RIGHT
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='V'
        IVALUE(ILISTL)=ICOLL
        VALUE(ILISTL)=ICOLL
        IN(ILISTL)=NINEW
C
        IF(NEWNAM.EQ.'YES')THEN
          NUMNAM=NUMNAM+1
          NUMCOL=NUMCOL+1
        ENDIF
C
        DO4290J4=1,NUMNAM
          IF(IUSE(J4).EQ.'V'.AND.IVALUE(J4).EQ.ICOLL)THEN
            IUSE(J4)='V'
            IVALUE(J4)=ICOLL
            VALUE(J4)=ICOLL
            IN(J4)=NINEW
            GOTO4299
          ENDIF
 4290   CONTINUE
 4299   CONTINUE
C
      ENDIF
C
      IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON' .AND. ICHKNA.EQ.'DEFA')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,8011)IFLAG
 8011   FORMAT('THE STATUS FLAG FOR CHECK NAME = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
      GOTO9000
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCK')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTCK--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTCM(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--COMPARE TWO STRINGS AND RETURN A 1 IF THEY ARE
C              IDENTICAL AND A 0 IF THEY ARE NOT.
C     EXAMPLE--LET IFLAG = STRING COMPARE S1 S2
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2011/1
C     ORIGINAL VERSION--JANUARY   2011.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*132 ISTR
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='CM  '
      IERROR='NO'
C
      ILOC3=0
      NLEN1=0
      NLEN2=0
C
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCM')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTCM--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCM')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A PARAMETER (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCM')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'P')THEN
            ICASEL='PARA'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING COMPARE--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='PARA'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  LOOP THROUGH THE NAMES ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCM')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.LT.5)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3003)
 3003   FORMAT('      THERE ARE NO STRINGS SPECIFIED ON THE RIGHT ',
     1         'HAND SIDE.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      IF(NUMARG.GT.6)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3013)
 3013   FORMAT('      THERE ARE MORE THAN TWO STRINGS SPECIFIED ON ',
     1         'THE RIGHT HAND SIDE.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      ICNT=0
      DO3000I=5,NUMARG
C
        IHRIGH=IHARG(I)
        IHRIG2=IHARG2(I)
C
        DO3100J=1,NUMNAM
          I4=J
          IF(IHRIGH.EQ.IHNAME(J).AND.IHRIG2.EQ.IHNAM2(J))THEN
            IF(IUSE(I4).NE.'F')THEN
              WRITE(ICOUT,999)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,2001)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3103)IHRIGH,IHRIG2
 3103         FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1               A4,A4,')')
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3105)
 3105         FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
              CALL DPWRST('XXX','BUG ')
              IERROR='YES'
              GOTO9000
            ELSE
              IF(I.EQ.5)THEN
                ISTRT1=IVSTAR(I4)
                ISTOP1=IVSTOP(I4)
                NLEN1=ISTOP1-ISTRT1+1
              ELSE
                ISTRT2=IVSTAR(I4)
                ISTOP2=IVSTOP(I4)
                NLEN2=ISTOP2-ISTRT2+1
              ENDIF
              GOTO3199
            ENDIF
          ENDIF
 3100   CONTINUE
C
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3103)IHRIGH,IHRIG2
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3115)
 3115   FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
C
 3199   CONTINUE
C
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  DETERMINE IF STRINGS ARE THE SAME              **
C               *****************************************************
C
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCM')THEN
          ISTEPN='3B'
          CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
          WRITE(ICOUT,3211)I,ISTART,ISTOP,NLEN
 3211     FORMAT('I,ISTART,ISTOP,NLEN = ',4I8)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,3214)ICASEL
 3214     FORMAT('ICASEL = ',A4)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
        IVAL=1
        IF(NLEN1.NE.NLEN2)THEN
          IVAL=0
        ELSE
          DO3310II=1,NLEN1
            IINDX1=ISTRT1+II
            IINDX2=ISTRT2+II
            IF(IFUNC(IINDX1).NE.IFUNC(IINDX2))THEN
              IVAL=0
              GOTO3319
            ENDIF
 3310     CONTINUE
 3319     CONTINUE
        ENDIF
C
 3000 CONTINUE
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE PARAMETER                                 **
C               *****************************************************
C
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCM')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ISTRT1,ISTOP1,ISTRT2,ISTOP2
 4011   FORMAT('ISTRT1,ISTOP1,ISTRT2,ISTOP2 = ',4I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL
 4013   FORMAT('ICASEL = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(ICASEL.EQ.'PARA')THEN
C
        ISTEPN='4A'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCM')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='P'
        VALUE(ILISTL)=REAL(IVAL)
        IVALUE(ILISTL)=INT(VALUE(ILISTL)+0.5)
        IN(ILISTL)=1
        IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
C
        IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,15111)IHLEFT,IHLEF2,IVAL
15111     FORMAT(A4,A4,' CONTAINS THE VALUE ',I4)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
        ENDIF
      ELSEIF(ICASEL.EQ.'ELEM')THEN
C
C       SEARCH IANS STRING FOR "(xx) =".  IF NO PARENTHESIS
C       FOUND BEFORE "=", THEN DO NOT KNOW WHAT ROW OF THE
C       VARIABLE TO SAVE.  TREAT THIS AS AN ERROR.
C
        NLEFT=-1
        NRIGHT=-1
        NEQUAL=-1
        DO16001I=1,IWIDTH
          IF(IANS(I)(1:1).EQ.'(' .AND. NLEFT.LT.0)THEN
            NLEFT=I
          ELSEIF(IANS(I)(1:1).EQ.')' .AND. NRIGHT.LT.0)THEN
            NRIGHT=I
          ELSEIF(IANS(I)(1:1).EQ.'=' .AND. NEQUAL.LT.0)THEN
            NEQUAL=I
          ENDIF
16001   CONTINUE
C
C       NEED  NLEFT < NRIGHT < NEQUAL
C
        NSTRT=NLEFT+1
        NSTOP=NRIGHT-1
        NLEN=NSTOP-NSTRT+1
        IF(NLEFT.GT.NRIGHT .OR. NRIGHT.GT.NEQUAL .OR.
     1     NSTRT.GT.NSTOP .OR. NLEN.GT.8) THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16011)
16011     FORMAT('      UNRECOGNIZED SYNTAX FOR VARIABLE ELEMENT ON')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16013)
16013     FORMAT('      LEFT HAND SIDE EQUAL SIGN.')
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ELSE
          ISTR=' '
          DO16020I=1,NLEN
            ISTR(I:I)=IANS(NSTRT+I-1)(1:1)
16020     CONTINUE
          READ(ISTR,'(I8)',ERR=16029)IARGL
          GOTO16049
C
16029     CONTINUE
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16011)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16013)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
C
16049     CONTINUE
        ENDIF
C
        IF(IARGL.LT.1 .OR. IARGL.GT.MAXN)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16052)IARGL,ILEFT
16052     FORMAT('      THE SPECIFIED ROW (',I8,') OF VARIABLE ',A4,A4)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16054)
16054     FORMAT('      WAS LESS THAN 1 OR GREATER THAN THE')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16055)MAXN
16055     FORMAT('      MAXIMUM ALLOWABLE ',I8)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
C
        IF(NEWNAM.EQ.'YES')THEN
          NIOLD=1
        ENDIF
        NINEW=NIOLD
        IF(IARGL.GT.NINEW)NINEW=IARGL
        NS2=1
C
        RIGHT=REAL(IVAL)
        IJ=MAXN*(ICOLL-1)+IARGL
        IF(ICOLL.LE.MAXCOL)V(IJ)=RIGHT
        IF(ICOLL.EQ.MAXCP1)PRED(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP2)RES(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP3)YPLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP4)XPLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP5)X2PLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP6)TAGPLO(IARGL)=RIGHT
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='V'
        IVALUE(ILISTL)=ICOLL
        VALUE(ILISTL)=ICOLL
        IN(ILISTL)=NINEW
C
        IF(NEWNAM.EQ.'YES')THEN
          NUMNAM=NUMNAM+1
          NUMCOL=NUMCOL+1
        ENDIF
C
        DO16200J4=1,NUMNAM
          IF(IUSE(J4).EQ.'V'.AND.IVALUE(J4).EQ.ICOLL)THEN
            IUSE(J4)='V'
            IVALUE(J4)=ICOLL
            VALUE(J4)=ICOLL
            IN(J4)=NINEW
            GOTO16209
          ENDIF
16200   CONTINUE
16209   CONTINUE
C
        IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16211)IVAL
16211     FORMAT('THE RESULT OF THE STRING COMPARISON  = ',I4)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
        ENDIF
      ENDIF
      GOTO9000
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCM')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTCM--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTCN(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--CONCATENATE ONE OR MORE STRINGS.
C     EXAMPLE--LET SOUT = STRING CONCATENATE S1 S2 S3 S4
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/11
C     ORIGINAL VERSION--NOVEMBER  2008.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*4 ILAB(10)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='CN  '
      IERROR='NO'
C
      ILOC3=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCN')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTCN--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCN')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCN')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING CONCATENATE--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  LOOP THROUGH THE NAMES ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCN')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.LT.5)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3003)
 3003   FORMAT('      THERE ARE NO STRINGS SPECIFIED ON THE RIGHT ',
     1         'HAND SIDE')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      ICNT=0
      DO3000I=5,NUMARG
C
        IHRIGH=IHARG(I)
        IHRIG2=IHARG2(I)
C
        DO3100J=1,NUMNAM
          I4=J
          IF(IHRIGH.EQ.IHNAME(J).AND.IHRIG2.EQ.IHNAM2(J))THEN
            IF(IUSE(I4).NE.'F')THEN
              WRITE(ICOUT,999)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,2001)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3103)IHRIGH,IHRIG2
 3103         FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1               A4,A4,')')
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3105)
 3105         FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
              CALL DPWRST('XXX','BUG ')
              IERROR='YES'
              GOTO9000
            ELSE
              ISTART=IVSTAR(I4)
              ISTOP=IVSTOP(I4)
              NLEN=ISTOP-ISTART+1
              GOTO3199
            ENDIF
          ENDIF
 3100   CONTINUE
C
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3103)IHRIGH,IHRIG2
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3115)
 3115   FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
C
 3199   CONTINUE
C
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  ADD THE CURRENT STRING                         **
C               *****************************************************
C
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCN')THEN
          ISTEPN='3B'
          CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
          WRITE(ICOUT,3211)I,ISTART,ISTOP,NLEN
 3211     FORMAT('I,ISTART,ISTOP,NLEN = ',4I8)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,3214)ICASEL
 3214     FORMAT('ICASEL = ',A4)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
        IF(NLEN.GE.1)THEN
          DO3310II=ISTART,ISTOP
            ICNT=ICNT+1
            IFUNC2(ICNT)=IFUNC(II)
 3310     CONTINUE
        ENDIF
C
 3000 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      IF(ICASEL.EQ.'STRI')THEN
C
        ISTEPN='4'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCN')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        CALL DPINFU(IFUNC2,ICNT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1              NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
CCCCC1              NEWNAM,MAXN3,
     1              NEWNAM,MAXNAM,
     1              IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,6606)IHLEFT,IHLEF2
 6606     FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
          CALL DPWRST('XXX','BUG ')
          ILAB(1)='TO T'
          ILAB(2)='HE F'
          ILAB(3)='UNCT'
          ILAB(4)='ION '
          ILAB(5)='    '
          ILAB(6)=' -- '
          NUMWDL=6
          CALL DPPRIF(ILAB,NUMWDL,IFUNC2,ICNT,IBUGA3)
C
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
C
        ENDIF
C
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCN')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTCN--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTCB(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--IMPLEMENT THE FOLLOWING COMMAND
C
C                LET SOUT = STRING COMBINE X1 TO X8
C
C              THIS COMMAND IS SIMILAR TO THE STRING CONCATENATE, BUT
C              DIFFERS IN THE FOLLOWING WAYS:
C
C                 1) WITH STRING CONCATENATE, THE ARGUMENTS ARE
C                    PREVIOUSLY DEFINED STRINGS.  WITH STRING COMBINE,
C                    WE FIRST CHECK TO SEE IF THE ARGUMENT IS THE
C                    NAME OF A PREVIOUSLY DEFINED STRING.  IF YES,
C                    ADD THE CONTENTS OF THE STRING TO THE OUTPUT
C                    STRING.  IF NOT, TREAT THE ARGUMENT AS LITERAL TEXT
C                    TO BE ADDED TO THE STRING.
C
C                 2) THIS COMMAND PUTS SPACES BETWEEN THE ARGUMENTS
C                    IN THE OUTPUT STRING.
C
C                 3) YOU CAN USE THE "TO" SYNTAX WITH THIS COMMAND.
C
C               THIS IS A RATHER SPECIALIZED COMMAND.  IT IS USEFUL FOR
C               CREATING COMMAND STRINGS WHERE THE "TO" SYNTAX IS NOT
C               SUPPORTED.  FOR EXAMPLE,
C
C                   LET STALL = STRING COMBINE X1 TO X^K
C                   LET FLAG  = DEX CHECK CLASSIC ^STALL
C
C                IN ADDITION, IT CAN BE USED AS AN ALTERNATIVE TO
C                "STRING CONCATENATE" WITH THE DIFFERENCE BEING THAT
C                A SPACE IS AUTOMATICALLY ADDED BETWEEN THE INPUT
C                STRINGS (YOU HAVE TO DO THIS MANUALLY WITH "STRING
C                CONCATENATE).
C
C     EXAMPLES--LET SOUT = STRING COMBINE S1 S2 S3 S4
C               LET SOUT = STRING COMBINE Y X1 TO X8
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2018/02
C     ORIGINAL VERSION--FEBRUARY  2018.
C     UPDATED         --SEPTEMBER 2019. ALLOW USER TO SPECIFY THE
C                                       SEPARATOR CHARACTER
C     UPDATED         --FEBRUARY  2020. FIX:
C                                       LET S = STRING COMBINE X1 TO X1
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 ISEPZZ
C
      CHARACTER*4 ILAB(10)
C
      PARAMETER (MAXIND=100)
      CHARACTER*4 IVARN1(MAXIND)
      CHARACTER*4 IVARN2(MAXIND)
      CHARACTER*4 IVARTY(MAXIND)
      REAL PVAR(MAXIND)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='CB  '
      IERROR='NO'
      ISEPZZ='    '
C
      ILOC3=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCB')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTCB--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,ISTRCS,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,ISTRCS,NUMNAM = ',3(A4,2X),I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,2A4,2X,A4,2I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
      IF(ISTRCS.EQ.'SP()' .OR. ISTRCS.EQ.'    ')THEN
        ISEPZZ=' '
        NCSEP=1
      ELSE
        DO110II=4,1,-1
          IF(ISTRCS(II:II).NE.' ')THEN
            ISEPZZ(1:II)=ISTRCS(1:II)
            NCSEP=II
            GOTO119
          ENDIF
  110   CONTINUE
  119   CONTINUE
      ENDIF
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')THEN
        ISTEPN='2'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,101)NCSEP,ISEPZZ
  101   FORMAT('NCSEP,ISEPZZ = ',I5,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING COMBINE--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  LOOP THROUGH THE NAMES ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.LT.5)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3003)
 3003   FORMAT('      THERE ARE NO STRINGS SPECIFIED ON THE RIGHT ',
     1         'HAND SIDE')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C     USE EXTVA2 TO EXTRACT LIST OF NAMES (EXTVA2 SUPPORTS THE "TO"
C     SYNTAX AND WILL ALSO RETURN TYPE OF EACH NAME).
C
      IFLAGM=1
      IFLAGP=1
      IFLAGT=1
      JMIN=5
      JMAX=NUMARG
      CALL EXTVA2(IHARG,IHARG2,IARGT,ARG,NUMARG,JMIN,JMAX,MAXIND,
     1            IHNAME,IHNAM2,IUSE,NUMNAM,
     1            IVARN1,IVARN2,IVARTY,PVAR,NUMIND,
     1            IFLAGM,IFLAGP,IFLAGT,
     1            IBUGA3,ISUBRO,IERROR)
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')THEN
        WRITE(ICOUT,3006)IERROR,NUMIND
 3006   FORMAT('AFTER EXTVA2: IERROR,NUMIND = ',A4,2X,I5)
        CALL DPWRST('XXX','BUG ')
        DO3007KK=1,NUMIND
          WRITE(ICOUT,3008)KK,IVARN1(KK),IVARN2(KK),IVARTY(KK)
 3008     FORMAT('KK,IVARN1(KK),IVARN2(KK),IVARTY(KK) = ',I5,3(2X,A4))
          CALL DPWRST('XXX','BUG ')
 3007   CONTINUE
      ENDIF
C
      IF(IERROR.EQ.'YES')GOTO9000
C
C     NOW LOOP THROUGH EACH NAME.  IF NAME IS A PREVIOUSLY DEFINED
C     STRING, INSERT THE CONTENTS OF THAT STRING.  OTHERWISE, SIMPLY
C     INSERT THE LITERAL NAME INTO THE STRING.
C
      ICNT=0
      DO3000I=1,NUMIND
C
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')THEN
          ISTEPN='3B'
          CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
          WRITE(ICOUT,3001)I,ICASEL,IVARN1(I),IVARN2(I),IVARTY(I)
 3001     FORMAT('I,ICASEL,IVARN1(I),IVARN2(I),IVARTY(I) = ',
     1           I5,A4,2X,2A4,2X,A4)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
C               *****************************************************
C               **  STEP 3A--                                      **
C               **  ADD CURRENT ARGUMENT AS LITERAL STRING         **
C               *****************************************************
C
        IF(IVARTY(I).NE.'F   ')THEN
          NLEN=1
          DO3110J=4,1,-1
            IF(IVARN2(I)(J:J).NE.' ')THEN
              NLEN=4+J
              GOTO3119
            ENDIF
 3110     CONTINUE
          DO3115J=4,1,-1
            IF(IVARN1(I)(J:J).NE.' ')THEN
              NLEN=J
              GOTO3119
            ENDIF
 3115     CONTINUE
 3119     CONTINUE
C
          DO3120J=1,MIN(4,NLEN)
            ICNT=ICNT+1
            IFUNC2(ICNT)=IVARN1(I)(J:J)
 3120     CONTINUE
          IF(NLEN.GE.5)THEN
            DO3125J=5,MIN(8,NLEN)
              ICNT=ICNT+1
              IFUNC2(ICNT)=IVARN2(I)(J-4:J-4)
 3125       CONTINUE
          ENDIF
C
          IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')THEN
            ISTEPN='3C'
            CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
            WRITE(ICOUT,3127)I,NLEN,ICNT
 3127       FORMAT('I,NLEN,ICNT = ',3I8)
            CALL DPWRST('XXX','BUG ')
            DO3128KK=1,ICNT
              WRITE(ICOUT,3129)KK,IFUNC2(KK)
 3129         FORMAT('KK,IFUNC2(KK) = ',I8,2X,A4)
            CALL DPWRST('XXX','BUG ')
 3128       CONTINUE
          ENDIF
C
        ELSE
C
C               *****************************************************
C               **  STEP 3B--                                      **
C               **  ADD THE CURRENT STRING                         **
C               *****************************************************
C
          DO3201KK=1,NUMNAM
            IF(IVARN1(I).EQ.IHNAME(KK).AND.IVARN2(I).EQ.IHNAM2(KK))THEN
              I4=KK
              GOTO3209
            ENDIF
 3201     CONTINUE
 3209     CONTINUE
          ISTART=IVSTAR(I4)
          ISTOP=IVSTOP(I4)
          NLEN=ISTOP-ISTART+1
C
          IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')THEN
            ISTEPN='3B'
            CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
            WRITE(ICOUT,3211)ICASEL,I,I4,ISTART,ISTOP,NLEN
 3211       FORMAT('ICASEL: I,I4,ISTART,ISTOP,NLEN = ',A4,2X,5I8)
            CALL DPWRST('XXX','BUG ')
          ENDIF
C
          IF(NLEN.GE.1)THEN
            DO3310II=ISTART,ISTOP
              ICNT=ICNT+1
              IFUNC2(ICNT)=IFUNC(II)
 3310       CONTINUE
          ENDIF
        ENDIF
C
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')THEN
          WRITE(ICOUT,3311)I,NUMIND,NCSEP,ICNT
 3311     FORMAT('I,NUMIND,NCSEP,ICNT = ',4I5)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
        IF(I.LT.NUMIND)THEN
          DO3315II=1,NCSEP
            ICNT=ICNT+1
            IFUNC2(ICNT)=ISEPZZ(II:II)
C
            IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')THEN
              WRITE(ICOUT,3318)II,ICNT,IFUNC2(ICNT)
 3318         FORMAT('II,ICNT,IFUNC2(ICNT) = ',2I5,2X,A4)
              CALL DPWRST('XXX','BUG ')
            ENDIF
C
 3315     CONTINUE
        ENDIF
C
 3000 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      IF(ICASEL.EQ.'STRI')THEN
C
        ISTEPN='4'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCB')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        CALL DPINFU(IFUNC2,ICNT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1              NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
CCCCC1              NEWNAM,MAXN3,
     1              NEWNAM,MAXNAM,
     1              IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,6606)IHLEFT,IHLEF2
 6606     FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
          CALL DPWRST('XXX','BUG ')
          ILAB(1)='TO T'
          ILAB(2)='HE F'
          ILAB(3)='UNCT'
          ILAB(4)='ION '
          ILAB(5)='    '
          ILAB(6)=' -- '
          NUMWDL=6
          CALL DPPRIF(ILAB,NUMWDL,IFUNC2,ICNT,IBUGA3)
C
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
C
        ENDIF
C
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCB')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTCB--')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTCO(ICASEZ,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--IMPLEMENT THE FOLLOWING COMMAND:
C
C                 LET IFLAG = STRING CONTAINS SORG SMATCH
C
C              THAT IS, WE CHECK WHETHER A STRING (SORG) CONTAINS THE
C              SPECIFIC SUBSTRING (SMATCH) OR NOT.  IF SO, IFLAG IS SET
C              TO 1, OTHERWISE IT IS SET TO 0.
C     NOTE--THE FOLLOWING SYNTAX IS NOT SUPPORTED FOR THIS COMMAND:
C              LET Y(2) = STRING CONTINS  S  SUBSTRING
C           ALSO, THE STRINGS ON THE RIGHT HAND SIDE MUST BOTH BE
C           PREVIOUSLY DEFINED.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2019/01
C     ORIGINAL VERSION--JANUARY   2019.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASEZ
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWNA2
      CHARACTER*4 NEWCOL
      CHARACTER*4 NEWCO2
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 IHRI21
      CHARACTER*4 IHRI22
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='CO  '
      IERROR='NO'
C
      ILOC3=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCO')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTCO--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM,NUMCHF,MAXCHF
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM,NUMCHF,MAXCHF = ',2(A4,2X),4I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCO')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWNA2='NO'
      NEWCOL='NO'
      NEWCO2='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE FIRST ARGUMENT ON THE                *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A PARAMETER (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCO')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'P')THEN
            ICASEL='PARA'
            ILISTL=I2
            NUMTMP=NUMNAM
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING CONTAINS--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE FIRST NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='PARA'
C
      ILISTL=NUMNAM+1
      NUMTMP=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCO')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IINDX=5
      IHRIGH=IHARG(IINDX)
      IHRIG2=IHARG2(IINDX)
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT1=IVSTAR(I4)
            ISTOP1=IVSTOP(I4)
            NLEN1=ISTOP1-ISTRT1+1
            GOTO3099
          ENDIF
        ENDIF
 3000 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               *****************************************************
C               **  STEP 3B-                                       **
C               **  EXTRACT THE SECOND NAME ON THE RIGHT HAND SIDE **
C               *****************************************************
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCO')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IINDX=IINDX+1
      IHRI21=IHARG(IINDX)
      IHRI22=IHARG2(IINDX)
      DO3100I=1,NUMNAM
        I4=I
        IF(IHRI21.EQ.IHNAME(I).AND.IHRI22.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRI21,IHRI22
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT2=IVSTAR(I4)
            ISTOP2=IVSTOP(I4)
            NLEN2=ISTOP2-ISTRT2+1
            GOTO3199
          ENDIF
        ENDIF
 3100 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRI21,IHRI22
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3199 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  CHECK FOR MATCHING STRINGS                     **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCO')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ISTRT1,ISTOP1,NLEN1
 4011   FORMAT('ISTRT1,ISTOP1,NLEN1 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4012)ISTRT2,ISTOP2,NLEN2
 4012   FORMAT('ISTART,ISTOP,NLEN2 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL,ICASEZ
 4013   FORMAT('ICASEL,ICASEZ = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IMATCH=0
      IF(NLEN2.GT.NLEN1)GOTO4199
      NTEMP=ISTOP1-NLEN2+1
      DO4100J=ISTRT1,NTEMP
        ICNT=ISTRT2
        DO4110I=J,J+NLEN2-1
          IF(IFUNC(I)(1:1).NE.IFUNC(ICNT)(1:1))GOTO4100
          ICNT=ICNT+1
 4110   CONTINUE
        IMATCH=1
        GOTO4199
 4100 CONTINUE
 4199 CONTINUE
C
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE PARAMETER                                 **
C               *****************************************************
C
C
      IF(ICASEL.EQ.'PARA')THEN
C
        ISTEPN='5'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCO')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='P'
        VALUE(ILISTL)=REAL(IMATCH)
        IVALUE(ILISTL)=IMATCH
        IN(ILISTL)=1
        IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
C
        IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          IF(IMATCH.EQ.0)THEN
            WRITE(ICOUT,5011)IHLEFT,IHLEF2,IMATCH
5011        FORMAT('MATCH NOT FOUND, ',2A4,'  = ',I3)
            CALL DPWRST('XXX','BUG ')
          ELSE
            WRITE(ICOUT,5013)IHLEFT,IHLEF2,IMATCH
5013        FORMAT('MATCH FOUND, ',2A4,'  = ',I3)
            CALL DPWRST('XXX','BUG ')
          ENDIF
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
        ENDIF
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCO')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTCO--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTCR(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--IMPLEMENT THE COMMAND
C
C              LET SNEW = STRING COMPARE AND REPLACE SOLD SREPLACE ...
C                                SC1 ... SCK
C
C              WHERE
C                  SC1 ... SCK   = A LIST OF PRE-EXISTING STRINGS
C                  SOLD          = A PRE-EXISTING STRING THAT WILL BE
C                                  COMPARED TO SC1 ... SCK
C                  SREPLACE      = IF A MATCH IS FOUND BETWEEN SOLD
C                                  AND ONE OF THE SC1 ... SCK, SET
C                                  SNEW TO SREPLACE.
C
C     EXAMPLE--LET SNEW = STRING SOLD SREPL S1 TO S16
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2018/03
C     ORIGINAL VERSION--MARCH     2018.
C     UPDATED         --JANUARY   2021. FIX BUG IF NO MATCH FOUND AND
C                                       FEEDBACK SWITCH OFF
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      INCLUDE 'DPCOPA.INC'
C
      CHARACTER*4 IWRITE
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*8 IHTEMP
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*255 ISTR1
      CHARACTER*255 ISTR2
      CHARACTER*255 ISTR3
      CHARACTER*4 ILAB(10)
C
      PARAMETER(MAXIND=1000)
      CHARACTER*4 ISTRN1(MAXIND)
      CHARACTER*4 ISTRN2(MAXIND)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='CR  '
      IERROR='NO'
C
      ILOC3=0
      MAXTMP=80
      IFLAG2=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCR')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTCR--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM,NUMCHF,MAXCHF
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM,NUMCHF,MAXCHF = ',2(A4,2X),3I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,2A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING COMPARE AND REPLACE--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  LOOP THROUGH THE NAMES ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.LT.9)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3003)
 3003   FORMAT('      THERE MUST BE AT LEAST THREE STRINGS SPECIFIED ',
     1         'TO THE RIGHT HAND SIDE OF THE EQUAL SIGN')
        CALL DPWRST('XXX','BUG ')
        NTEMP=NUMARG-6
        WRITE(ICOUT,3005)NTEMP
 3005   FORMAT('      THE NUMBER OF STRINGS SPECIFIED IS ',I8)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C               ******************************************************
C               **  STEP 3A--                                        *
C               **  THE FIRST ARGUMENT ON THE RHS IS THE "OLD"       *
C               **  STRING.  THIS MUST BE A PREVIOUSLY DEFINED       *
C               ******************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     CHECK FOR THE "OLD" STRING (THIS MUST CURRENTLY EXIST)
C
      IHRIGH=IHARG(7)
      IHRIG2=IHARG2(7)
C
      DO3100I=1,NUMNAM
        I2=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ISTART=IVSTAR(I2)
            ISTOP=IVSTOP(I2)
            NLEN1=ISTOP-ISTART+1
            IF(NLEN1.GE.1)THEN
              ICNT=0
              DO3110II=ISTART,ISTOP
                ICNT=ICNT+1
                ISTR1(ICNT:ICNT)=IFUNC(II)(1:1)
 3110         CONTINUE
              GOTO3109
            ELSE
              WRITE(ICOUT,999)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,2001)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3101)IHRIGH,IHRIG2
 3101         FORMAT('      THE FIRST NAME ON THE RIGHT HAND SIDE (',
     1               A4,A4,')')
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3102)
 3102         FORMAT('      IS A ZERO LENGTH STRING.')
              CALL DPWRST('XXX','BUG ')
              IERROR='YES'
              GOTO9000
            ENDIF
            GOTO3109
          ELSE
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3103)IHRIGH,IHRIG2
 3103       FORMAT('      THE FIRST NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3105)
 3105       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 3100 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3113)IHRIGH,IHRIG2
 3113 FORMAT('      THE FIRST NAME ON THE RIGHT HAND SIDE (',
     1       A4,A4,')')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3115)
 3115 FORMAT('      IS NOT DEFINED (THIS NAME DEFINES THE ORIGINAL ',
     1       'STRING).')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3109 CONTINUE
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')THEN
        WRITE(ICOUT,3121)NLEN1,ISTR1(1:NLEN1)
 3121   FORMAT('NLEN1,ISTR1(1:NLEN1) = ',I5,2X,A80)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               *********************************************************
C               **  STEP 3B--                                           *
C               **  THE SECOND ARGUMENT ON THE RHS IS THE "REPLACEMENT" *
C               **  STRING.  THIS MUST BE A PREVIOUSLY DEFINED          *
C               *********************************************************
C
C     FOR THE REPLACEMENT STRING, HAVE 2 CASES:
C
C        1. THE STRING IS CURRENTLY IN THE NAME TABLE.  SET IFLAG = 1.
C
C        2. THE STRING IS NOT CURRENTLY IN THE NAME TABLE.  SET IFLAG = 0.
C           IN THIS CASE, TREAT THE STRING AS THE "BASE" NAME.  THEN
C           IF A MATCH IS FOUND, LOOK FOR THE STRING WITH AN "INDEX"
C           ATTACHED.  FOR EXAMPLE, IF THE REPLACEMENT STRING IS
C           STC AND IT IS THE SECOND STRING IN THE LIST THAT IS MATCHED
C           TO THE "OLD" STRING, THEN LOOK FOR THE STRING "STC2" TO BE
C           THE REPLACEMENT STRING.
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
      IHRIGH=IHARG(8)
      IHRIG2=IHARG2(8)
      ICNT=0
C
      DO3200I=1,NUMNAM
        I2=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ISTART=IVSTAR(I2)
            ISTOP=IVSTOP(I2)
            NLEN2=ISTOP-ISTART+1
            IF(NLEN2.GE.1)THEN
              ICNT=0
              DO3210II=ISTART,ISTOP
                ICNT=ICNT+1
                IF(ICNT.LE.MAXTMP)THEN
                  ISTR2(ICNT:ICNT)=IFUNC(II)(1:1)
                ELSE
                  WRITE(ICOUT,999)
                  CALL DPWRST('XXX','BUG ')
                  WRITE(ICOUT,2001)
                  CALL DPWRST('XXX','BUG ')
                  WRITE(ICOUT,3241)IHRIGH,IHRIG2
 3241             FORMAT('      THE SECOND NAME ON THE RIGHT HAND ',
     1                   'SIDE (',2A4,')')
                  CALL DPWRST('XXX','BUG ')
                  WRITE(ICOUT,3243)MAXTMP
 3243             FORMAT('      EXCEEDS ',I5,' CHARACTERS.')
                  CALL DPWRST('XXX','BUG ')
                  IERROR='YES'
                  GOTO9000
                ENDIF
 3210         CONTINUE
            ELSE
              WRITE(ICOUT,999)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,2001)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3201)IHRIGH,IHRIG2
 3201         FORMAT('      THE SECOND NAME ON THE RIGHT HAND SIDE (',
     1               A4,A4,')')
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3202)
 3202         FORMAT('      IS A ZERO LENGTH STRING.')
              CALL DPWRST('XXX','BUG ')
              IERROR='YES'
              GOTO9000
            ENDIF
            IFLAG=1
            GOTO3209
          ELSE
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3203)IHRIGH,IHRIG2
 3203       FORMAT('      THE SECOND NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3205)
 3205       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 3200 CONTINUE
C
      IFLAG=0
CCCCC WRITE(ICOUT,999)
CCCCC CALL DPWRST('XXX','BUG ')
CCCCC WRITE(ICOUT,2001)
CCCCC CALL DPWRST('XXX','BUG ')
CCCCC WRITE(ICOUT,3213)IHRIGH,IHRIG2
C3213 FORMAT('      THE SECOND NAME ON THE RIGHT HAND SIDE (',
CCCCC1       A4,A4,')')
CCCCC CALL DPWRST('XXX','BUG ')
CCCCC WRITE(ICOUT,3215)
C3215 FORMAT('      IS NOT DEFINED (THIS NAME DEFINES THE ',
CCCCC1       'REPLACEMENT STRING).')
CCCCC CALL DPWRST('XXX','BUG ')
CCCCC IERROR='YES'
CCCCC GOTO9000
C
 3209 CONTINUE
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')THEN
        WRITE(ICOUT,3221)IFLAG,NLEN2,ICNT,ISTR2(1:MAX(1,NLEN2))
 3221   FORMAT('IFLAG,NLEN2,ICNT,ISTR2(1:NLEN2) = ',3I5,2X,A80)
        CALL DPWRST('XXX','BUG ')
        IF(IFLAG.EQ.0)THEN
          WRITE(ICOUT,3223)
 3223     FORMAT('NO MATCH FOUND FOR REPLACEMENT STRING.  DEFER ',
     1           'CHECK UNTIL LATER.')
          CALL DPWRST('XXX','BUG ')
        ENDIF
      ENDIF
C
C               *********************************************************
C               **  STEP 3C--                                           *
C               **  NOW SEARCH THROUGH THE REMAINING STRINGS AND SEE    *
C               **  IF A MATCH FOUND.                                   *
C               *********************************************************
C
      ISTEPN='3c'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      JMIN=9
      JMAX=NUMARG
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCR')THEN
        WRITE(ICOUT,3301)JMIN,JMAX,MAXIND
 3301   FORMAT('JMIN,JMAX,MAXIND = ',3I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IWRITE='OFF'
      IERROR='NO'
C
      CALL EXTSTR(IHARG,IHARG2,NUMARG,JMIN,JMAX,MAXIND,
     1            IHNAME,IHNAM2,IUSE,NUMNAM,
     1            ISTRN1,ISTRN2,NUMSTR,
     1            IWRITE,IBUGA3,ISUBRO,IERROR)
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCR')THEN
        WRITE(ICOUT,3303)NUMSTR,IERROR
 3303   FORMAT('NUMSTR,IERROR = ',I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        IF(NUMSTR.GE.1)THEN
          DO3305JJ=1,NUMSTR
            WRITE(ICOUT,3304)JJ,ISTRN1(JJ),ISTRN2(JJ)
 3304       FORMAT('JJ,ISTRN1(JJ),ISTRN2(JJ) = ',I8,2X,2A4)
            CALL DPWRST('XXX','BUG ')
 3305     CONTINUE
        ENDIF
      ENDIF
C
C     FOR NOW, ONLY SUPPORT THE CASE FOR PRE-DEFINED STRINGS.
C     THAT IS, LITERAL TEXT (E.G., "Case 1") NOT SUPPORTED.
C
      IF(IERROR.EQ.'YES')GOTO9000
C
C     CASE WHERE WE ARE EXTRACTING PREVIOUSLY DEFINED STRINGS
C
      DO3310I2=1,NUMSTR
C
        IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCR')THEN
          WRITE(ICOUT,3311)I2
 3311     FORMAT('AT 3310L I2 = ',I5)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
        ICNT=0
        DO3315I=1,NUMNAM
          II=I
          IF(ISTRN1(I2).EQ.IHNAME(I) .AND. ISTRN2(I2).EQ.IHNAM2(I))THEN
            IINDX=I2
            ISTART=IVSTAR(II)
            ISTOP=IVSTOP(II)
            NLEN3=ISTOP-ISTART+1
C
C           ZERO LENGTH STRINGS ARE NOT A MATCH
C
            IF(NLEN3.LT.1)GOTO3310
            ICNT=0
            DO3330II=ISTART,ISTOP
              ICNT=ICNT+1
              IF(ICNT.LE.MAXTMP)THEN
                ISTR3(ICNT:ICNT)=IFUNC(II)(1:1)
              ELSE
                WRITE(ICOUT,999)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,2001)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,3361)ISTRN1(I),ISTRN2(I)
 3361           FORMAT('      THE STRING ',2A4,' EXCEEDS ',I5,
     1                 ' CHARACTERS.')
                CALL DPWRST('XXX','BUG ')
                IERROR='YES'
                GOTO9000
              ENDIF
 3330       CONTINUE
            GOTO3319
          ENDIF
 3315   CONTINUE
C
C       NO MATCH FOUND FOR STRING NAME.  DON'T TREAT THIS AS AN
C       ERROR, SIMPLY IGNORE AND MOVE ON TO NEXT STRING.
C
CCCCC   WRITE(ICOUT,999)
CCCCC   CALL DPWRST('XXX','BUG ')
CCCCC   WRITE(ICOUT,2001)
CCCCC   CALL DPWRST('XXX','BUG ')
CCCCC   WRITE(ICOUT,3323)ISTRN1(I2),ISTRN2(I2)
C3323   FORMAT('       STRING ',A4,A4,' NOT MATCHED IN NAME ',
CCCCC1         'TABLE.')
CCCCC   CALL DPWRST('XXX','BUG ')
CCCCC   IERROR='YES'
        GOTO3310
C
 3319   CONTINUE
C
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')THEN
          WRITE(ICOUT,3321)IFLAG,NLEN3,ICNT,ISTR3(1:MAX(1,NLEN3))
 3321     FORMAT('IFLAG,NLEN3,ICNT,ISTR3(1:ICNT) = ',3I5,2X,A80)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
C       NOW SEE IF CURRENT STRING MATCHES.
C
        IF(NLEN1.NE.NLEN3)GOTO3310
        DO3340JJ=1,NLEN3
          IF(ISTR1(JJ:JJ).NE.ISTR3(JJ:JJ))GOTO3310
 3340   CONTINUE
C
C       STRING MATCH FOUND, SAVE "REPLACEMENT" STRING IN IFUNC2.  BUT
C       FIRST CHECK TO SEE IF WE NEED TO RECHECK REPLACEMENT STRING.
C
        IF(IFLAG.EQ.0)THEN
C
          ISTEPN='3D'
          IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1      CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
          IHTEMP(1:4)=IHRIGH(1:4)
          IHTEMP(5:8)=IHRIG2(1:4)
          ILAST=8
          DO3501JJ=8,1,-1
            IF(IHTEMP(JJ:JJ).NE.' ')THEN
              ILAST=JJ
              GOTO3509
            ENDIF
 3501     CONTINUE
 3509     CONTINUE
          IF(IINDX.LE.9)THEN
            IF(ILAST.LE.7)THEN
              IFLAG2=1
              WRITE(IHTEMP(ILAST+1:ILAST+1),'(I1)')IINDX
            ELSE
              IFLAG2=0
            ENDIF
          ELSEIF(IINDX.LE.99)THEN
            IF(ILAST.LE.6)THEN
              IFLAG2=1
              WRITE(IHTEMP(ILAST+1:ILAST+2),'(I2)')IINDX
            ELSE
              IFLAG2=0
            ENDIF
          ELSEIF(IINDX.LE.999)THEN
            IF(ILAST.LE.5)THEN
              IFLAG2=1
              WRITE(IHTEMP(ILAST+1:ILAST+3),'(I3)')IINDX
            ELSE
              IFLAG2=0
            ENDIF
          ENDIF
          IHRIGH=IHTEMP(1:4)
          IHRIG2=IHTEMP(5:8)
          IF(IFLAG2.EQ.0)THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3213)IHRIGH,IHRIG2
 3213       FORMAT('      THE SECOND NAME ON THE RIGHT HAND SIDE (',
     1             2A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3215)
 3215       FORMAT('      IS NOT DEFINED (THIS NAME DEFINES THE ',
     1             'REPLACEMENT STRING).')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
C
          DO3500KK=1,NUMNAM
C
            IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCR')THEN
              WRITE(ICOUT,3506)KK
 3506         FORMAT('AT 3500L KK = ',I5)
              CALL DPWRST('XXX','BUG ')
            ENDIF
C
            K2=KK
            IF(IHRIGH.EQ.IHNAME(KK).AND.IHRIG2.EQ.IHNAM2(KK))THEN
              IF(IUSE(K2).EQ.'F')THEN
                ISTART=IVSTAR(K2)
                ISTOP=IVSTOP(K2)
                NLEN2=ISTOP-ISTART+1
                IF(NLEN2.GE.1)THEN
                  ICNT=0
                  DO3510II=ISTART,ISTOP
                    ICNT=ICNT+1
                    ISTR2(ICNT:ICNT)=IFUNC(II)(1:1)
 3510             CONTINUE
                ELSE
                  WRITE(ICOUT,999)
                  CALL DPWRST('XXX','BUG ')
                  WRITE(ICOUT,2001)
                  CALL DPWRST('XXX','BUG ')
                  WRITE(ICOUT,3201)IHRIGH,IHRIG2
                  CALL DPWRST('XXX','BUG ')
                  WRITE(ICOUT,3202)
                  CALL DPWRST('XXX','BUG ')
                  IERROR='YES'
                  GOTO9000
                ENDIF
                IFLAG=1
                GOTO3519
              ELSE
                WRITE(ICOUT,999)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,2001)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,3203)IHRIGH,IHRIG2
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,3205)
                CALL DPWRST('XXX','BUG ')
                IERROR='YES'
                GOTO9000
              ENDIF
            ENDIF
 3500     CONTINUE
C
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,3213)IHRIGH,IHRIG2
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,3215)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
C
 3519     CONTINUE
        ENDIF
C
        DO3345JJ=1,NLEN2
          IFUNC2(JJ)='   '
          IFUNC2(JJ)(1:1)=ISTR2(JJ:JJ)
 3345   CONTINUE
C
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')THEN
          ISTEPN='3D'
          CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
          WRITE(ICOUT,3350)NLEN2
 3350     FORMAT('NLEN2 = ',I5)
          CALL DPWRST('XXX','BUG ')
          DO3355JJ=1,NLEN2
            WRITE(ICOUT,3357)JJ,IFUNC2(JJ)
 3357       FORMAT('JJ,IFUNC2(JJ) = ',I5,2X,A4)
            CALL DPWRST('XXX','BUG ')
 3355     CONTINUE
        ENDIF
C
        GOTO3390
 3310 CONTINUE
C
      ISTEPN='3E'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     2021/01: MOVE THE GOTO9000 OUTSIDE THE FEEDBACK BLOCK
C
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3401)IHARG(7),IHARG2(7)
 3401   FORMAT('NO MATCH FOUND FOR ',2A4,'.  NOTHING DONE.')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
CCCCC   GOTO9000
      ENDIF
      GOTO9000
C
 3390 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      ISTEPN='4'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CALL DPINFU(IFUNC2,NLEN2,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1            NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
     1            NEWNAM,MAXNAM,
     1            IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
C
      ISTEPN='4B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCR')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(IERROR.EQ.'YES')GOTO9000
C
      ILAB(1)='TO T'
      ILAB(2)='HE F'
      ILAB(3)='UNCT'
      ILAB(4)='ION '
      ILAB(5)='    '
      ILAB(6)=' -- '
      NUMWDL=6
      IF(IFEEDB.EQ.'ON' .OR. IPRINT.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,6606)IHLEFT,IHLEF2
 6606   FORMAT('THE NAME ',2A4,' HAS JUST BEEN EQUIVALENCED ')
        CALL DPWRST('XXX','BUG ')
        CALL DPPRIF(ILAB,NUMWDL,IFUNC2,NLEN2,IBUGA3)
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCR')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTCR--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTCT(ICASE,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--CHECK IF THE ARGUMENTS SPECIFIED ON THE COMMAND ARE
C              OF THE SPECIFIED TYPE
C     EXAMPLE--LET IFLAG = CHECK TYPE VARIABLE S1 S2 S3
C            --LET IFLAG = CHECK TYPE STRING S1 S2 S3
C            --LET IFLAG = CHECK TYPE S1 S2 S3
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2015/03
C     ORIGINAL VERSION--MARCH     2015.
C     UPDATED         --MAY       2018. ADD OPTION TO CHECK IF
C                                       TYPE IS LITERAL NUMERIC
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASE
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*8 ISTR
C
      PARAMETER (MAXIND=100)
      CHARACTER*4 IVARN1(MAXIND)
      CHARACTER*4 IVARN2(MAXIND)
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='CT  '
      IERROR='NO'
C
      ILOC3=0
      ICOLL=0
      ILISTL=0
      NIOLD=0
C
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCT')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTCT--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,ICASE,NUMNAM,NUMCHF,MAXCHF
   52   FORMAT('IBUGA3,ISUBRO,ICASE,NUMNAM,NUMCHF,MAXCHF = ',
     1         3(A4,2X),3I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCT')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE LEFT-HAND SIDE--                     *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A PARAMETER (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCT')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO1910I=1,4
        IF(IHLEFT(I:I).EQ.'(')THEN
          IHLEFT(I:4)=' '
          IHLEF2=' '
          ICASEL='ELEM'
          GOTO1999
        ENDIF
 1910 CONTINUE
      DO1920I=1,4
        IF(IHLEF2(I:I).EQ.'(')THEN
          IHLEF2(I:4)=' '
          ICASEL='ELEM'
          GOTO1999
        ENDIF
 1920 CONTINUE
 1999 CONTINUE
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'P')THEN
            ICASEL='PARA'
            ILISTL=I2
            GOTO2900
          ELSEIF(IUSE(I2).EQ.'V')THEN
            ICASEL='ELEM'
            ILISTL=I2
            ICOLL=IVALUE(ILISTL)
            NIOLD=IN(ILISTL)
            GOTO2900
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN CHECK TYPE--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='PARA'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2900 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE NAMES ON THE RIGHT HAND SIDE       **
C               *****************************************************
C
      ISTEPN='3'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCT')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IFRST=5
      IF(ICASE.NE.'UNKN')IFRST=IFRST+1
      IF(NUMARG.LT.IFRST)THEN
        IFLAG=-1
        GOTO3900
      ENDIF
      IFLAG=1
C
      JMIN=IFRST
      JMAX=NUMARG
      CALL EXTVA3(IHARG,IHARG2,NUMARG,JMIN,JMAX,MAXIND,
     1            IHNAME,IHNAM2,NUMNAM,
     1            IVARN1,IVARN2,NUMIND,
     1            IBUGA3,ISUBRO,IERROR)
      IERROR='NO'
C
      DO3010II=1,NUMIND
        IHRIGH=IVARN1(II)
        IHRIG2=IVARN2(II)
        DO3020I=1,NUMNAM
          I4=I
          IF(ICASE.EQ.'VARI')THEN
            IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
              IF(IUSE(I).NE.'V')THEN
                WRITE(ICOUT,999)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,2001)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,3021)IHRIGH,IHRIG2
 3021           FORMAT('      NAME ',2A4,' IS NOT A VARIABLE.')
                CALL DPWRST('XXX','BUG ')
                IFLAG=0
              ENDIF
              GOTO3010
            ENDIF
          ELSEIF(ICASE.EQ.'STRI')THEN
            IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
              IF(IUSE(I).NE.'F')THEN
                WRITE(ICOUT,999)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,2001)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,3022)IHRIGH,IHRIG2
 3022           FORMAT('      NAME ',2A4,' IS NOT A STRING/FUNCTION.')
                CALL DPWRST('XXX','BUG ')
                IFLAG=0
              ENDIF
              GOTO3010
            ENDIF
          ELSEIF(ICASE.EQ.'PARA')THEN
            IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
              IF(IUSE(I).NE.'P')THEN
                WRITE(ICOUT,999)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,2001)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,3023)IHRIGH,IHRIG2
 3023           FORMAT('      NAME ',2A4,' IS NOT A PARAMETER.')
                CALL DPWRST('XXX','BUG ')
                IFLAG=0
              ENDIF
              GOTO3010
            ENDIF
          ELSEIF(ICASE.EQ.'MATR')THEN
            IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
              IF(IUSE(I).NE.'M')THEN
                WRITE(ICOUT,999)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,2001)
                CALL DPWRST('XXX','BUG ')
                WRITE(ICOUT,3024)IHRIGH,IHRIG2
 3024           FORMAT('      NAME ',2A4,' IS NOT A MATRIX.')
                CALL DPWRST('XXX','BUG ')
                IFLAG=0
              ENDIF
              GOTO3010
            ENDIF
          ELSEIF(ICASE.EQ.'NUMB')THEN
            IVAL=IFRST+II-1
            IF(IARGT(IVAL).NE.'NUMB')THEN
              WRITE(ICOUT,999)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,2001)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,3033)IHRIGH,IHRIG2
 3033         FORMAT('      NAME ',2A4,' IS NOT A NUMBER.')
              CALL DPWRST('XXX','BUG ')
              IFLAG=0
            ENDIF
            GOTO3010
          ELSE
            IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
              IF(IUSE(I).EQ.'V')ICASE='VARI'
              IF(IUSE(I).EQ.'F')ICASE='STRI'
              IF(IUSE(I).EQ.'P')ICASE='PARA'
              IF(IUSE(I).EQ.'M')ICASE='MATR'
              GOTO3010
            ENDIF
          ENDIF
 3020   CONTINUE
C
        IFLAG=0
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3043)IHRIGH,IHRIG2
 3043   FORMAT('      NAME ',2A4,' WAS NOT FOUND IN THE CURRENT NAME ',
     1         'LIST.')
        CALL DPWRST('XXX','BUG ')
C
 3010 CONTINUE
C
 3900 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  SAVE PARAMETER                                 **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCT')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ISTART,ISTOP,IVAL
 4011   FORMAT('ISTART,ISTOP,IVAL = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL
 4013   FORMAT('ICASEL = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(ICASEL.EQ.'PARA')THEN
C
        ISTEPN='4A'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STCT')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='P'
        VALUE(ILISTL)=REAL(IFLAG)
        IVALUE(ILISTL)=IFLAG
        IN(ILISTL)=1
        IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
      ELSEIF(ICASEL.EQ.'ELEM')THEN
C
C       SEARCH IANS STRING FOR "(xx) =".  IF NO PARENTHESIS
C       FOUND BEFORE "=", THEN DO NOT KNOW WHAT ROW OF THE
C       VARIABLE TO SAVE.  TREAT THIS AS AN ERROR.
C
        NLEFT=-1
        NRIGHT=-1
        NEQUAL=-1
        DO4201I=1,IWIDTH
          IF(IANS(I)(1:1).EQ.'(' .AND. NLEFT.LT.0)THEN
            NLEFT=I
          ELSEIF(IANS(I)(1:1).EQ.')' .AND. NRIGHT.LT.0)THEN
            NRIGHT=I
          ELSEIF(IANS(I)(1:1).EQ.'=' .AND. NEQUAL.LT.0)THEN
            NEQUAL=I
          ENDIF
 4201   CONTINUE
C
C       NEED  NLEFT < NRIGHT < NEQUAL
C
        NSTRT=NLEFT+1
        NSTOP=NRIGHT-1
        NLEN=NSTOP-NSTRT+1
        IF(NLEFT.GT.NRIGHT .OR. NRIGHT.GT.NEQUAL .OR.
     1     NSTRT.GT.NSTOP .OR. NLEN.GT.8) THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4211)
 4211     FORMAT('      UNRECOGNIZED SYNTAX FOR VARIABLE ELEMENT ON ',
     1           'LEFT HAND SIDE EQUAL SIGN.')
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ELSE
          ISTR=' '
          DO4216I=1,NLEN
            ISTR(I:I)=IANS(NSTRT+I-1)(1:1)
 4216     CONTINUE
          READ(ISTR,'(I8)',ERR=4218)IARGL
          GOTO4219
C
 4218     CONTINUE
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4211)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
C
 4219     CONTINUE
        ENDIF
C
        IF(IARGL.LT.1 .OR. IARGL.GT.MAXN)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4231)IARGL,ILEFT
 4231     FORMAT('      THE SPECIFIED ROW (',I8,') OF VARIABLE ',A4,A4)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4233)
 4233     FORMAT('      WAS LESS THAN 1 OR GREATER THAN THE')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,4235)MAXN
 4235     FORMAT('      MAXIMUM ALLOWABLE ',I8)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
C
        IF(NEWNAM.EQ.'YES')THEN
          NIOLD=1
        ENDIF
        NINEW=NIOLD
        IF(IARGL.GT.NINEW)NINEW=IARGL
        NS2=1
C
        RIGHT=REAL(IFLAG)
        IJ=MAXN*(ICOLL-1)+IARGL
        IF(ICOLL.LE.MAXCOL)V(IJ)=RIGHT
        IF(ICOLL.EQ.MAXCP1)PRED(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP2)RES(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP3)YPLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP4)XPLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP5)X2PLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP6)TAGPLO(IARGL)=RIGHT
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='V'
        IVALUE(ILISTL)=ICOLL
        VALUE(ILISTL)=ICOLL
        IN(ILISTL)=NINEW
C
        IF(NEWNAM.EQ.'YES')THEN
          NUMNAM=NUMNAM+1
          NUMCOL=NUMCOL+1
        ENDIF
C
        DO4290J4=1,NUMNAM
          IF(IUSE(J4).EQ.'V'.AND.IVALUE(J4).EQ.ICOLL)THEN
            IUSE(J4)='V'
            IVALUE(J4)=ICOLL
            VALUE(J4)=ICOLL
            IN(J4)=NINEW
            GOTO4299
          ENDIF
 4290   CONTINUE
 4299   CONTINUE
C
      ENDIF
C
      IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,8011)IFLAG
 8011   FORMAT('THE STATUS FLAG FOR CHECK TYPE = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
      GOTO9000
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STCT')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTCT--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTDE(ICASEZ,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--IMPLEMENT THE FOLLOWING COMMAND:
C
C                 LET SOUT = STRING DELETE SORG SCHAR
C
C              FOR EACH CHARACTER IN THE STRING "SCHAR", SEARCH
C              THE STRING "SORG" AND REMOVE THAT CHARACTER IF FOUND.
C
C              THE STRINGS ON THE RIGHT HAND SIDE MUST BOTH BE
C              PREVIOUSLY DEFINED.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2018/10
C     ORIGINAL VERSION--OCTOBER   2018.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASEZ
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWNA2
      CHARACTER*4 NEWCOL
      CHARACTER*4 NEWCO2
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 IHRI21
      CHARACTER*4 IHRI22
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*4 ILAB(10)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='DE  '
      IERROR='NO'
C
      ILOC3=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STDE')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTDE--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',2(A4,2X),I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,2A4,2X,A4,2I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STDE')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWNA2='NO'
      NEWCOL='NO'
      NEWCO2='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE FIRST ARGUMENT ON THE                *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A PARAMETER (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STDE')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING DELETE--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE FIRST NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      NUMTMP=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STDE')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IINDX=5
      IHRIGH=IHARG(IINDX)
      IHRIG2=IHARG2(IINDX)
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT1=IVSTAR(I4)
            ISTOP1=IVSTOP(I4)
            NLEN1=ISTOP1-ISTRT1+1
            GOTO3099
          ENDIF
        ENDIF
 3000 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               *****************************************************
C               **  STEP 3B-                                       **
C               **  EXTRACT THE SECOND NAME ON THE RIGHT HAND SIDE **
C               *****************************************************
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STDE')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IINDX=IINDX+1
      IHRI21=IHARG(IINDX)
      IHRI22=IHARG2(IINDX)
      DO3100I=1,NUMNAM
        I4=I
        IF(IHRI21.EQ.IHNAME(I).AND.IHRI22.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRI21,IHRI22
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT2=IVSTAR(I4)
            ISTOP2=IVSTOP(I4)
            NLEN2=ISTOP2-ISTRT2+1
            GOTO3199
          ENDIF
        ENDIF
 3100 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRI21,IHRI22
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3199 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  CHECK FOR CHARACTERS TO DELETE                 **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STDE')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ISTRT1,ISTOP1,NLEN1
 4011   FORMAT('ISTRT1,ISTOP1,NLEN1 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4012)ISTRT2,ISTOP2,NLEN2
 4012   FORMAT('ISTART,ISTOP,NLEN2 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL,ICASEZ
 4013   FORMAT('ICASEL,ICASEZ = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      ICNT=0
      DO4100I=ISTRT1,ISTOP1
        DO4200J=ISTRT2,ISTOP2
          IF(IFUNC(I).EQ.IFUNC(J))GOTO4199
 4200   CONTINUE
        ICNT=ICNT+1
        IFUNC2(ICNT)=' '
        IFUNC2(ICNT)(1:1)=IFUNC(I)(1:1)
 4199 CONTINUE
 4100 CONTINUE
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE STRING                                    **
C               *****************************************************
C
C
      IF(ICASEL.EQ.'STRI')THEN
C
        ISTEPN='5'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STDE')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        CALL DPINFU(IFUNC2,ICNT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1              NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
     1              NEWNAM,MAXNAM,
     1              IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,6606)IHLEFT,IHLEF2
 6606     FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
          CALL DPWRST('XXX','BUG ')
          ILAB(1)='TO T'
          ILAB(2)='HE F'
          ILAB(3)='UNCT'
          ILAB(4)='ION '
          ILAB(5)='    '
          ILAB(6)=' -- '
          NUMWDL=6
          CALL DPPRIF(ILAB,NUMWDL,IFUNC2,ICNT,IBUGA3)
C
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
C
        ENDIF
C
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STDE')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTDE--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTED(IFLAGG,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--EDIT A PREVIOUSLY DEFINED STRING
C     EXAMPLE--LET SOUT = STRING EDIT SORG SOLD SNEW
C     WRITTEN BY--JAMES J. FILLIBEN
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2855
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/11
C     ORIGINAL VERSION--NOVEMBER  2008.
C     UPDATED         --FEBRUARY  2015. SUPPORT "NULL()" FOR
C                                       REPLACEMENT STRING
C     UPDATED         --FEBRUARY  2015. ADD A "GLOBAL" OPTION
C                                       (IFLAGG PARAMETER)
C     UPDATED         --MARCH     2015. CALL LIST TO DPINFU
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWNA2
      CHARACTER*4 NEWCOL
      CHARACTER*4 NEWCO2
      CHARACTER*4 ICASEL
      CHARACTER*4 ICASE2
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 IHRI21
      CHARACTER*4 IHRI22
      CHARACTER*4 IHRI31
      CHARACTER*4 IHRI32
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*4 ILAB(10)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='ED  '
      IERROR='NO'
C
      ILOC3=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STED')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTED--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM,IFLAGG
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM,IFLAGG = ',2(A4,2X),2I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STED')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWNA2='NO'
      NEWCOL='NO'
      NEWCO2='NO'
      ICASEL='UNKN'
      ICASE2='UNKN'
      NIOLD1=0
      NIOLD2=0
      ICOLL=0
      ICOL2=0
C
      IPASS=0
C
 1000 CONTINUE
      IPASS=IPASS+1
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STED')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING EDIT--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE FIRST NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STED')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     2015/02: FOR GLOBAL EDIT CASE, ON SECOND PASS OR GREATER WE
C              WILL BE EDITING THE "OUTPUT" STRING RATHER THAN THE
C              INPUT STRING.
C
      IF(IPASS.EQ.1)THEN
        NINDX=5
        IF(IFLAGG.EQ.1)NINDX=6
      ELSE
        NINDX=1
      ENDIF
      IHRIGH=IHARG(NINDX)
      IHRIG2=IHARG2(NINDX)
C
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT1=IVSTAR(I4)
            ISTOP1=IVSTOP(I4)
            NLEN1=ISTOP1-ISTRT1+1
            GOTO3099
          ENDIF
        ENDIF
 3000 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               *****************************************************
C               **  STEP 3B-                                       **
C               **  EXTRACT THE SECOND NAME ON THE RIGHT HAND SIDE **
C               *****************************************************
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STED')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(IPASS.GT.1)GOTO3199
C
      NINDX=NINDX+1
      IHRI21=IHARG(NINDX)
      IHRI22=IHARG2(NINDX)
      DO3100I=1,NUMNAM
        I4=I
        IF(IHRI21.EQ.IHNAME(I).AND.IHRI22.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRI21,IHRI22
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT2=IVSTAR(I4)
            ISTOP2=IVSTOP(I4)
            NLEN2=ISTOP2-ISTRT2+1
            GOTO3199
          ENDIF
        ENDIF
 3100 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRI21,IHRI22
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3199 CONTINUE
C
C               *****************************************************
C               **  STEP 3C-                                       **
C               **  EXTRACT THE THIRD  NAME ON THE RIGHT HAND SIDE **
C               *****************************************************
C
      ISTEPN='3C'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STED')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(IPASS.GT.1)GOTO3299
C
C     2015/02: CHECK TO SEE IF LITERAL "NULL()" GIVEN AS ARGUMENT
C
      NINDX=NINDX+1
      IF(IHARG(NINDX).EQ.'NULL' .AND. IHARG2(NINDX).EQ.'()')THEN
        NLEN3=0
        GOTO3299
      ENDIF
C
      IHRI31=IHARG(NINDX)
      IHRI32=IHARG2(NINDX)
      DO3200I=1,NUMNAM
        I4=I
        IF(IHRI31.EQ.IHNAME(I).AND.IHRI32.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRI31,IHRI32
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            I4SAVE=I4
            ISTRT3=IVSTAR(I4)
            ISTOP3=IVSTOP(I4)
            NLEN3=ISTOP3-ISTRT3+1
C
C           2015/02: CHECK FOR "NULL()" OR "null()" TO
C                    DENOTE A NULL STRING
C
            IF(NLEN3.EQ.6 .AND. IFUNC(ISTOP3-1).EQ.'(' .AND.
     1         IFUNC(ISTOP3).EQ.')')THEN
              IF(IFUNC(ISTRT3).EQ.'N' .AND.
     1           IFUNC(ISTRT3+1).EQ.'U' .AND.
     1           IFUNC(ISTRT3+2).EQ.'L' .AND.
     1           IFUNC(ISTRT3+3).EQ.'L')THEN
                NLEN3=0
              ELSEIF(IFUNC(ISTRT3).EQ.'n' .AND.
     1           IFUNC(ISTRT3+1).EQ.'u' .AND.
     1           IFUNC(ISTRT3+2).EQ.'l' .AND.
     1           IFUNC(ISTRT3+3).EQ.'l')THEN
                NLEN3=0
              ENDIF
            ENDIF
            GOTO3299
          ENDIF
        ENDIF
 3200 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRI31,IHRI32
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3299 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  PERFORM THE STRING EDIT                        **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STED')THEN
        ISTEPN='4A'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ISTRT1,ISTOP1,NLEN1,IPASS
 4011   FORMAT('ISTRT1,ISTOP1,NLEN1,IPASS = ',4I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4012)ISTRT2,ISTOP2,NLEN2
 4012   FORMAT('ISTRT2,ISTOP2,NLEN2 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ISTRT3,ISTOP3,NLEN3
 4013   FORMAT('ISTRT3,ISTOP3,NLEN3 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4014)ICASEL
 4014   FORMAT('ICASEL = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C     CHECK TO SEE IF A MATCH FOUND
C
      IMATCH=0
      NLAST=ISTOP1-NLEN2+1
      IF(NLAST.GE.ISTRT1)THEN
        DO4100I=ISTRT1,NLAST
          ICNT=0
          DO4110J=I,I+NLEN2-1
            ICNT=ICNT+1
            IINDX=ISTRT2+ICNT-1
            IF(IFUNC(J)(1:1).NE.IFUNC(IINDX)(1:1))GOTO4100
 4110     CONTINUE
          IMATCH=1
          NSTART=I
          GOTO4199
 4100   CONTINUE
 4199   CONTINUE
      ENDIF
C
      IF(IMATCH.EQ.0)THEN
C
        IF(IPASS.GT.1)GOTO8000
C
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4191)IHRI21,IHRI22,IHRIGH,IHRIG2
 4191   FORMAT('       NO MATCH FOR STRING ',A4,A4,' FOUND IN ',
     1         'STRING ',A4,A4)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C     IF MATCH FOUND, PERFORM THE EDIT
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STED')THEN
        ISTEPN='4B'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4196)IMATCH,NSTART
 4196   FORMAT('IMATCH,NSTART = ',2I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C     COPY THE PORTION OF THE STRING UP TO THE EDIT POINT
C
      ICNT=0
      IF(NSTART.GT.1)THEN
        DO4210I=ISTRT1,NSTART-1
          ICNT=ICNT+1
          IFUNC2(ICNT)=IFUNC(I)
 4210   CONTINUE
      ENDIF
C
C     NOW INSERT THE "NEW" STRING
C
      IF(NLEN3.GE.1)THEN
        DO4120I=1,NLEN2
          ICNT=ICNT+1
          IINDX=I+ISTRT3-1
          IFUNC2(ICNT)=IFUNC(IINDX)
 4120   CONTINUE
      ENDIF
C
C     NOW INSERT THE PART OF THE ORIGINAL STRING AFTER THE EDIT POINT
C
      NTEMP=NSTART+NLEN2
      IF(NTEMP.LE.ISTOP1)THEN
        DO4130I=NTEMP,ISTOP1
          ICNT=ICNT+1
          IFUNC2(ICNT)=IFUNC(I)
 4130   CONTINUE
      ENDIF
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      ISTEPN='5'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STED')
     1  CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CALL DPINFU(IFUNC2,ICNT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1            NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
CCCCC1            NEWNAM,MAXN3,
     1            NEWNAM,MAXNAM,
     1            IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C     2015/02: FOR GLOBAL EDIT, RESTART THE EDIT
C
      IF(IFLAGG.EQ.0)GOTO8000
      GOTO1000
C
 8000 CONTINUE
C
      IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,8606)IHLEFT,IHLEF2
 8606   FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
        CALL DPWRST('XXX','BUG ')
        ILAB(1)='TO T'
        ILAB(2)='HE F'
        ILAB(3)='UNCT'
        ILAB(4)='ION '
        ILAB(5)='    '
        ILAB(6)=' -- '
        NUMWDL=6
        CALL DPPRIF(ILAB,NUMWDL,IFUNC2,ICNT,IBUGA3)
C
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
C
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STED')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTED--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTE2(Y,W,N,YS,MAXN,IBUGG3,ISUBRO,IERROR)
C
C     PURPOSE--FORM A STEM AND LEAF DIAGRAM
C     OUTPUT--A STEM AND LEAF DIAGRAM
C             OF SMOOTHED VALUES.
C     NOTE--THE VECTOR Y REMAINS UNCHANGED.
C     LANGUAGE--ANSI FORTRAN (1977)
C     REFERENCES--MCNEIL, INTERACTIVE DATA ANALYSIS
C                 1977, PAGE 23
C                 (= SOURCE OF ALGORITHM).
C     WRITTEN BY--JAMES J. FILLIBEN
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2855
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     VERSION NUMBER--83.6
C     ORIGINAL VERSION--JULY      1983.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IBUGG3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*1 IA
      CHARACTER*1 M
      CHARACTER*1 IOUT
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION W(*)
      DIMENSION YS(*)
C
      DIMENSION IOUT(132)
      DIMENSION IA(20)
      DIMENSION M(4)
C
      INCLUDE 'DPCOP2.INC'
C
C---------------------------------------------------------------------
C
      DATA IA(1)/'0'/
      DATA IA(2)/'1'/
      DATA IA(3)/'2'/
      DATA IA(4)/'3'/
      DATA IA(5)/'4'/
      DATA IA(6)/'5'/
      DATA IA(7)/'6'/
      DATA IA(8)/'7'/
      DATA IA(9)/'8'/
      DATA IA(10)/'9'/
      DATA IA(11)/'0'/
      DATA IA(12)/'1'/
      DATA IA(13)/'2'/
      DATA IA(14)/'3'/
      DATA IA(15)/'4'/
      DATA IA(16)/'5'/
      DATA IA(17)/'6'/
      DATA IA(18)/'7'/
      DATA IA(19)/'8'/
      DATA IA(20)/'9'/
C
      DATA M(1)/'-'/
      DATA M(2)/' '/
      DATA M(3)/':'/
      DATA M(4)/'+'/
C
C-----START POINT-----------------------------------------------------
C
      IERROR='NO'
C
      IWIDTH=50
      SCALE=1.0
      EPS=0.00000001
C
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'STE2')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTE2--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGG3,ISUBRO,N,MAXN
   52   FORMAT('IBUGG3,ISUBRO,N,MAXN = ',2(A4,2X),2I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,N
          WRITE(ICOUT,56)I,Y(I),W(I)
   56     FORMAT('I,Y(I),W(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
      ENDIF
C
C               ************************************
C               **  FORM A STEM-AND-LEAF DIAGRAM  **
C               ************************************
C
      IF(N.LT.2)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,111)
  111   FORMAT('***** ERROR IN STEM AND LEAF PLOT--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,112)
  112   FORMAT('     NUMBER OF OBSERVATIONS IS LESS THAN TWO.')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,113)N
  113   FORMAT('NUMBER OF OBSERVATIONS = ',I8)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C
C               *********************
C               **  STEP 1--       **
C               **  SORT THE DATA  **
C               *********************
C
      CALL SORT(Y,N,YS)
C
C     STEP 2--
C     DEFINE C
C
      RANGE=YS(N)-YS(1)
      R=EPS+RANGE/SCALE
C
      C=10.0**(11-INT(LOG10(R)+10.0))
      ARG1=INT(R*C/25.0)
      ARG2=0
      MAX=INT(ARG1+0.1)
      IF(ARG2.GT.ARG1)MAX=INT(ARG2+0.1)
C
      ARG1=2
      ARG2=MAX
      MM=INT(ARG1+0.1)
      IF(ARG2.LT.ARG1)MM=INT(ARG2+0.1)
C
      K=3*MM+2-150/(N+50)
      IPROD=(K-1)*(K-2)*(K-5)
      IF(IPROD.EQ.0)C=C*10
C
C     STEP 3--
C     DEFINE MU
C
      MU=10
      IPROD=K*(K-4)*(K-8)
      IF(IPROD.EQ.0)MU=5
      IPROD=(K-1)*(K-5)*(K-6)
      IF(IPROD.EQ.0)MU=20
C
      I=1
      IF(YS(1).GE.0)I=2
      I2=1
      TERM=INT(YS(I2)*C/MU)+I-2
      D=MU*TERM/10.0
C
C               *****************************************
C               **  STEP XX--                          **
C               **  SET UP A LOOP IN WHICH             **
C               **  EACH ITERATION OF THE LOOP         **
C               **  WILL FORM A NEW LINE (ROW) OF THE  **
C               **  STEM AND LEAF DIAGRAM              **
C               *****************************************
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
 1000 CONTINUE
C
C               *****************************************************
C               **  STEP XX--                                      **
C               **  FORM THE NEXT OUTPUT LINE OF THE STEM AND LEAF **
C               **  DIAGRAM.  FILL THE OUTPUT LINE WITH BLANKS.    **
C               *****************************************************
C
      DO1100K=1,IWIDTH
      IOUT(K)=' '
 1100 CONTINUE
C
      IF(I.EQ.2.OR.D.LE.0)GOTO1290
      I=2
      D=D-MU/10.0
 1290 CONTINUE
C
      ICOL=0
 1300 CONTINUE
      ICOL=ICOL+1
      TERM1=YS(I2)*C-10*INT(D)
      IY=INT(0.5+ABS(TERM1))
      IF(YS(I2)*C-10*D.GE.0.5+(MU-1)*(I-1))GOTO1390
      IF(ICOL.LE.IWIDTH)IOUT(ICOL)=IA(1+IY)
      I2=I2+1
      IF(I2.GT.N)GOTO1390
      GOTO1300
 1390 CONTINUE
C
      ID=MOD(IABS(INT(D)),100)
      K1=1+ID/10
      K2=1+ID-10*(K1-1)
      IF(ICOL.LE.IWIDTH+1)GOTO1490
      IOUT(IWIDTH-2)='+'
      IOUT(IWIDTH-1)=IA(1+(ICOL-IWIDTH+2)/10)
      IOUT(IWIDTH)=IA(ICOL-IWIDTH+3-10*((ICOL-IWIDTH+2)/10))
 1490 CONTINUE
C
C               **********************************************
C               **  STEP XX--                               **
C               **  WRITE OUT THE OUTPUT LINE FOR THIS ROW  **
C               **********************************************
C
      K=IWIDTH
      IF(ICOL.LT.IWIDTH)K=ICOL
      WRITE(ICOUT,1510)M(I),IA(K1),IA(K2),M(2),M(3),M(2),
     1(IOUT(ICOL),ICOL=1,K)
 1510 FORMAT(132A1)
      CALL DPWRST('XXX','BUG ')
C
C               *****************************************************
C               **  STEP XX--                                      **
C               **  JUMP BACK TO THE BEGINNING OF THE LOOP         **
C               **  TO WORK ON THE NEXT LINE (ROW) OF THE DIAGRAM  **
C               *****************************************************
C
      IF(I2.GT.N)GOTO9000
      D=D+MU/10.0
      GOTO1000
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT.      **
C               *****************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'ON'.OR.ISUBRO.EQ.'STE2')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTE2--')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTEM(IBUGA2,IBUGA3,IBUGQ,ISUBRO,IFOUND,IERROR)
C
C     PURPOSE--GENERATE A STEM AND LEAF DIAGRAM.
C     WRITTEN BY--JAMES J. FILLIBEN
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2855
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--83/7
C     ORIGINAL VERSION--JULY      1983.
C     UPDATED         --JUNE      1990. TEMPORARY ARRAYS TO GARBAGE COMMON
C     UPDATED         --MARCH     2011. USE DPPARS AND DPPAR3
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IBUGA2
      CHARACTER*4 IBUGA3
      CHARACTER*4 IBUGQ
      CHARACTER*4 ISUBRO
      CHARACTER*4 IFOUND
      CHARACTER*4 IERROR
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*4 ICASE
      CHARACTER*40 INAME
      PARAMETER (MAXSPN=10)
      CHARACTER*4 IVARN1(MAXSPN)
      CHARACTER*4 IVARN2(MAXSPN)
      CHARACTER*4 IVARTY(MAXSPN)
      REAL PVAR(MAXSPN)
      INTEGER ILIS(MAXSPN)
      INTEGER NRIGHT(MAXSPN)
      INTEGER ICOLR(MAXSPN)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
C
      DIMENSION TEMP(MAXOBV)
C
      DIMENSION W(MAXOBV)
CCCCC FOLLOWING LINES ADDED JUNE, 1990
      INCLUDE 'DPCOZZ.INC'
      EQUIVALENCE (GARBAG(IGARB1),W(1))
      EQUIVALENCE (GARBAG(IGARB2),TEMP(1))
CCCCC END CHANGE
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCOSU.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPSU'
      ISUBN2='MM  '
      IFOUND='NO'
      IERROR='NO'
C
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
C
C               ************************************
C               **  TREAT THE STEM AND LEAF CASE  **
C               ************************************
C
      IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'STEM')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTEM--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA2,IBUGA3,IBUGQ,ISUBRO
   52   FORMAT('IBUGA2,IBUGA3,IBUGQ,ISUBRO = ',3(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ***************************
C               **  STEP 1--             **
C               **  EXTRACT THE COMMAND  **
C               ***************************
C
      IF(NUMARG.GE.1.AND.IHARG(1).EQ.'PLOT')THEN
        ISHIFT=1
      ELSEIF(NUMARG.GE.1.AND.
     1       IHARG(1).EQ.'DIAG'.AND.IHARG2(1).EQ.'RAM')THEN
        ISHIFT=1
      ELSEIF(NUMARG.GE.2.AND.
     1       IHARG(1).EQ.'AND'.AND.IHARG(2).EQ.'PLOT')THEN
        ISHIFT=2
      ELSEIF(NUMARG.GE.2.AND.
     1       IHARG(2).EQ.'AND'.AND.IHARG(2).EQ.'DIAG'.AND.
     1       IHARG2(2).EQ.'RAM')THEN
        ISHIFT=2
      ELSEIF(NUMARG.GE.2.AND.
     1       IHARG(1).EQ.'LEAF'.AND.IHARG(2).EQ.'PLOT')THEN
        ISHIFT=2
      ELSEIF(NUMARG.GE.2.AND.
     1       IHARG(2).EQ.'LEAF'.AND.
     1       IHARG(2).EQ.'DIAG'.AND.IHARG2(2).EQ.'RAM')THEN
        ISHIFT=2
      ELSEIF(NUMARG.GE.3.AND.
     1       IHARG(1).EQ.'AND'.AND.IHARG(2).EQ.'LEAF'.AND.
     1       IHARG(3).EQ.'PLOT')THEN
        ISHIFT=3
      ELSEIF(NUMARG.GE.3.AND.
     1       IHARG(1).EQ.'AND'.AND.IHARG(2).EQ.'LEAF'.AND.
     1       IHARG(3).EQ.'DIAG'.AND.IHARG2(3).EQ.'RAM')THEN
        ISHIFT=3
      ELSEIF(NUMARG.GE.3.AND.
     1       IHARG(1).EQ.'AND'.AND.IHARG(2).EQ.'LEAF')THEN
        ISHIFT=2
      ELSE
        GOTO9000
      ENDIF
C
      IFOUND='YES'
      CALL ADJUST(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG)
C
C               ****************************************
C               **  STEP 2--                          **
C               **  EXTRACT THE VARIABLE LIST         **
C               ****************************************
C
      ISTEPN='2'
      IF(IBUGA2.EQ.'ON'.OR.ISUBRO.EQ.'STEM')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      INAME='STEM AND LEAF PLOT'
      MINNA=1
      MAXNA=100
      MINN2=2
      IFLAGE=1
      IFLAGM=1
      IFLAGP=0
      JMIN=1
      JMAX=NUMARG
      MINNVA=1
      MAXNVA=1
C
      CALL DPPARS(IHARG,IHARG2,IARGT,ARG,NUMARG,IANS,IWIDTH,
     1            IHNAME,IHNAM2,IUSE,NUMNAM,IN,IVALUE,VALUE,
     1            JMIN,JMAX,
     1            MINN2,MINNA,MAXNA,MAXSPN,IFLAGE,INAME,
     1            IVARN1,IVARN2,IVARTY,PVAR,
     1            ILIS,NRIGHT,ICOLR,ISUB,NQ,ILOCQ,NUMVAR,
     1            MINNVA,MAXNVA,
     1            IFLAGM,IFLAGP,
     1            IBUGA2,IBUGQ,ISUBRO,IFOUND,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      IF(IBUGA2.EQ.'ON'.OR.ISUBRO.EQ.'STEM')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,281)
  281   FORMAT('***** AFTER CALL DPPARS--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,282)NQ,NUMVAR
  282   FORMAT('NQ,NUMVAR = ',2I8)
        CALL DPWRST('XXX','BUG ')
        IF(NUMVAR.GT.0)THEN
          DO285I=1,NUMVAR
            WRITE(ICOUT,287)I,IVARN1(I),IVARN2(I),ILIS(I),NRIGHT(I),
     1                      ICOLR(I),IVARTY(I)
  287       FORMAT('I,IVARN1(I),IVARN2(I),ILIS(I),NRIGHT(I),',
     1             'ICOLR(I),IVARTY(I) = ',I8,2X,A4,A4,2X,3I8,2X,A4)
            CALL DPWRST('XXX','BUG ')
  285     CONTINUE
        ENDIF
      ENDIF
C
      ICOL=1
      CALL DPPAR3(ICOL,IVALUE,IVALU2,IN,MAXN,MAXOBV,
     1            INAME,IVARN1,IVARN2,IVARTY,
     1            ILIS,NRIGHT,ICOLR,ISUB,NQ,NUMVAR,
     1            MAXCOL,MAXCP1,MAXCP2,MAXCP3,
     1            MAXCP4,MAXCP5,MAXCP6,
     1            V,PRED,RES,YPLOT,XPLOT,X2PLOT,TAGPLO,
     1            Y,Y,Y,NS,NLOCA2,NLOCA3,ICASE,
     1            IBUGA3,ISUBRO,IFOUND,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               ****************************************************
C               **  STEP 8--                                      **
C               **  PREPARE FOR ENTRANCE INTO DPSTE2--            **
C               **  SET THE WEIGHT VECTOR TO UNITY THROUGHOUT.    **
C               ****************************************************
C
      ISTEPN='8'
      IF(IBUGA2.EQ.'ON'.OR.ISUBRO.EQ.'STEM')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      DO1110I=1,NS
      W(I)=1.0
 1110 CONTINUE
C
      IF(IBUGA2.EQ.'ON'.OR.ISUBRO.EQ.'STEM')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,1211)
 1211   FORMAT('***** FROM DPSTEM, AS WE ARE ABOUT TO CALL DPSTE2--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,1212)NS
 1212   FORMAT('NS = ',I8)
        CALL DPWRST('XXX','BUG ')
        DO1215I=1,NS
          WRITE(ICOUT,1216)I,Y(I),W(I)
 1216     FORMAT('I,Y(I),W(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','BUG ')
 1215   CONTINUE
      ENDIF
C
      CALL DPSTE2(Y,W,NS,TEMP,MAXN,IBUGA3,ISUBRO,IERROR)
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'STEM')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTEM--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)IFOUND,IERROR
 9016   FORMAT('IFOUND,IERROR = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTGL(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--EXTRACT GROUP LABELS TO STRING
C     EXAMPLE--LET STEMP = GROUP LABEL TO STRINGS IG
C
C              SO IF THE GROUP LABEL VARIABLE "IG" HAS 10 LABELS,
C              THEN THESE WILL BE EXTRACTED TO STRINGS STEMP1, STEMP2,
C              ..., STEMP10
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2011/10
C     ORIGINAL VERSION--OCTOBER   2011.
C     UPDATED         --MARCH     2015. CALL LIST TO DPINFU
C     UPDATED         --JUNE      2018. ADD BASE STRING TO
C                                       CONTAIN ALL LEVELS
C                                       COMBINED
C     UPDATED         --JUNE      2018. ADD PARAMETER THAT
C                                       SPECIFIES NUMBER OF
C                                       LEVELS
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*8 IHLEFT
      CHARACTER*8 ISTRIN
      CHARACTER*8 ISTRBA
      CHARACTER*4 IHLEF3
      CHARACTER*4 IHLEF4
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
C
      CHARACTER*40 ISTRZZ
      CHARACTER*4  ISTRZ2(1000)
      CHARACTER*1000 ISTRZX
C
      CHARACTER*4 ISUBN0
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCOHO.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='GL  '
      IERROR='NO'
C
      ILOC3=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STGL')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTGL--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  CHECK IF VARIABLE ON RHS IS **
C               **  A GROUP LABEL VARIABLE.     **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IGVAR=0
      IHRIGH=IHARG(7)
      IHRIG2=IHARG2(7)
C
      DO110I=1,MAXGRP
        IF(IHRIGH(1:4).EQ.IGRPVN(I)(1:4) .AND.
     1     IHRIG2(1:4).EQ.IGRPVN(I)(5:8))THEN
            IGVAR=I
            GOTO119
        ENDIF
  110 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,121)
  121 FORMAT('***** ERROR IN GROUP LABEL TO STRINGS COMMAND--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,123)IHRIGH,IHRIG2
  123 FORMAT('      ',A4,A4,' NOT RECOGNIZED AS A PREVIOUSLY DEFINED ',
     1       'GROUP LABEL VARIABLE.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
  119 CONTINUE
C
C               **********************************
C               **  STEP 2--                    **
C               **  DETERMINE NUMBER OF STRINGS **
C               **  TO CREATE (LOOK FOR FIRST   **
C               **  BLANK LABEL).               **
C               **********************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NSTR=200
      DO210I=1,MAXGLA
        IF(IGRPLA(I,IGVAR).EQ.' ')THEN
          NSTR=I-1
          GOTO219
        ENDIF
  210 CONTINUE
  219 CONTINUE
      IF(NSTR.GT.999)NSTR=999
      IF(NSTR.LT.1)GOTO9000
C
C               *************************************************
C               **  STEP 3--                                   **
C               **  EXTRACT THE BASE NAME ON THE LHS OF THE    **
C               **  EQUAL SIGN AND THEN LOOP THROUGH THE       **
C               **  NUMBER OF STRINGS TO CREATE.               **
C               *************************************************
C
      ISTEPN='3'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT(1:4)=IHARG(1)
      IHLEFT(5:8)=IHARG2(1)
      NBASE=1
      DO310I=8,1,-1
        IF(IHLEFT(I:I).NE.' ')THEN
          NBASE=I
          GOTO319
        ENDIF
  310 CONTINUE
  319 CONTINUE
C
      IF(NSTR.LE.9)THEN
        IF(NBASE.GT.7)NBASE=7
      ELSEIF(NSTR.LE.99)THEN
        IF(NBASE.GT.6)NBASE=6
      ELSE
        IF(NBASE.GT.5)NBASE=5
      ENDIF
      ISTRBA(1:8)=IHLEFT(1:8)
      ISTRBA(NBASE+1:8)=' '
C
      ISTEPN='4'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NTOT=0
      ISTRZX=' '
      DO410I=1,NSTR
C
        ISTRZZ=' '
        ISTRIN=' '
        ISTRIN(1:NBASE)=IHLEFT(1:NBASE)
        IF(I.LE.9)THEN
          WRITE(ISTRIN(NBASE+1:NBASE+1),'(I1)')I
        ELSEIF(I.LE.99)THEN
          WRITE(ISTRIN(NBASE+1:NBASE+2),'(I2)')I
        ELSE
          WRITE(ISTRIN(NBASE+1:NBASE+3),'(I3)')I
        ENDIF
C
        NCHAR=40
        DO420J=40,1,-1
          IF(IGRPLA(I,IGVAR)(J:J).NE.' ')THEN
            NCHAR=J
            GOTO429
          ENDIF
  420   CONTINUE
        NCHAR=1
  429   CONTINUE
C
        ISTRZZ(1:NCHAR)=IGRPLA(I,IGVAR)(1:NCHAR)
        NSTRT=NTOT+1
        NSTOP=NTOT+NCHAR
        IF(NSTOP.LE.999)THEN
          ISTRZX(NSTRT:NSTOP)=ISTRZZ(1:NCHAR)
          NTOT=NSTOP
          IF(I.LT.NSTR)THEN
            NTOT=NTOT+1
            ISTRZX(NTOT:NTOT)=' '
          ENDIF
        ENDIF
C
        NEWNAM='NO'
        NEWCOL='NO'
        ICASEL='UNKN'
        NIOLD1=0
        ICOLL=0
C
C               ******************************************************
C               **  STEP 5--                                         *
C               **  EXAMINE THE CURRENT STRING--                     *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
        ISTEPN='5'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1     CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        DO510II=1,NUMNAM
          I2=II
          IF(ISTRIN(1:4).EQ.IHNAME(I2).AND.
     1       ISTRIN(5:8).EQ.IHNAM2(I2))THEN
            IF(IUSE(I2).EQ.'F')THEN
              ICASEL='STRI'
              ILISTL=I2
              GOTO519
            ELSE
              WRITE(ICOUT,999)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,121)
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,513)ISTRIN
  513         FORMAT('      THE NAME ',A8,' ALREADY EXISTS, BUT NOT ',
     1               'AS A STRING.')
              CALL DPWRST('XXX','BUG ')
              WRITE(ICOUT,515)
  515         FORMAT('      THIS STRING WILL NOT BE CREATED.')
              CALL DPWRST('XXX','BUG ')
              GOTO9000
            ENDIF
          ENDIF
  510   CONTINUE
  519   CONTINUE
C
        NEWNAM='YES'
        ICASEL='STRI'
C
        ILISTL=NUMNAM+1
        IF(ILISTL.GT.MAXNAM)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,121)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,522)
  522     FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1           'FUNCTION')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,524)MAXNAM
  524     FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
C
C               *****************************************************
C               **  STEP 6--                                       **
C               **  ADD THE CURRENT STRING                         **
C               *****************************************************
C
        ISTEPN='6'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1     CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        IHLEF3=ISTRIN(1:4)
        IHLEF4=ISTRIN(5:8)
        DO411J=1,NCHAR
          ISTRZ2(J)=' '
          ISTRZ2(J)(1:1)=ISTRZZ(J:J)
  411   CONTINUE
C
        CALL DPINFU(ISTRZ2,NCHAR,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1              NUMNAM,IANS,IWIDTH,IHLEF3,IHLEF4,ILISTL,
CCCCC1              NEWNAM,MAXN3,
     1              NEWNAM,MAXNAM,
     1              IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
  410 CONTINUE
C
C               *****************************************************
C               **  STEP 7--                                       **
C               **  PRINT FEEDBACK MESSAGE                         **
C               *****************************************************
C
      ISTEPN='4'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1  CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,710)NSTR
  710   FORMAT(I5,' STRINGS HAVE BEEN CREATED FROM THE GROUP LABELS.')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IHLEF3='NUMS'
      IHLEF4='TRIN'
      VALUE0=REAL(NSTR)
      CALL DPADDP(IHLEF3,IHLEF4,VALUE0,IHOST1,ISUBN0,
     1            IHNAME,IHNAM2,IUSE,VALUE,IVALUE,NUMNAM,MAXNAM,
     1            IANS,IWIDTH,IBUGA3,IERROR)
C
C               ******************************************************
C               **  STEP 8--                                         *
C               **  EXAMINE THE BASE STRING--                        *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='8'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
      DO810II=1,NUMNAM
        I2=II
        IF(ISTRBA(1:4).EQ.IHNAME(I2).AND.
     1     ISTRBA(5:8).EQ.IHNAM2(I2))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO819
          ELSE
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,121)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,513)ISTRBA
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,515)
            CALL DPWRST('XXX','BUG ')
            GOTO9000
          ENDIF
        ENDIF
  810 CONTINUE
  819 CONTINUE
C
      NEWNAM='YES'
      ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,121)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,522)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,524)MAXNAM
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C               *****************************************************
C               **  STEP 8B--                                      **
C               **  ADD THE BASE STRING                            **
C               *****************************************************
C
      ISTEPN='8B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STGL')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEF3=ISTRBA(1:4)
      IHLEF4=ISTRBA(5:8)
      DO831J=1,NTOT
        ISTRZ2(J)=' '
        ISTRZ2(J)(1:1)=ISTRZX(J:J)
  831 CONTINUE
C
      CALL DPINFU(ISTRZ2,NTOT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1            NUMNAM,IANS,IWIDTH,IHLEF3,IHLEF4,ILISTL,
     1            NEWNAM,MAXNAM,
     1            IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STGL')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTGL--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTHD(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--RETURN THE HAMMING DISTANCE BETWEEN TWO STRINGS.
C              THE HAMMING DISTANCE IS THE NUMBER OF CHARACTERS
C              IN THE TWO STRINGS THAT ARE DIFFERENT.
C     EXAMPLE--LET NLEN = STRING HAMMING DISTANCE S1 S2
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2018/09
C     ORIGINAL VERSION--SEPTEMBER 2018.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 IHRIG3
      CHARACTER*4 IHRIG4
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*8 ISTR
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='HD  '
      IERROR='NO'
C
      ILOC3=0
C
C               *****************************************************
C               **  TREAT THE SUBCASE OF THE LET FUNCTION COMMAND  **
C               **  WHICH DEFINES A FUNCTION                       **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STHD')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTHD--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STHD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
      NIOLD=0
      ICOLL=0
      ICOL2=0
C
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE LEFT-HAND SIDE--                     *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A PARAMETER (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STHD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO1910I=1,4
        IF(IHLEFT(I:I).EQ.'(')THEN
          IHLEFT(I:4)=' '
          IHLEF2=' '
          ICASEL='ELEM'
          GOTO1999
        ENDIF
 1910 CONTINUE
      DO1920I=1,4
        IF(IHLEF2(I:I).EQ.'(')THEN
          IHLEF2(I:4)=' '
          ICASEL='ELEM'
          GOTO1999
        ENDIF
 1920 CONTINUE
 1999 CONTINUE
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'P')THEN
            ICASEL='PARA'
            ILISTL=I2
            GOTO2900
          ELSEIF(IUSE(I2).EQ.'V')THEN
            ICASEL='ELEM'
            ILISTL=I2
            ICOLL=IVALUE(ILISTL)
            NIOLD=IN(ILISTL)
            GOTO2900
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING HAMMING DISTANCE--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='PARA'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2900 CONTINUE
C
C               *****************************************************
C               **  STEP 3A--                                      **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STHD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHRIGH=IHARG(6)
      IHRIG2=IHARG2(6)
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE FIRST NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT1=IVSTAR(I4)
            ISTOP1=IVSTOP(I4)
            IVAL1=ISTOP1-ISTRT1+1
            GOTO3099
          ENDIF
        ENDIF
 3000 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               *****************************************************
C               **  STEP 3B--                                      **
C               **  EXTRACT THE SECOND NAME ON THE RIGHT HAND SIDE **
C               *****************************************************
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STHD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHRIG3=IHARG(7)
      IHRIG4=IHARG2(7)
      DO3100I=1,NUMNAM
        I4=I
        IF(IHRIG3.EQ.IHNAME(I).AND.IHRIG4.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3103)IHRIG3,IHRIG4
 3103       FORMAT('      THE SECOND NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT2=IVSTAR(I4)
            ISTOP2=IVSTOP(I4)
            IVAL2=ISTOP2-ISTRT2+1
            GOTO3900
          ENDIF
        ENDIF
 3100 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3900 CONTINUE
C
      IF(IVAL1.NE.IVAL2)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3901)
 3901   FORMAT('      THE TWO STRINGS ARE NOT THE SAME LENGTH.')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3902)IVAL1
 3902   FORMAT('      STRING ONE HAS ',I6,' CHARACTERS.')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3903)IVAL2
 3903   FORMAT('      STRING TWO HAS ',I6,' CHARACTERS.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  SAVE PARAMETER                                 **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STHD')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ISTRT1,ISTOP1,IVAL1
 4011   FORMAT('ISTRT1,ISTOP1,IVAL1 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4012)ISTRT2,ISTOP2,IVAL2
 4012   FORMAT('ISTRT2,ISTOP2,IVAL2 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL
 4013   FORMAT('ICASEL = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      ICNT=0
      DO4020I=1,IVAL1
        IF(IFUNC(ISTRT1+I-1).NE.IFUNC(ISTRT2+I-1))ICNT=ICNT+1
 4020 CONTINUE
C
      IF(ICASEL.EQ.'PARA')THEN
C
        ISTEPN='4A'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STHD')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='P'
        VALUE(ILISTL)=REAL(ICNT)
        IVALUE(ILISTL)=INT(VALUE(ILISTL)+0.5)
        IN(ILISTL)=1
        IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
C
        IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,15111)IHLEFT,IHLEF2,IHRIGH,IHRIG2,IHRIG3,IHRIG4
15111     FORMAT(2A4,' CONTAINS THE HAMMING DISTANCE OF STRINGS ',
     1           2A4,' AND ',2A4)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,15113)ICNT
15113     FORMAT('THE HAMMING DISTANCE = ',I8)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
        ENDIF
      ELSEIF(ICASEL.EQ.'ELEM')THEN
C
C       SEARCH IANS STRING FOR "(xx) =".  IF NO PARENTHESIS
C       FOUND BEFORE "=", THEN DO NOT KNOW WHAT ROW OF THE
C       VARIABLE TO SAVE.  TREAT THIS AS AN ERROR.
C
        NLEFT=-1
        NRIGHT=-1
        NEQUAL=-1
        DO16001I=1,IWIDTH
          IF(IANS(I)(1:1).EQ.'(' .AND. NLEFT.LT.0)THEN
            NLEFT=I
          ELSEIF(IANS(I)(1:1).EQ.')' .AND. NRIGHT.LT.0)THEN
            NRIGHT=I
          ELSEIF(IANS(I)(1:1).EQ.'=' .AND. NEQUAL.LT.0)THEN
            NEQUAL=I
          ENDIF
16001   CONTINUE
C
C       NEED  NLEFT < NRIGHT < NEQUAL
C
        NSTRT=NLEFT+1
        NSTOP=NRIGHT-1
        NLEN=NSTOP-NSTRT+1
        IF(NLEFT.GT.NRIGHT .OR. NRIGHT.GT.NEQUAL .OR.
     1     NSTRT.GT.NSTOP .OR. NLEN.GT.8) THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16011)
16011     FORMAT('      UNRECOGNIZED SYNTAX FOR VARIABLE ELEMENT ON')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16013)
16013     FORMAT('      LEFT HAND SIDE EQUAL SIGN.')
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ELSE
          ISTR=' '
          DO16020I=1,NLEN
            ISTR(I:I)=IANS(NSTRT+I-1)(1:1)
16020     CONTINUE
          READ(ISTR,'(I8)',ERR=16029)IARGL
          GOTO16049
C
16029     CONTINUE
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16011)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16013)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
C
16049     CONTINUE
        ENDIF
C
        IF(IARGL.LT.1 .OR. IARGL.GT.MAXN)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16052)IARGL,ILEFT
16052     FORMAT('      THE SPECIFIED ROW (',I8,') OF VARIABLE ',A4,A4)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16054)
16054     FORMAT('      WAS LESS THAN 1 OR GREATER THAN THE')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16055)MAXN
16055     FORMAT('      MAXIMUM ALLOWABLE ',I8)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
C
        IF(NEWNAM.EQ.'YES')THEN
          NIOLD=1
        ENDIF
        NINEW=NIOLD
        IF(IARGL.GT.NINEW)NINEW=IARGL
        NS2=1
C
        RIGHT=REAL(ICNT)
        IJ=MAXN*(ICOLL-1)+IARGL
        IF(ICOLL.LE.MAXCOL)V(IJ)=RIGHT
        IF(ICOLL.EQ.MAXCP1)PRED(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP2)RES(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP3)YPLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP4)XPLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP5)X2PLOT(IARGL)=RIGHT
        IF(ICOLL.EQ.MAXCP6)TAGPLO(IARGL)=RIGHT
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='V'
        IVALUE(ILISTL)=ICOLL
        VALUE(ILISTL)=ICOLL
        IN(ILISTL)=NINEW
C
        IF(NEWNAM.EQ.'YES')THEN
          NUMNAM=NUMNAM+1
          NUMCOL=NUMCOL+1
        ENDIF
C
        DO16200J4=1,NUMNAM
          IF(IUSE(J4).EQ.'V'.AND.IVALUE(J4).EQ.ICOLL)THEN
            IUSE(J4)='V'
            IVALUE(J4)=ICOLL
            VALUE(J4)=ICOLL
            IN(J4)=NINEW
            GOTO16209
          ENDIF
16200   CONTINUE
16209   CONTINUE
C
        IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,16211)IHRIGH,IHRIG2,IHRIG3,IHRIG4,ICNT
16211     FORMAT('THE HAMMING DISTANCE OF STRINGS ',2A4,
     1           ' AND ',2A4,' = ',I8)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
        ENDIF
      ENDIF
      GOTO9000
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STHD')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTHD--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTIN(ICASEZ,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--RETURN THE START AND STOP POSITIONS OF A SUBSTRING
C              OF A PREVIOUSLY DEFINED STRING.
C     EXAMPLE--LET STRING SORG = ABCDEF
C              LET STRING SUB  = DE
C              LET NSTART NSTOP = STRING INDEX SORG SUB
C     NOTE--THE FOLLOWING SYNTAX IS NOT SUPPORTED FOR THIS COMMAND:
C              LET Y(2) Y(8) = STRING INDEX S  SUBSTRING
C           ALSO, THE STRINGS ON THE RIGHT HAND SIDE MUST BOTH BE
C           PREVIOUSLY DEFINED.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/11
C     ORIGINAL VERSION--NOVEMBER  2008.
C     UPDATED         --SEPTEMBER 2018. STRING FIND AS ALIAS
C     UPDATED         --SEPTEMBER 2018. STRING RIGHT INDEX (TO SEARCH
C                                       STRING FROM RIGHT TO LEFT)
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASEZ
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWNA2
      CHARACTER*4 NEWCOL
      CHARACTER*4 NEWCO2
      CHARACTER*4 ICASEL
      CHARACTER*4 ICASE2
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHLE21
      CHARACTER*4 IHLE22
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 IHRI21
      CHARACTER*4 IHRI22
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='IN  '
      IERROR='NO'
C
      ILOC3=0
      ILIST2=0
      NUMTMP=0
      IMATCH=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STIN')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTIN--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWNA2='NO'
      NEWCOL='NO'
      NEWCO2='NO'
      ICASEL='UNKN'
      ICASE2='UNKN'
      NIOLD1=0
      NIOLD2=0
      ICOLL=0
      ICOL2=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE FIRST ARGUMENT ON THE                *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A PARAMETER (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'P')THEN
            ICASEL='PARA'
            ILISTL=I2
            NUMTMP=NUMNAM
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING INDEX--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE FIRST NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='PARA'
C
      ILISTL=NUMNAM+1
      NUMTMP=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               ******************************************************
C               **  STEP 2B-                                         *
C               **  EXAMINE THE SECOND ARGUMENT ON THE               *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A PARAMETER (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLE21=IHARG(2)
      IHLE22=IHARG2(2)
C
      DO2300I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'P')THEN
            ICASE2='PARA'
            ILISTL=I2
            GOTO2399
          ELSE
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2303)IHLEFT,IHLEF2
 2303       FORMAT('      THE SECOND NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2300 CONTINUE
C
      NEWNA2='YES'
      IF(ICASE2.EQ.'UNKN')ICASE2='PARA'
C
      ILIST2=NUMTMP+1
      IF(ILIST2.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2399 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IINDX=6
      IF(ICASEZ.EQ.'STIR')IINDX=7
      IHRIGH=IHARG(IINDX)
      IHRIG2=IHARG2(IINDX)
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT1=IVSTAR(I4)
            ISTOP1=IVSTOP(I4)
            NLEN1=ISTOP1-ISTRT1+1
            GOTO3099
          ENDIF
        ENDIF
 3000 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               *****************************************************
C               **  STEP 3B-                                       **
C               **  EXTRACT THE SECOND NAME ON THE RIGHT HAND SIDE **
C               *****************************************************
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IINDX=IINDX+1
      IHRI21=IHARG(IINDX)
      IHRI22=IHARG2(IINDX)
      DO3100I=1,NUMNAM
        I4=I
        IF(IHRI21.EQ.IHNAME(I).AND.IHRI22.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRI21,IHRI22
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT2=IVSTAR(I4)
            ISTOP2=IVSTOP(I4)
            NLEN2=ISTOP2-ISTRT2+1
            GOTO3199
          ENDIF
        ENDIF
 3100 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRI21,IHRI22
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3199 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  CHECK FOR MATCHING STRINGS                     **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ISTRT1,ISTOP1,NLEN1
 4011   FORMAT('ISTRT1,ISTOP1,NLEN1 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4012)ISTRT2,ISTOP2,NLEN2
 4012   FORMAT('ISTART,ISTOP,NLEN2 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL,ICASE2,ICASEZ
 4013   FORMAT('ICASEL,ICASE2,ICASEZ = ',2(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(ICASEZ.EQ.'STIN')THEN
        NTEMP=ISTOP1-NLEN2+1
        IMATCH=0
        IF(ISTRT1.GT.NTEMP)GOTO4199
        DO4100I=ISTRT1,NTEMP
          NSTRT=I
          NSTOP=NSTRT+NLEN2-1
          ICNT=0
C
          IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')THEN
            WRITE(ICOUT,4801)I,NSTRT,NSTOP
 4801       FORMAT('I,NSTRT,NSTOP = ',3I8)
            CALL DPWRST('XXX','BUG ')
          ENDIF
C
          DO4110J=NSTRT,NSTOP
            IF(IFUNC(J)(1:1).NE.IFUNC(ISTRT2+ICNT)(1:1))GOTO4100
            ICNT=ICNT+1
 4110     CONTINUE
          IMATCH=1
          GOTO4199
 4100   CONTINUE
C
 4199   CONTINUE
C
      ELSEIF(ICASEZ.EQ.'STIR')THEN
C
C       DO RIGHT TO LEFT MATCHING
C
        NTEMP=ISTRT1+NLEN2-1
        IMATCH=0
        IF(ISTRT1.GT.NTEMP)GOTO4299
        DO4200I=ISTOP1,NTEMP,-1
          NSTOP=I
          NSTRT=NSTOP-NLEN2+1
          ICNT=0
C
          IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')THEN
            WRITE(ICOUT,4801)I,NSTRT,NSTOP
            CALL DPWRST('XXX','BUG ')
          ENDIF
C
          DO4210J=NSTRT,NSTOP
            IF(IFUNC(J)(1:1).NE.IFUNC(ISTRT2+ICNT)(1:1))GOTO4200
            ICNT=ICNT+1
 4210     CONTINUE
          IMATCH=1
          GOTO4299
 4200   CONTINUE
C
 4299   CONTINUE
      ENDIF
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')THEN
        ISTEPN='4B'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4901)NSTRT,NSTOP,IMATCH
 4901   FORMAT('NSTRT,NSTOP,IMATCH = ',3I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE PARAMETERS                                **
C               *****************************************************
C
C
      IF(IMATCH.EQ.0)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,5001)IHRI21,IHRI22,IHRIGH,IHRIG2
 5001   FORMAT('STRING  ',A4,A4,'  WAS NOT MATCHED IN STRING ',A4,A4)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ELSE
        NSTRT=NSTRT-ISTRT1+1
        NSTOP=NSTOP-ISTRT1+1
      ENDIF
C
      IF(ICASEL.EQ.'PARA' .AND. ICASE2.EQ.'PARA')THEN
C
        ISTEPN='5'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIN')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        IHNAME(ILISTL)=IHLEFT
        IHNAM2(ILISTL)=IHLEF2
        IUSE(ILISTL)='P'
        VALUE(ILISTL)=REAL(NSTRT)
        IVALUE(ILISTL)=INT(VALUE(ILISTL)+0.5)
        IN(ILISTL)=1
        IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
C
        IHNAME(ILIST2)=IHLE21
        IHNAM2(ILIST2)=IHLE22
        IUSE(ILIST2)='P'
        VALUE(ILIST2)=REAL(NSTOP)
        IVALUE(ILISTL)=INT(VALUE(ILISTL)+0.5)
        IN(ILISTL)=1
        IF(NEWNAM.EQ.'YES')NUMNAM=NUMNAM+1
C
        IF(IPRINT.EQ.'ON' .AND. IFEEDB.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,5011)IHLEFT,IHLEF2,NSTRT
5011      FORMAT(A4,A4,' (START OF SUBSTRING) = ',I8)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,5013)IHLE21,IHLE22,NSTOP
5013      FORMAT(A4,A4,' (END OF SUBSTRING)   = ',I8)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
        ENDIF
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STIN')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTIN--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTIP(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--IMPLEMENT THE COMMAND
C
C              LET SNEW = STRING INTERACTION S1 ... SK
C
C              WHERE
C                  S1 ... SK     = A LIST OF PRE-EXISTING PARAMETERS
C
C              FOR EXAMPLE,
C
C                  LET SNEW = STRING INTERACTION 1 3 5 -9999 -9999
C
C              WILL GENERATE THE STRING
C
C                   X1 * X3 * X5
C
C              THE STRING GENERATION WILL TERMINATE WHEN THE FIRST
C              "-9999" VALUE IS ENCOUNTERED IN THE S1 ... SK LIST.
C
C              THIS COMMAND IS USED IN THE 10-STEP MACROS.
C
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2018/03
C     ORIGINAL VERSION--MARCH     2018.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 IWRITE
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWCOL
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*255 ISTR1
      CHARACTER*4 ILAB(10)
C
      PARAMETER(MAXIND=50)
      REAL PVAR(MAXIND)
      CHARACTER*4 IVARN1(MAXIND)
      CHARACTER*4 IVARN2(MAXIND)
      CHARACTER*4 IVARTY(MAXIND)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='IP  '
      IERROR='NO'
C
      NCSTR=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STIP')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTIP--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM,NUMCHF,MAXCHF
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM,NUMCHF,MAXCHF = ',2(A4,2X),3I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,2A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIP')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWCOL='NO'
      ICASEL='UNKN'
      NIOLD1=0
      ICOLL=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIP')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING INTERACTION--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  LOOP THROUGH THE NAMES ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIP')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.LT.5)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3003)
 3003   FORMAT('      THERE MUST BE AT LEAST ONE STRINGS SPECIFIED ',
     1         'TO THE RIGHT HAND SIDE OF THE EQUAL SIGN')
        CALL DPWRST('XXX','BUG ')
        NTEMP=NUMARG-4
        WRITE(ICOUT,3005)NTEMP
 3005   FORMAT('      THE NUMBER OF STRINGS SPECIFIED IS ',I8)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C               *********************************************************
C               **  STEP 3A--                                           *
C               **  NOW SEARCH THROUGH THE ARGUMENTS                    *
C               *********************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIP')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      JMIN=5
      JMAX=NUMARG
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STIP')THEN
        WRITE(ICOUT,3301)JMIN,JMAX,MAXIND
 3301   FORMAT('JMIN,JMAX,MAXIND = ',3I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IWRITE='OFF'
      IERROR='NO'
C
      IFLAGM=0
      IFLAGP=1
      IFLAGT=0
      CALL EXTVA2(IHARG,IHARG2,IARGT,ARG,NUMARG,JMIN,JMAX,MAXIND,
     1            IHNAME,IHNAM2,IUSE,NUMNAM,
     1            IVARN1,IVARN2,IVARTY,PVAR,NUMIND,
     1            IFLAGM,IFLAGP,IFLAGT,
     1            IBUGA3,ISUBRO,IERROR)
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STIP')THEN
        WRITE(ICOUT,3303)NUMIND,IERROR
 3303   FORMAT('NUMIND,IERROR = ',I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        IF(NUMIND.GE.1)THEN
          DO3305JJ=1,NUMIND
            WRITE(ICOUT,3304)JJ,IVARN1(JJ),IVARN2(JJ),IVARTY(JJ),
     1                       PVAR(JJ)
 3304       FORMAT('JJ,IVARN1(JJ),IVARN2(JJ),IVARTY(JJ),PVAR(JJ) = ',
     1             I8,2X,2A4,2X,A4,2X,G15.7)
            CALL DPWRST('XXX','BUG ')
 3305     CONTINUE
        ENDIF
      ENDIF
C
      IF(IERROR.EQ.'YES')GOTO9000
C
C     NOW LOOP THROUGH AND CREATE THE OUTPUT STRING
C
      DO3310I2=1,NUMIND
        IF(IVARTY(I2).NE.'PARA')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,2001)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,3312)I2,IVARN1(I2),IVARN2(I2)
 3312     FORMAT('      ARGUMENT ',I5,' (',2A4,') IS NOT A PARAMETER.')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
C
        DO3320I=1,NUMNAM
          IF(IVARN1(I2).EQ.IHNAME(I).AND.IVARN2(I2).EQ.IHNAM2(I))THEN
            AVAL=VALUE(I)
            GOTO3329
          ENDIF
 3320   CONTINUE
 3329   CONTINUE
C
        IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STIP')THEN
          WRITE(ICOUT,3333)I2,I,AVAL
 3333     FORMAT('I2,I,AVAL = ',2I8,G15.7)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
        IF(AVAL.GE.0.0)THEN
          IVAL=INT(AVAL+0.1)
          IF(IVAL.GT.999)GOTO3390
          IF(I2.GT.1)THEN
            NCSTR=NCSTR+1
            ISTR1(NCSTR:NCSTR+2)=' * '
            NCSTR=NCSTR+2
          ENDIF
          NCSTR=NCSTR+1
          ISTR1(NCSTR:NCSTR)='X'
          IF(IVAL.LE.9)THEN
            NCSTR=NCSTR+1
            WRITE(ISTR1(NCSTR:NCSTR),'(I1)')IVAL
          ELSEIF(IVAL.LE.99)THEN
            NCSTR=NCSTR+1
            WRITE(ISTR1(NCSTR:NCSTR+1),'(I2)')IVAL
            NCSTR=NCSTR+1
          ELSEIF(IVAL.LE.999)THEN
            NCSTR=NCSTR+1
            WRITE(ISTR1(NCSTR:NCSTR+2),'(I3)')IVAL
            NCSTR=NCSTR+2
          ENDIF
        ELSE
          IF(I2.EQ.1)THEN
            ISTR1='X1'
            NCSTR=2
          ENDIF
          GOTO3390
        ENDIF
 3310 CONTINUE
 3390 CONTINUE
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIP')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      DO3410JJ=1,NCSTR
        IFUNC2(JJ)='   '
        IFUNC2(JJ)(1:1)=ISTR1(JJ:JJ)
 3410 CONTINUE
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIP')THEN
        ISTEPN='3C'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,3450)NCSTR
 3450   FORMAT('NLEN2 = ',I5)
        CALL DPWRST('XXX','BUG ')
        DO3455JJ=1,NCSTR
          WRITE(ICOUT,3457)JJ,IFUNC2(JJ)
 3457     FORMAT('JJ,IFUNC2(JJ) = ',I5,2X,A4)
          CALL DPWRST('XXX','BUG ')
 3455   CONTINUE
      ENDIF
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      ISTEPN='4'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STIP')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CALL DPINFU(IFUNC2,NCSTR,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1            NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
     1            NEWNAM,MAXNAM,
     1            IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,6606)IHLEFT,IHLEF2
 6606   FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
        CALL DPWRST('XXX','BUG ')
        ILAB(1)='TO T'
        ILAB(2)='HE F'
        ILAB(3)='UNCT'
        ILAB(4)='ION '
        ILAB(5)='    '
        ILAB(6)=' -- '
        NUMWDL=6
        CALL DPPRIF(ILAB,NUMWDL,IFUNC2,NCSTR,IBUGA3)
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STIP')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTIP--')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTJU(ICASE,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--LEFT/CENTER/RIGHT JUSTIFY A STRING.  THAT IS,
C              BASED ON THE REQUESTED LENGTH OF THE STRING, ADD
C              LEADING OR TRAILING SPACES.
C     EXAMPLE--LET SOUT = STRING <LEFT/CENTER/RIGHT> JUSTIFY SIN NLEN
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2019/01
C     ORIGINAL VERSION--JANUARY   2019.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASE
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWNA2
      CHARACTER*4 NEWCOL
      CHARACTER*4 NEWCO2
      CHARACTER*4 ICASEL
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 IHRI21
      CHARACTER*4 IHRI22
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*1 IVALC
C
      CHARACTER*4 ILAB(10)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='JU  '
      IERROR='NO'
      IVALC=' '
      IF(ISJUCH.NE.'SPAC')IVALC=ISJUCH(1:1)
C
      ILOC3=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STJU')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTJU--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,2A4,2X,A4,2I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF,NUMARG
   57   FORMAT('NUMCHF,MAXCHF,NUMARG = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
        IF(NUMARG.GE.1)THEN
          DO70I=1,NUMARG
            WRITE(ICOUT,76)I,IHARG(I),IHARG2(I),IARGT(I),IARG(I)
   76       FORMAT('I,IHARG(I),IHARG2(I),IARGT(I),IARG(I) = ',
     1             I8,2X,2A4,2X,A4,2X,I8)
            CALL DPWRST('XXX','BUG ')
   70     CONTINUE
        ENDIF
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STJU')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWNA2='NO'
      NEWCOL='NO'
      NEWCO2='NO'
      ICASEL='UNKN'
      NIOLD1=0
      NIOLD2=0
      ICOLL=0
      ICOL2=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STJU')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING JUSTIFY--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE FIRST NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STJU')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHRIGH=IHARG(6)
      IHRIG2=IHARG2(6)
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT1=IVSTAR(I4)
            ISTOP1=IVSTOP(I4)
            NLEN1=ISTOP1-ISTRT1+1
            GOTO3099
          ENDIF
        ENDIF
 3000 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               *****************************************************
C               **  STEP 3B-                                       **
C               **  EXTRACT THE SECOND NAME ON THE RIGHT HAND SIDE **
C               *****************************************************
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STJU')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHRI21=IHARG(7)
      IHRI22=IHARG2(7)
      DO3100I=1,NUMNAM
        I4=I
        IF(IHRI21.EQ.IHNAME(I).AND.IHRI22.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'P')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRI21,IHRI22
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3115)
 3115       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ILISR1=I4
            NLEN2=IVALUE(ILISR1)
            GOTO3199
          ENDIF
        ENDIF
 3100 CONTINUE
C
      IF(NUMARG.GE.7)THEN
        IF(IARGT(7).EQ.'NUMB')THEN
          NLEN2=IARG(7)
          GOTO3199
        ENDIF
      ENDIF
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRI21,IHRI22
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3199 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  CREATE THE JUSTIFIED STRING                    **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STJU')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)ILISR1,ILISR2,NLEN
 4011   FORMAT('ILISR1,ILISR2,NLEN = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4012)ISTRT1,ISTOP1,NLEN1,NLEN2
 4012   FORMAT('ISTRT1,ISTOP1,NLEN1,NLEN2 = ',4I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASE
 4013   FORMAT('ICASE = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(NLEN1.GE.NLEN2)THEN
        ICNT=0
        DO4100I=ISTRT1,ISTOP1
          ICNT=ICNT+1
          IINDX=I+ISTRT1-1
          IFUNC2(ICNT)=IFUNC(IINDX)
 4100   CONTINUE
      ELSEIF(ICASE.EQ.'RIGH')THEN
        ISPACE=NLEN2-NLEN1
        ICNT=0
        DO4110I=1,ISPACE
          ICNT=ICNT+1
          IFUNC2(ICNT)=IVALC
 4110   CONTINUE
        DO4112I=ISTRT1,ISTOP1
          ICNT=ICNT+1
          IFUNC2(ICNT)=IFUNC(I)
 4112   CONTINUE
      ELSEIF(ICASE.EQ.'LEFT')THEN
        ISPACE=NLEN2-NLEN1
        ICNT=0
        DO4120I=ISTRT1,ISTOP1
          ICNT=ICNT+1
          IFUNC2(ICNT)=IFUNC(I)
 4120   CONTINUE
        DO4122I=1,ISPACE
          ICNT=ICNT+1
          IFUNC2(ICNT)=IVALC
 4122   CONTINUE
      ELSEIF(ICASE.EQ.'CENT')THEN
        ISPACE=NLEN2-NLEN1
        IVAL=MOD(ISPACE,2)
        IF(IVAL.EQ.0)THEN
          ISPACL=ISPACE/2
          ISPACR=ISPACL
        ELSE
          ISPACL=(ISPACE/2) + 1
          ISPACR=ISPACE/2
        ENDIF
        ICNT=0
        DO4130I=1,ISPACL
          ICNT=ICNT+1
          IFUNC2(ICNT)=IVALC
 4130   CONTINUE
        DO4132I=ISTRT1,ISTOP1
          ICNT=ICNT+1
          IFUNC2(ICNT)=IFUNC(I)
 4132   CONTINUE
        DO4134I=1,ISPACR
          ICNT=ICNT+1
          IFUNC2(ICNT)=IVALC
 4134   CONTINUE
      ENDIF
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      ISTEPN='5'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STJU')
     1  CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CALL DPINFU(IFUNC2,ICNT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1            NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
     1            NEWNAM,MAXNAM,
     1            IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,6606)IHLEFT,IHLEF2
 6606   FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
        CALL DPWRST('XXX','BUG ')
        ILAB(1)='TO T'
        ILAB(2)='HE F'
        ILAB(3)='UNCT'
        ILAB(4)='ION '
        ILAB(5)='    '
        ILAB(6)=' -- '
        NUMWDL=6
        CALL DPPRIF(ILAB,NUMWDL,IFUNC2,ICNT,IBUGA3)
C
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
C
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STJU')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTJU--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,2A4,2X,A4,2I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTLC(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--CONVERT A STRING TO LOWER CASE
