 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STNS')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IINDX=6
      IHRIGH=IHARG(IINDX)
