C
      DO7260I=1,MAXRCL
        ISTOR1(I)=' '
        ISTOR2(I)=' '
        ISTOR3(I)=' '
        IB(I)=' '
 7260 CONTINUE
C
      IF(NUMV.GT.0)THEN
