C              DO NOT CHECK IF SOUT IS A CURRENTLY DEFINED STRING
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2019/09
C     ORIGINAL VERSION--SEPTEMBER 2019.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      INCLUDE 'DPCOPA.INC'
C
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*9999 ISTR
      CHARACTER*20 IFRMT
      CHARACTER*20 IFRMT2
      CHARACTER*4 IOP
      CHARACTER*4 IOPEN
      CHARACTER*8 IACC
      CHARACTER*4 IEXIST
      CHARACTER*4 MESSAG
      CHARACTER*4 IWRITE
      CHARACTER*4 ICASE
      CHARACTER*4 ISUBN0
      CHARACTER*4 IERRFI
      CHARACTER*25 ISTR2
CCCCC CHARACTER*255 ISTRIN
CCCCC CHARACTER*255 ISTRI2
      CHARACTER (LEN=MAXSTR) :: ISTRIN
      CHARACTER (LEN=MAXSTR) :: ISTRI2
      PARAMETER(MAXIND=100)
      CHARACTER*4 ISTRN1(MAXIND)
      CHARACTER*4 ISTRN2(MAXIND)
      CHARACTER*4 IHTEMP(255)
      CHARACTER*4 IFILQ2
C
CCCCC CHARACTER*80 IFILE1
      CHARACTER (LEN=MAXFNC) :: IFILE1
      CHARACTER*12 ISTAT1
      CHARACTER*12 IFORM1
      CHARACTER*12 IACCE1
      CHARACTER*12 IPROT1
      CHARACTER*12 ICURS1
      CHARACTER*4 IERRF1
      CHARACTER*4 IENDF1
      CHARACTER*4 IREWI1
C
CCCCC CHARACTER*80 IFILE2
      CHARACTER (LEN=MAXFNC) :: IFILE2
      CHARACTER*12 ISTAT2
      CHARACTER*12 IFORM2
      CHARACTER*12 IACCE2
      CHARACTER*12 IPROT2
      CHARACTER*12 ICURS2
      CHARACTER*4 IERRF2
      CHARACTER*4 IENDF2
      CHARACTER*4 IREWI2
C
CCCCC CHARACTER*80 IFILE3
      CHARACTER (LEN=MAXFNC) :: IFILE3
      CHARACTER*12 ISTAT3
      CHARACTER*12 IFORM3
      CHARACTER*12 IACCE3
      CHARACTER*12 IPROT3
      CHARACTER*12 ICURS3
      CHARACTER*4 IERRF3
      CHARACTER*4 IENDF3
      CHARACTER*4 IREWI3
C
CCCCC CHARACTER*80 IFILE4
      CHARACTER (LEN=MAXFNC) :: IFILE4
      CHARACTER*12 ISTAT4
      CHARACTER*12 IFORM4
      CHARACTER*12 IACCE4
      CHARACTER*12 IPROT4
      CHARACTER*12 ICURS4
      CHARACTER*4 IERRF4
      CHARACTER*4 IENDF4
      CHARACTER*4 IREWI4
C
CCCCC CHARACTER*80 IFILE5
      CHARACTER (LEN=MAXFNC) :: IFILE5
      CHARACTER*12 ISTAT5
      CHARACTER*12 IFORM5
      CHARACTER*12 IACCE5
      CHARACTER*12 IPROT5
      CHARACTER*12 ICURS5
      CHARACTER*4 IERRF5
      CHARACTER*4 IENDF5
      CHARACTER*4 IREWI5
C
      COMMON/FILTMP/IFILE1, ISTAT1, IFORM1, IACCE1, IPROT1, ICURS1,
     1              IERRF1, IENDF1, IREWI1,
     1              IFILE2, ISTAT2, IFORM2, IACCE2, IPROT2, ICURS2,
     1              IERRF2, IENDF2, IREWI2,
     1              IFILE3, ISTAT3, IFORM3, IACCE3, IPROT3, ICURS3,
     1              IERRF3, IENDF3, IREWI3,
     1              IFILE4, ISTAT4, IFORM4, IACCE4, IPROT4, ICURS4,
     1              IERRF4, IENDF4, IREWI4,
     1              IFILE5, ISTAT5, IFORM5, IACCE5, IPROT5, ICURS5,
     1              IERRF5, IENDF5, IREWI5
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOF2.INC'
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='VA  '
      IERROR='NO'
      IFILQ2=IFILQU
C
      ILOC3=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTVA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMARG
   52   FORMAT('IBUGA3,ISUBRO,NUMARG = ',2(A4,2X),I8)
        CALL DPWRST('XXX','BUG ')
        IF(NUMARG.GE.1)THEN
          DO70I=1,NUMARG
            WRITE(ICOUT,76)I,IHARG(I),IHARG2(I),IARGT(I),IARG(I)
   76       FORMAT('I,IHARG(I),IHARG2(I),IARGT(I),IARG(I) = ',
     1             I8,2X,2A4,2X,A4,2X,I8)
            CALL DPWRST('XXX','BUG ')
   70     CONTINUE
        ENDIF
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STVA')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      ISTR=' '
      ISTR2=' '
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE LEFT-HAND SIDE       *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STVA')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
C     AS THIS VARIABLE WILL BE SAVED IN THE DPZCHF.DAT FILE AND NOT THE
C     INTERNAL NAME TABLE, CHECK THE DPZCHF.DAT FILE FOR AN EXISTING NAME.
C
      IOUNI4=IZCHNU
      IFILE4=IZCHNA
      ISTAT4=IZCHST
      IFORM4=IZCHFO
      IACCE4=IZCHAC
      IPROT4=IZCHPR
      ICURS4=IZCHCS
C
C     IF "SET STRING VARIABLE OVERWRITE" ENTERED, THEN ALSO
C     SET DPZCHF.DAT AS "NOT EXIST".
C
      IF(ISTRVA.EQ.'OVER')THEN
        IEXIST='NO'
      ELSE
        IERRFI='NO'
        ISUBN0='STVA'
        IERRFI='NO'
        CALL DPINFI(IFILE4,IEXIST,IOPEN,IACC,ISUBN0,IBUGA3,
     1              ISUBRO,IERROR)
       ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
        WRITE(ICOUT,111)IEXIST
  111   FORMAT('IEXIST = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      CALL DPOPFI(IOUNI4,IFILE4,ISTAT4,IFORM4,IACCE4,IPROT4,ICURS4,
     1            IREWI4,ISUBN0,IERRFI,IBUGA3,ISUBRO,IERROR)
C
      IOP='OPEN'
      IFLG11=0
      IFLG21=1
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
C
      MXROW=0
      IVAR=0
      IFLAG1=0
      IF(IEXIST.EQ.'YES')THEN
        READ(IOUNI4,*,ERR=210,END=210)IVAR
        IFLAG1=1
      ENDIF
      GOTO300
C
  210 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,211)
  211 FORMAT('*****ERROR IN LET ... = CHARACTER VARIABLE (DPSTVA)--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,213)IFILE4
  213 FORMAT('     ERROR OR END OF FILE READING FIRST LINE OF ',A80)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
C               *************************************************
C               **  STEP 3--                                   **
C               **  PROCESS RIGHT HAND SIDE.                   **
C               *************************************************
C
  300 CONTINUE
      ISTEPN='3'
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      JMIN=5
      JMAX=NUMARG
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
        WRITE(ICOUT,4001)JMIN,JMAX,MAXIND
 4001   FORMAT('JMIN,JMAX,MAXIND = ',3I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(JMAX.LT.JMIN)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,211)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,301)
  301   FORMAT('     NO STRINGS ON THE RIGHT HAND SIDE.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
      IWRITE='OFF'
      IERROR='NO'
C
      CALL EXTSTR(IHARG,IHARG2,NUMARG,JMIN,JMAX,MAXIND,
     1            IHNAME,IHNAM2,IUSE,NUMNAM,
     1            ISTRN1,ISTRN2,NUMSTR,
     1            IWRITE,IBUGA3,ISUBRO,IERROR)
C
      IF(IERROR.EQ.'NO')THEN
        ICASE='STRI'
      ELSE
        ICASE='LITE'
      ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
        WRITE(ICOUT,303)IERROR,ICASE,NUMSTR
  303   FORMAT('AFTER CALL EXTSTR: IERROR,ICASE,NUMSTR = ',2(A4,2X),I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C     STEP 1: DETERMINE NUMBER OF STRINGS FOR LITERAL CASE
C
      IF(ICASE.EQ.'LITE')THEN
        ICNT=0
        NUMSTR=0
        IFRST=5
        IFILQU='ON'
        ISTRIN=' '
        DO318I=1,IWIDTH
          ISTRIN(I:I)=IANSLC(I)(1:1)
  318   CONTINUE
C
  320   CONTINUE
        IFRST=IFRST+1
        ICNT=ICNT+1
        ISTART=1
        ISTOP=IWIDTH
        IERROR='NO'
        CALL DPEXWO(ISTRIN,ISTART,ISTOP,IFRST,
     1              ICOL1,ICOL2,ISTRI2,NCSTR2,
     1              IBUGA3,ISUBRO,IERROR)
        IF(NCSTR2.GT.0)THEN
          NUMSTR=NUMSTR+1
          IF(ICOL2.LT.IWIDTH)GOTO320
        ENDIF
      ENDIF
C
C     STEP 2: UPDATE HEADER FOR DPZCHF.DAT FILE AND WRITE TO dpst2f.dat
C
      IVARSV=IVAR
      IVAR=IVAR+1
      NTEMP1=25*IVARSV
      NTEMP2=25*IVAR
      IF(NTEMP2.GT.9999)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,211)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,331)
  331   FORMAT('      MAXIMUM NUMBER OF CHARACTER VARIABLES EXCEEDED.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      IF(IEXIST.EQ.'YES')THEN
        REWIND(IOUNI4)
        READ(IOUNI4,*,ERR=897,END=897)IJUNK
        WRITE(IOUNI2,'(I8)')IVAR
        DO330KK=1,IVARSV
          READ(IOUNI4,'(2A4,I10)',ERR=897,END=897)IHRIGH,IHRIG2,NTEMP
          WRITE(IOUNI2,'(2A4,I10)')IHRIGH,IHRIG2,NTEMP
          IF(NTEMP.GT.MXROW)MXROW=NTEMP
  330   CONTINUE
        WRITE(IOUNI2,'(2A4,I10)')IHLEFT,IHLEF2,NUMSTR
        IF(NUMSTR.GT.MXROW)MXROW=NUMSTR
      ELSE
        WRITE(IOUNI2,'(I8)')IVAR
        WRITE(IOUNI2,'(2A4,I10)')IHLEFT,IHLEF2,NUMSTR
        IFLAG2=1
        MXROW=NUMSTR
      ENDIF
C
C     STEP 3: NOW LOOP THROUGH THE ROWS OF THE STRING VARIABLES
C
      IFRMT=' '
      IFRMT='(A    )'
      WRITE(IFRMT(3:6),'(I4)')NTEMP1
      IFRMT2=' '
      IFRMT2='(A    ,A25)'
      WRITE(IFRMT2(3:6),'(I4)')NTEMP1
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
        WRITE(ICOUT,313)MXROW
  313   FORMAT('MXROW = ',I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(ICASE.EQ.'STRI')THEN
C
C       STEP 3A: ARGUMENTS ON RHS ARE NAMES OF PRE-EXISTING STRINGS
C
        DO360I2=1,MXROW
          ILAST=0
          ISTR2=' '
          NH=0
          IF(I2.GT.NUMSTR)GOTO379
          DO365I=1,NUMNAM
            II=I
            IF(ISTRN1(I2).EQ.IHNAME(I) .AND.
     1         ISTRN2(I2).EQ.IHNAM2(I) .AND. IUSE(I).EQ.'F')GOTO369
  365     CONTINUE
C
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,211)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,373)ISTRN1(I2),ISTRN2(I2)
  373     FORMAT('       STRING ',2A4,' NOT MATCHED IN NAME ',
     1           'TABLE AS A PRE-EXISTING STRING.')
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
C
  369     CONTINUE
          IVAL=IVALUE(II)
          VAL=VALUE(II)
          IL1=IVSTAR(II)
          IL2=IVSTOP(II)
C
          IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
            WRITE(ICOUT,381)IL1,IL2
  381       FORMAT('II,IL1,IL2 = ',3I8)
            CALL DPWRST('XXX','BUG ')
          ENDIF
C
          CALL DPCOFH(IL1,IL2,IFUNC,NUMCHF,IHTEMP,NH,IBUGA3,IERROR)
          ISTRT=NH
          DO382LL=1,NH
            IF(IHTEMP(LL)(1:1).NE.' ')THEN
              ISTRT=LL
              GOTO383
            ENDIF
  382     CONTINUE
  383     CONTINUE
C
  379     CONTINUE
C
          IF(NH.GT.0)THEN
            DO390J=ISTRT,NH
              ILAST=ILAST+1
              ISTR2(ILAST:ILAST)=IHTEMP(J)(1:1)
  390       CONTINUE
            IF(ILAST.GT.25)ILAST=25
C
            IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
              WRITE(ICOUT,394)I2,ILAST,ISTR2(1:ILAST)
  394         FORMAT('I2,ILAST,ISTR2(1:ILAST) = ',2I8,A25)
              CALL DPWRST('XXX','BUG ')
            ENDIF
          ENDIF
C
          IF(I2.LE.NUMSTR)THEN
            ISTR=' '
            IF(IEXIST.EQ.'YES')THEN
              READ(IOUNI4,IFRMT,ERR=897,END=897)ISTR(1:NTEMP1)
              WRITE(IOUNI2,IFRMT2)ISTR(1:NTEMP1),ISTR2(1:25)
            ELSE
              WRITE(IOUNI2,'(A25)')ISTR2(1:25)
            ENDIF
          ELSE
            ISTR=' '
            IF(IEXIST.EQ.'YES')THEN
              READ(IOUNI4,IFRMT,ERR=897,END=897)ISTR(1:NTEMP1)
              WRITE(IOUNI2,IFRMT2)ISTR(1:NTEMP1),ISTR2(1:25)
            ELSE
              WRITE(IOUNI2,'(A25)')ISTR2(1:25)
            ENDIF
          ENDIF
  360   CONTINUE
      ELSE
C
C       STEP 3B: ARGUMENTS ON RHS ARE LITERAL STRINGS
C
        ICNT=0
        IFRST=5
        MESSAG='OFF'
        ISTRIN=' '
        DO418I=1,IWIDTH
          ISTRIN(I:I)=IANSLC(I)(1:1)
  418   CONTINUE
C
        DO460I2=1,MXROW
C
          ISTR2=' '
          IF(I2.LE.NUMSTR)THEN
            IFRST=IFRST+1
            ICNT=ICNT+1
            ISTART=1
            ISTOP=IWIDTH
            IERROR='NO'
            CALL DPEXWO(ISTRIN,ISTART,ISTOP,IFRST,
     1                  ICOL1,ICOL2,ISTRI2,NCSTR2,
     1                  IBUGA3,ISUBRO,IERROR)
            ILAST=MIN(25,NCSTR2)
            IF(ILAST.GT.0)THEN
              IF(ISTRI2(1:1).EQ.'"' .AND.
     1           ISTRI2(ILAST:ILAST).EQ.'"')THEN
                ISTR2(1:ILAST-2)=ISTRI2(2:ILAST-1)
                ILAST=ILAST-2
              ELSEIF(ISTRI2(1:1).EQ.'"')THEN
                ISTR2(1:ILAST-1)=ISTRI2(2:ILAST)
                ILAST=ILAST-1
              ELSE
                ISTR2(1:ILAST)=ISTRI2(1:ILAST)
              ENDIF
            ENDIF
C
            IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
              WRITE(ICOUT,394)I2,ILAST,ISTR2(1:ILAST)
              CALL DPWRST('XXX','BUG ')
            ENDIF
          ENDIF
C
          IF(I2.LE.NUMSTR)THEN
            ISTR=' '
            IF(IEXIST.EQ.'YES')THEN
              READ(IOUNI4,IFRMT,ERR=897,END=897)ISTR(1:NTEMP1)
              WRITE(IOUNI2,IFRMT2)ISTR(1:NTEMP1),ISTR2(1:25)
            ELSE
              WRITE(IOUNI2,'(A25)')ISTR2(1:25)
            ENDIF
          ELSE
            ISTR=' '
            IF(IEXIST.EQ.'YES')THEN
              READ(IOUNI4,IFRMT,ERR=897,END=897)ISTR(1:NTEMP1)
              WRITE(IOUNI2,IFRMT2)ISTR(1:NTEMP1),ISTR2(1:25)
            ELSE
              WRITE(IOUNI2,'(A25)')ISTR2(1:25)
            ENDIF
          ENDIF
  460   CONTINUE
        GOTO499
      ENDIF
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  NOW COPY dpst2f.dat TO dpzchf.dat              **
C               *****************************************************
C
  499 CONTINUE
      CALL DPCLFI(IOUNI4,IFILE4,ISTAT4,IFORM4,IACCE4,IPROT4,ICURS4,
     1            IENDF4,IREWI4,ISUBN0,IERRFI,IBUGA3,ISUBRO,IERROR)
      IZCHCS='CLOSED'
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
C
C      STEP 4B: COPY "dpst2f.dat" TO CHARACTER VARIABLE FILE
C
      CALL COPYFI(IFILE2,IFILE4,IBUGA3,ISUBRO,IERROR)
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  PRINT FEEDBACK MESSAGE                         **
C               *****************************************************
C
      IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,501)IHLEFT,IHLEF2
  501   FORMAT('THE NAME ',A4,A4,' HAS BEEN ADDED TO THE CHARACTER ',
     1         'VARIABLE LIST')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,503)IZCHNA
  503   FORMAT('IN FILE ',A80)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
      ENDIF
      GOTO9000
C
C     ERROR OR END OF FILE READING dpzchf.dat
C
  897 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,211)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,898)
  898 FORMAT('      ERROR READING THE CHARACTER VARIABLE FILE.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO499
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IFILQU=IFILQ2
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVA')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTVA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)IERROR
 9013   FORMAT('IERROR = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTVS(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--CONVERT A VECTOR OF INTEGERS TO A STRING.  FOR EXAMPLE,
C              GIVEN A VECTOR THAT CONTAINS THE VALUES 2, 3, AND 5,
C              CREATE A STRING THAT CONTAINS "X2*X3*X5".  THIS IS
C              USED BY THE 10-STEP ANALYSIS OF FACTORIAL DESIGNS.
C
C     EXAMPLE--LET SOUT = DIGITS TO STRING Y
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2015/03
C     ORIGINAL VERSION--MARCH     2015.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWNA2
      CHARACTER*4 NEWCOL
      CHARACTER*4 NEWCO2
      CHARACTER*4 ICASEL
      CHARACTER*4 ICASE2
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*10 ICSTR
C
      CHARACTER*4 ILAB(10)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='VS  '
      IERROR='NO'
C
      ILOC3=0
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
      AVAL=0.0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVS')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTVS--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',2(A4,2X),I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF
   57   FORMAT('NUMCHF,MAXCHF = ',2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STVS')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWNA2='NO'
      NEWCOL='NO'
      NEWCO2='NO'
      ICASEL='UNKN'
      ICASE2='UNKN'
      NIOLD1=0
      NIOLD2=0
      ICOLL=0
      ICOL2=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STVS')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN DIGITS TO STRING--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE FIRST NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE NAME ON THE RIGHT HAND SIDE        **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STVS')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NINDX=6
      IF(NUMARG.LT.NINDX)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,3001)
 3001   FORMAT('      NO VARIABLE GIVEN ON THE RIGHT HAND SIDE.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      IHRIGH=IHARG(NINDX)
      IHRIG2=IHARG2(NINDX)
C
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'V')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A VARIABLE.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            NLEN=IN(I4)
            ICOLR=IVALUE(I4)
            GOTO3099
          ENDIF
        ENDIF
 3000 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  EXTRACT VARIABLE AND CONVERT TO STRING         **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STVS')THEN
        ISTEPN='4A'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4011)NLEN,ICASEL
 4011   FORMAT('NLEN1,ICASEL = ',I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      ICNT=0
      J=0
C
      DO4016I=1,NLEN
C
        J=J+1
        IJ=MAXN*(ICOLR-1)+I
        IF(ICOLR.LE.MAXCOL)AVAL=V(IJ)
        IF(ICOLR.EQ.MAXCP1)AVAL=PRED(I)
        IF(ICOLR.EQ.MAXCP2)AVAL=RES(I)
        IF(ICOLR.EQ.MAXCP3)AVAL=YPLOT(I)
        IF(ICOLR.EQ.MAXCP4)AVAL=XPLOT(I)
        IF(ICOLR.EQ.MAXCP5)AVAL=X2PLOT(I)
        IF(ICOLR.EQ.MAXCP6)AVAL=TAGPLO(I)
C
        IVALT=INT(ABS(AVAL+0.5))
        ICNT=ICNT+1
        IFUNC2(ICNT)(1:1)='X'
        IF(IVALT.GE.0 .AND. IVALT.LE.9)THEN
          ICNT=ICNT+1
          WRITE(IFUNC2(ICNT)(1:1),'(I1)')IVALT
        ELSEIF(IVALT.GE.10 .AND. IVALT.LE.99)THEN
          WRITE(ICSTR(1:2),'(I2)')IVALT
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(1:1)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(2:2)
        ELSEIF(IVALT.GE.100 .AND. IVALT.LE.999)THEN
          WRITE(ICSTR(1:3),'(I3)')IVALT
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(1:1)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(2:2)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(3:3)
        ELSEIF(IVALT.GE.1000 .AND. IVALT.LE.9999)THEN
          WRITE(ICSTR(1:4),'(I4)')IVALT
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(1:1)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(2:2)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(3:3)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(4:4)
        ELSEIF(IVALT.GE.10000 .AND. IVALT.LE.99999)THEN
          WRITE(ICSTR(1:5),'(I5)')IVALT
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(1:1)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(2:2)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(3:3)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(4:4)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(5:5)
        ELSEIF(IVALT.GE.100000 .AND. IVALT.LE.999999)THEN
          WRITE(ICSTR(1:5),'(I6)')IVALT
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(1:1)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(2:2)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(3:3)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(4:4)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(5:5)
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)=ICSTR(6:6)
        ENDIF
C
        IF(I.LT.NLEN)THEN
          ICNT=ICNT+1
          IFUNC2(ICNT)(1:1)='*'
        ENDIF
C
 4016 CONTINUE
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      ISTEPN='5'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STVS')
     1  CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CALL DPINFU(IFUNC2,ICNT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1            NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
     1            NEWNAM,MAXNAM,
     1            IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,8606)IHLEFT,IHLEF2
 8606   FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
        CALL DPWRST('XXX','BUG ')
        ILAB(1)='TO T'
        ILAB(2)='HE F'
        ILAB(3)='UNCT'
        ILAB(4)='ION '
        ILAB(5)='    '
        ILAB(6)=' -- '
        NUMWDL=6
        CALL DPPRIF(ILAB,NUMWDL,IFUNC2,ICNT,IBUGA3)
C
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
C
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STVS')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTVS--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPSTWD(ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--EXTRACT A SPECIFIED WORD OF A STRING.
C     EXAMPLE--LET SOUT = STRING WORD SIN INDEX
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2010/10
C     ORIGINAL VERSION--OCTOBER   2010.
C     UPDATED         --OCTOBER   2014. ADD OPTIONAL THIRD ARGUMENT TO
C                                       SPECIFY A DELIMITER
C     UPDATED         --MARCH     2015. CALL LIST TO DPINFU
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*4 NEWNAM
      CHARACTER*4 NEWNA2
      CHARACTER*4 NEWCOL
      CHARACTER*4 NEWCO2
      CHARACTER*4 ICASEL
      CHARACTER*4 ICASE2
      CHARACTER*4 IHLEFT
      CHARACTER*4 IHLEF2
      CHARACTER*4 IHRIGH
      CHARACTER*4 IHRIG2
      CHARACTER*4 IHRI21
      CHARACTER*4 IHRI22
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*1 IDELIM
C
      CHARACTER*4 ILAB(10)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPST'
      ISUBN2='WD  '
      IERROR='NO'
C
      ILOC3=0
      ISTART=0
      ISTOP=0
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STWD')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPSTWD--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,NUMNAM
   52   FORMAT('IBUGA3,ISUBRO,NUMNAM = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO55I=1,NUMNAM
          WRITE(ICOUT,56)I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),
     1                   IVSTOP(I)
   56     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
   55   CONTINUE
        WRITE(ICOUT,57)NUMCHF,MAXCHF,NUMARG
   57   FORMAT('NUMCHF,MAXCHF,NUMARG = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,60)(IFUNC(I),I=1,MIN(120,MAXCHF))
   60   FORMAT('IFUNC(.)  = ',120A1)
        CALL DPWRST('XXX','BUG ')
        IF(NUMARG.GE.1)THEN
          DO70I=1,NUMARG
            WRITE(ICOUT,76)I,IHARG(I),IHARG2(I),IARGT(I),IARG(I)
   76       FORMAT('I,IHARG(I),IHARG2(I),IARGT(I),IARG(I) = ',
     1             I8,2X,A4,A4,2X,A4,2X,I8)
            CALL DPWRST('XXX','BUG ')
   70     CONTINUE
        ENDIF
      ENDIF
C
C               **********************************
C               **  STEP 1--                    **
C               **  INITIALIZE SOME VARIABLES.  **
C               **********************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STWD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NEWNAM='NO'
      NEWNA2='NO'
      NEWCOL='NO'
      NEWCO2='NO'
      ICASEL='UNKN'
      ICASE2='UNKN'
      NIOLD1=0
      NIOLD2=0
      ICOLL=0
      ICOL2=0
C
C               ******************************************************
C               **  STEP 2--                                         *
C               **  EXAMINE THE ARGUMENT ON THE                      *
C               **  LEFT-HAND SIDE--                                 *
C               **  IF THIS IS A PREVIOUSLY DEFINED NAME, IT SHOULD  *
C               **  BE A STRING    (IF NOT, REPORT AN ERROR).        *
C               ******************************************************
C
      ISTEPN='2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STWD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHLEFT=IHARG(1)
      IHLEF2=IHARG2(1)
C
      DO2000I=1,NUMNAM
        I2=I
        IF(IHLEFT.EQ.IHNAME(I).AND.IHLEF2.EQ.IHNAM2(I))THEN
          IF(IUSE(I2).EQ.'F')THEN
            ICASEL='STRI'
            ILISTL=I2
            GOTO2299
          ELSE
            WRITE(ICOUT,999)
  999       FORMAT(1X)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
 2001       FORMAT('***** ERROR IN STRING WORD--')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2003)IHLEFT,IHLEF2
 2003       FORMAT('      THE FIRST NAME ON THE LEFT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2005)
 2005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
 2000 CONTINUE
C
      NEWNAM='YES'
      IF(ICASEL.EQ.'UNKN')ICASEL='STRI'
C
      ILISTL=NUMNAM+1
      IF(ILISTL.GT.MAXNAM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2202)
 2202   FORMAT('      THE NUMBER OF VARIABLE, PARAMETER, AND ',
     1         'FUNCTION')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2203)MAXNAM
 2203   FORMAT('      NAMES HAS JUST EXCEEDED THE ALLOWABLE ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2204)
 2204   FORMAT('      ENTER      STATUS')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2205)
 2205   FORMAT('      TO FIND OUT THE FULL LIST OF USED NAMES, AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2206)
 2206   FORMAT('      THEN REDEFINE OR DELETE SOME OF THE ALREADY ',
     1         'USED NAMES.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2299 CONTINUE
C
C               *****************************************************
C               **  STEP 3--                                       **
C               **  EXTRACT THE FIRST NAME ON THE RIGHT HAND SIDE  **
C               *****************************************************
C
      ISTEPN='3A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STWD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHRIGH=IHARG(5)
      IHRIG2=IHARG2(5)
      DO3000I=1,NUMNAM
        I4=I
        IF(IHRIGH.EQ.IHNAME(I).AND.IHRIG2.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'F')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRIGH,IHRIG2
 3003       FORMAT('      THE NAME ON THE RIGHT HAND SIDE (',
     1             A4,A4,')')
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3005)
 3005       FORMAT('      ALREADY EXISTS, BUT NOT AS A STRING.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ISTRT1=IVSTAR(I4)
            ISTOP1=IVSTOP(I4)
            NLEN1=ISTOP1-ISTRT1+1
            GOTO3099
          ENDIF
        ENDIF
 3000 CONTINUE
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRIGH,IHRIG2
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
 3015 FORMAT('      WAS NOT FOUND IN THE CURRENT NAME LIST.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3099 CONTINUE
C
C               ******************************************************
C               **  STEP 3B-                                        **
C               **  EXTRACT THE SECOND NAME ON THE RIGHT HAND SIDE. **
C               **  THIS SHOULD BE A NUMERIC VALUE.                 **
C               ******************************************************
C
      ISTEPN='3B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STWD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IHRI21=IHARG(6)
      IHRI22=IHARG2(6)
      DO3100I=1,NUMNAM
        I4=I
        IF(IHRI21.EQ.IHNAME(I).AND.IHRI22.EQ.IHNAM2(I))THEN
          IF(IUSE(I4).NE.'P')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,2001)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3003)IHRI21,IHRI22
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,3115)
 3115       FORMAT('      ALREADY EXISTS, BUT NOT AS A PARAMETER.')
            CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
          ELSE
            ILISR1=I4
            IINDX=IVALUE(ILISR1)
            GOTO3199
          ENDIF
        ENDIF
 3100 CONTINUE
C
      IF(NUMARG.GE.6)THEN
        IF(IARGT(6).EQ.'NUMB')THEN
          IINDX=IARG(6)
          GOTO3199
        ENDIF
      ENDIF
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,2001)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3003)IHRI21,IHRI22
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,3015)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 3199 CONTINUE
C
C               ******************************************************
C               **  STEP 3C-                                        **
C               **  EXTRACT THE THIRD ARGUMENT ON THE RIGHT HAND    **
C               **  SIDE.  THE FIRST CHARACTER WILL BE USED TO      **
C               **  DEFINE A DELIMITER.                             **
C               ******************************************************
C
      ISTEPN='3C'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STWD')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.GE.7)THEN
        IDELIM=IHARG(7)(1:1)
        IDELC=ICHAR(IDELIM)
      ELSE
        IDELIM=' '
        IDELC=-1
      ENDIF
C
C               *****************************************************
C               **  STEP 4--                                       **
C               **  FIND THE WORD BASED ON THE INDEX               **
C               *****************************************************
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STWD')THEN
        ISTEPN='4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,4012)IIDX,ISTRT1,ISTOP1,NLEN1
 4012   FORMAT('IINDX,ISTRT1,ISTOP1,NLEN1 = ',4I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4013)ICASEL
 4013   FORMAT('ICASEL = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(IINDX.LT.1 .OR. IINDX.GT.NLEN1)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2001)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4021)NLEN1
 4021   FORMAT('      THE WORD INDEX IS LESS THAN 1 OR GREATER ',
     1         'THAN ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4023)IINDX
 4023   FORMAT('      THE VALUE OF THE WORD INDEX IS ',I8)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C     NOW EXTRACT WORD BASED ON IINDX
C
C     WORD BOUNDARIES ARE DEFINED BY SPACES (NON-PRINTING CHARACTERS
C     ARE DEFINED AS SPACES).
C
C     2014/10: USER CAN SPECIFY DELIMITER OTHER THAN SPACE
C
C     STEP 1: DETERMINE START/STOP POSITION OF WORD
C
      NWORD=0
      IFLAG=0
C
      DO4100I=ISTRT1,ISTOP1
        IPOS=I
        IXTEMP=ICHAR(IFUNC(IPOS)(1:1))
        IF((IDELC.EQ.-1 .AND. IXTEMP.LE.32 .OR. IXTEMP.GE.127) .OR.
     1     (IDELC.GE.1 .AND. IXTEMP.EQ.IDELC))THEN
          IF(IFLAG.EQ.1)THEN
            IF(NWORD.EQ.IINDX)GOTO4200
            IFLAG=0
          ELSEIF(IFLAG.EQ.0)THEN
            CONTINUE
          ENDIF
C
C       CASE 2: NOT A WORD BOUNDARY DETECTED.  IS THIS START OF
C               NEW WORD OR CONTINUATION OF CURRENT WORD?
C
        ELSE
          IF(IFLAG.EQ.0)THEN
            ISTART=I
            ISTOP=I
            NWORD=NWORD+1
            IFLAG=1
          ELSEIF(IFLAG.EQ.1)THEN
            ISTOP=I
          ENDIF
        ENDIF
 4100 CONTINUE
C
      IF(NWORD.EQ.IINDX)THEN
        ISTOP=ISTOP1
        GOTO4200
      ENDIF
C
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,4160)IINDX
 4160 FORMAT('     UNABLE TO EXTRACT WORD ',I8,' FROM STRING.')
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
C
 4200 CONTINUE
      ICNT=0
      DO4210I=ISTART,ISTOP
        ICNT=ICNT+1
        IPOS=I
        IFUNC2(ICNT)=IFUNC(IPOS)
 4210 CONTINUE
C
C               *****************************************************
C               **  STEP 5--                                       **
C               **  SAVE STRING AND PRINT FEEDBACK MESSAGE         **
C               *****************************************************
C
C
      IF(ICASEL.EQ.'STRI')THEN
C
        ISTEPN='5'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'STWD')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        CALL DPINFU(IFUNC2,ICNT,IHNAME,IHNAM2,IUSE,IN,IVSTAR,IVSTOP,
     1              NUMNAM,IANS,IWIDTH,IHLEFT,IHLEF2,ILISTL,
CCCCC1              NEWNAM,MAXN3,
     1              NEWNAM,MAXNAM,
     1              IFUNC,NUMCHF,MAXCHF,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        IF(IFEEDB.EQ.'ON' .AND. IPRINT.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,6606)IHLEFT,IHLEF2
 6606     FORMAT('THE NAME ',A4,A4,' HAS JUST BEEN EQUIVALENCED ')
          CALL DPWRST('XXX','BUG ')
          ILAB(1)='TO T'
          ILAB(2)='HE F'
          ILAB(3)='UNCT'
          ILAB(4)='ION '
          ILAB(5)='    '
          ILAB(6)=' -- '
          NUMWDL=6
          CALL DPPRIF(ILAB,NUMWDL,IFUNC2,ICNT,IBUGA3)
C
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
C
        ENDIF
C
      ENDIF
C
C
C               ****************
C               **  STEP 90-- **
C               **  EXIT.     **
C               ****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'STWD')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPSTWD--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMNAM
 9013   FORMAT('NUMNAM,IVALUE = ',2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NUMNAM
          WRITE(ICOUT,9016)I,IHNAME(I),IHNAM2(I),IUSE(I),
     1                     IVSTAR(I),IVSTOP(I)
 9016     FORMAT('I,IHNAME(I),IHNAM2(I),IUSE(I),IVSTAR(I),',
     1           'IVSTOP(I)=',I8,2X,A4,A4,2X,A4,I8,I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
