        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
 2300 CONTINUE
      N5TEMP=0
      L=0
C
      DO2310J=1,N1
        Y1J=Y1(J)
        DO2320K=1,N3
          IF(Y3(K).EQ.Y1J)GOTO2310
 2320   CONTINUE
