 9016 FORMAT('I,PXS(I),PYS(I) = ',I8,2E15.7)
      CALL DPWRST('XXX','BUG ')
 9015 CONTINUE
      WRITE(ICOUT,9017)PXSPA,PYSPA
 9017 FORMAT('PXSPA,PYSPA = ',2E15.7)
