      ENDIF
      IF(ICAIJS.EQ.'MITE')THEN
        IJOIN=2
      ELSEIF(ICAIJS.EQ.'ROUN')THEN
        IJOIN=3
      ELSEIF(ICAICA.EQ.'BEVE')THEN
