        CALL GRTRSD(PX(I),PY(I),IX,IY,ISUBN0)
        NCSTR=NCSTR+1
        NCSTR2=NCSTR+1
        ICSTR(NCSTR:NCSTR2)='V['
        NCSTR=NCSTR2
        CALL GRTRIN(IX,NCHTOT,ICSTR,NCSTR)
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR)=','
        CALL GRTRIN(IY,NCHTOT,ICSTR,NCSTR)
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR)=']'
