      NCHTOT=-5
      DO9130I=2,NP
        IF(NCSTR.GE.110)THEN
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          NCSTR=0
        ENDIF
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR)=ICARAT
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR)='D'
        PYTEMP=100.-PY(I)
        CALL QUICPT(PX(I),PYTEMP,IX,IY,ISUBN0)
