           DO10611I=2,NP
             CALL GRTRSD(PX(I),PY(I),IX(I),IY(I),ISUBN0)
10611      CONTINUE
           NTEMP=NP
        END IF
CTURB CALL TCFIRE(IX,IY,NTEMP)
      ELSE
        IFACTO=-999
        GOTO8900
      ENDIF
      GOTO9000
C
C               ******************************************************
C               **  STEP 110--                                      **
C               **  TREAT THE GKS                DRIVER             **
C               ******************************************************
C
11000 CONTINUE
      IF(IPATT.EQ.'EMPT' .OR. IPATT.EQ.'BLAN' .OR. IPATT.EQ.'    ' .OR.
     1   IPATT.EQ.'NONE' .OR. NP.LE.1)GOTO9000
      IF(IPATT.EQ.'SOLI' .OR. IPATT.EQ.'FILL')THEN
        IF(NP.LE.3)GOTO9000
#ifdef HAVE_GKS
        CALL GFA(PX,PY,NTEMP)
#endif
      ELSE
        IFACTO=-999
        GOTO8900
      ENDIF
      GOTO9000
C
C               ******************************************************
C               **  STEP 120--                                      **
C               **  TREAT THE GD                     DRIVER         **
C               **  THIS LIBRARY PROVIDES SUPPORT FOR:              **
C               **  1) JPEG                                         **
C               **  2) PNG                                          **
C               **  3) WINDOWS BMP (BLACK/WHITE ONLY)               **
C               ******************************************************
C
12000 CONTINUE
CCCCC GD SOLID FILL FOR NON-RECTANGULAR REGIONS BLOWS UP FOR
CCCCC PIE CHARTS (MAYBE OTHERS).  MAKE SWITCHABLE, BUT FOR NOW
CCCCC SIMPLY DO NON-RECTANGULAR SOLID FILLS IN SOFTWARE.
CCCCC IF(IGDFS.EQ.'OFF'.AND.IFIG.NE.'BOX')GOTO12030
C
      IF(IPATT.EQ.'EMPT' .OR. IPATT.EQ.'BLAN' .OR. IPATT.EQ.'    ' .OR.
     1   IPATT.EQ.'NONE' .OR. NP.LE.1)GOTO9000
      IF(IPATT.EQ.'SOLI' .OR. IPATT.EQ.'FILL' .AND. IFIG.EQ.'BOX')THEN
        IF(NP.LE.3)GOTO9000
        CALL GRTRSD(PX(1),PY(1),IX(1),IY(1),ISUBN0)
        IF(IFIG.EQ.'BOX')THEN
          CALL GRTRSD(PX(3),PY(3),IX(2),IY(2),ISUBN0)
          NTEMP=2
        ELSE
          DO12011I=2,MAX(100,NP)
            CALL GRTRSD(PX(I),PY(I),IX(I),IY(I),ISUBN0)
12011     CONTINUE
          NTEMP=NP
        END IF
#ifdef HAVE_GD
      CALL GDRGFL(IX,IY,NTEMP,JCOLF)
CCCCC CALL GDRGF2(IX,IY,NTEMP,ICOLR,ICOLG,ICOLB)
#endif
      ELSE
        IFACTO=-999
        GOTO8900
      ENDIF
      GOTO9000
C
C               ******************************************************
C               **  STEP 130--                                      **
C               **  TREAT THE ABSOFT                 DRIVER         **
C               **  LIBRARY FROM ABSOFT COMPILER                    **
C               ******************************************************
C
13000 CONTINUE
      GOTO9000
C
C               ******************************************************
C               **  STEP 135--                                      **
C               **  TREAT THE MAC OSX AQUATERM       DRIVER         **
C               ******************************************************
C
13500 CONTINUE
C
      IF(IPATT.EQ.'EMPT' .OR. IPATT.EQ.'BLAN' .OR. IPATT.EQ.'    ' .OR.
     1   IPATT.EQ.'NONE' .OR. NP.LE.1)GOTO9000
      IF(IPATT.EQ.'SOLI' .OR. IPATT.EQ.'FILL' .AND. IAQUFS.EQ.'ON')THEN
        IF(NP.LE.3)GOTO9000
        IF(IFIG.EQ.'BOX')THEN
          CALL GRTRSD(PX(1),PY(1),IX1,IY1,ISUBN0)
          CALL GRTRSD(PX(3),PY(3),IX2,IY2,ISUBN0)
          IF(IX2.LT.IX1)THEN
            IXTEMP=IX2
            IX2=IX1
            IX1=IXTEMP
          ENDIF
          IX2=IX2-IX1
          IF(IY2.LT.IY1)THEN
            IYTEMP=IY2
            IY2=IY1
            IY1=IYTEMP
          ENDIF
          IY2=IY2-IY1
COLD      CALL aqtAddFilledRect(AX1,AX2,AY1,AY2)
#ifdef HAVE_AQUA
          CALL aqrect(IX1,IY1,IX2,IY2)
#endif
        ELSE
C
C         FOR AQUATERM, LIMIT MAXIMUM NUMBER OF POINTS IN
C         REGION TO 100 POINTS.
C
          DO13511I=1,MAX(100,NP)
            CALL GRTRSD(PX(I),PY(I),IX1,IY1,ISUBN0)
            PXP(I)=REAL(IX1)
            PYP(I)=REAL(IY1)
13511     CONTINUE
          NTEMP=NP
#ifdef HAVE_AQUA
          CALL aqrgfl(PXP,PYP,NTEMP)
#endif
        END IF
      ELSE
        IFACTO=-999
        GOTO8900
      ENDIF
      GOTO9000
C
C               ******************************************************
C               **  STEP 150--                                      **
C               **  TREAT THE LATEX (USING EEPIC)    DRIVER         **
C               ******************************************************
C
15000 CONTINUE
C
      IF(IPATT.EQ.'EMPT' .OR. IPATT.EQ.'BLAN' .OR. IPATT.EQ.'    ' .OR.
     1   IPATT.EQ.'NONE' .OR. NP.LE.1)GOTO9000
      IFACTO=-999
      GOTO8900
C
C               ******************************************************
C               **  STEP 160--                                      **
C               **  TREAT THE SVG (SCALABE VECTOR GRAPHICS) DRIVER  **
C               ******************************************************
C
16000 CONTINUE
C
      IF(IPATT.EQ.'EMPT' .OR. IPATT.EQ.'BLAN' .OR. IPATT.EQ.'    ' .OR.
     1   IPATT.EQ.'NONE' .OR. NP.LE.1)GOTO9000
      CALL DPCONA(34,IQUOTE)
C
      IF(ISVGFS.EQ.'OFF'.AND.IFIG.NE.'BOX')GOTO8900
      IF(IPATT.EQ.'SOLI' .OR. IPATT.EQ.'FILL')THEN
        IF(NP.LE.3)GOTO9000
        CALL GRTRSD(PX(1),PY(1),IX(1),IY(1),ISUBN0)
        IF(IFIG.EQ.'BOX')THEN
          CALL GRTRSD(PX(3),PY(3),IX(2),IY(2),ISUBN0)
          IF(IX(1).LE.IX(2))THEN
            IXSTRT=IX(1)
            IXSTOP=IX(2)
          ELSE
            IXSTRT=IX(2)
            IXSTOP=IX(1)
          ENDIF
          IF(IY(1).LE.IY(2))THEN
            IYSTRT=IY(1)
            IYSTOP=IY(2)
          ELSE
            IYSTRT=IY(2)
            IYSTOP=IY(1)
          ENDIF
          IWID=IXSTOP-IXSTRT+1
          IHEIG=IYSTOP-IYSTRT+1
C
          ICSTR(1:11)='   <rect x='
          ICSTR(12:12)=IQUOTE
          NCSTR=12
          IF(IXSTRT.GE.10000)THEN
            NCHTOT=5
          ELSEIF(IXSTRT.GE.1000)THEN
            NCHTOT=4
          ELSEIF(IXSTRT.GE.100)THEN
            NCHTOT=3
          ELSEIF(IXSTRT.GE.10)THEN
            NCHTOT=2
          ELSE
            NCHTOT=1
          ENDIF
          CALL GRTRIN(IXSTRT,NCHTOT,ICSTR,NCSTR)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR)=IQUOTE
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR+2)=' y='
          NCSTR=NCSTR+3
          ICSTR(NCSTR:NCSTR)=IQUOTE
          IF(IYSTRT.GE.10000)THEN
            NCHTOT=5
          ELSEIF(IYSTRT.GE.1000)THEN
            NCHTOT=4
          ELSEIF(IYSTRT.GE.100)THEN
            NCHTOT=3
          ELSEIF(IYSTRT.GE.10)THEN
            NCHTOT=2
          ELSE
            NCHTOT=1
          ENDIF
          CALL GRTRIN(IYSTRT,NCHTOT,ICSTR,NCSTR)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR)=IQUOTE
          NCSTR=-NCSTR
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
C
          ICSTR(1:15)='         width='
          ICSTR(16:16)=IQUOTE
          NCSTR=16
          IF(IWID.GE.10000)THEN
            NCHTOT=5
          ELSEIF(IWID.GE.1000)THEN
            NCHTOT=4
          ELSEIF(IWID.GE.100)THEN
            NCHTOT=3
          ELSEIF(IWID.GE.10)THEN
            NCHTOT=2
          ELSE
            NCHTOT=1
          ENDIF
          CALL GRTRIN(IWID,NCHTOT,ICSTR,NCSTR)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR)=IQUOTE
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR+7)=' height='
          NCSTR=NCSTR+8
          ICSTR(NCSTR:NCSTR)=IQUOTE
          IF(IHEIG.GE.10000)THEN
            NCHTOT=5
          ELSEIF(IHEIG.GE.1000)THEN
            NCHTOT=4
          ELSEIF(IHEIG.GE.100)THEN
            NCHTOT=3
          ELSEIF(IHEIG.GE.10)THEN
            NCHTOT=2
          ELSE
            NCHTOT=1
          ENDIF
          CALL GRTRIN(IHEIG,NCHTOT,ICSTR,NCSTR)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR)=IQUOTE
          NCSTR=-NCSTR
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
C
          ICSTR(1:17)='           style='
          ICSTR(18:18)=IQUOTE
          ICSTR(19:31)='stroke:none; '
          NCSTR=-31
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          ICSTR(1:19)='             fill:#'
          NCSTR=19
          NCHTOT=2
          JTEMP=JCOLF
          IF(JTEMP.LE.0)THEN
C
C           DATAPLOT CURRENTLY ALLOWS GREYSCALE VALUES IN
C           THE RANGE 0 TO 100.  FOR SPECIFYING COLOR TO SVG,
C           SCALE THAT 0 TO 100 VALUE TO A 0 TO 255 VALUE.
C
            AVAL=(255./100.)*REAL(ABS(JTEMP))
            IF(AVAL.LE.0.0)AVAL=0.0
            IF(AVAL.GE.255.0)AVAL=255.0
            IF(IRGBFL.EQ.0)THEN
              JRED=INT(AVAL+0.5)
              JBLUE=JRED
              JGREEN=JRED
            ELSE
              JRED=ICOLFR
              JBLUE=ICOLFB
              JGREEN=ICOLFG
            ENDIF
          ELSE
            IF(JTEMP.LT.1 .OR. JTEMP.GT.MAXCLR)JTEMP=1
            IF(IRGBFL.EQ.0)THEN
              JRED=IRED(JTEMP)
              JGREEN=IGREEN(JTEMP)
              JBLUE=IBLUE(JTEMP)
            ELSE
              JRED=ICOLFR
              JBLUE=ICOLFB
              JGREEN=ICOLFG
            ENDIF
          ENDIF
          CALL DPCONX(JRED,ICJUNK)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
          NCSTR=NCSTR+1
          CALL DPCONX(JGREEN,ICJUNK)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
          NCSTR=NCSTR+1
          CALL DPCONX(JBLUE,ICJUNK)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
          NCSTR=NCSTR+2
          ICSTR(NCSTR:NCSTR)=IQUOTE
          NCSTR=-NCSTR
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          ICSTR(1:7)='     />'
          NCSTR=-7
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
C
        ELSE
          ICSTR(1:11)='   <polygon'
          NCSTR=-11
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          ICSTR(1:17)='           style='
          ICSTR(18:18)=IQUOTE
          ICSTR(19:31)='stroke:none; '
          NCSTR=-31
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          ICSTR(1:19)='             fill:#'
          NCSTR=19
          NCHTOT=2
          JTEMP=JCOLF
          IF(JTEMP.LE.0)THEN
C
C           DATAPLOT CURRENTLY ALLOWS GREYSCALE VALUES IN
C           THE RANGE 0 TO 100.  FOR SPECIFYING COLOR TO SVG,
C           SCALE THAT 0 TO 100 VALUE TO A 0 TO 255 VALUE.
C
            AVAL=(255./100.)*REAL(ABS(JTEMP))
            IF(AVAL.LE.0.0)AVAL=0.0
            IF(AVAL.GE.255.0)AVAL=255.0
            IF(IRGBFL.EQ.0)THEN
              JRED=INT(AVAL+0.5)
              JBLUE=JRED
              JGREEN=JRED
            ELSE
              JRED=ICOLFR
              JBLUE=ICOLFB
              JGREEN=ICOLFG
            ENDIF
          ELSE
            IF(JTEMP.LT.1 .OR. JTEMP.GT.MAXCLR)JTEMP=1
            IF(IRGBFL.EQ.0)THEN
              JRED=IRED(JTEMP)
              JGREEN=IGREEN(JTEMP)
              JBLUE=IBLUE(JTEMP)
            ELSE
              JRED=ICOLFR
              JBLUE=ICOLFB
              JGREEN=ICOLFG
            ENDIF
          ENDIF
          CALL DPCONX(JRED,ICJUNK)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
          NCSTR=NCSTR+1
          CALL DPCONX(JGREEN,ICJUNK)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
          NCSTR=NCSTR+1
          CALL DPCONX(JBLUE,ICJUNK)
          NCSTR=NCSTR+1
          ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
          NCSTR=NCSTR+2
          ICSTR(NCSTR:NCSTR)=IQUOTE
          NCSTR=-NCSTR
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          ICSTR(1:12)='     points='
          NCSTR=13
          ICSTR(NCSTR:NCSTR)=IQUOTE
          NCSTR=-NCSTR
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          NCHTOT=5
C
          NCSTR=0
          DO16011I=1,NP
            IF(NCSTR.GT.80)THEN
              NCSTR=-NCSTR
              CALL GRWRST(ICSTR,NCSTR,ISUBN0)
              NCSTR=3
              ICSTR(1:NCSTR)='   '
            ENDIF
            CALL GRTRSD(PX(I),PY(I),IXTEMP,IYTEMP,ISUBN0)
            CALL GRTRIN(IXTEMP,NCHTOT,ICSTR,NCSTR)
            NCSTR=NCSTR+1
            ICSTR(NCSTR:NCSTR)=','
            CALL GRTRIN(IYTEMP,NCHTOT,ICSTR,NCSTR)
            NCSTR=NCSTR+1
            ICSTR(NCSTR:NCSTR)=' '
            IF(NCSTR.LE.80)GOTO16011
            NCSTR=-NCSTR
            CALL GRWRST(ICSTR,NCSTR,ISUBN0)
            NCSTR=3
            ICSTR(1:NCSTR)='   '
16011     CONTINUE
C
          IF(NCSTR.GT.3)THEN
            NCSTR=-NCSTR
            CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          ENDIF
C
          ICSTR(1:3)='   '
          ICSTR(4:4)=IQUOTE
          ICSTR(5:6)='/>'
          NCSTR=-6
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
        END IF
      ELSE
        IFACTO=-999
        GOTO8900
      ENDIF
      GOTO9000
C
C               ******************************************************
C               **  STEP 170--                                      **
C               **  TREAT THE CAIRO                          DRIVER **
C               ******************************************************
C
17000 CONTINUE
#ifdef HAVE_CAIRO
C
      IF(IPATT.EQ.'EMPT' .OR. IPATT.EQ.'BLAN' .OR. IPATT.EQ.'    ' .OR.
     1   IPATT.EQ.'NONE' .OR. NP.LE.1)GOTO9000
C
C     FILL IN SOFTWARE
C
      IF(ICAIFS.EQ.'OFF'.AND.IFIG.NE.'BOX')THEN
        IFACTO=-999
        GOTO8900
      ENDIF
C
      IF(IPATT.EQ.'SOLI' .OR. IPATT.EQ.'FILL')THEN
        IF(NP.LE.3)GOTO9000
C
        IVAL2=1
        IF(IGUNIT.EQ.IPL1NU)IVAL2=2
        IF(IGUNIT.EQ.IPL2NU)IVAL2=3
C
        PX2(1)=PX(1)
        PY2(1)=PY(1)
        CALL GRTRSD(PX2(1),PY2(1),IX(1),IY(1),ISUBN0)
        IF(IFIG.EQ.'BOX')THEN
          PX2(2)=PX(3)
          PY2(2)=PY(3)
          CALL GRTRSD(PX2(2),PY2(2),IX(2),IY(2),ISUBN0)
          NTEMP=2
          CALL CARGFL(IVAL2,PX2,PY2,NTEMP)
        ELSE
          DO17011I=1,NP
            PXP(I)=PX(I)
            PYP(I)=PY(I)
            CALL GRTRSD(PXP(I),PYP(I),IX1,IY1,ISUBN0)
17011     CONTINUE
          NTEMP=NP
          CALL CARGFL(IVAL2,PXP,PYP,NTEMP)
        END IF
      ELSE
        IFACTO=-999
        GOTO8900
      ENDIF
#endif
      GOTO9000
C
C               ******************************************************
C               **  STEP 180--                                      **
C               **  TREAT THE WMF                            DRIVER **
C               ******************************************************
C
18000 CONTINUE
      GOTO9000
C
C               ******************************************************
C               **  STEP 190--                                      **
C               **  TREAT THE D3                             DRIVER **
C               ******************************************************
C
19000 CONTINUE
      GOTO9000
C
C               ******************************************************
C               **  STEP 99--GENERIC CODE FOR DEVICES THAT DO NOT   **
C               **           SUPPORT HARDWARE FILLS                 **
C               ******************************************************
C
 8900 CONTINUE
      IF(IPATT.EQ.'EMPT' .OR. IPATT.EQ.'BLAN' .OR. IPATT.EQ.'    ' .OR.
     1   IPATT.EQ.'NONE' .OR. NP.LE.1)GOTO9000
      IF(IFIG.EQ.'BOX')THEN
        CALL GRDRBP(PX,PY,NP,PXSPA2,PYSPA2,IFACTO,
     1              IHORPA,IVERPA,IDUPPA,IDDOPA,
     1              IPATT2,PTHICK,ICOL,ICOLR,ICOLG,ICOLB,IRGBFL)
      ELSEIF(IFIG.NE.'BOX')THEN
        IF(IFLAG.EQ.'SOLI')THEN
          CALL GRFIR2(PX,PY,NP,PXSPA2,PYSPA2,IFACTO,
     1              IHORPA,IVERPA,IDUPPA,IDDOPA,JCOLF)
        ELSE
          CALL GRFIR3(PX,PY,NP,PXSPA2,PYSPA2,IFACTO,
     1              IHORPA,IVERPA,IDUPPA,IDDOPA,IPATT2,PTHICK,
     1              ICOL,ICOLR,ICOLG,ICOLB,IRGBFL)
        ENDIF
      ENDIF
      GOTO9000
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGG4.EQ.'ON'.OR.ISUBG4.EQ.'FIRE')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF GRFIRE--')
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NP
          WRITE(ICOUT,9016)PX(I),PY(I),IX(I),IY(I)
 9016     FORMAT('PX(I),PY(I),IX(I),IY(I) = ',2G15.7,2I8)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
C
        WRITE(ICOUT,9033)NCSTR
 9033   FORMAT('NCSTR = ',I8)
        CALL DPWRST('XXX','BUG ')
        IF(NCSTR.GE.1)THEN
          DO9035I=1,NCSTR
            CALL DPCOAN(ICSTR(I:I),IASCNE)
            WRITE(ICOUT,9036)I,ICSTR(I:I),IASCNE
 9036       FORMAT('I,ICSTR(I:I),IASCNE = ',I8,2X,A1,I8)
            CALL DPWRST('XXX','BUG ')
 9035     CONTINUE
        ENDIF
      ENDIF
C
      RETURN
      END
