C    11                      [ 99,165]                [ 88,165]
C    12                      [108,180]                [ 96,180]
C    13                      [117,195]                [104,195]
C    14                      [126,210]                [112,210]
C    15                      [135,225]                [120,225]
C    16                      [144,240]                [128,240]
