            IJUNK=I
            GOTO8697
 8695     CONTINUE
 8697     CONTINUE
          ICSTR(1:1)='/'
          ICSTR(2:41)=IPSTT2(IJUNK)(1:40)
          ICSTR(42:51)=' findfont '
          NCHTOT=5
          NCSTR=51
          CALL GRTRIN(IPSTPS,NCHTOT,ICSTR,NCSTR)
          NCSTR=NCSTR+1
          NCSTR2=NCSTR+17
          ICSTR(NCSTR:NCSTR2)=' scalefont setfont'
          NCSTR=NCSTR2
          CALL GRWRST(ICSTR,NCSTR,ISUBN0)
          IPSTFC=IPSTFN
          IPSTPC=IPSTPS
