        CALL GRTRIN(IX,NCHTOT,ICSTR,NCSTR)
        ICSTR(10:18)=' def /IY '
        NCSTR=18
        CALL GRTRIN(IY,NCHTOT,ICSTR,NCSTR)
        ICSTR(24:27)=' def'
        NCSTR=27
        CALL GRWRST(ICSTR,NCSTR,ISUBN0)
C
        ICSTR(1:1)='('
