        CALL GRWRST(ICSTR,NCSTR,ISUBN0)
        NCSTR=19
        ICSTR(1:NCSTR)='             fill:#'
        NCHTOT=2
        JTEMP=JCOL
        IF(JTEMP.LE.0)THEN
C
C         DATAPLOT CURRENTLY ALLOWS GREYSCALE VALUES IN
C         THE RANGE 0 TO 100.  FOR SPECIFYING COLOR TO SVG,
C         SCALE THAT 0 TO 100 VALUE TO A 0 TO 255 VALUE.
C
          AVAL=(255./100.)*REAL(ABS(JTEMP))
          IF(AVAL.LE.0.0)AVAL=0.0
          IF(AVAL.GE.255.0)AVAL=255.0
          IF(IRGBFL.EQ.0)THEN
            JRED=INT(AVAL+0.5)
            JBLUE=JRED
            JGREEN=JRED
          ELSE
            JRED=ICOLR
            JBLUE=ICOLG
            JGREEN=ICOLB
          ENDIF
        ELSE
          IF(JTEMP.LT.1 .OR. JTEMP.GT.MAXCLR)JTEMP=1
          IF(IRGBFL.EQ.0)THEN
            JRED=IRED(JTEMP)
            JGREEN=IGREEN(JTEMP)
            JBLUE=IBLUE(JTEMP)
          ELSE
            JRED=ICOLR
            JBLUE=ICOLG
            JGREEN=ICOLB
          ENDIF
        ENDIF
        CALL DPCONX(JRED,ICJUNK)
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
        NCSTR=NCSTR+1
        CALL DPCONX(JGREEN,ICJUNK)
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
        NCSTR=NCSTR+1
        CALL DPCONX(JBLUE,ICJUNK)
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR+1)=ICJUNK(1:2)
        NCSTR=NCSTR+2
        ICSTR(NCSTR:NCSTR)=';'
        NCSTR=-NCSTR
        CALL GRWRST(ICSTR,NCSTR,ISUBN0)
        NCSTR=22
        ICSTR(1:NCSTR)='            font-size:'
        NCHTOT=3
        CALL GRTRIN(JHEIG2,NCHTOT,ICSTR,NCSTR)
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR+2)='pt;'
        NCSTR=NCSTR+2
        NCSTR=-NCSTR
        CALL GRWRST(ICSTR,NCSTR,ISUBN0)
        NCSTR=13
        ICSTR(1:NCSTR)='             '
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR)=IQUOTE
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR)='>'
        NCSTR=-NCSTR
        CALL GRWRST(ICSTR,NCSTR,ISUBN0)
C
      ELSE
        NCSTR=14
        ICSTR(1:NCSTR)='        style='
        NCSTR=NCSTR+1
        ICSTR(NCSTR:NCSTR)=IQUOTE
        CALL GRWRST(ICSTR,NCSTR,ISUBN0)
C
        NCSTR=21
        ICSTR(1:NCSTR)='         font-family:'
        DO16010I=32,1,-1
          NCTEMP=I
          IF(ISVGFN(I:I).NE.' ')GOTO16011
16010   CONTINUE
16011   CONTINUE
        NCSTR=NCSTR+1
