      DO9605I=1,NCTEXT
        IC1=ICTEXT(I)(1:1)
        CALL DPCOAN(IC1,IJUNK)
        STRING(I)=IJUNK
 9605 CONTINUE
      STRING(NCTEXT+1)=0
