      CALL XTATTR(IADE,IXERR)
      IF(IXERR.EQ.1) THEN
        WRITE(ICOUT,9621)
 9621   FORMAT(1X,'WARNING: X11 FONT NAME NOT FOUND--USE CURRENT FONT')
        CALL DPWRST('XXX','BUG ')
      ELSEIF(IXERR.EQ.2)THEN
        WRITE(ICOUT,9622)
 9622   FORMAT(1X,'WARNING: X11 FONT NAME NOT FOUND--USE DEFAULT FONT')
        CALL DPWRST('XXX','BUG ')
      END IF
C
      IFONTH=0
      IFONTV=0
C
      IF(IJUST.EQ.'LEFT'.OR.IJUST.EQ.'LJUS')THEN
        IFONTH=1
        IFONTV=1
      ELSEIF(IJUST.EQ.'CENT'.OR.IJUST.EQ.'CJUS')THEN
        IFONTH=1
        IFONTV=0
      ELSEIF(IJUST.EQ.'RIGH'.OR.IJUST.EQ.'RJUS')THEN
        IFONTH=1
        IFONTV=2
      ELSEIF(IJUST.EQ.'LEBO')THEN
        IFONTH=0
        IFONTV=1
      ELSEIF(IJUST.EQ.'CEBO')THEN
        IFONTH=1
        IFONTV=1
      ELSEIF(IJUST.EQ.'RIBO')THEN
        IFONTH=2
        IFONTV=1
      ELSEIF(IJUST.EQ.'LECE')THEN
        IFONTH=0
        IFONTV=0
      ELSEIF(IJUST.EQ.'CECE')THEN
        IFONTH=1
        IFONTV=0
      ELSEIF(IJUST.EQ.'RICE')THEN
        IFONTH=2
        IFONTV=0
      ELSEIF(IJUST.EQ.'LETO')THEN
        IFONTH=0
        IFONTV=2
      ELSEIF(IJUST.EQ.'CETO')THEN
        IFONTH=1
        IFONTV=2
      ELSEIF(IJUST.EQ.'RITO')THEN
        IFONTH=2
        IFONTV=2
      ENDIF
C
      IXERR=0
      CALL XTEXTV(STRING,IX,IY,IFONTH,IFONTV,IXERR)
