      CALL DPWRST('XXX','BUG ')
   90 CONTINUE
C
C               **************************************
C               **  STEP 1--                        **
C               **  BRANCH TO THE APPROPRIATE CASE  **
C               **************************************
C
      ISTEPN='1'
      IF(IBUGP2.EQ.'ON')CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.GE.1)THEN
         IF(IHARG(NUMARG).EQ.'ON')GOTO1100
         IF(IHARG(NUMARG).EQ.'OFF')GOTO1100
         IF(IHARG(NUMARG).EQ.'AUTO')GOTO1100
         IF(IHARG(NUMARG).EQ.'DEFA')GOTO1100
      ENDIF
      GOTO1900
 1100 CONTINUE
      BARHEF=1.0
      BARWEF=1.0
      GOTO8000
 1900 CONTINUE
C
      IF(NUMARG.EQ.1)THEN
         BARHEF=1.0
         BARWEF=1.0
         GOTO8000
      ENDIF
C
      IF(NUMARG.GE.2)THEN
         IF(IARGT(NUMARG-1).EQ.'NUMB'.AND.IARGT(NUMARG).EQ.'NUMB')THEN
            BARHEF=ARG(NUMARG-1)
            BARWEF=ARG(NUMARG)
            GOTO8000
         ELSE
            WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,1041)
 1041       FORMAT('***** ERROR IN DPBAEF--')
