            WRITE(ICOUT,1043)
 1043       FORMAT('      BAR EXPANSION FACTORS     COMMAND')
      CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,1044)
 1044       FORMAT('      MUST BE NUMBERS.')
      CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,1045)
 1045       FORMAT('      SUCH WAS NOT THE CASE HERE')
      CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,1046)
 1046       FORMAT('      EXAMPLE--BAR EXPANSION FACTORS 1.1 0.8')
      CALL DPWRST('XXX','BUG ')
            IERROR='YES'
            GOTO9000
         ENDIF
      ENDIF
C
