      IF(IFEEDB.EQ.'OFF')GOTO8090
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,8011)
 8011 FORMAT('THE BAR EXPANSION FACTORS (HEIGHT & WIDTH)')
