      ICUMUL='OFF'
      ICASPL='    '
      ILASTC=0
C
      IF(ICOM.EQ.'BIAS'.AND.ICOM2.EQ.'H   ')THEN
        IFOUND='YES'
        ICASPL='BIAS'
        IRELAT='ON'
        ILASTC=0
        GOTO119
      ENDIF
C
      IPOSH=NUMVAR
      IF(ICOM.EQ.'BIHI')THEN
        ICASPL='BIHI'
        IFOUND='YES'
        IPOSH=0
      ELSEIF(ICOM.EQ.'BIRO')THEN
        ICASPL='BIRO'
        IFOUND='YES'
        IPOSH=0
      ELSEIF(ICOM.EQ.'RELA')THEN
        IRELAT='ON'
      ELSEIF(ICOM.EQ.'CUMU')THEN
        ICUMUL='ON'
      ELSEIF(ICOM.EQ.'HIGH' .OR. ICOM.EQ.'SUBS')THEN
        IHIGH='ON'
      ENDIF
C
C     NOTE: "SUBSET" AND "=" CAN APPEAR AS PART OF SUBSET
C           CLAUSE, SO NEED TO BE CAREFUL WHERE IT OCCURS
C           IN THE COMMAND.  HANDLE THIS BY REQUIRING THAT
C           THEY APPEAR BEFORE THE "BIHISTOGRAM" CLAUSE.
C
      DO110I=1,NUMARG
        IF(IHARG(I).EQ.'=' .AND. I.LT.IPOSH)THEN
          IFOUND='NO'
          GOTO9000
        ELSEIF(IHARG(I).EQ.'BIHI')THEN
          ICASPL='BIHI'
          ILASTC=I
          IFOUND='YES'
          IPOSH=I
        ELSEIF(IHARG(I).EQ.'BIRO')THEN
          ICASPL='BIRO'
          ILASTC=I
          IFOUND='YES'
        ELSEIF(IHARG(I).EQ.'BIAS')THEN
          ICASPL='BIAS'
          ILASTC=I
          IFOUND='YES'
        ELSEIF(IHARG(I).EQ.'RELA')THEN
          ILASTC=I
          IRELAT='ON'
        ELSEIF(IHARG(I).EQ.'CUMU')THEN
          ILASTC=I
          ICUMUL='ON'
        ELSEIF(IHARG(I).EQ.'HIGH' .OR. IHARG(I).EQ.'SUBS')THEN
          IF(I.LT.IPOSH)THEN
            ILASTC=I
            IHIGH='ON'
          ENDIF
        ENDIF
  110 CONTINUE
C
      IF(ICASPL.EQ.'BIHI' .AND. ICUMUL.EQ.'ON')THEN
        ICASPL='CBIH'
      ELSEIF(ICASPL.EQ.'BIRO' .AND. ICUMUL.EQ.'ON')THEN
        ICASPL='CBIR'
      ENDIF
C
      IF((ICASPL.EQ.'BIRO' .OR. ICASPL.EQ.'CBIR') .AND.
     1   IRELAT.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,130)
  130   FORMAT('****** ERROR IN BIROOTOGRAM--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,131)
  131   FORMAT('      RELATIVE OPTION NOT CURRENTLY SUPPORTED FOR ',
     1         'THE ROOTOGRAM COMMAND.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      IF(ICASPL.EQ.'BIAS' .AND. IHIGH.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,140)
  140   FORMAT('****** ERROR IN BIASH--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,141)
  141   FORMAT('      HIGHLIGHT OPTION NOT SUPPORTED FOR BIASH ',
     1         'COMMAND.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
  119 CONTINUE
      IF(ILASTC.GE.1)THEN
        CALL ADJUST(ILASTC,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG)
        ILASTC=0
      ENDIF
C
      IF(IFOUND.EQ.'NO')GOTO9000
C
      IF(IBUGG2.EQ.'ON' .OR. ISUBRO.EQ.'BIHI')THEN
        WRITE(ICOUT,112)ICASPL,IRELAT,IHIGH
  112   FORMAT('ICASPL,IRELAT,IHIGH = ',A4,2X,A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ****************************************
C               **  STEP 2--                          **
C               **  EXTRACT THE VARIABLE LIST         **
C               ****************************************
C
      ISTEPN='2'
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'BIHI')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      INAME='BIHISTOGRAM'
      MINNA=1
      MAXNA=100
      MINN2=2
      IFLAGE=0
      IFLAGM=1
      IFLAGP=0
      JMIN=1
      JMAX=NUMARG
      IF(IHIGH.EQ.'ON')THEN
        MINNVA=4
        MAXNVA=4
        IFLAGM=0
      ELSEIF(ICASPL.EQ.'BIAS')THEN
        MINNVA=2
        MAXNVA=2
      ELSE
        MINNVA=2
        MAXNVA=2
      ENDIF
C
      CALL DPPARS(IHARG,IHARG2,IARGT,ARG,NUMARG,IANS,IWIDTH,
     1            IHNAME,IHNAM2,IUSE,NUMNAM,IN,IVALUE,VALUE,
     1            JMIN,JMAX,
     1            MINN2,MINNA,MAXNA,MAXSPN,IFLAGE,INAME,
     1            IVARN1,IVARN2,IVARTY,PVAR,
     1            ILIS,NRIGHT,ICOLR,ISUB,NQ,ILOCQ,NUMVAR,
     1            MINNVA,MAXNVA,
     1            IFLAGM,IFLAGP,
     1            IBUGG3,IBUGQ,ISUBRO,IFOUND,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C     IF HIGHLIGHTING TURNED ON, THEN VARIABLES 1 AND 2 MUST HAVE
C     SAME LENGTH AND VARIABLES 3 AND 4 MUST HAVE SAME LENGTH.
C
      IF(IHIGH.EQ.'ON')THEN
        IF(NRIGHT(1).NE.NRIGHT(2))THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,150)
  150     FORMAT('****** ERROR IN BIHISTOGRAM--')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,151)
  151     FORMAT('       FOR HIGHLIGHTED CASE, VARIABLES ONE AND TWO ',
     1           'MUST HAVE THE SAME LENGTH.')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,153)IVARN1(1),IVARN2(1),NRIGHT(1)
  153     FORMAT('       THE NUMBER OF OBSERVATIONS FOR ',A4,A4,
     1           '   = ',I8)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,153)IVARN1(2),IVARN1(2),NRIGHT(2)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
        IF(NRIGHT(3).NE.NRIGHT(4))THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,150)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,161)
  161     FORMAT('       FOR HIGHLIGHTED CASE, VARIABLES THREE AND ',
     1           'FOUR MUST HAVE THE SAME LENGTH.')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,153)IVARN1(3),IVARN2(3),NRIGHT(3)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,153)IVARN1(4),IVARN2(4),NRIGHT(4)
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
      ENDIF
C
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'BIHI')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,281)
  281   FORMAT('***** AFTER CALL DPPARS--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,282)NQ,NUMVAR
  282   FORMAT('NQ,NUMVAR = ',2I8)
        CALL DPWRST('XXX','BUG ')
        IF(NUMVAR.GT.0)THEN
          DO285I=1,NUMVAR
            WRITE(ICOUT,287)I,IVARN1(I),IVARN2(I),ILIS(I),NRIGHT(I),
     1                      ICOLR(I)
  287       FORMAT('I,IVARN1(I),IVARN2(I),ILIS(I),NRIGHT(I),',
     1             'ICOLR(I) = ',I8,2X,A4,A4,2X,3I8)
            CALL DPWRST('XXX','BUG ')
  285     CONTINUE
        ENDIF
      ENDIF
C
C               ****************************************
C               **  STEP 3--                          **
C               **  EXTRACT THE DATA                  **
C               ****************************************
C
      ISTEPN='3'
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'BIHI')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(IHIGH.EQ.'OFF')THEN
        ICOL=1
        NUMVA2=1
        CALL DPPAR3(ICOL,IVALUE,IVALU2,IN,MAXN,MAXOBV,
     1              INAME,IVARN1,IVARN2,IVARTY,
     1              ILIS,NRIGHT,ICOLR,ISUB,NQ,NUMVA2,
     1              MAXCOL,MAXCP1,MAXCP2,MAXCP3,
     1              MAXCP4,MAXCP5,MAXCP6,
     1              V,PRED,RES,YPLOT,XPLOT,X2PLOT,TAGPLO,
     1              Y1TEMP,Y1TEMP,Y1TEMP,NS1,NS1,NS1,ICASE,
     1              IBUGG3,ISUBRO,IFOUND,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        ICOL=2
        NUMVA2=1
        CALL DPPAR3(ICOL,IVALUE,IVALU2,IN,MAXN,MAXOBV,
     1              INAME,IVARN1,IVARN2,IVARTY,
     1              ILIS,NRIGHT,ICOLR,ISUB,NQ,NUMVA2,
     1              MAXCOL,MAXCP1,MAXCP2,MAXCP3,
     1              MAXCP4,MAXCP5,MAXCP6,
     1              V,PRED,RES,YPLOT,XPLOT,X2PLOT,TAGPLO,
     1              Y2TEMP,Y2TEMP,Y2TEMP,NS2,NS2,NS2,ICASE,
     1              IBUGG3,ISUBRO,IFOUND,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
      ELSE
        ICOL=1
        NUMVA2=2
        CALL DPPAR5(ICOL,IVALUE,IVALU2,IN,MAXN,MAXOBV,
     1              INAME,IVARN1,IVARN2,IVARTY,
     1              ILIS,NRIGHT,ICOLR,ISUB,NQ,NUMVA2,
     1              MAXCOL,MAXCP1,MAXCP2,MAXCP3,
     1              MAXCP4,MAXCP5,MAXCP6,
     1              V,PRED,RES,YPLOT,XPLOT,X2PLOT,TAGPLO,
     1              Y1TEMP,X1HIGH,
     1              Y1TEMP,Y1TEMP,Y1TEMP,Y1TEMP,Y1TEMP,NS1,
     1              IBUGG3,ISUBRO,IFOUND,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
        ICOL=3
        CALL DPPAR5(ICOL,IVALUE,IVALU2,IN,MAXN,MAXOBV,
     1              INAME,IVARN1,IVARN2,IVARTY,
     1              ILIS,NRIGHT,ICOLR,ISUB,NQ,NUMVA2,
     1              MAXCOL,MAXCP1,MAXCP2,MAXCP3,
     1              MAXCP4,MAXCP5,MAXCP6,
     1              V,PRED,RES,YPLOT,XPLOT,X2PLOT,TAGPLO,
     1              Y2TEMP,X2HIGH,
     1              Y2TEMP,Y2TEMP,Y2TEMP,Y2TEMP,Y2TEMP,NS2,
     1              IBUGG3,ISUBRO,IFOUND,IERROR)
        NLOCAL=NS2
        IF(IERROR.EQ.'YES')GOTO9000
      ENDIF
C
C               ****************************************
C               **  STEP 4--                          **
C               **  CALL DPBIH3 TO DETERMINE THE      **
C               **  BINNING (THIS IS BASED ON THE     **
C               **  COMBINED DATA).                   **
C               ****************************************
C
      ISTEPN='4'
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'BIHI')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CLWID=CLWIDT(1)
      XSTART=CLLIMI(1)
      XSTOP=CLLIMI(2)
C
C     PARAMETER FOR ASH HISTROGRAM
C
      IHP='M   '
      IHP2='    '
      IHWUSE='P'
      MESSAG='NO'
      CALL CHECKN(IHP,IHP2,IHWUSE,
     1            IHNAME,IHNAM2,IUSE,IN,IVALUE,VALUE,NUMNAM,MAXNAM,
     1            ISUBN1,ISUBN2,MESSAG,IANS,IWIDTH,ILOCP,IERROR)
      IF(IERROR.EQ.'YES')THEN
        IF(NLOCAL.LE.100)THEN
          M=4
        ELSEIF(NLOCAL.LE.1000)THEN
          M=8
        ELSE
          M=16
        ENDIF
      ELSE
        M=INT(VALUE(ILOCP)+0.5)
        IF(M.LE.0)M=1
        IF(M.GT.64)M=64
      ENDIF
C
C     NOTE: ASH BINNINNG ALGORITHM RETURNS THE BINNED DATA, NOT JUST
C           CLASS WIDTHS, SO HANDLE THAT CASE IN DPBIH2.
C
      CALL DPBIH3(Y1TEMP,X1HIGH,NS1,Y2TEMP,X2HIGH,NS2,
     1            ICASPL,IRELAT,IHIGH,CLWID,XSTART,XSTOP,
     1            XTEMP1,XTEMP2,MAXOBV,
     1            IRHSTG,IHSTCW,IHSTEB,IHSTOU,
     1            DCLWID,DXSTAR,DXSTOP,
     1            Y,X,D,N2,IFLAG,
     1            IBUGG3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
      IF(IFLAG.EQ.1)THEN
        NPLOTV=3
        NPLOTP=N2
        GOTO9000
      ENDIF
C
C               ****************************************
C               **  STEP 5--                          **
C               **  CALL DPBIH2 TO GENERATE THE CLASS **
C               **  FREQUENCIES FOR EACH SAMPLE.      **
C               ****************************************
C
      ISTEPN='5'
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'BIHI')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      N2=0
      ICASET=1
      CALL DPBIH2(Y1TEMP,X1HIGH,NS1,ICASPL,ICASET,
     1            XTEMP1,XTEMP2,XIDTEM,
     1            DCLWID,DXSTAR,DXSTOP,
     1            IHIGH,IRELAT,IRHSTG,IHSTCW,IHSTEB,IHSTOU,
     1            IASHWT,M,
     1            Y,X,X3D,D,N2,NPLOTV,
     1            IBUGG3,ISUBRO,IERROR)
C
      ICASET=2
      CALL DPBIH2(Y2TEMP,X2HIGH,NS2,ICASPL,ICASET,
     1            XTEMP1,XTEMP2,XIDTEM,
     1            DCLWID,DXSTAR,DXSTOP,
     1            IHIGH,IRELAT,IRHSTG,IHSTCW,IHSTEB,IHSTOU,
     1            IASHWT,M,
     1            Y,X,X3D,D,N2,NPLOTV,
     1            IBUGG3,ISUBRO,IERROR)
C
      NPLOTV=3
      NPLOTP=N2
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BIHI')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBIHI--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASPL,IRELAT,IERROR,NPLOTP,NPLOTV
 9012   FORMAT('ICASPL,IRELAT,IERROR,NPLOTP,NPLOTV = ',3(A4,2X),2I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,NPLOTP
          WRITE(ICOUT,9014)I,X(I),Y(I),D(I)
 9014     FORMAT('I,X(I),Y(I),D(I) = ',I8,3G15.7)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBIH2(Y,XHIGH,N,ICASPL,ICASE,
     1                  XTEMP1,XTEMP2,XIDTEM,
     1                  DCLWID,DXSTAR,DXSTOP,
     1                  IHIGH,IRELAT,IRHSTG,IHSTCW,IHSTEB,IHSTOU,
     1                  IASHWT,M,
     1                  Y2,X2,X3D,D2,N2,NPLOTV,
     1                  IBUGG3,ISUBRO,IERROR)
C
C     PURPOSE--USED BY DPBIHI TO GENERATE THE CLASS FREQUENCIES
C              FOR ONE OF THE GROUPS.
C
C              NOTE THAT THE DPBIH3 ROUTINE IS CALLED FIRST TO DETERMINE
C              THE CLASS START, CLASS STOP, AND CLASS WIDTH.  ALSO, THE
C              VARIABLE ICASE SPECIFIES WHETHER THIS IS THE FOR THE
C              FIRST SAMPLE OR THE SECOND SAMPLE (IF THE SECOND SAMPLE,
C              THE Y-COORDINATE WILL BE NEGATED).  ALSO, DPBIH3 DOES THE
C              BASIC ERROR CHECKING, SO DO NOT REPEAT THAT HERE.
C
C              NOTE THAT BIHISTOGRAM CURRENTLY ONLY WORKS ON RAW
C              DATA (I.E., PRE-BINNED DATA NOT SUPPORTED).
C     WRITTEN BY--JAMES J. FILLIBEN
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2855
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2011/6
C     ORIGINAL VERSION--JUNE      2011. EXTRACTED FROM DPBIHI ROUTINE
C                                       SOME OPTIONS FROM STANDARD
C                                       HISTOGRAM INCORPORATED INTO
C                                       BIHISTOGRAM AS PART OF THIS
C                                       EXTENSION
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASPL
      CHARACTER*4 IRELAT
      CHARACTER*4 IHIGH
      CHARACTER*4 IRHSTG
      CHARACTER*4 IHSTCW
      CHARACTER*4 IHSTEB
      CHARACTER*4 IHSTOU
      CHARACTER*4 IASHWT
      CHARACTER*4 IBUGG3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*4 IWRIT2
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
C
C---------------------------------------------------------------------
 
      DOUBLE PRECISION DCLWID
      DOUBLE PRECISION DXSTAR
      DOUBLE PRECISION DXSTOP
      DOUBLE PRECISION DCLMNJ
      DOUBLE PRECISION DCLMDJ
      DOUBLE PRECISION DCLMXJ
      DOUBLE PRECISION DJ
      DOUBLE PRECISION DXI
      DOUBLE PRECISION DABSDE
      DOUBLE PRECISION DTOTWI
      DOUBLE PRECISION DN3
      DOUBLE PRECISION DSUM
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION XHIGH(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION X3D(*)
      DIMENSION D2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XIDTEM(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPBI'
      ISUBN2='S2  '
C
      IERROR='NO'
      IWRIT2='OFF'
C
      K=(-999)
      DCLMDJ=(-999.0D0)
      KP3=0
      AN3=0.0
      DENOM=0.0
C
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BIH2')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,70)
   70   FORMAT('***** AT THE BEGINNING OF DPBIH2--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,81)IHIGH,IHSTCW,IHSTOU
   81   FORMAT('IHIGH,IHSTCW,IHSTOU = ',2(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,82)N,DCLWID,DXSTAR,DXSTOP
   82   FORMAT('N,DCLWID,DXSTAR,DXSTOP = ',I8,3G15.7)
        CALL DPWRST('XXX','BUG ')
        DO83I=1,N
          WRITE(ICOUT,84)I,Y(I),XHIGH(I)
   84     FORMAT('I,Y(I),XHIGH(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','BUG ')
   83   CONTINUE
      ENDIF
C
C               **********************************************
C               **  STEP 2--                                **
C               **  HANDLE ASH CASE SEPARATELY.             **
C               **********************************************
C
      IF(ICASPL.EQ.'BIAS')THEN
        N2SAVE=N2
        CALL DPBINA(Y,N,REAL(DCLWID),REAL(DXSTAR),REAL(DXSTOP),M,
     1              XTEMP1,MAXOBV,
     1              IRELAT,IASHWT,IHSTCW,
     1              XTEMP2,XIDTEM,NTEMP,IBUGG3,IERROR)
        DO112I=1,NTEMP
          Y2(N2SAVE+I)=XTEMP2(I)
          X2(N2SAVE+I)=XIDTEM(I)
          D2(N2SAVE+I)=1.0
          IF(ICASE.EQ.2)THEN
            Y2(N2SAVE+I)=-Y2(N2SAVE+I)
            D2(N2SAVE+I)=2.0
          ENDIF
  112   CONTINUE
        N2=N2SAVE+NTEMP
        GOTO9000
      ENDIF
C
      DTOTWI=DXSTOP-DXSTAR
      ANUMCL=DTOTWI/DCLWID
      NUMCLA=INT(ANUMCL+1.0+0.1)
C
      J=NUMCLA-1
      DJ=J
      DCLMXJ=DXSTAR+DJ*DCLWID
      DABSDE=DABS(DCLMXJ-DXSTOP)
      IF(DABSDE.LE.0.0001D0)NUMCLA=NUMCLA-1
C
C               *******************************************************
C               **  STEP 3--                                         **
C               **  DETERMINE THE FREQUENCY (COUNTS) FOR EACH CLASS  **
C               *******************************************************
C
      IF(IHIGH.EQ.'ON')THEN
        CALL DISTIN(XHIGH,N,IWRIT2,XIDTEM,NDIST,IBUGG3,IERROR)
        CALL SORT(XIDTEM,NDIST,XIDTEM)
      ELSE
        NDIST=1
      ENDIF
      NPOINT=N2
      N2SAVE=N2
C
      DO300IREPL=1,NDIST
C
        IF(IREPL.EQ.1)THEN
          DO301ISET=1,N
            XTEMP2(ISET)=Y(ISET)
  301     CONTINUE
          NTEMP=N
          ATAG=1.0
          IF(ICASE.EQ.2)ATAG=2.0
        ELSE
          ICNT=0
          AHOLD=XIDTEM(IREPL-1)
          DO306ISET=1,N
            IF(XHIGH(ISET).EQ.AHOLD)THEN
              ICNT=ICNT+1
              XTEMP2(ICNT)=Y(ISET)
            ENDIF
  306     CONTINUE
          NTEMP=ICNT
          ATAG=REAL(NDIST - IREPL + 2)
        ENDIF
C
        DO310J=1,NUMCLA
          XTEMP1(J)=0.0
  310   CONTINUE
C
        IBELOW=0
        IABOVE=0
        DO420I=1,NTEMP
          DXI=XTEMP2(I)
          IF(DXI.LT.DXSTAR)THEN
            IBELOW=IBELOW+1
            GOTO420
          ELSEIF(DXI.GT.DXSTOP)THEN
            IABOVE=IABOVE+1
            GOTO420
          ENDIF
          DO430J=1,NUMCLA
            J2=J
            DJ=J
            DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
            DCLMXJ=DXSTAR+DJ*DCLWID
            IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
            IF(DCLMNJ.LE.DXI.AND.DXI.LT.DCLMXJ)GOTO440
  430     CONTINUE
          GOTO420
  440     CONTINUE
          XTEMP1(J2)=XTEMP1(J2)+1.0
  420   CONTINUE
C
C       TREAT THE SPECIAL CASE OF EQUALITY
C       WITH THE UPPER LIMIT OF THE LAST (RIGHT-MOST) CLASS
C
        J=NUMCLA
        DO450I=1,NTEMP
          DJ=J
          DCLMXJ=DXSTAR+DJ*DCLWID
          IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
          DXI=XTEMP2(I)
          IF(DXI.EQ.DCLMXJ)XTEMP1(J)=XTEMP1(J)+1.0
  450   CONTINUE
C
        IF(IBELOW.GE.1)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1591)IBELOW,DXSTAR
 1591     FORMAT('***** WARNING: ',I8,' DATA POINTS ARE BELOW THE ',
     1           'MINIMUM CLASS VALUE OF ',G15.7)
          CALL DPWRST('XXX','BUG ')
        ENDIF
        IF(IABOVE.GE.1)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1691)IABOVE,DXSTOP
 1691     FORMAT('***** WARNING: ',I8,' DATA POINTS ARE ABOVE THE ',
     1           'MAXIMUM CLASS VALUE OF ',G15.7)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
        IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BIH2')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,591)
  591     FORMAT('***** IN THE MIDDLE    OF DPBIH2--')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,592)DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA
  592     FORMAT('DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA= ',
     1           4D11.4,F10.0,I8)
          CALL DPWRST('XXX','BUG ')
          DO593J=1,NUMCLA
            DJ=J
            DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
            DCLMXJ=DXSTAR+DJ*DCLWID
            IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
            FJ=XTEMP1(J)
            WRITE(ICOUT,594)J,DCLMNJ,DCLMXJ,FJ
  594       FORMAT('J,DCLMNJ,DCLMXJ,FJ = ',I8,3G15.7)
            CALL DPWRST('XXX','BUG ')
  593     CONTINUE
        ENDIF
C
C               **********************************
C               **  STEP 4--                    **
C               **  DETERMINE PLOT COORDINATES  **
C               **********************************
C
        DSUM=0.0D0
        IF(ICASPL.EQ.'BIRO' .OR. ICASPL.EQ.'CBIR')THEN
          DO1108J=1,NUMCLA
            FJ=SQRT(XTEMP1(J))
            DSUM=DSUM+DBLE(FJ)
 1108     CONTINUE
        ELSE
          DO1110J=1,NUMCLA
            FJ=XTEMP1(J)
            DSUM=DSUM+DBLE(FJ)
 1110     CONTINUE
        ENDIF
        DN3=DSUM
        AN3=DN3
C
        DENOM=1.0
        IF(IRELAT.EQ.'ON')THEN
          IF(IRHSTG.EQ.'PERC')THEN
            DENOM=DN3
          ELSE
            DENOM=DN3*DCLWID
          ENDIF
        ENDIF
C
        NSTRT=NPOINT+1
        DSUM=0.0D0
        DO1120J=1,NUMCLA
          K=J
          NPOINT=NPOINT+1
          D2(NPOINT)=ATAG
          DJ=J
          DCLMDJ=DXSTAR+(DJ-0.5D0)*DCLWID
          X2(NPOINT)=DCLMDJ
          FJ=XTEMP1(J)
          IF(ICASPL.EQ.'BIRO')FJ=SQRT(FJ)
C
          IF(IREPL.GT.2)THEN
            ABASE=Y2(NPOINT-NUMCLA)
          ELSE
            ABASE=0.0
          ENDIF
C
          IF(ICASPL.EQ.'BIHI' .OR. ICASPL.EQ.'BIRO')THEN
            Y2(NPOINT)=(FJ/DENOM) + ABASE
          ELSEIF(ICASPL.EQ.'CBIH')THEN
            IF(IRELAT.EQ.'ON' .AND. IRHSTG.EQ.'AREA')THEN
              Y2(NPOINT)=(FJ/DENOM) + ABASE
            ELSE
              DSUM=DSUM+FJ
              CUMFJ=(DSUM/DENOM)
              Y2(NPOINT)=CUMFJ + ABASE
            ENDIF
          ELSEIF(ICASPL.EQ.'CBIR')THEN
            IF(IRELAT.EQ.'ON' .AND. IRHSTG.EQ.'AREA')THEN
              Y2(NPOINT)=(SQRT(FJ)/DENOM) + ABASE
            ELSE
              DSUM=DSUM+FJ
              CUMFJ=(DSQRT(DSUM)/DENOM)
              Y2(NPOINT)=CUMFJ + ABASE
            ENDIF
          ENDIF
 1120   CONTINUE
C
C       FOR CUMULATIVE RELATIVE HISTOGRAM (AREA CASE), COMPUTE
C       CUMULATIVE INTEGRAL OF POINTS.
C
        IF((ICASPL.EQ.'CBIH' .OR. ICASPL.EQ.'CBIR') .AND.
     1    IRELAT.EQ.'ON' .AND. IRHSTG.EQ.'AREA')THEN
          NSTOP=NPOINT
          NTOT=NSTOP-NSTRT+1
          NJUNK=2
          IWRIT2='OFF'
          CALL CUMINT(Y2(NSTRT),X2(NSTRT),NTOT,NJUNK,IWRIT2,XTEMP1,
     1                IBUGG3,IERROR)
          IF(ICASPL.EQ.'CUMH')THEN
            DO1129II=NSTRT,NSTOP
              Y2(II)=XTEMP1(II)
 1129       CONTINUE
          ELSEIF(ICASPL.EQ.'CBIR')THEN
            DO1139II=NSTRT,NSTOP
              Y2(II)=SQRT(XTEMP1(II))
 1139       CONTINUE
          ENDIF
        ENDIF
C
  300 CONTINUE
C
      N2=NPOINT
      NPLOTV=2
C
      IF(IHSTEB.EQ.'OFF')THEN
        ICNT=0
        DO1140J=1,N2
          IF(Y2(J).GT.0.0)THEN
            ICNT=ICNT+1
            X2(ICNT)=X2(J)
            Y2(ICNT)=Y2(J)
            X3D(ICNT)=X3D(J)
            D2(ICNT)=D2(J)
          ENDIF
 1140   CONTINUE
        N2=ICNT
      ENDIF
C
      IF(ICASE.EQ.2)THEN
        DO2110I=N2SAVE+1,N2
          Y2(I)=-Y2(I)
 2110   CONTINUE
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BIH2')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBIH2--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IERROR,N2,AN3,DENOM
 9012   FORMAT('IERROR,N2,AN3,DENOM = ',A4,2X,I8,2G15.7)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,N2
          WRITE(ICOUT,9016)I,Y2(I),X2(I),D2(I)
 9016     FORMAT('I,Y2(I),X2(I),D2(I) = ',I8,2G15.7,F9.2)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBIH3(Y1,X1HIGH,N1,Y2,X2HIGH,N2,
     1                  ICASPL,IRELAT,IHIGH,CLWID,XSTART,XSTOP,
     1                  XTEMP1,XTEMP2,MAXOBV,
     1                  IRHSTG,IHSTCW,IHSTEB,IHSTOU,
     1                  DCLWID,DXSTAR,DXSTOP,
     1                  Y3,X2,D2,N3,IFLAG,
     1                  IBUGG3,ISUBRO,IERROR)
C
C     PURPOSE--COMPUTE THE CLASS INTERVALS FOR THE BI-HISTOGRAM COMMAND
C              AND PERFORM BASIC ERROR CHECKING.  NOTE THAT THE CLASS
C              WIDTH WILL BE DETERMINED FROM THE COMBINED DATA.
C              NOTE THAT THE BIHISTOGRAM CURRENTLY ONLY WORKS ON RAW
C              DATA (I.E., PRE-BINNED DATA NOT SUPPORTED).
C     WRITTEN BY--JAMES J. FILLIBEN
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2855
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2011/6
C     ORIGINAL VERSION--JUNE      2011. EXTRACTED FROM DPBIHI ROUTINE
C                                       SOME OPTIONS FROM STANDARD
C                                       HISTOGRAM INCORPORATED INTO
C                                       BIHISTOGRAM AS PART OF THIS
C                                       EXTENSION
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASPL
      CHARACTER*4 IRELAT
      CHARACTER*4 IHIGH
      CHARACTER*4 IBUGG3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*4 IRHSTG
      CHARACTER*4 IHSTCW
      CHARACTER*4 IHSTEB
      CHARACTER*4 IHSTOU
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
C
C---------------------------------------------------------------------
 
      DOUBLE PRECISION DCLWID
      DOUBLE PRECISION DXSTAR
      DOUBLE PRECISION DXSTOP
C
C---------------------------------------------------------------------
C
      DIMENSION Y1(*)
      DIMENSION Y2(*)
      DIMENSION X1HIGH(*)
      DIMENSION X2HIGH(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION Y3(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPBI'
      ISUBN2='S3  '
      IERROR='NO'
C
      IFLAG=0
      DCLWID=CLWID
      DXSTAR=XSTART
      DXSTOP=XSTOP
C
C               ********************************************
C               **  STEP 1--                              **
C               **  CHECK THE INPUT ARGUMENTS FOR ERRORS  **
C               ********************************************
C
      IF(N1.LT.2)THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,31)
   31   FORMAT('***** ERROR IN BIHISTOGRAM--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,32)
   32   FORMAT('      THE NUMBER OF OBSERVATIONS FOR THE FIRST ',
     1         'RESPONSE VARIABLE MUST BE AT LEAST 2;')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,34)N1
   34   FORMAT('      THE ENTERED NUMBER OF OBSERVATIONS HERE = ',I6)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      IF(N2.LT.2)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,31)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,42)
   42   FORMAT('      THE NUMBER OF OBSERVATIONS FOR THE SECOND ',
     1         'RESPONSE VARIABLE MUST BE AT LEAST 2;')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,34)N2
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C     NOW COMBINE THE DATA
C
      DO50I=1,N1
        XTEMP1(I)=Y1(I)
   50 CONTINUE
      DO55I=1,N2
        XTEMP1(N1+I)=Y2(I)
   55 CONTINUE
      N=N1+N2
C
C     NOW CHECK TO SEE IF THERE IS A SINGLE VALUE
C
      HOLD=XTEMP1(1)
      DO60I=1,N1+N2
        IF(XTEMP1(I).NE.HOLD)GOTO69
   60 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,61)
   61 FORMAT('***** WARNING IN BIHISTOGRAM--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,62)
   62 FORMAT('      ALL INPUT HORIZONTAL AXIS ELEMENTS')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,63)HOLD
   63 FORMAT('      ARE IDENTICALLY EQUAL TO ',G15.7)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
C
      DCLWID=2.0
      DXSTAR=HOLD-1.0
      DXSTOP=HOLD+1.0
C
C     HANDLE AS SPECIAL CASE
C
      IFLAG=1
      N3=6
      X2(1)=HOLD-1.0
      X2(2)=HOLD
      X2(3)=HOLD+1.0
      IF(IRELAT.EQ.'ON')THEN
        Y3(1)=0.0
        Y3(2)=1.0
        Y3(3)=0.0
      ELSE
        Y3(1)=0.0
        Y3(2)=REAL(N1)
        Y3(3)=0.0
      ENDIF
      D2(1)=1.0
      D2(2)=1.0
      D2(3)=1.0
C
      X2(4)=HOLD-1.0
      X2(5)=HOLD
      X2(6)=HOLD+1.0
      IF(IRELAT.EQ.'ON')THEN
        Y3(4)=0.0
        Y3(5)=-1.0
        Y3(6)=0.0
      ELSE
        Y3(4)=0.0
        Y3(5)=-REAL(N2)
        Y3(6)=0.0
      ENDIF
      D2(4)=2.0
      D2(5)=2.0
      D2(6)=2.0
C
      GOTO9000
C
   69 CONTINUE
C
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BIH3')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,70)
   70   FORMAT('***** AT THE BEGINNING OF DPBIH3--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,80)IRHSTG,IHIGH,IHSTCW,IHSTOU
   80   FORMAT('IRHSTG,IHIGH,IHSTCW,IHSTOU = ',3(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,81)ICASPL,IHSTEB
   81   FORMAT('ICASPL,IHSTEB = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,82)N1,N2,CLWID,XSTART,XSTOP
   82   FORMAT('N1,N2,CLWID,XSTART,XSTOP = ',2I8,3G15.7)
        CALL DPWRST('XXX','BUG ')
        DO83I=1,N1
          WRITE(ICOUT,84)I,Y1(I),X1HIGH(I)
   84     FORMAT('I,Y1(I),X1HIGH(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','BUG ')
   83   CONTINUE
        DO88I=1,N1
          WRITE(ICOUT,89)I,Y2(I),X2HIGH(I)
   89     FORMAT('I,Y2(I),X2HIGH(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','BUG ')
   88   CONTINUE
      ENDIF
C
C               **********************************************
C               **  STEP 2--                                **
C               **  IF NECESSARY, DETERMINE CLASS WIDTH,    **
C               **  START VALUE, AND STOP VALUE.            **
C               **********************************************
C
      CALL DPBINZ(XTEMP1,N,CLWID,XSTART,XSTOP,
     1            XTEMP2,MAXOBV,IHSTCW,IHSTOU,
     1            DCLWID,DXSTAR,DXSTOP,
     1            ISUBRO,IBUGG3,IERROR)
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BIH3')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBIH3--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IERROR
 9012   FORMAT('IERROR = ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBIN(Y,N,IRELAT,CLWID,XSTART,XSTOP,IRHSTG,
CCCCC MARCH 2006: ADD FOLLOWING LINE TO ALLOW DIFFERENT
CCCCC             ALTERNATIVES TO BINNING
     1TEMP1,MAXNXT,IHSTCW,IHSTOU,
     1Y2,X2,N2,IBUGG3,IERROR)
C
C     PURPOSE--BIN A VARIABLE Y INTO X2 Y2.
C              THAT IS CONVERT RAW DATA TO FREQUENCY DATA.
C              BINNING CAN BE EITHER TO COUNTS OR TO RELATIVE
C              FREQUENCY.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--98/11
C     ORIGINAL VERSION--NOVEMBER  1998.
C     UPDATED         --MARCH     2006. SUPPORT FOR DIFFERENT
C                                       CLASS WIDTH ALGORITHMS
C     UPDATED         --JANUARY   2010. SET HISTOGRAM OUTLIERS
C     UPDATED         --JANUARY   2010. SPLIT CLASS WIDTH TO
C                                       DPBINZ
C     UPDATED         --JUNE      2016. BUG FIX FOR IQ RANGE METHOD
C     UPDATED         --JUNE      2016. LOWER AND UPPER CLASS LIMITS
C                                       FOR IQ RANGE METHOD
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IRELAT
      CHARACTER*4 IBUGG3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*4 IRHSTG
      CHARACTER*4 IHSTCW
      CHARACTER*4 IHSTOU
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
C
C---------------------------------------------------------------------
 
      DOUBLE PRECISION DCLWID
      DOUBLE PRECISION DXSTAR
      DOUBLE PRECISION DXSTOP
      DOUBLE PRECISION DCLMNJ
      DOUBLE PRECISION DCLMDJ
      DOUBLE PRECISION DCLMXJ
      DOUBLE PRECISION DJ
      DOUBLE PRECISION DXI
      DOUBLE PRECISION DABSDE
      DOUBLE PRECISION DTOTWI
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION TEMP1(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPBI'
      ISUBN2='N   '
      ISUBRO='XXXX'
C
      IERROR='NO'
C
      K=(-999)
      DCLMDJ=(-999.0D0)
C
      KP3=0
C
      AN3=0.0
      DENOM=0.0
C
      DCLWID=CLWID
      DXSTAR=XSTART
      DXSTOP=XSTOP
C
C               ********************************************
C               **  STEP 1--                              **
C               **  CHECK THE INPUT ARGUMENTS FOR ERRORS  **
C               ********************************************
C
      IF(N.LT.1)THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,31)
   31   FORMAT('***** ERROR IN BINNING DATA (DPBIN)--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,32)
   32   FORMAT('      THE NUMBER OF OBSERVATIONS MUST BE AT LEAST 1;')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,34)N
   34   FORMAT('      THE ENTERED NUMBER OF OBSERVATIONS HERE = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      HOLD=Y(1)
      DO60I=1,N
        IF(Y(I).NE.HOLD)GOTO69
   60 CONTINUE
      N2=3
      X2(1)=HOLD-1.0
      X2(2)=HOLD
      X2(3)=HOLD+1
      IF(IRELAT.EQ.'ON')THEN
        Y2(1)=0.0
        Y2(2)=1.0
        Y2(3)=0.0
      ELSE
        Y2(1)=0.0
        Y2(2)=REAL(N)
        Y2(3)=0.0
      ENDIF
      GOTO9000
   69 CONTINUE
C
      IF(IBUGG3.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,70)
   70   FORMAT('***** AT THE BEGINNING OF DPBIN--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,71)IRELAT,IRHSTG,IHSTCW
   71   FORMAT('IRELAT,IRHSTG,IHSTCW = ',3(A4,1X))
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,72)N,CLWID,XSTART,XSTOP
   72   FORMAT('N,CLWID,XSTART,XSTOP = ',I6,3E15.7)
        CALL DPWRST('XXX','BUG ')
        DO73I=1,N
          WRITE(ICOUT,74)I,Y(I)
   74     FORMAT('I, Y(I) = ',I8,E15.7)
          CALL DPWRST('XXX','BUG ')
   73   CONTINUE
      ENDIF
C
C               **********************************************
C               **  STEP 2--                                **
C               **  IF NECESSARY,                           **
C               **  DETERMINE CLASS WIDTH,                  **
C               **  START VALUE, STOP VALUE,                **
C               **  AND NUMBER OF CLASSES.                  **
C               **********************************************
C
C  MARCH 2006: ALLOW DIFFERENT DEFAULT BINNING ALGORITHMS (AS
C              SPECIFIED BY IHSTCW).
C
      CALL DPBINZ(Y,N,CLWID,XSTART,XSTOP,
     1            TEMP1,MAXNXT,IHSTCW,IHSTOU,
     1            DCLWID,DXSTAR,DXSTOP,
     1            ISUBRO,IBUGG3,IERROR)
C
      DTOTWI=DXSTOP-DXSTAR
      ANUMCL=DTOTWI/DCLWID
      NUMCLA=INT(ANUMCL+1.0+0.1)
C
      J=NUMCLA-1
      DJ=J
      DCLMXJ=DXSTAR+DJ*DCLWID
      DABSDE=DABS(DCLMXJ-DXSTOP)
      IF(DABSDE.LE.0.0001D0)NUMCLA=NUMCLA-1
C
      DO181I=1,NUMCLA
        X2(I)=0.0
        Y2(I)=0.0
  181 CONTINUE
C
C               *******************************************************
C               **  STEP 3--                                         **
C               **  DETERMINE THE FREQUENCY (COUNTS) FOR EACH CLASS  **
C               *******************************************************
C
      DO420I=1,N
      DXI=Y(I)
      DO430J=1,NUMCLA
      J2=J
      DJ=J
      DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
      DCLMXJ=DXSTAR+DJ*DCLWID
      IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
      IF(DCLMNJ.LE.DXI.AND.DXI.LT.DCLMXJ)GOTO440
  430 CONTINUE
      GOTO420
  440 CONTINUE
      Y2(J2)=Y2(J2)+1.0
  420 CONTINUE
C
C     FOR THIS RAW DATA CASE,
C     TREAT THE SPECIAL CASE OF EQUALITY
C     WITH THE UPPER LIMIT OF THE LAST (RIGHT-MOST) CLASS
C
      J=NUMCLA
      DO450I=1,N
      DJ=J
      DCLMXJ=DXSTAR+DJ*DCLWID
      IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
      DXI=Y(I)
      IF(DXI.EQ.DCLMXJ)Y2(J)=Y2(J)+1.0
  450 CONTINUE
C
      IF(IBUGG3.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,591)
  591   FORMAT('***** IN THE MIDDLE    OF DPBIN--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,592)DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA
  592   FORMAT('DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA= ',
     1        4D11.4,F10.0,I8)
        CALL DPWRST('XXX','BUG ')
        DO593J=1,NUMCLA
          DJ=J
          DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
          DCLMXJ=DXSTAR+DJ*DCLWID
          IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
          FJ=Y2(J)
          WRITE(ICOUT,594)J,DCLMNJ,DCLMXJ,FJ
  594     FORMAT('J,DCLMNJ,DCLMXJ,FJ = ',I8,2D15.7,E15.7)
          CALL DPWRST('XXX','BUG ')
  593   CONTINUE
      ENDIF
C
      SUM=0.0
      DO1110J=1,NUMCLA
      FJ=Y2(J)
      SUM=SUM+FJ
 1110 CONTINUE
      AN3=SUM
C
      DENOM=1.0
C
      IF(IRELAT.EQ.'ON')THEN
        IF(IRHSTG.EQ.'PERC')THEN
          DENOM=AN3
        ELSE
          DENOM=AN3*DCLWID
        ENDIF
      ENDIF
C
      DO1120J=1,NUMCLA
        K=J
        DJ=J
        DCLMDJ=DXSTAR+(DJ-0.5D0)*DCLWID
        FJ=Y2(J)
        X2(K)=DCLMDJ
        Y2(K)=FJ/DENOM
 1120 CONTINUE
      N2=K
C
      DO1130J=NUMCLA,1,-1
        IF(Y2(J).GT.0.0)THEN
          N2=J
          GOTO1139
        ENDIF
 1130 CONTINUE
      N2=1
 1139 CONTINUE
C
      DO1140J=1,N2
        IF(Y2(J).GT.0.0)THEN
          IFRST=J
          GOTO1149
        ENDIF
 1140 CONTINUE
      IFRST=1
 1149 CONTINUE
      K=0
      DO1150J=IFRST,N2
        K=K+1
        X2(K)=X2(J)
        Y2(K)=Y2(J)
 1150 CONTINUE
C
      IF(N2.LT.NUMCLA)THEN
        DO1160I=N2+1,NUMCLA
          Y2(I)=0.0
          X2(I)=0.0
 1160   CONTINUE
      ENDIF
      N2=K
      GOTO9000
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBIN--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IRELAT,IERROR,N2
 9012   FORMAT('IRELAT,IERROR,N2 = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)AN3,DENOM
 9013   FORMAT('AN3,DENOM = ',E15.8,E15.8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,N2
          WRITE(ICOUT,9016)I,X2(I),Y2(I)
 9016     FORMAT('I,X2(I),Y2(I) = ',I8,E15.7,F9.2)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
        WRITE(ICOUT,9017)N,DCLWID,DXSTAR,DXSTOP
 9017   FORMAT('N,DCLWID,DXSTAR,DXSTOP = ',I6,3D15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBINA(Y,N,CLWID,XSTART,XSTOP,M,
     1XTEMP1,MAXOBV,
     1IRELAT,IASHWT,IHSTCW,
     1Y2,X2,N2,IBUGG3,IERROR)
C
C     PURPOSE--COMPUTE HISTOGRAM BINS USING THE "AVERAGE SHIFTED
C              HISTOGRAM" (ASH) ALGORITHM DOCUMENTED BY DAVID SCOTT,
C              1992, "MULTIVARIATE DENSITY ESTIMATION: THEORY,
C              PRACTICE, AND VISUALIZATION", WILEY, CHAPTER 5.
C              WE IMPLEMENT THE ALGORITHMS BIN1 AND ASH1 GIVEN ON
C              PAGES 117-118.  THE BINNED DATA IS RETURNED IN
C              Y2 AND X2.
C
C              NOTE THAT SINCE THE ASH BINNING IS INTENDED TO BE A
C              SIMPLE DENSITY ESTIMATOR, THIS ALGORITHM IS
C              IMPLEMENTED FOR THE "RELATIVE FREQUENCY" CASE, NOT
C              RAW COUNTS.
C
C              THE BASIC IDEA IS:
C
C              1) GIVEN A CLASS WIDTH OF H
C              2) CHOOSE M WHERE WE CONSTRUCT A COLLECTION OF M
C                 HISTOGRAMS, EACH WITH A CLASS WIDTH OF H, BUT
C                 WITH START POINTS
C                 t0 = 0, h/m, 2*h/m, ... , (m-1)*h/m
C              3) THIS RESULTS IN A SMOOTHED HISTOGRAM WITH
C                 A BIN WIDTH OF DELTA=H/M.  HIGHER VALUES OF M
C                 RESULT IN A SMOOTHER ESTIMATE.  VALUES OF M ARE
C                 TYPICALLY IN THE RANGE 4 TO 32.  IN THIS
C                 SUBROUTINE, VALUES OF M < 1 ARE SET TO 1 AND
C                 VALUES OF M > 64 ARE SET TO 64.
C
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2004/9
C     ORIGINAL VERSION--SEPTEMBER 2004.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      PARAMETER(MAXM=64)
C
      CHARACTER*4 IBUGG3
      CHARACTER*4 IERROR
C
      CHARACTER*4 IWRIT2
      CHARACTER*4 IRELAT
      CHARACTER*4 IASHWT
      CHARACTER*4 IHSTCW
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
C
C---------------------------------------------------------------------
 
      DOUBLE PRECISION DCLWID
      DOUBLE PRECISION DXSTAR
      DOUBLE PRECISION DXSTOP
      DOUBLE PRECISION DH
      DOUBLE PRECISION DELTA
      DOUBLE PRECISION DX
      DOUBLE PRECISION DNBIN
      DOUBLE PRECISION DN
C
C-----------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION XTEMP1(*)
C
      DIMENSION WTM(2*MAXM)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPBI'
      ISUBN2='N   '
      IERROR='NO'
C
      DCLWID=CLWID
      DXSTAR=XSTART
      DXSTOP=XSTOP
      XMEAN=CPUMIN
      XSD=CPUMIN
      XMIN=CPUMIN
      XMAX=CPUMIN
C
C               ********************************************
C               **  STEP 1--                              **
C               **  CHECK THE INPUT ARGUMENTS FOR ERRORS  **
C               ********************************************
C
      IF(N.LE.5)THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,31)
   31   FORMAT('***** ERROR IN AVERAGE SHIFTED HISTOGRAM BINNING--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,32)
   32   FORMAT('      THE NUMBER OF OBSERVATIONS MUST BE AT LEAST 5;')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,34)N
   34   FORMAT('      THE ENTERED NUMBER OF OBSERVATIONS HERE = ',I6)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      IF(M.LT.1 .OR. M.GT.MAXM)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,31)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,42)
   42   FORMAT('      THE VALUE OF THE M PARAMETER MUST BE AT ',
     1         'LEAST 1 AND')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,44)MAXM
   44   FORMAT('      LESS THAN OR EQUAL TO ',I5,' (RECOMMENDED ',
     1         'VALUES ARE 4, 8, 16, OR 32).')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,46)M
   46   FORMAT('      THE ENTERED VALUE OF M HERE = ',I6)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      HOLD=Y(1)
      DO60I=1,N
        IF(Y(I).NE.HOLD)GOTO69
   60 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,31)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,62)HOLD
   62 FORMAT('      ALL INPUT OBSERVATIONS ARE IDENTICALLY EQUAL ',
     1       'TO ',G15.7)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
   69 CONTINUE
C
      IF(IBUGG3.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,70)
   70   FORMAT('***** AT THE BEGINNING OF DPBINA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,71)N,M,CLWID,XSTART,XSTOP
   71   FORMAT('N,M,CLWID,XSTART,XSTOP = ',2I6,3G15.7)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,72)IASHWT
   72   FORMAT('IASHWT = ',A4)
        CALL DPWRST('XXX','BUG ')
        DO73I=1,N
          WRITE(ICOUT,74)I,Y(I)
   74     FORMAT('I, Y(I) = ',I8,E15.7)
          CALL DPWRST('XXX','BUG ')
   73   CONTINUE
      ENDIF
C
C               ***********************************
C               **  STEP 2--                     **
C               **  IF NECESSARY, DETERMINE:     **
C               **  1) CLASS WIDTH               **
C               **  2) START VALUE               **
C               **  3) STOP VALUE                **
C               **  4) NUMBER OF BINS            **
C               ***********************************
C
      IF(CLWID.EQ.CPUMIN.OR.XSTART.EQ.CPUMIN.OR.
     1XSTOP.EQ.CPUMAX)THEN
        IWRIT2='OFF'
        CALL MEAN(Y,N,IWRIT2,XMEAN,IBUGG3,IERROR)
        CALL SD(Y,N,IWRIT2,XSD,IBUGG3,IERROR)
        CALL MINIM(Y,N,IWRIT2,XMIN,IBUGG3,IERROR)
        CALL MAXIM(Y,N,IWRIT2,XMAX,IBUGG3,IERROR)
        IF(IHSTCW.EQ.'DEFA')THEN
          IF(CLWID.EQ.CPUMIN)DCLWID=0.3*XSD
        ELSEIF(IHSTCW.EQ.'NORM')THEN
          IF(CLWID.EQ.CPUMIN)DCLWID=3.5*XSD/(REAL(N)**(1./3.))
        ELSEIF(IHSTCW.EQ.'NCOR')THEN
          IF(CLWID.EQ.CPUMIN)THEN
            CALL STMOM3(Y,N,IWRIT2,XSKEW,IBUGG3,IERROR)
            CALL STMOM4(Y,N,IWRIT2,XKURT,IBUGG3,IERROR)
            TERM1=3.5*XSD/(REAL(N)**(1./3.))
            IF(XSKEW.GT.0.0 .AND. XSKEW.LT.3.0)THEN
              TERM2=1.0/(1.0 - 0.0060*XSKEW + 0.27*XSKEW**2 -
     1              0.0069*XSKEW**3)
            ELSE
              TERM2=1.0
            ENDIF
            XKURT=XKURT - 3.0
            IF(XKURT.GT.0.0 .AND. XKURT.LT.6.0)THEN
              TERM3=1.0 - 0.2*(1.0 - EXP(-0.7*XKURT))
            ELSE
              TERM3=1.0
            ENDIF
            DCLWID=DBLE(TERM1*TERM2*TERM3)
          ENDIF
        ELSEIF(IHSTCW.EQ.'IQ  ')THEN
          CALL LOWQUA(Y,N,IWRIT2,XTEMP1,MAXOBV,XLOWQ,IBUGG3,IERROR)
          CALL UPPQUA(Y,N,IWRIT2,XTEMP1,MAXOBV,XUPPQ,IBUGG3,IERROR)
          XIQ=XUPPQ - XLOWQ
          IF(CLWID.EQ.CPUMIN)DCLWID=2.603*XIQ/(REAL(N)**(1./3.))
        ELSE
          IF(CLWID.EQ.CPUMIN)DCLWID=0.3*XSD
        ENDIF
C
        IF(XSTART.EQ.CPUMIN)THEN
CCCCC     DXSTAR=XMEAN-6.0*XSD
          ABIN=ABS((XMEAN-XMIN)/REAL(DCLWID))
          IBINL=INT(ABIN+1.0)
          DXSTAR=DBLE(XMEAN)-DBLE(IBINL)*DCLWID
        ELSE
          ABIN=ABS((XMEAN-XSTART)/REAL(DCLWID))
          IBINL=INT(ABIN+1.0)
          DXSTAR=DBLE(XMEAN)-DBLE(IBINL)*DCLWID
        ENDIF
        IF(XSTOP.EQ.CPUMAX)THEN
CCCCCC    DXSTOP=XMEAN+6.0*XSD
          ABIN=ABS((XMAX-XMEAN)/REAL(DCLWID))
          IBINU=INT(ABIN+1.0)
          DXSTOP=DBLE(XMEAN)+DBLE(IBINU)*DCLWID
        ELSE
          ABIN=ABS((XSTOP-XMEAN)/REAL(DCLWID))
          IBINU=INT(ABIN+1.0)
          DXSTOP=DBLE(XMEAN)+DBLE(IBINU)*DCLWID
        ENDIF
        NBIN=IBINL + IBINU
      ELSE
        ABIN=(XMEAN-XSTART)/REAL(DCLWID)
        IBINL=INT(ABIN+1.0)
        DXSTAR=DBLE(XMEAN)-DBLE(IBINL)*DCLWID
C
        ABIN=(XSTOP-XMEAN)/REAL(DCLWID)
        IBINU=INT(ABIN+1.0)
        DXSTOP=DBLE(XMEAN)+DBLE(IBINU)*DCLWID
C
        NBIN=IBINL + IBINU
      ENDIF
C
      IF(IBUGG3.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,170)
  170   FORMAT('***** MIDDLE OF DPBINA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,171)XMEAN,XSD,XMIN,XMAX
  171   FORMAT('XMEAN,XSD,XMIN,XMAX = ',4G15.7)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,172)DCLWID,DXSTAR,DXSTOP
  172   FORMAT('DCLWID,DXSTAR,DXSTOP = ',3G15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ***********************************************
C               **  STEP 3--                                 **
C               **  IMPLEMENT BIN1 ALGORITHM FROM PAGE 117.  **
C               ***********************************************
C
      DELTA=DCLWID/DBLE(M)
      NBIN=INT((DXSTOP-DXSTAR)/DELTA + 0.5D0)
      DNBIN=DBLE(NBIN)
      DO410I=1,NBIN
        Y2(I)=0.0
  410 CONTINUE
C
      DO420I=1,N
        DX=DBLE(Y(I))
        DK=((DX - DXSTAR)/DELTA) + 1.0D0
        IK=INT(DK)
        IF(IK.GE.1 .AND. IK.LE.NBIN)Y2(IK)=Y2(IK)+1.0
  420 CONTINUE
C
      IF(IRELAT.EQ.'OFF')THEN
        DO430I=1,NBIN
          X2(I)=REAL(DXSTAR + (DBLE(I)-0.5D0)*DELTA)
  430   CONTINUE
        N2=NBIN
        GOTO9000
      ENDIF
C
C               ***********************************************
C               **  STEP 4--                                 **
C               **  IMPLEMENT ASH1 ALGORITHM FROM PAGE 118.  **
C               ***********************************************
C
      IF(IASHWT.EQ.'BIWE')THEN
        ISTRT=1-M
        ISTOP=M-1
        ASUM=0.0
        DO510I=ISTRT,ISTOP
          T=REAL(I)/REAL(M)
          IF(-1.0.LE.T .AND. T.LE.1.0)THEN
            ASUM=ASUM + (15./16.)*(1.0-T**2)**2
          ENDIF
  510   CONTINUE
C
        DO520I=ISTRT,ISTOP
          T=REAL(I)/REAL(M)
          IF(-1.0.LE.T .AND. T.LE.1.0)THEN
            TERM1=(15./16.)*(1.0-T**2)**2
          ELSE
            TERM1=0.0
          ENDIF
          WTM(I+M+1)=REAL(M)*TERM1/ASUM
  520   CONTINUE
      ELSE
        ISTRT=1-M
        ISTOP=M-1
        DO560I=ISTRT,ISTOP
          WTM(I+M)=1.0 - ABS(REAL(I))/REAL(M)
  560   CONTINUE
      ENDIF
C
      DH=DBLE(M)*DELTA
      DO610I=1,NBIN
        XTEMP1(I)=0.0
  610 CONTINUE
C
      DO620K=1,NBIN
        IF(Y2(K).GT.0.0)THEN
          IFRST=MAX(1,K-M+1)
          ILAST=MIN(NBIN,K+M-1)
          IF(ILAST.GE.IFRST)THEN
            DO630I=IFRST,ILAST
              XTEMP1(I)= XTEMP1(I) + Y2(K)*WTM(I-K+M)
  630       CONTINUE
          ENDIF
        ENDIF
  620 CONTINUE
C
      DN=DBLE(N)
      DO680I=1,NBIN
        Y2(I)=XTEMP1(I)/(REAL(DH*DN))
        X2(I)=REAL(DXSTAR + (DBLE(I)-0.5D0)*DELTA)
  680 CONTINUE
      N2=NBIN
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBINA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IRELAT,IERROR,N2
 9012   FORMAT('IRELAT,IERROR,N2 = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,N2
          WRITE(ICOUT,9016)I,X2(I),Y2(I)
 9016     FORMAT('I,X2(I),Y2(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
        DO9025I=1,M
          WRITE(ICOUT,9026)I,WTM(I)
 9026     FORMAT('I,WTM(I) = ',I8,G15.7)
          CALL DPWRST('XXX','BUG ')
 9025   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBINC(Y,N,IRELAT,CLWID,XSTART,XSTOP,IRHSTG,
     1                  TEMP1,MAXNXT,IHSTCW,IHSTOU,
     1                  Y2,X2,N2,YCODE,
     1                  ISUBRO,IBUGG3,IERROR)
C
C     PURPOSE--THIS ROUTINE IS SIMILAR TO DPBIN IN THAT IT WILL BIN A
C              VARIABLE Y INTO X2 Y2 (THAT IS CONVERT RAW DATA TO
C              FREQUENCY DATA).  HOWEVER, IT THEN TAKES AN ADDITIONAL
C              STEP TO GENERATE A CODED VARIABLE THAT IDENTIFIES THE
C              THE BIN THAT EACH OF THE ORIGINAL POINTS FALLS INTO.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2012/11
C     ORIGINAL VERSION--NOVEMBER  2012.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IRELAT
      CHARACTER*4 IBUGG3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*4 IRHSTG
      CHARACTER*4 IHSTCW
      CHARACTER*4 IHSTOU
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
C
C---------------------------------------------------------------------
 
      DOUBLE PRECISION DCLWID
      DOUBLE PRECISION DXSTAR
      DOUBLE PRECISION DXSTOP
      DOUBLE PRECISION DCLMNJ
      DOUBLE PRECISION DCLMDJ
      DOUBLE PRECISION DCLMXJ
      DOUBLE PRECISION DJ
      DOUBLE PRECISION DXI
      DOUBLE PRECISION DABSDE
      DOUBLE PRECISION DTOTWI
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION TEMP1(*)
      DIMENSION YCODE(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPBI'
      ISUBN2='NC  '
      IERROR='NO'
C
      HOLD=CPUMIN
C
      IF(IBUGG3.EQ.'ON' .OR.ISUBRO.EQ.'BINC')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,50)
   50   FORMAT('***** AT THE BEGINNING OF DPBINC--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)IRELAT,IRHSTG,IHSTCW
   51   FORMAT('IRELAT,IRHSTG,IHSTCW = ',3(A4,1X))
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)N,CLWID,XSTART,XSTOP
   52   FORMAT('N,CLWID,XSTART,XSTOP = ',I6,3G15.7)
        CALL DPWRST('XXX','BUG ')
        DO53I=1,N
          WRITE(ICOUT,54)I,Y(I)
   54     FORMAT('I, Y(I) = ',I8,G15.7)
          CALL DPWRST('XXX','BUG ')
   53   CONTINUE
      ENDIF
C
      DO59I=1,N
        YCODE(I)=1.0
   59 CONTINUE
      K=(-999)
      DCLMDJ=(-999.0D0)
      KP3=0
      AN3=0.0
      DENOM=0.0
      DCLWID=CLWID
      DXSTAR=XSTART
      DXSTOP=XSTOP
C
C               ********************************************
C               **  STEP 1--                              **
C               **  CHECK THE INPUT ARGUMENTS FOR ERRORS  **
C               ********************************************
C
      IF(N.LT.1)THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,31)
   31   FORMAT('***** ERROR IN BINNING CODED--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,32)
   32   FORMAT('      THE NUMBER OF OBSERVATIONS MUST BE AT LEAST 1;')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,34)N
   34   FORMAT('      THE ENTERED NUMBER OF OBSERVATIONS HERE = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ELSEIF(N.EQ.1)THEN
        N2=1
        X2(1)=HOLD
        Y2(1)=1.0
        YCODE(1)=1.0
        GOTO9000
      ENDIF
C
      HOLD=Y(1)
      DO60I=1,N
        IF(Y(I).NE.HOLD)GOTO69
   60 CONTINUE
      N2=3
      X2(1)=HOLD-1.0
      X2(2)=HOLD
      X2(3)=HOLD+1
      IF(IRELAT.EQ.'ON')THEN
        Y2(1)=0.0
        Y2(2)=1.0
        Y2(3)=0.0
      ELSE
        Y2(1)=0.0
        Y2(2)=REAL(N)
        Y2(3)=0.0
      ENDIF
      DO67I=1,N
        YCODE(I)=1.0
   67 CONTINUE
      GOTO9000
   69 CONTINUE
C
C
C               **********************************************
C               **  STEP 2--                                **
C               **  IF NECESSARY,                           **
C               **  DETERMINE CLASS WIDTH,                  **
C               **  START VALUE, STOP VALUE,                **
C               **  AND NUMBER OF CLASSES.                  **
C               **********************************************
C
      CALL DPBINZ(Y,N,CLWID,XSTART,XSTOP,
     1            TEMP1,MAXNXT,IHSTCW,IHSTOU,
     1            DCLWID,DXSTAR,DXSTOP,
     1            ISUBRO,IBUGG3,IERROR)
C
      DTOTWI=DXSTOP-DXSTAR
      ANUMCL=DTOTWI/DCLWID
      NUMCLA=INT(ANUMCL+1.0+0.1)
C
      J=NUMCLA-1
      DJ=J
      DCLMXJ=DXSTAR+DJ*DCLWID
      DABSDE=DABS(DCLMXJ-DXSTOP)
      IF(DABSDE.LE.0.0001D0)NUMCLA=NUMCLA-1
C
      DO181I=1,NUMCLA
        X2(I)=0.0
        Y2(I)=0.0
  181 CONTINUE
C
C               *******************************************************
C               **  STEP 3--                                         **
C               **  DETERMINE THE FREQUENCY (COUNTS) FOR EACH CLASS  **
C               *******************************************************
C
      DO420I=1,N
        DXI=Y(I)
        DO430J=1,NUMCLA
          J2=J
          DJ=J
          DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
          DCLMXJ=DXSTAR+DJ*DCLWID
          IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
          IF(DCLMNJ.LE.DXI.AND.DXI.LT.DCLMXJ)THEN
            Y2(J2)=Y2(J2)+1.0
            GOTO420
          ENDIF
  430   CONTINUE
  420 CONTINUE
C
C     FOR THIS RAW DATA CASE,
C     TREAT THE SPECIAL CASE OF EQUALITY
C     WITH THE UPPER LIMIT OF THE LAST (RIGHT-MOST) CLASS
C
      J=NUMCLA
      DO450I=1,N
        DJ=J
        DCLMXJ=DXSTAR+DJ*DCLWID
        IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
        DXI=Y(I)
        IF(DXI.EQ.DCLMXJ)Y2(J)=Y2(J)+1.0
  450 CONTINUE
C
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BINC')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,591)
  591   FORMAT('***** IN THE MIDDLE    OF DPBINC--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,592)DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA
  592   FORMAT('DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA= ',
     1        4D11.4,F10.0,I8)
        CALL DPWRST('XXX','BUG ')
        DO593J=1,NUMCLA
          DJ=J
          DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
          DCLMXJ=DXSTAR+DJ*DCLWID
          IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
          FJ=Y2(J)
          WRITE(ICOUT,594)J,DCLMNJ,DCLMXJ,FJ
  594     FORMAT('J,DCLMNJ,DCLMXJ,FJ = ',I8,3G15.7)
          CALL DPWRST('XXX','BUG ')
  593   CONTINUE
      ENDIF
C
      SUM=0.0
      DO1110J=1,NUMCLA
        FJ=Y2(J)
        SUM=SUM+FJ
 1110 CONTINUE
      AN3=SUM
C
      DENOM=1.0
C
      IF(IRELAT.EQ.'ON')THEN
        IF(IRHSTG.EQ.'PERC')THEN
          DENOM=AN3
        ELSE
          DENOM=AN3*DCLWID
        ENDIF
      ENDIF
C
      DO1120J=1,NUMCLA
        K=J
        DJ=J
        DCLMDJ=DXSTAR+(DJ-0.5D0)*DCLWID
        FJ=Y2(J)
        X2(K)=DCLMDJ
        Y2(K)=FJ/DENOM
 1120 CONTINUE
      N2=K
C
      DO1130J=NUMCLA,1,-1
        IF(Y2(J).GT.0.0)THEN
          N2=J
          GOTO1139
        ENDIF
 1130 CONTINUE
      N2=1
 1139 CONTINUE
C
      DO1140J=1,N2
        IF(Y2(J).GT.0.0)THEN
          IFRST=J
          GOTO1149
        ENDIF
 1140 CONTINUE
      IFRST=1
 1149 CONTINUE
      DJ=DBLE(IFRST)
      DXSTAR=DXSTAR+(DJ-1.0D0)*DCLWID
      K=0
      DO1150J=IFRST,N2
        K=K+1
        X2(K)=X2(J)
        Y2(K)=Y2(J)
 1150 CONTINUE
C
      IF(N2.LT.NUMCLA)THEN
        DO1160I=N2+1,NUMCLA
          Y2(I)=0.0
          X2(I)=0.0
 1160   CONTINUE
      ENDIF
      N2=K
C
C
C               *******************************************************
C               **  STEP 4--                                         **
C               **  NOW DETERMINE WHICH BIN EACH DATA POINT FALLS IN **
C               *******************************************************
C
      DO2050I=1,N
        DO2060J=1,N2
          DJ=J
          DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
          DCLMXJ=DXSTAR+DJ*DCLWID
          IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
          IF(Y(I).GE.DCLMNJ .AND. Y(I).LE.DCLMXJ)THEN
            YCODE(I)=REAL(J)
            GOTO2069
          ENDIF
 2060   CONTINUE
 2069   CONTINUE
 2050 CONTINUE
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BINC')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBINC--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)IERROR,N2,IFRST,ILAST,AN3,DENOM
 9013   FORMAT('IERROR,N2,IFRST,ILAST,AN3,DENOM = ',A4,2X,3I8,2X,2G15.7)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,N2
          WRITE(ICOUT,9016)I,X2(I),Y2(I)
 9016     FORMAT('I,X2(I),Y2(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
        DO9017I=1,N
          WRITE(ICOUT,9018)I,YCODE(I)
 9018     FORMAT('I,YCODE(I) = ',I8,G15.7)
          CALL DPWRST('XXX','BUG ')
 9017   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBINI(Y,N,IRELAT,CLWID,XSTART,XSTOP,IRHSTG,
     1Y2,X2,N2,IBUGG3,IERROR)
C
C     PURPOSE--BIN A VARIABLE Y INTO X2 Y2.
C              THAT IS CONVERT RAW DATA TO FREQUENCY DATA.
C              BINNING IS TO COUNTS (AND NOT TO RELATIVE)
C              FREQUENCY.
C              THIS IS A SPECIFAL FORM OF BINNING WHERE THE
C              BINS ARE THE INTEGERS FROM THE MINIMUM TO THE MAXIMUM
C              VALUE.  THIS ROUTINE IS USED TO BIN FOR GOODNESS OF
C              FIT TESTS FOR DISCRETE DISTRIBUTIONS.
C
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--98/11
C     ORIGINAL VERSION--NOVEMBER  1998.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IRELAT
      CHARACTER*4 IBUGG3
      CHARACTER*4 IERROR
C
      CHARACTER*4 IWRIT2
      CHARACTER*4 IRHSTG
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
C
C---------------------------------------------------------------------
 
      DOUBLE PRECISION DCLWID
      DOUBLE PRECISION DXSTAR
      DOUBLE PRECISION DXSTOP
      DOUBLE PRECISION DCLMNJ
      DOUBLE PRECISION DCLMDJ
      DOUBLE PRECISION DCLMXJ
      DOUBLE PRECISION DJ
      DOUBLE PRECISION DXI
      DOUBLE PRECISION DABSDE
      DOUBLE PRECISION DTOTWI
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPBI'
      ISUBN2='NI  '
C
      IERROR='NO'
C
      K=(-999)
      DCLMDJ=(-999.0D0)
C
      KP3=0
C
      AN3=0.0
      DENOM=0.0
C
      DCLWID=CLWID
      DXSTAR=XSTART
      DXSTOP=XSTOP
C
C               ********************************************
C               **  STEP 1--                              **
C               **  CHECK THE INPUT ARGUMENTS FOR ERRORS  **
C               ********************************************
C
      IF(N.GE.1)GOTO39
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,31)
   31 FORMAT('***** ERROR IN DPBINI--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,32)
   32 FORMAT('      THE NUMBER OF OBSERVATIONS')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,33)
   33 FORMAT('      MUST BE AT LEAST 1;')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,34)N
   34 FORMAT('      THE ENTERED NUMBER OF OBSERVATIONS HERE = ',I6)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
   39 CONTINUE
C
      IF(N.GE.2)GOTO49
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,46)
   46 FORMAT('***** ERROR IN DPBINI--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,47)
   47 FORMAT('      THE NUMBER OF OBSERVATIONS')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,48)
   48 FORMAT('      WAS EXACTLY EQUAL TO 1.')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
   49 CONTINUE
C
      HOLD=Y(1)
      DO60I=1,N
      IF(Y(I).NE.HOLD)GOTO69
   60 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,61)
   61 FORMAT('***** ERROR IN DPBINI--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,62)
   62 FORMAT('      ALL INPUT AXIS ELEMENTS')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,63)HOLD
   63 FORMAT('      ARE IDENTICALLY EQUAL TO ',E15.7)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
   69 CONTINUE
C
      IF(IBUGG3.EQ.'OFF')GOTO80
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,70)
   70 FORMAT('***** AT THE BEGINNING OF DPBINI--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,72)N,CLWID,XSTART,XSTOP
   72 FORMAT('N,CLWID,XSTART,XSTOP = ',I6,3E15.7)
      CALL DPWRST('XXX','BUG ')
      DO73I=1,N
      WRITE(ICOUT,74)I,Y(I)
   74 FORMAT('I, Y(I) = ',I8,E15.7)
      CALL DPWRST('XXX','BUG ')
   73 CONTINUE
   80 CONTINUE
C
C               **********************************************
C               **  STEP 2--                                **
C               **  IF NECESSARY,                           **
C               **  DETERMINE CLASS WIDTH,                  **
C               **  START VALUE, STOP VALUE,                **
C               **  AND NUMBER OF CLASSES.                  **
C               **********************************************
C
      IF(CLWID.NE.CPUMIN.AND.XSTART.NE.CPUMIN.AND.
     1XSTOP.NE.CPUMAX)GOTO119
      IWRIT2='OFF'
      CALL MINIM(Y,N,IWRIT2,YMIN,IBUGG3,IERROR)
      CALL MAXIM(Y,N,IWRIT2,YMAX,IBUGG3,IERROR)
      IF(CLWID.EQ.CPUMIN)DCLWID=1.0
      IF(XSTART.EQ.CPUMIN)DXSTAR=REAL(INT(YMIN+0.5))-0.5
      IF(XSTOP.EQ.CPUMAX)DXSTOP=REAL(INT(YMAX+0.5))+0.5
  119 CONTINUE
C
      DO181I=1,N
        X2(I)=0.0
        Y2(I)=0.0
  181 CONTINUE
      DTOTWI=DXSTOP-DXSTAR
      ANUMCL=DTOTWI/DCLWID
      NUMCLA=INT(ANUMCL+1.0+0.1)
C
      J=NUMCLA-1
      DJ=J
      DCLMXJ=DXSTAR+DJ*DCLWID
      DABSDE=DABS(DCLMXJ-DXSTOP)
      IF(DABSDE.LE.0.0001D0)NUMCLA=NUMCLA-1
C
C               *******************************************************
C               **  STEP 3--                                         **
C               **  DETERMINE THE FREQUENCY (COUNTS) FOR EACH CLASS  **
C               *******************************************************
C
      DO420I=1,N
      DXI=Y(I)
      DO430J=1,NUMCLA
      J2=J
      DJ=J
      DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
      DCLMXJ=DXSTAR+DJ*DCLWID
      IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
      IF(DCLMNJ.LE.DXI.AND.DXI.LT.DCLMXJ)GOTO440
  430 CONTINUE
      GOTO420
  440 CONTINUE
      Y2(J2)=Y2(J2)+1.0
  420 CONTINUE
C
C     FOR THIS RAW DATA CASE,
C     TREAT THE SPECIAL CASE OF EQUALITY
C     WITH THE UPPER LIMIT OF THE LAST (RIGHT-MOST) CLASS
C
      J=NUMCLA
      DO450I=1,N
      DJ=J
      DCLMXJ=DXSTAR+DJ*DCLWID
      IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
      DXI=Y(I)
      IF(DXI.EQ.DCLMXJ)Y2(J)=Y2(J)+1.0
  450 CONTINUE
C
      IF(IBUGG3.EQ.'OFF')GOTO595
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,591)
  591 FORMAT('***** IN THE MIDDLE    OF DPBINI--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,592)DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA
  592 FORMAT('DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA= ',
     14D11.4,F10.0,I8)
      CALL DPWRST('XXX','BUG ')
      DO593J=1,NUMCLA
      DJ=J
      DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
      DCLMXJ=DXSTAR+DJ*DCLWID
      IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
      FJ=Y2(J)
      WRITE(ICOUT,594)J,DCLMNJ,DCLMXJ,FJ
  594 FORMAT('J,DCLMNJ,DCLMXJ,FJ = ',I8,2D15.7,E15.7)
      CALL DPWRST('XXX','BUG ')
  593 CONTINUE
  595 CONTINUE
C
      SUM=0.0
      DO1110J=1,NUMCLA
      FJ=Y2(J)
      SUM=SUM+FJ
 1110 CONTINUE
      AN3=SUM
C
      DENOM=1.0
C
      IF(IRELAT.EQ.'ON')THEN
        IF(IRHSTG.EQ.'PERC')THEN
          DENOM=AN3
        ELSE
          DENOM=AN3*DCLWID
        ENDIF
      ENDIF
C
      DO1120J=1,NUMCLA
        K=J
        DJ=J
        DCLMDJ=DXSTAR+(DJ-0.5D0)*DCLWID
        X2(K)=REAL(DCLMDJ)
        FJ=Y2(K)
        Y2(K)=FJ/DENOM
 1120 CONTINUE
      N2=K
C
      DO1130J=NUMCLA,1,-1
        IF(Y2(J).GT.0.0)THEN
          N2=J
          GOTO1139
        ENDIF
 1130 CONTINUE
      N2=1
 1139 CONTINUE
C
      DO1140J=1,N2
        IF(Y2(J).GT.0.0)THEN
          IFRST=J
          GOTO1149
        ENDIF
 1140 CONTINUE
      IFRST=1
 1149 CONTINUE
      K=0
      DO1150J=IFRST,N2
        K=K+1
        X2(K)=X2(J)
        Y2(K)=Y2(J)
 1150 CONTINUE
C
      IF(N2.LT.NUMCLA)THEN
        DO1160I=N2+1,NUMCLA
          Y2(I)=0.0
          X2(I)=0.0
 1160   CONTINUE
      ENDIF
      N2=K
      GOTO9000
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'OFF')GOTO9090
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,9011)
 9011 FORMAT('***** AT THE END       OF DPBINI--')
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,9012)IRELAT,IERROR,N2
 9012 FORMAT('IRELAT,IERROR,N2 = ',A4,2X,A4,2X,I8)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,9013)AN3,DENOM
 9013 FORMAT('AN3,DENOM = ',E15.8,E15.8)
      CALL DPWRST('XXX','BUG ')
      DO9015I=1,N2
      WRITE(ICOUT,9016)I,X2(I),Y2(I)
 9016 FORMAT('I,X2(I),Y2(I) = ',I8,E15.7,F9.2)
      CALL DPWRST('XXX','BUG ')
 9015 CONTINUE
      WRITE(ICOUT,9017)N,DCLWID,DXSTAR,DXSTOP
 9017 FORMAT('N,DCLWID,DXSTAR,DXSTOP = ',I6,3D15.7)
      CALL DPWRST('XXX','BUG ')
 9090 CONTINUE
C
      RETURN
      END
      SUBROUTINE DPBINP(Y,N,IRELAT,CLWID,XSTART,XSTOP,IRHSTG,
     1                  TEMP1,DIFFY2,Y3,X3,MAXNXT,IHSTCW,
     1                  Y2,X2,N2,IBUGG3,ISUBRO,IERROR)
C
C     PURPOSE--BIN A VARIABLE Y INTO X2 Y2.  THEN THIN BY EXTRACTING THE
C              "PEAKS" IN THE FREQUENCY TABLE.  PEAKS ARE DETERMINED BY:
C
C              STEP 1: DIFFERENCE THE FREQUENCY COUNTS
C              STEP 2: WHERE THE DIFFERENCES CHANGE SIGN INDICATES
C                      A PEAK.
C
C              BINNING CAN BE EITHER TO COUNTS OR TO RELATIVE
C              FREQUENCY.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/5
C     ORIGINAL VERSION--MAY       2008.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IRELAT
      CHARACTER*4 IBUGG3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*4 IWRIT2
      CHARACTER*4 IRHSTG
      CHARACTER*4 IHSTCW
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTAT
C
C---------------------------------------------------------------------
 
      DOUBLE PRECISION DCLWID
      DOUBLE PRECISION DXSTAR
      DOUBLE PRECISION DXSTOP
      DOUBLE PRECISION DCLMNJ
      DOUBLE PRECISION DCLMDJ
      DOUBLE PRECISION DCLMXJ
      DOUBLE PRECISION DJ
      DOUBLE PRECISION DXI
      DOUBLE PRECISION DABSDE
      DOUBLE PRECISION DTOTWI
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION TEMP1(*)
      DIMENSION DIFFY2(*)
      DIMENSION Y3(*)
      DIMENSION X3(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPBI'
      ISUBN2='NP  '
      IERROR='NO'
      ISTAT='DIFF'
C
      KP3=0
      K=(-999)
      DCLMDJ=(-999.0D0)
C
      AN3=0.0
      DENOM=0.0
      DCLWID=CLWID
      DXSTAR=XSTART
      DXSTOP=XSTOP
C
C               ********************************************
C               **  STEP 1--                              **
C               **  CHECK THE INPUT ARGUMENTS FOR ERRORS  **
C               ********************************************
C
      IF(N.LE.1)THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,31)
   31   FORMAT('***** ERROR IN BINNING DATA (DPBINP)--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,32)
   32   FORMAT('      THE NUMBER OF OBSERVATIONS MUST BE AT LEAST 2;')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,34)N
   34   FORMAT('      THE ENTERED NUMBER OF OBSERVATIONS HERE = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      HOLD=Y(1)
      DO60I=1,N
        IF(Y(I).NE.HOLD)GOTO69
   60 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,31)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,62)HOLD
   62 FORMAT('      ALL INPUT DATA ELEMENTS ARE IDENTICALLY EQUAL ',
     1       'TO ',G15.7)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','BUG ')
      IERROR='YES'
      GOTO9000
   69 CONTINUE
C
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BINP')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,70)
   70   FORMAT('***** AT THE BEGINNING OF DPBINP--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,71)IRELAT,IRHSTG,IHSTCW
   71   FORMAT('IRELAT,IRHSTG,IHSTCW = ',3(A4,1X))
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,72)N,MAXNXT,CLWID,XSTART,XSTOP
   72   FORMAT('N,MAXNXT,CLWID,XSTART,XSTOP = ',2I8,3G15.7)
        CALL DPWRST('XXX','BUG ')
        DO73I=1,N
          WRITE(ICOUT,74)I,Y(I)
   74     FORMAT('I, Y(I) = ',I8,G15.7)
          CALL DPWRST('XXX','BUG ')
   73   CONTINUE
      ENDIF
C
C               **********************************************
C               **  STEP 2--                                **
C               **  IF NECESSARY,                           **
C               **  DETERMINE CLASS WIDTH,                  **
C               **  START VALUE, STOP VALUE,                **
C               **  AND NUMBER OF CLASSES.                  **
C               **********************************************
C
C  ALLOW DIFFERENT DEFAULT BINNING ALGORITHMS (AS
C  SPECIFIED BY IHSTCW).
C
      IF(CLWID.EQ.CPUMIN.OR.XSTART.EQ.CPUMIN.OR.
     1  XSTOP.EQ.CPUMAX)THEN
        IWRIT2='OFF'
        CALL MEAN(Y,N,IWRIT2,XMEAN,IBUGG3,IERROR)
        CALL SD(Y,N,IWRIT2,XSD,IBUGG3,IERROR)
C
        IF(IHSTCW.EQ.'DEFA')THEN
          IF(CLWID.EQ.CPUMIN)DCLWID=0.3*XSD
        ELSEIF(IHSTCW.EQ.'NORM')THEN
          IF(CLWID.EQ.CPUMIN)DCLWID=3.5*XSD/(REAL(N)**(1./3.))
        ELSEIF(IHSTCW.EQ.'NCOR')THEN
          IF(CLWID.EQ.CPUMIN)THEN
            CALL STMOM3(Y,N,IWRIT2,XSKEW,IBUGG3,IERROR)
            CALL STMOM4(Y,N,IWRIT2,XKURT,IBUGG3,IERROR)
            TERM1=3.5*XSD/(REAL(N)**(1./3.))
            IF(XSKEW.GT.0.0 .AND. XSKEW.LT.3.0)THEN
              TERM2=1.0/(1.0 - 0.0060*XSKEW + 0.27*XSKEW**2 -
     1              0.0069*XSKEW**3)
            ELSE
              TERM2=1.0
            ENDIF
            XKURT=XKURT - 3.0
            IF(XKURT.GT.0.0 .AND. XKURT.LT.6.0)THEN
              TERM3=1.0 - 0.2*(1.0 - EXP(-0.7*XKURT))
            ELSE
              TERM3=1.0
            ENDIF
            DCLWID=DBLE(TERM1*TERM2*TERM3)
          ENDIF
        ELSEIF(IHSTCW.EQ.'IQ  ')THEN
          IF(CLWID.EQ.CPUMIN)THEN
            CALL LOWQUA(Y,N,IWRIT2,TEMP1,MAXOBV,XLOWQ,
     1                  IBUGG3,IERROR)
            CALL UPPQUA(Y,N,IWRIT2,TEMP1,MAXOBV,XUPPQ,
     1                  IBUGG3,IERROR)
            XIQ=XUPPQ - XLOWQ
            DCLWID=2.603*XIQ/(REAL(N)**(1./3.))
          ENDIF
        ELSE
          IF(CLWID.EQ.CPUMIN)DCLWID=0.3*XSD
        ENDIF
C
        IF(XSTART.EQ.CPUMIN)DXSTAR=XMEAN-6.0*XSD
        IF(XSTOP.EQ.CPUMAX)DXSTOP=XMEAN+6.0*XSD
      ENDIF
C
      DO181I=1,N
        X2(I)=0.0
        Y2(I)=0.0
  181 CONTINUE
      DTOTWI=DXSTOP-DXSTAR
      ANUMCL=DTOTWI/DCLWID
      NUMCLA=INT(ANUMCL+1.0+0.1)
C
      J=NUMCLA-1
      DJ=J
      DCLMXJ=DXSTAR+DJ*DCLWID
      DABSDE=DABS(DCLMXJ-DXSTOP)
      IF(DABSDE.LE.0.0001D0)NUMCLA=NUMCLA-1
C
C               *******************************************************
C               **  STEP 3--                                         **
C               **  DETERMINE THE FREQUENCY (COUNTS) FOR EACH CLASS  **
C               *******************************************************
C
      DO420I=1,N
      DXI=Y(I)
      DO430J=1,NUMCLA
      J2=J
      DJ=J
      DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
      DCLMXJ=DXSTAR+DJ*DCLWID
      IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
      IF(DCLMNJ.LE.DXI.AND.DXI.LT.DCLMXJ)GOTO440
  430 CONTINUE
      GOTO420
  440 CONTINUE
      Y2(J2)=Y2(J2)+1.0
  420 CONTINUE
C
C     FOR THIS RAW DATA CASE,
C     TREAT THE SPECIAL CASE OF EQUALITY
C     WITH THE UPPER LIMIT OF THE LAST (RIGHT-MOST) CLASS
C
      J=NUMCLA
      DO450I=1,N
      DJ=J
      DCLMXJ=DXSTAR+DJ*DCLWID
      IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
      DXI=Y(I)
      IF(DXI.EQ.DCLMXJ)Y2(J)=Y2(J)+1.0
  450 CONTINUE
C
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BINP')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,591)
  591   FORMAT('***** IN THE MIDDLE    OF DPBINP--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,592)DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA
  592   FORMAT('DCLWID,DXSTAR,DXSTOP,DTOTWI,ANUMCL,NUMCLA= ',
     1        4D11.4,F10.0,I8)
        CALL DPWRST('XXX','BUG ')
        DO593J=1,NUMCLA
          DJ=J
          DCLMNJ=DXSTAR+(DJ-1.0D0)*DCLWID
          DCLMXJ=DXSTAR+DJ*DCLWID
          IF(DCLMXJ.GT.DXSTOP)DCLMXJ=DXSTOP
          FJ=Y2(J)
          WRITE(ICOUT,594)J,DCLMNJ,DCLMXJ,FJ
  594     FORMAT('J,DCLMNJ,DCLMXJ,FJ = ',I8,2D15.7,E15.7)
          CALL DPWRST('XXX','BUG ')
  593   CONTINUE
      ENDIF
C
      SUM=0.0
      DO1110J=1,NUMCLA
      FJ=Y2(J)
      SUM=SUM+FJ
 1110 CONTINUE
      AN3=SUM
C
      DENOM=1.0
C
      IF(IRELAT.EQ.'ON')THEN
        IF(IRHSTG.EQ.'PERC')THEN
          DENOM=AN3
        ELSE
          DENOM=AN3*DCLWID
        ENDIF
      ENDIF
C
      DO1120J=1,NUMCLA
        K=J
        DJ=J
        DCLMDJ=DXSTAR+(DJ-0.5D0)*DCLWID
        FJ=Y2(J)
        X2(K)=DCLMDJ
        Y2(K)=FJ/DENOM
 1120 CONTINUE
      N2=K
C
      DO1130J=NUMCLA,1,-1
        IF(Y2(J).GT.0.0)THEN
          N2=J
          GOTO1139
        ENDIF
 1130 CONTINUE
      N2=1
 1139 CONTINUE
C
      DO1140J=1,N2
        IF(Y2(J).GT.0.0)THEN
          IFRST=J
          GOTO1149
        ENDIF
 1140 CONTINUE
      IFRST=1
 1149 CONTINUE
      K=0
      DO1150J=IFRST,N2
        K=K+1
        X2(K)=X2(J)
        Y2(K)=Y2(J)
 1150 CONTINUE
C
      IF(N2.LT.NUMCLA)THEN
        DO1160I=N2+1,NUMCLA
          Y2(I)=0.0
          X2(I)=0.0
 1160   CONTINUE
      ENDIF
      N2=K
C
C     AT THIS POINT, WE HAVE THE INITIAL FREQUENCY TABLE.  NOW
C     NEED TO EXTRACT THE "PEAKS" (WHICH WILL BE BASED ON WHERE
C     THE DIFFERENCE OF SUCCESSIVE FREQUENCIES CHANGES SIGNS).
C
C     STEP 1: COMPUTE THE SEQUENTIAL DIFFERENCES OF THE
C             FREQUENCIES.  BUT FIRST, REMOVE ANY ZERO
C             FREQUENCY CELLS.
C
      ICNT=0
      DO1900I=1,N2
        IF(Y2(I).GT.0.0)THEN
          ICNT=ICNT+1
          Y2(ICNT)=Y2(I)
          X2(ICNT)=X2(I)
        ENDIF
 1900 CONTINUE
      N2=ICNT
C
      CALL SEQDIF(Y2,N2,IWRIT2,DIFFY2,NDIF,ISTAT,
     1            IBUGG3,ISUBRO,IERROR)
      DIFFY2(N2)=-1
C
C     STEP 2: LOOP THROUGH AND DETECT SIGN CHANGES
C
      INEG=1
      IPOS=0
      ICNTNG=0
      DO2100I=1,N2
        DEL=DIFFY2(I)
        IF(IPOS.EQ.1 .AND. DEL.LT.0.0)THEN
          ICNTNG=ICNTNG+1
          Y3(ICNTNG)=Y2(I)
          X3(ICNTNG)=X2(I)
          IPOS=0
          INEG=1
        ELSEIF(INEG.EQ.1 .AND. DEL.GT.0.0)THEN
          IPOS=1
          INEG=0
        ENDIF
 2100 CONTINUE
C
      IF(ICNTNG.LE.0)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2211)
 2211   FORMAT('***** ERROR IN PEAKS OF FREQUENCY TABLE--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2213)
 2213   FORMAT('      NO PEAKS WERE DETECTED.')
        CALL DPWRST('XXX','BUG ')
        N2=1
        Y2(1)=-9999
        X2(1)=-9999
        IERROR='YES'
        GOTO9000
      ENDIF
C
C     STEP 3: COPY BACK TO Y2 AND X2
C
      DO2200I=1,ICNTNG
        Y2(I)=Y3(I)
        X2(I)=X3(I)
 2200 CONTINUE
      N2=ICNTNG
C
      GOTO9000
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BINP')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBIN--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IRELAT,IERROR,N2
 9012   FORMAT('IRELAT,IERROR,N2 = ',A4,2X,A4,2X,I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)AN3,DENOM
 9013   FORMAT('AN3,DENOM = ',E15.8,E15.8)
        CALL DPWRST('XXX','BUG ')
        DO9015I=1,N2
          WRITE(ICOUT,9016)I,X2(I),Y2(I)
 9016     FORMAT('I,X2(I),Y2(I) = ',I8,E15.7,F9.2)
          CALL DPWRST('XXX','BUG ')
 9015   CONTINUE
        WRITE(ICOUT,9017)N,DCLWID,DXSTAR,DXSTOP
 9017   FORMAT('N,DCLWID,DXSTAR,DXSTOP = ',I6,3D15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBINZ(Y,N,CLWID,XSTART,XSTOP,
     1                  TEMP1,MAXNXT,IHSTCW,IHSTOU,
     1                  DCLWID,DXSTAR,DXSTOP,
     1                  ISUBRO,IBUGG3,IERROR)
C
C     PURPOSE--UTILITY ROUTINE CALLED BY DPBIN AND DPHIS2.
C              FOR RAW DATA, DETERMINE THE APPROPRIATE CLASS WIDTH,
C              CLASS START, AND CLASS STOP.
C     WRITTEN BY--JAMES J. FILLIBEN
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2855
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2010/1
C     ORIGINAL VERSION--JANUARY   2010. EXTRACTED AS A SEPARATE SUBROUTINE
C     ORIGINAL VERSION--JUNE      2016. MAXOBV ARGUMENT (FOR LOWQUA,
C                                       UPPQUA)
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGG3
      CHARACTER*4 IERROR
C
      CHARACTER*4 IWRIT2
      CHARACTER*4 IHSTCW
      CHARACTER*4 IHSTOU
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
C
C---------------------------------------------------------------------
C
      DOUBLE PRECISION DCLWID
      DOUBLE PRECISION DXSTAR
      DOUBLE PRECISION DXSTOP
      DOUBLE PRECISION DXSTA2
      DOUBLE PRECISION DXSTO2
      DOUBLE PRECISION DARG1
      DOUBLE PRECISION DARG3
      DOUBLE PRECISION DARG4
      DOUBLE PRECISION DINT
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION TEMP1(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPBI'
      ISUBN2='NZ  '
      IERROR='NO'
C
      XIQ=0.0
C
C     IF USER HAS SPECIFIED CLASS WIDTH AND CLASS LIMITS, THEN WE
C     JUST DEFAULT TO THESE VALUES.
C
      DCLWID=CLWID
      DXSTAR=XSTART
      DXSTOP=XSTOP
C
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BINZ')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,70)
   70   FORMAT('***** AT THE BEGINNING OF DPBINZ--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,72)N,IHSTCW,IHSTOU,CLWID,XSTART,XSTOP
   72   FORMAT('N,IHSTCW,IHSTOU,CLWID,XSTART,XSTOP = ',
     1         I6,2(1X,A4),3G15.7)
        CALL DPWRST('XXX','BUG ')
        DO73I=1,N
          WRITE(ICOUT,74)I,Y(I)
   74     FORMAT('I, Y(I) = ',I8,G15.7)
          CALL DPWRST('XXX','BUG ')
   73   CONTINUE
      ENDIF
C
C               **********************************************
C               **  STEP 2--                                **
C               **  DETERMINE CLASS WIDTH,                  **
C               **  START VALUE, STOP VALUE,                **
C               **  AND NUMBER OF CLASSES.                  **
C               **********************************************
C
C     MARCH 2006: ALLOW DIFFERENT DEFAULT BINNING ALGORITHMS (AS
C                 SPECIFIED BY IHSTCW).
C
      IF(CLWID.EQ.CPUMIN.OR.XSTART.EQ.CPUMIN.OR.
     1   XSTOP.EQ.CPUMAX.OR.XSTOP.EQ.CPUMIN)THEN
        IWRIT2='OFF'
        CALL MINIM(Y,N,IWRIT2,XMIN,IBUGG3,IERROR)
        CALL MAXIM(Y,N,IWRIT2,XMAX,IBUGG3,IERROR)
        CALL MEAN(Y,N,IWRIT2,XMEAN,IBUGG3,IERROR)
        CALL SD(Y,N,IWRIT2,XSD,IBUGG3,IERROR)
C
        IF(IHSTCW.EQ.'DEFA')THEN
          IF(CLWID.EQ.CPUMIN)DCLWID=0.3*XSD
        ELSEIF(IHSTCW.EQ.'NORM')THEN
          IF(CLWID.EQ.CPUMIN)DCLWID=3.5*XSD/(REAL(N)**(1./3.))
        ELSEIF(IHSTCW.EQ.'NCOR')THEN
          IF(CLWID.EQ.CPUMIN)THEN
            CALL STMOM3(Y,N,IWRIT2,XSKEW,IBUGG3,IERROR)
            CALL STMOM4(Y,N,IWRIT2,XKURT,IBUGG3,IERROR)
            TERM1=3.5*XSD/(REAL(N)**(1./3.))
            IF(XSKEW.GT.0.0 .AND. XSKEW.LT.3.0)THEN
              TERM2=1.0/(1.0 - 0.0060*XSKEW + 0.27*XSKEW**2 -
     1              0.0069*XSKEW**3)
            ELSE
              TERM2=1.0
            ENDIF
            XKURT=XKURT - 3.0
            IF(XKURT.GT.0.0 .AND. XKURT.LT.6.0)THEN
              TERM3=1.0 - 0.2*(1.0 - EXP(-0.7*XKURT))
            ELSE
              TERM3=1.0
            ENDIF
            DCLWID=DBLE(TERM1*TERM2*TERM3)
          ENDIF
        ELSEIF(IHSTCW.EQ.'IQ  ')THEN
          IF(CLWID.EQ.CPUMIN)THEN
            CALL LOWQUA(Y,N,IWRIT2,TEMP1,MAXNXT,XLOWQ,
     1                  IBUGG3,IERROR)
            CALL UPPQUA(Y,N,IWRIT2,TEMP1,MAXNXT,XUPPQ,
     1                  IBUGG3,IERROR)
            XIQ=XUPPQ - XLOWQ
            DCLWID=2.603*XIQ/(REAL(N)**(1./3.))
          ENDIF
        ELSE
          IF(CLWID.EQ.CPUMIN)DCLWID=0.3*XSD
        ENDIF
C
C       NOW THAT CLASS WIDTH IS DETERMINED, DETERMINE START AND STOP
C       VALUES.  DEFAULT IS +/- 6*STANDARD DEVIATION.  HOWEVER, IF
C       USER HAS SPECIFIED "OUTLIERS ON", EXTEND THESE DEFAULT LIMITS
C       TO INCORPORATE ALL THE DATA.
C
C       FOR "IQ RANGE" CLASS WIDTH, USE +/- 6*NORMALIZED IQR
C
        IF(IHSTOU.EQ.'ON')THEN
          IF(XSTART.EQ.CPUMIN)THEN
            IF(IHSTCW.EQ.'IQ  ')THEN
              ANIQ=0.7413*XIQ
              DXSTAR=XMEAN-6.0*ANIQ
            ELSE
              DXSTAR=XMEAN-6.0*XSD
            ENDIF
            DARG1=DBLE(XMEAN-XMIN)/DCLWID
            IARG2=INT(DARG1)
            DARG3=DBLE(IARG2)
            DARG4=DARG1-DARG3
            DINT=DARG3
            IF(DARG4.NE.0.0D0)DINT=DINT+1.0D0
            DXSTA2=DBLE(XMEAN)-DINT*DCLWID
            DXSTAR=MIN(DXSTAR,DXSTA2)
          ENDIF
          IF(XSTOP.EQ.CPUMAX .OR. XSTOP.EQ.CPUMIN)THEN
            IF(IHSTCW.EQ.'IQ  ')THEN
              ANIQ=0.7413*XIQ
              DXSTOP=XMEAN+6.0*ANIQ
            ELSE
              DXSTOP=XMEAN+6.0*XSD
            ENDIF
            DARG1=DBLE(XMAX-XMEAN)/DCLWID
            IARG2=INT(DARG1)
            DARG3=DBLE(IARG2)
            DARG4=DARG1-DARG3
            DINT=DARG3
            IF(DARG4.NE.0.0D0)DINT=DINT+1.0D0
            DXSTO2=DBLE(XMEAN)+DINT*DCLWID
            DXSTOP=MAX(DXSTOP,DXSTO2)
          ENDIF
        ELSE
          IF(IHSTCW.EQ.'IQ  ')THEN
            ANIQ=0.7413*XIQ
            IF(XSTART.EQ.CPUMIN)DXSTAR=XMEAN-6.0*ANIQ
            IF(XSTOP.EQ.CPUMAX.OR.XSTOP.EQ.CPUMIN)DXSTOP=XMEAN+6.0*ANIQ
          ELSE
            IF(XSTART.EQ.CPUMIN)DXSTAR=XMEAN-6.0*XSD
            IF(XSTOP.EQ.CPUMAX.OR.XSTOP.EQ.CPUMIN)DXSTOP=XMEAN+6.0*XSD
          ENDIF
        ENDIF
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
      IF(IBUGG3.EQ.'ON' .OR. ISUBRO.EQ.'BINZ')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBINZ--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)XMEAN,XSD,XMIN,XMAX
 9012   FORMAT('XMEAN,XSD,XMIN,XMAX = ',4G15.7)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)XSKEW,XKURT
 9013   FORMAT('XSKEW,XKURT ',2G15.7)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9017)DCLWID,DXSTAR,DXSTOP
 9017   FORMAT('DCLWID,DXSTAR,DXSTOP = ',3G15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBIPL(NPLOTV,NPLOTP,NS,ICASPL,IAND1,IAND2,
     1                  IBUGG2,IBUGG3,ISUBRO,IBUGQ,IFOUND,IERROR)
C
C     PURPOSE--GENERATE A BINARY <TYPE> PLOT Y1 Y2 X
C
C              WHERE <TYPE> IS ONE OF
C
C                 CORRECT MATCHES
C                 FALSE POSITIVES
C                 FALSE NEGATIVES
C                 TRUE POSITIVES
C                 TRUE NEGATIVES
C
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2007/5
C     ORIGINAL VERSION--MAY       2007.
C     UPDATED         --MARCH     2011. USE DPPARS, DPPAR5
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASPL
      CHARACTER*4 IAND1
      CHARACTER*4 IAND2
      CHARACTER*4 IBUGG2
      CHARACTER*4 IBUGG3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGQ
      CHARACTER*4 IFOUND
      CHARACTER*4 IERROR
C
      CHARACTER*4 ICASP2
      CHARACTER*4 ICASE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      CHARACTER*40 INAME
      PARAMETER (MAXSPN=10)
      CHARACTER*4 IVARN1(MAXSPN)
      CHARACTER*4 IVARN2(MAXSPN)
      CHARACTER*4 IVARTY(MAXSPN)
      REAL PVAR(MAXSPN)
      INTEGER ILIS(MAXSPN)
      INTEGER NRIGHT(MAXSPN)
      INTEGER ICOLR(MAXSPN)
C
C---------------------------------------------------------------------
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
C
      DIMENSION Y1(MAXOBV)
      DIMENSION Y2(MAXOBV)
      DIMENSION XGROUP(MAXOBV)
      DIMENSION XGROU2(MAXOBV)
      DIMENSION XIDTEM(MAXOBV)
      DIMENSION TEMP1(MAXOBV)
      DIMENSION TEMP2(MAXOBV)
      DIMENSION TEMP3(MAXOBV)
C
      INCLUDE 'DPCOZZ.INC'
      EQUIVALENCE (GARBAG(IGARB1),Y1(1))
      EQUIVALENCE (GARBAG(IGARB2),Y2(1))
      EQUIVALENCE (GARBAG(IGARB3),XGROUP(1))
      EQUIVALENCE (GARBAG(IGARB4),XGROU2(1))
      EQUIVALENCE (GARBAG(IGARB5),XIDTEM(1))
      EQUIVALENCE (GARBAG(IGARB6),TEMP1(1))
      EQUIVALENCE (GARBAG(IGARB7),TEMP2(1))
      EQUIVALENCE (GARBAG(IGARB8),TEMP3(1))
C
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
C
C-----COMMON VARIABLES (GENERAL)--------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      IFOUND='NO'
      IERROR='NO'
C
      ISUBN1='DPBI'
      ISUBN2='PL  '
C
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
C
C               **********************************
C               **  TREAT THE BINARY PLOT CASE  **
C               **********************************
C
      IF(IBUGG2.EQ.'ON' .OR. ISUBRO.EQ.'BIPL')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPBIPL--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,53)ICASPL,IAND1,IAND2,MAXNPP
   53   FORMAT('ICASPL,IAND1,IAND2,MAXNPP = ',3(A4,2X),I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,54)IBUGG2,IBUGG3,ISUBRO,IBUGQ,ISUBRO
   54   FORMAT('IBUGG2,IBUGG3,ISUBRO,IBUGQ,ISUBRO = ',3(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               *******************************************
C               **  STEP 1--                             **
C               **  SEARCH FOR BINARY <TYPE> PLOT        **
C               *******************************************
C
      ISTEPN='1'
      IF(IBUGG2.EQ.'ON'.OR.ISUBRO.EQ.'BIPL')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      ICASE='PERC'
      ICASPL='BIPL'
      ICASP2='CORM'
      ILASTC=0
C
      IF(IHARG(1).EQ.'PROP')THEN
        ICASE='PROP'
        ILASTC=ILASTC+1
      ELSEIF(IHARG(1).EQ.'PERC')THEN
        ICASE='PERC'
        ILASTC=ILASTC+1
      ENDIF
C
      IF(ILASTC.GT.0)THEN
        CALL SHIFTL(ILASTC,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1              IBUGG2,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
      ENDIF
C
      ILASTC=0
      IF(NUMARG.GE.1.AND.IHARG(1).EQ.'PLOT')THEN
        ILASTC=1
        IFOUND='YES'
        ICASP2='CORM'
      ELSEIF(NUMARG.GE.4.AND.
     1  IHARG(1).EQ.'CORR' .AND. IHARG(2).EQ.'MATC'.AND.
     1  IHARG(4).EQ.'PLOT')THEN
        IF(IHARG(3).EQ.'PROP')THEN
          ICASE='PROP'
        ELSEIF(IHARG(3).EQ.'PERC')THEN
          ICASE='PERC'
        ENDIF
        ILASTC=4
        IFOUND='YES'
