      IH2T(7)='ET  '
      IHT(8)=IVARN1(4)
      IH2T(8)=IVARN2(4)
      IHT(9)='=   '
      IH2T(9)='    '
      IHT(10)='=   '
      IH2T(10)='    '
      NCCOMM=10
C
C     ADD ANY USER-SUPPLIED <SUBSET/EXCEPT/FOR> CLAUSES TO
C     END OF COMMAND LINE
C
      ISHIFT=ILOCQ-1
      CALL SHIFTL(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1IBUGG2,IERROR)
      IF(IERROR.EQ.'YES')GOTO8000
      ISHIFT=NCCOMM
      CALL SHIFTR(ISHIFT,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG,
     1            IBUGG2,IERROR)
C
      ICOM=ICT
      ICOM2=IC2T
      DO7006II=1,NCCOMM
        IHARG(II)=IHT(II)
        IHARG2(II)=IH2T(II)
        IARGT(II)='WORD'
 7006 CONTINUE
      IARGT(10)='NUMB'
      ARG(10)=0.0
C
      IPLOTT='BIPL'
      IFLGIN='NO'
      IF((IY1MIN.EQ.'FLOA'.AND.IY1MAX.EQ.'FLOA').OR.
     1   (IX1MIN.EQ.'FLOA'.AND.IX1MAX.EQ.'FLOA'))THEN
        IF((PCPYLL(1).EQ.CPUMIN.AND.PCPYUL(1).EQ.CPUMIN).OR.
     1     (PCPXLL(1).EQ.CPUMIN.AND.PCPYUL(1).EQ.CPUMIN))THEN
          IFLGIN='YES'
          IFLGY='ON'
          IFLGX='ON'
        ENDIF
      ENDIF
C
C               **********************************************
C               **   STEP 9--                               **
C               **   GENERATE THE BINARY PLOT        PLOTS  **
C               **********************************************
C
      ISTEPN='9'
      IF(IBUGG3.EQ.'ON'.OR.ISUBRO.EQ.'BITA')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NARGT=NUMARG
C
      DO7020I=1,NARGT
        IHT(I)=IHARG(I)
        IH2T(I)=IHARG2(I)
        IARGTT(I)=IARGT(I)
        ARGT(I)=ARG(I)
 7020 CONTINUE
C
C     CHECK TO SEE IF A GROUP LABEL HAS BEEN DEFINED
C
      IGLCOL=-1
      IF(IBTAGN.NE.' ')THEN
        DO7810I=1,MAXGRP
          IF(IBTAGN(1:8).EQ.IGRPVN(I)(1:8))THEN
            IGLCOL=I
            GOTO7819
          ENDIF
 7810   CONTINUE
 7819   CONTINUE
      ENDIF
C
      IX=1
      IXLIST=1
      NPLOTS=IROWT*ICOLT
      IPLOT=0
      DO7200IRES=1,IROWT
      DO7100IFAC=1,ICOLT
C
C       RESTORE COMMAND LINE ARGUMENTS
C
        DO7105II=1,NARGT
          IHARG(II)=IHT(II)
          IHARG2(II)=IH2T(II)
          IARGT(II)=IARGTT(II)
          ARG(II)=ARGT(II)
 7105   CONTINUE
        ARG(10)=TEMP3(IRES)
        NUMARG=NARGT
C
        IPLOT=IPLOT+1
        IF(IFAC.EQ.1)THEN
          IHARG(1)='CORR'
          IHARG2(1)='ECT '
          IHARG(2)='MATC'
          IHARG2(2)='H   '
        ELSEIF(IFAC.EQ.2)THEN
          IHARG(1)='FALS'
          IHARG2(1)='E   '
          IHARG(2)='POSI'
          IHARG2(2)='TIVE'
        ELSEIF(IFAC.EQ.3)THEN
          IHARG(1)='FALS'
          IHARG2(1)='E   '
          IHARG(2)='NEGA'
          IHARG2(2)='TIVE'
        ENDIF
C
        IF(ISUBRO.EQ.'BITA' .OR. IBUGG2.EQ.'ON')THEN
          DO7706I=1,NUMARG
            WRITE(ICOUT,7707)I,IHARG(I),IHARG2(I)
 7707       FORMAT('I,IHARG(I),IHARG2(I) = ',I5,2X,A4,2X,A4)
            CALL DPWRST('XXX','BUG ')
 7706     CONTINUE
        ENDIF
C
        ICASPL='BITA'
        IROW=IRES
        ICOL=IFAC
        IF(IGLCOL.GT.0)THEN
          NCY1LA=0
          NCY2LA=0
        ENDIF
C
        CALL DPSPM1(ICASPL,IVARN1,IVARN2,ICOLL,
     1              IMPNR,IMPNC,IROW,ICOL,IRES,IX,IPLOT,
     1              NPLOTS,NUMVAR,
     1              ICHAP2,ILINP2,
     1              GY1MNS,GY1MXS,GY2MNS,GY2MXS,
     1              GX1MNS,GX1MXS,GX2MNS,GX2MXS,
     1              IY1MNS,IY1MXS,IY2MNS,IY2MXS,
     1              IX1MNS,IX1MXS,IX2MNS,IX2MXS,
     1              IX1TSV,IX2TSV,IY1TSV,IY2TSV,
     1              IX1ZSV,IX2ZSV,IY1ZSV,IY2ZSV,
     1              PX1LD2,PX2LD2,
     1              IY1LJ2,IY1LD2,PY1LD2,PY1LA2,
     1              IX1LT2,IX2LT2,IY1LT2,IY2LT2,
     1              NCX1L2,NCX2L2,NCY1L2,NCY2L2,
     1              PCPXLL,PCPXUL,PCPYLL,PCPYUL,IXLIST,
     1              ICPLLA,ISPMLD,IPLOTT,ICPLFR,ICPLXA,ICPLYA,
     1              ISPMDI,ISPX1L,
     1              ISPMXT,ISPMXL,ISPMYT,ISPMYL,
     1              ICPLTD,PCPLTD,IVNMEX,
     1              IBUGG2,ISUBRO)
C
        IF(IRES.EQ.1)THEN
          IF(IFAC.EQ.1)THEN
            ITITTE(1)='C'
            ITITTE(2)='o'
            ITITTE(3)='r'
            ITITTE(4)='r'
            ITITTE(5)='e'
            ITITTE(6)='c'
            ITITTE(7)='t'
            ITITTE(8)=' '
            ITITTE(9)='M'
            ITITTE(10)='a'
            ITITTE(11)='t'
            ITITTE(12)='c'
            ITITTE(13)='h'
            NCTEMP=13
          ELSEIF(IFAC.EQ.2)THEN
            ITITTE(1)='F'
            ITITTE(2)='a'
            ITITTE(3)='l'
            ITITTE(4)='s'
            ITITTE(5)='e'
            ITITTE(6)=' '
            ITITTE(7)='P'
            ITITTE(8)='o'
            ITITTE(9)='s'
            ITITTE(10)='i'
            ITITTE(11)='t'
            ITITTE(12)='i'
            ITITTE(13)='v'
            ITITTE(14)='e'
            NCTEMP=14
          ELSEIF(IFAC.EQ.3)THEN
            ITITTE(1)='F'
            ITITTE(2)='a'
            ITITTE(3)='l'
            ITITTE(4)='s'
            ITITTE(5)='e'
            ITITTE(6)=' '
            ITITTE(7)='N'
            ITITTE(8)='e'
            ITITTE(9)='g'
            ITITTE(10)='a'
            ITITTE(11)='t'
            ITITTE(12)='i'
            ITITTE(13)='v'
            ITITTE(14)='e'
            NCTEMP=14
          ENDIF
        ELSE
          NCTEMP=0
          ITITTE(1)=' '
        ENDIF
        NCTITL=NCTEMP
C
C       IF A GROUP LABEL HAS BEEN SPECIFIED, SET Y1LABEL OR
C       Y2LABEL IF APPROPRIATE
C
        IF(IGLCOL.GT.0 .AND. IGLCOL.LE.MAXGRP)THEN
          IFLAG=0
          IF(ICPLYA.EQ.'LEFT')THEN
            IF(IFAC.EQ.1)IFLAG=1
          ELSEIF(ICPLYA.EQ.'RIGH')THEN
            IF(IFAC.EQ.3)IFLAG=2
          ELSEIF(ICPLYA.EQ.'ALTE')THEN
            IF(MOD(IRES,2).EQ.1)THEN
              IF(IFAC.EQ.1)IFLAG=1
            ELSEIF(MOD(IRES,2).EQ.0)THEN
              IF(IFAC.EQ.3)IFLAG=2
            ENDIF
          ENDIF
        ENDIF
        IF(IFLAG.EQ.1)THEN
CCCCC     DO7860I=1,24
          DO7860I=1,MAXGR2
            IY1LTE(I)=' '
            IY1LTE(I)(1:1)=IGRPLA(IRES,IGLCOL)(I:I)
            IF(IY1LTE(I).NE.' ')NCY1LA=I
 7860     CONTINUE
        ELSEIF(IFLAG.EQ.2)THEN
CCCCC     DO7870I=1,24
          DO7870I=1,MAXGR2
            IY2LTE(I)=' '
            IY2LTE(I)(1:1)=IGRPLA(IRES,IGLCOL)(I:I)
            IF(IY2LTE(I).NE.' ')NCY2LA=I
 7870     CONTINUE
        ELSE
          NCY1LA=0
          NCY2LA=0
        ENDIF
C
        CALL MAINGR(ANOPL1,ANOPL2,NPLOTV,NPLOTP,NS,ICASPL,
     1              MAXNPP,ISEED,IBOOSS,
     1              IX1TSV,IX2TSV,IY1TSV,IY2TSV,
     1              IX1ZSV,IX2ZSV,IY1ZSV,IY2ZSV,
     1              BARHEF,BARWEF,
     1              IRHSTG,IHSTCW,IHSTEB,IHSTOU,IASHWT,IHSTMC,IHSTOP,
     1              ICAPSW,IFORSW,IGUIFL,IERRFA,IFRALI,
     1              IAND1,IAND2,ICONT,NUMHPP,NUMVPP,MAXNXT,
     1              ISUBRO,IFOUND,IERROR)
C
        ICONT=IDCONT(1)
        IPOWE=IDPOWE(1)
        NUMHPP=IDNHPP(1)
        IMPARG=2
        CALL DPGRAP(Y,X,X3D,D,N,NPLOTP,ICASPL,ICONT,IPOWE,NUMHPP,
     1              XMATN,YMATN,XMITN,YMITN,
     1              ISQUAR,
     1              IVGMSW,IHGMSW,
     1              IHNAME,IHNAM2,IUSE,IN,IVALUE,VALUE,NUMNAM,
     1              IVSTAR,IVSTOP,IFUNC,NUMCHF,IREPCH,
     1              YPLOT,XPLOT,X2PLOT,TAGPLO,
     1              IMPSW,IMPNR,IMPNC,IMPCO,IMPCO9,
     1              IMPARG,
     1              PMXMIN,PMXMAX,PMYMIN,PMYMAX,
     1              MAXCOL,
     1              DSIZE,DSYMB,DCOLOR,DFILL,
     1              ICAPSW,
     1              IBUGUG,IBUGU2,IBUGU3,IBUGU4,ISUBRO,
     1              IERROR)
        IF(IERROR.EQ.'NO')IAND1=IAND2
        IF(IERROR.EQ.'YES')GOTO7199
C
 7199   CONTINUE
C
        PX1LDS=PX1LD2
        IF(IYAXIS.EQ.0.AND.IFLGIN.EQ.'NO')THEN
          GY1MNS=GY1MIN
          GY1MXS=GY1MAX
          GY2MNS=GY2MIN
          GY2MXS=GY2MAX
          IY1MNS=IY1MIN
          IY1MXS=IY1MAX
          IY2MNS=IY2MIN
          IY2MXS=IY2MAX
        ENDIF
        IF(IXAXIS.EQ.0.AND.IFLGIN.EQ.'NO')THEN
          GX1MNS=GX1MIN
          GX1MXS=GX1MAX
          GX2MNS=GX2MIN
          GX2MXS=GX2MAX
          IX1MNS=IX1MIN
          IX1MXS=IX1MAX
          IX2MNS=IX2MIN
          IX2MXS=IX2MAX
        ENDIF
        PX1ZDS=PX1ZD2
        PX2ZDS=PX2ZD2
        PY1ZDS=PY1ZD2
        PY2ZDS=PY2ZD2
        IF(IEMPTY.EQ.'YES')THEN
          DO7107I=1,MAXSUB
            ISUBSW(I)=ISU2SW(I)
 7107     CONTINUE
        ENDIF
        DO7108I=1,100
            ICHAPA(I)=ICHAP2(I)
            ILINPA(I)=ILINP2(I)
            ISPISW(I)=ISPIS2(I)
            IBARSW(I)=IBARS2(I)
 7108     CONTINUE
C
 7100 CONTINUE
 7200 CONTINUE
C
      IF(IYAXIS.EQ.1)THEN
        GY1MIN=GY1MNS
        GY1MAX=GY1MXS
        GY2MIN=GY2MNS
        GY2MAX=GY2MXS
        IY1MIN=IY1MNS
        IY1MAX=IY1MXS
        IY2MIN=IY2MNS
        IY2MAX=IY2MXS
      ENDIF
      IF(IXAXIS.EQ.1)THEN
        GX1MIN=GX1MNS
        GX1MAX=GX1MXS
        GX2MIN=GX2MNS
        GX2MAX=GX2MXS
        IX1MIN=IX1MNS
        IX1MAX=IX1MXS
        IX2MIN=IX2MNS
        IX2MAX=IX2MXS
      ENDIF
      GOTO8000
C
C               **************************************************
C               **   STEP 28--                                  **
C               **   REINSTATE INITIAL SETTINGS                 **
C               **************************************************
C
 8000 CONTINUE
      ISTEPN='28'
      IF(IBUGG3.EQ.'ON'.OR.ISUBRO.EQ.'BITA')THEN
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,8807)IMANUF,NUMDEV,IDMANU(1)
 8807   FORMAT('IMANUF,NUMDEV,IDMANU(1) = ',A4,I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      PWXMIN=PWXMN2
      PWXMAX=PWXMX2
      PWYMIN=PWYMN2
      PWYMAX=PWYMX2
      PXMIN=PXMN2
      PXMAX=PXMX2
      PYMIN=PYMN2
      PYMAX=PYMX2
C
      DO8820I=1,100
        ICHAPA(I)=ICHAP2(I)
        ILINPA(I)=ILINP2(I)
        IBARSW(I)=IBARS2(I)
        ISPISW(I)=ISPIS2(I)
 8820 CONTINUE
C
CCCCC IMPSW=IMPSW3
      IMPSW='OFF'
      IMPCO=1
      IMPNR=IMPNR2
      IMPNC=IMPNC2
C
      IERASW='ON'
      IX1TSW=IX1TSV
      IX2TSW=IX2TSV
      IY1TSW=IY1TSV
      IY2TSW=IY2TSV
      IX1ZSW=IX1ZSV
      IX2ZSW=IX2ZSV
      IY1ZSW=IY1ZSV
      IY2ZSW=IY2ZSV
      GY1MIN=GY1MNS
      GY1MAX=GY1MXS
      GY2MIN=GY2MNS
      GY2MAX=GY2MXS
      GX1MIN=GX1MNS
      GX1MAX=GX1MXS
      GX2MIN=GX2MNS
      GX2MAX=GX2MXS
      IY1MIN=IY1MNS
      IY1MAX=IY1MXS
      IY2MIN=IY2MNS
      IY2MAX=IY2MXS
      IX1MIN=IX1MNS
      IX1MAX=IX1MXS
      IX2MIN=IX2MNS
      IX2MAX=IX2MXS
      PX1ZDS=PX1ZD2
      PX2ZDS=PX2ZD2
      PY1ZDS=PY1ZD2
      PY2ZDS=PY2ZD2
      ISORSW=ISORS2
      IPPTBI=IPPTB2
C
      ICPLFR=ICPLFZ
C
      DO8500I=1,MAXCH
        IX1LTE(I)=IX1LT2(I)
        IX2LTE(I)=IX2LT2(I)
        IY1LTE(I)=IY1LT2(I)
        IY2LTE(I)=IY2LT2(I)
 8500 CONTINUE
      NCX1LA=NCX1L2
      NCX2LA=NCX2L2
      NCY1LA=NCY1L2
      NCY2LA=NCY2L2
C
      IFEEDB=IFEED9
C
      DO8809I=1,MAXCH
        ITITTE(I)=ITITSV(I)
 8809 CONTINUE
      NCTITL=NCTITS
      PTITDS=PTITDZ
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLAG1,IFLAG2,IFLAG3,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGG3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
      GOTO9000
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGG2.EQ.'ON' .OR. ISUBRO.EQ.'BITA')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBITA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IFOUND,IERROR
 9012   FORMAT('IFOUND,IERROR = ',A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NPLOTV,NPLOTP,NS,ICASPL,IAND1,IAND2
 9013   FORMAT('NPLOTV,NPLOTP,NS,ICASPL,IAND1,IAND2 = ',
     1         I8,I8,I8,2X,A4,2X,A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9014)NUMARG
 9014   FORMAT('NUMARG = ',I8)
        CALL DPWRST('XXX','BUG ')
        IF(NUMARG.GT.0)THEN
          DO9021I=1,NUMARG
            WRITE(ICOUT,9022)I,IHARG(I),IARGT(I)
 9022       FORMAT('I,IHARG(I),IARGT(I) = ',I8,2X,A4,2X,A4)
            CALL DPWRST('XXX','BUG ')
 9021     CONTINUE
        ENDIF
      ENDIF
C
      RETURN
      END
