 9012   FORMAT('ICASPL,ISUBRO,BARHEF,BARWEF = ',2(A4,2X),2G15.7)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)N,NDIST,N2,N3,IERROR
 9013   FORMAT('N,NDIST,N2,N3,IERROR = ',4I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
