      SUBROUTINE DPBPCO(IHARG,IARG,NUMARG,IDEBPC,ICASCL,MAXBAR,IRGBMX,
     1                  IBAPCO,IBAPC2,
     1                  IBUGP2,ISUBRO,IFOUND,IERROR)
C
C     PURPOSE--DEFINE THE BAR PATTERN COLORS = THE COLORS
C              OF THE LINES MAKING UP A PATTERN WITHIN A BAR.
C              THESE ARE LOCATED IN THE VECTOR IBAPCO(.).
C     INPUT  ARGUMENTS--IHARG  (A  CHARACTER VECTOR)
C                     --NUMARG
C                     --IDEBPC
C                     --MAXBAR
C                     --IBUGP2 ('ON' OR 'OFF' )
C     OUTPUT ARGUMENTS--IBAPCO (A CHARACTER VECTOR)
C                     --IFOUND ('YES' OR 'NO' )
C                     --IERROR ('YES' OR 'NO' )
C     WRITTEN BY--JAMES J. FILLIBEN
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2855
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--82/7
C     ORIGINAL VERSION--DECEMBER  1983.
C     UPDATED         --OCTOBER   2020. SUPPORT FOR RGB COLOR
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IHARG
      CHARACTER*4 IDEBPC
      CHARACTER*4 ICASCL
      CHARACTER*4 IBAPCO
      CHARACTER*4 IBUGP2
      CHARACTER*4 ISUBRO
      CHARACTER*4 IFOUND
      CHARACTER*4 IERROR
C
      CHARACTER*4 IHOLD1
      CHARACTER*4 IHOLD2
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      DIMENSION IHARG(*)
      DIMENSION IARG(*)
      DIMENSION IBAPCO(*)
      DIMENSION IBAPC2(MAXBAR,3)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      IFOUND='NO'
      IERROR='NO'
      ISUBN1='DPBP'
      ISUBN2='CO  '
      IHOLD1='-999'
      IHOLD2='-999'
C
      NUMBAR=0
      JHOLD1=-1
      JHOLD2=-1
      JHOLD3=-1
C
      IF(IBUGP2.EQ.'ON' .OR. ISUBRO.EQ.'BPCO')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPBPCO--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)IBUGP2,ISUBRO,IFOUND,IERROR
   52   FORMAT('IBUGP2,ISUBRO,IFOUND,IERROR = ',3(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,53)MAXBAR,NUMBAR,NUMARG,IRGBMX
   53   FORMAT('MAXBAR,NUMBAR,NUMARG,IRGBMX = ',4I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,54)IHOLD1,IHOLD2,IDEBBC
   54   FORMAT('IHOLD1,IHOLD2,IDEBBC = ',2(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
        DO65I=1,NUMARG
          WRITE(ICOUT,66)I,IARG(I),IHARG(I)
   66     FORMAT('I,IARG(I),IHARG(I) = ',2I8,2X,A4)
          CALL DPWRST('XXX','BUG ')
   65   CONTINUE
        DO75I=1,10
          WRITE(ICOUT,76)I,IBAPCO(I),
     1                   IBAPC2(I,1),IBAPC2(I,2),IBAPC2(I,3)
   76     FORMAT('I,IBAPCO(I),IBAPC2(I,1),IBAPC2(I,2),IBAPC2(I,3) = ',
     1           I8,2X,A4,2X,3I5)
          CALL DPWRST('XXX','BUG ')
   75   CONTINUE
      ENDIF
C
C               **************************************
C               **  STEP 1--                        **
C               **  BRANCH TO THE APPROPRIATE CASE  **
C               **************************************
C
      ISTEPN='1'
      IF(IBUGP2.EQ.'ON' .OR. ISUBRO.EQ.'BPCO')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.LE.1)GOTO9000
      IF(ICASCL.EQ.'RGB ')GOTO2000
C
      IF(NUMARG.EQ.2)THEN
        GOTO1200
      ELSEIF(NUMARG.EQ.3)THEN
        IF(IHARG(3).EQ.'ALL')THEN
          IHOLD1='    '
          GOTO1300
        ENDIF
        GOTO1200
      ELSEIF(NUMARG.EQ.4)THEN
        IF(IHARG(3).EQ.'ALL')THEN
          IHOLD1=IHARG(4)
          GOTO1300
        ELSEIF(IHARG(4).EQ.'ALL')THEN
          IHOLD1=IHARG(3)
          GOTO1300
        ENDIF
        GOTO1200
      ELSE
        GOTO1200
      ENDIF
C
C               *************************************************
C               **  STEP 2--                                   **
C               **  TREAT THE SINGLE      SPECIFICATION  CASE  **
C               *************************************************
C
 1200 CONTINUE
C
      ISTEPN='2'
      IF(IBUGP2.EQ.'ON' .OR. ISUBRO.EQ.'BPCO')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.LE.2)THEN
        NUMBAR=1
        IBAPCO(1)=IDEBPC
      ELSE
        NUMBAR=NUMARG-2
        IF(NUMBAR.GT.MAXBAR)NUMBAR=MAXBAR
        DO1225I=1,NUMBAR
          J=I+2
          IHOLD1=IHARG(J)
          IHOLD2=IHOLD1
          IF(IHOLD1.EQ.'ON')IHOLD2=IDEBPC
          IF(IHOLD1.EQ.'OFF')IHOLD2=IDEBPC
          IF(IHOLD1.EQ.'AUTO')IHOLD2=IDEBPC
          IF(IHOLD1.EQ.'DEFA')IHOLD2=IDEBPC
          IBAPCO(I)=IHOLD2
 1225   CONTINUE
      ENDIF
C
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        DO1278I=1,NUMBAR
          WRITE(ICOUT,1276)I,IBAPCO(I)
 1276     FORMAT('THE COLOR OF BAR PATTERN ',I6,
     1           ' HAS JUST BEEN SET TO ',A4)
          CALL DPWRST('XXX','BUG ')
 1278   CONTINUE
      ENDIF
C
      IFOUND='YES'
      GOTO9000
C
C               **************************
C               **  STEP 3--            **
C               **  TREAT THE ALL CASE  **
C               **************************
C
 1300 CONTINUE
      ISTEPN='3'
      IF(IBUGP2.EQ.'ON' .OR. ISUBRO.EQ.'BPCO')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NUMBAR=MAXBAR
      IHOLD2=IHOLD1
      IF(IHOLD1.EQ.'ON')IHOLD2=IDEBPC
      IF(IHOLD1.EQ.'OFF')IHOLD2=IDEBPC
      IF(IHOLD1.EQ.'AUTO')IHOLD2=IDEBPC
      IF(IHOLD1.EQ.'DEFA')IHOLD2=IDEBPC
      DO1315I=1,NUMBAR
        IBAPCO(I)=IHOLD2
 1315 CONTINUE
C
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        I=1
        WRITE(ICOUT,1316)IBAPCO(I)
 1316   FORMAT('THE COLOR OF ALL BAR PATTERNS',
     1         ' HAS JUST BEEN SET TO ',A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IFOUND='YES'
      GOTO9000
C
 2000 CONTINUE
C
      ISTEPN='4'
      IF(IBUGP2.EQ.'ON' .OR. ISUBRO.EQ.'BPCO')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      JHOLD1=-1
      JHOLD2=-1
      JHOLD3=-1
      NUMBAR=MAXBAR
      IF(NUMARG.EQ.2)THEN
        GOTO2200
      ELSEIF(NUMARG.EQ.3 .AND. IHARG(3).EQ.'ALL')THEN
        GOTO2200
      ELSEIF(NUMARG.EQ.5)THEN
        IF(IHARG(5).EQ.'ALL')THEN
          JHOLD1=IARG(2)
          JHOLD2=IARG(3)
          JHOLD3=IARG(4)
          IF(JHOLD1.LT.0 .OR. JHOLD1.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD2.LT.0 .OR. JHOLD2.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD3.LT.0 .OR. JHOLD3.GT.IRGBMX)JHOLD1=-1
          GOTO2300
        ELSEIF(IHARG(2).EQ.'ALL')THEN
          JHOLD1=IARG(3)
          JHOLD2=IARG(4)
          JHOLD3=IARG(5)
          IF(JHOLD1.LT.0 .OR. JHOLD1.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD2.LT.0 .OR. JHOLD2.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD3.LT.0 .OR. JHOLD3.GT.IRGBMX)JHOLD1=-1
          GOTO2300
        ENDIF
        GOTO2200
      ELSEIF(NUMARG.EQ.6)THEN
        IF(IHARG(6).EQ.'ALL')THEN
          JHOLD1=IARG(3)
          JHOLD2=IARG(4)
          JHOLD3=IARG(5)
          IF(JHOLD1.LT.0 .OR. JHOLD1.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD2.LT.0 .OR. JHOLD2.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD3.LT.0 .OR. JHOLD3.GT.IRGBMX)JHOLD1=-1
          GOTO2300
        ELSEIF(IHARG(3).EQ.'ALL')THEN
          JHOLD1=IARG(4)
          JHOLD2=IARG(5)
          JHOLD3=IARG(6)
          IF(JHOLD1.LT.0 .OR. JHOLD1.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD2.LT.0 .OR. JHOLD2.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD3.LT.0 .OR. JHOLD3.GT.IRGBMX)JHOLD1=-1
          GOTO2300
        ENDIF
        GOTO2200
      ELSE
        GOTO2200
      ENDIF
C
C               *************************************************
C               **  STEP 2--                                   **
C               **  TREAT THE SINGLE      SPECIFICATION  CASE  **
C               *************************************************
C
 2200 CONTINUE
      ISTEPN='5'
      IF(IBUGP2.EQ.'ON' .OR. ISUBRO.EQ.'BPCO')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(NUMARG.LE.2)THEN
        NUMBAR=1
        IBAPC2(1,1)=-1
        IBAPC2(1,2)=-1
        IBAPC2(1,3)=-1
      ELSE
        NTEMP=NUMARG-2
        NUMBAR=NTEMP/3
        IF(NUMBAR.GT.MAXBAR)NUMBAR=MAXBAR
        IF(NUMBAR.LT.1)THEN
          IBAPC2(1,1)=-1
          IBAPC2(1,2)=-1
          IBAPC2(1,3)=-1
          GOTO2229
        ELSEIF(NUMBAR.GT.MAXBAR)THEN
          NUMBAR=MAXBAR
        ENDIF
        IF(IHARG(1).EQ.'COLO')THEN
          IADD=2
        ELSE
          IADD=3
        ENDIF
        DO2225I=1,NUMBAR
          J1=(I-1)*3+IADD
          J2=J1+1
          J3=J1+2
          JHOLD1=IARG(J1)
          JHOLD2=IARG(J2)
          JHOLD3=IARG(J3)
          IF(JHOLD1.LT.0 .OR. JHOLD1.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD2.LT.0 .OR. JHOLD2.GT.IRGBMX)JHOLD1=-1
          IF(JHOLD3.LT.0 .OR. JHOLD3.GT.IRGBMX)JHOLD1=-1
          IBAPC2(I,1)=JHOLD1
          IBAPC2(I,2)=JHOLD2
          IBAPC2(I,3)=JHOLD3
 2225   CONTINUE
 2229   CONTINUE
      ENDIF
C
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        DO2278I=1,NUMBAR
          WRITE(ICOUT,2276)I,IBAPC2(I,1),IBAPC2(I,2),IBAPC2(I,3)
 2276     FORMAT('THE RGB COLOR OF BAR PATTERN ',I6,
     1          ' HAS JUST BEEN SET TO ',3I5)
          CALL DPWRST('XXX','BUG ')
 2278   CONTINUE
      ENDIF
      IFOUND='YES'
      GOTO9000
C
C               **************************
C               **  STEP 3--            **
C               **  TREAT THE ALL CASE  **
C               **************************
C
 2300 CONTINUE
      ISTEPN='3'
      IF(IBUGP2.EQ.'ON' .OR. ISUBRO.EQ.'BPCO')
     1   CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NUMBAR=MAXBAR
      DO2315I=1,NUMBAR
        IBAPC2(I,1)=JHOLD1
        IBAPC2(I,2)=JHOLD2
        IBAPC2(I,3)=JHOLD3
 2315 CONTINUE
C
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        I=1
        WRITE(ICOUT,2316)IBAPC2(I,1),IBAPC2(I,2),IBAPC2(I,3)
 2316   FORMAT('THE RGB COLOR OF ALL BAR PATTERNS HAS JUST BEEN SET ',
     1         'TO ',3I5)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IFOUND='YES'
      GOTO9000
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGP2.EQ.'ON' .OR. ISUBRO.EQ.'BPCO')THEN
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPBPCO--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IFOUND,IERROR,NUMBAR
 9012   FORMAT('IFOUND,IERROR,NUMBAR = ',2(A4,2X),I6)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9014)IHOLD1,IHOLD2,JHOLD1,JHOLD2,JHOLD3
 9014   FORMAT('IHOLD1,IHOLD2,JHOLD1,JHOLD2,JHOLD3 = ',2(A4,2X),3I5)
        CALL DPWRST('XXX','BUG ')
        DO9035I=1,10
          WRITE(ICOUT,76)I,IBAPCO(I),
     1                   IBAPC2(I,1),IBAPC2(I,2),IBAPC2(I,3)
          CALL DPWRST('XXX','BUG ')
 9035   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPBPLI(IHARG,IHARG2,NUMARG,IDEBPL,MAXBAR,IBAPLI,
