      IF(IBUGP2.EQ.'ON')CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NUMBAR=MAXBAR
      IHOLD2=IHOLD1
      IF(IHOLD1.EQ.'ON')IHOLD2='SOLI'
      IF(IHOLD1.EQ.'OFF')IHOLD2='    '
      IF(IHOLD1.EQ.'AUTO')IHOLD2=IDEBPL
      IF(IHOLD1.EQ.'DEFA')IHOLD2=IDEBPL
      DO1315I=1,NUMBAR
      IBAPLI(I)=IHOLD2
 1315 CONTINUE
